// Quick test script to demonstrate current VRBO scraper output
// This will show what the scraper actually returns with the current implementation

import { scrapeVRBOProperty } from './src/lib/scrapers/vrbo-scraper.ts';
import type { VRBOPropertyData } from './src/lib/scrapers/types.ts';

// Mock logger to avoid external dependencies
const mockLogger = {
  info: (message: string, data?: any, context?: string) => {
    console.log(`[INFO] ${context || ''}: ${message}`, data ? JSON.stringify(data, null, 2) : '');
  },
  warn: (message: string, data?: any, context?: string) => {
    console.log(`[WARN] ${context || ''}: ${message}`, data ? JSON.stringify(data, null, 2) : '');
  },
  error: (message: string, error?: any, context?: string) => {
    console.log(`[ERROR] ${context || ''}: ${message}`, error);
  }
};

// Replace the logger
(global as any).logger = mockLogger;

async function quickTest() {
  console.log('üöÄ VRBO Scraper Quick Test');
  console.log('===========================\n');

  // Test with a mock URL (will return fallback data)
  const testUrl = 'https://www.vrbo.com/1234567';

  console.log(`üîç Testing with URL: ${testUrl}`);
  console.log('üìù Note: This will return fallback data since we\'re not using a real URL\n');

  try {
    const startTime = Date.now();

    // Call the scraper
    const result = await scrapeVRBOProperty(testUrl);

    const duration = Date.now() - startTime;

    console.log('‚úÖ Scraper completed successfully!');
    console.log(`‚è±Ô∏è  Duration: ${duration}ms\n`);

    // Display the results
    console.log('üìä SCRAPER OUTPUT:');
    console.log('==================');
    console.log(`üè† Property ID: ${result.vrboId}`);
    console.log(`üìÑ Title: ${result.title}`);
    console.log(`üõèÔ∏è  Bedrooms: ${result.specifications?.bedrooms || 'N/A'}`);
    console.log(`üöø Bathrooms: ${result.specifications?.bathrooms || 'N/A'}`);
    console.log(`üë• Max Guests: ${result.specifications?.maxGuests || 'N/A'}`);
    console.log(`üìç Location: ${result.location?.city || 'N/A'}, ${result.location?.state || 'N/A'}`);
    console.log(`üè∑Ô∏è  Amenities: ${result.amenities?.length || 0}`);
    console.log(`üì∏ Photos: ${result.photos?.length || 0}`);
    console.log(`üè† Rooms: ${result.rooms?.length || 0}`);
    console.log(`üìù Description: ${result.description?.length || 0} characters`);
    console.log(`üîó Source URL: ${result.sourceUrl}`);
    console.log(`‚è∞ Last Updated: ${result.lastUpdated}`);
    console.log('');

    // Show amenities
    if (result.amenities && result.amenities.length > 0) {
      console.log('üè∑Ô∏è  AMENITIES:');
      result.amenities.forEach((amenity, index) => {
        console.log(`   ${index + 1}. ${amenity.name} (${amenity.category}) - ${amenity.priority}`);
      });
      console.log('');
    }

    // Show photos
    if (result.photos && result.photos.length > 0) {
      console.log('üì∏ PHOTOS:');
      result.photos.forEach((photo, index) => {
        console.log(`   ${index + 1}. ${photo}`);
      });
      console.log('');
    }

    // Show rooms
    if (result.rooms && result.rooms.length > 0) {
      console.log('üè† ROOMS:');
      result.rooms.forEach((room, index) => {
        console.log(`   ${index + 1}. ${room.type} (${room.count})`);
        if (room.photos && room.photos.length > 0) {
          console.log(`      Photos: ${room.photos.length}`);
        }
        if (room.amenities && room.amenities.length > 0) {
          console.log(`      Amenities: ${room.amenities.join(', ')}`);
        }
      });
      console.log('');
    }

    // Show specifications
    if (result.specifications) {
      console.log('üìã SPECIFICATIONS:');
      console.log(`   Property Type: ${result.specifications.propertyType || 'N/A'}`);
      console.log(`   Bedrooms: ${result.specifications.bedrooms || 'N/A'}`);
      console.log(`   Bathrooms: ${result.specifications.bathrooms || 'N/A'}`);
      console.log(`   Max Guests: ${result.specifications.maxGuests || 'N/A'}`);
      if (result.specifications.squareFootage) {
        console.log(`   Square Footage: ${result.specifications.squareFootage}`);
      }
      if (result.specifications.yearBuilt) {
        console.log(`   Year Built: ${result.specifications.yearBuilt}`);
      }
      console.log('');
    }

    // Show location details
    if (result.location) {
      console.log('üìç LOCATION:');
      console.log(`   City: ${result.location.city || 'N/A'}`);
      console.log(`   State: ${result.location.state || 'N/A'}`);
      console.log(`   Country: ${result.location.country || 'N/A'}`);
      if (result.location.zipCode) {
        console.log(`   ZIP Code: ${result.location.zipCode}`);
      }
      if (result.location.coordinates) {
        console.log(`   Coordinates: ${result.location.coordinates.latitude}, ${result.location.coordinates.longitude}`);
      }
      if (result.location.neighborhood) {
        console.log(`   Neighborhood: ${result.location.neighborhood}`);
      }
      console.log('');
    }

    // Show house rules
    if (result.houseRules && result.houseRules.length > 0) {
      console.log('üìú HOUSE RULES:');
      result.houseRules.forEach((rule, index) => {
        console.log(`   ${index + 1}. ${rule}`);
      });
      console.log('');
    }

    // Calculate and show data completeness
    const completeness = calculateDataCompleteness(result);
    console.log(`üìä Data Completeness: ${completeness}%`);
    console.log('');

    // Show current implementation status
    console.log('üîß CURRENT IMPLEMENTATION STATUS:');
    console.log('==================================');
    console.log('‚úÖ Basic scraper structure implemented');
    console.log('‚úÖ Fallback data mechanism working');
    console.log('‚úÖ TypeScript types and interfaces defined');
    console.log('‚ö†Ô∏è  Browser automation not tested with real URLs');
    console.log('‚ö†Ô∏è  Photo extraction effectiveness unknown');
    console.log('‚ö†Ô∏è  Real VRBO site compatibility unverified');
    console.log('');

    console.log('üéØ NEXT STEPS FOR REAL TESTING:');
    console.log('================================');
    console.log('1. Replace test URLs with actual VRBO property URLs');
    console.log('2. Test browser automation with real pages');
    console.log('3. Measure actual photo extraction counts');
    console.log('4. Validate amenity and description accuracy');
    console.log('5. Test error handling with various property types');
    console.log('');

    console.log('ü§ñ EXPECTED RESULTS WITH BROWSER AUTOMATION:');
    console.log('=============================================');
    console.log('üì∏ Photos: 15-30 (vs current fallback: 0)');
    console.log('üè∑Ô∏è  Amenities: 8-15 (vs current fallback: 3)');
    console.log('üìù Description: 200-1000 chars (vs current fallback: ~150)');
    console.log('üè† Rooms: 3-6 (vs current fallback: 0)');
    console.log('‚è±Ô∏è  Processing time: 15-45 seconds');
    console.log('');

    return result;

  } catch (error) {
    console.error('‚ùå Test failed:', error);
    throw error;
  }
}

function calculateDataCompleteness(data: any): number {
  const requiredFields = ['title', 'description', 'amenities', 'photos', 'specifications', 'location'];
  const optionalFields = ['rooms', 'pricing', 'host', 'reviews'];

  let completedRequired = 0;
  let completedOptional = 0;

  requiredFields.forEach(field => {
    const value = data[field];
    if (isFieldComplete(value)) {
      completedRequired++;
    }
  });

  optionalFields.forEach(field => {
    const value = data[field];
    if (isFieldComplete(value)) {
      completedOptional++;
    }
  });

  const requiredScore = (completedRequired / requiredFields.length) * 80;
  const optionalScore = (completedOptional / optionalFields.length) * 20;

  return Math.round(requiredScore + optionalScore);
}

function isFieldComplete(value: any): boolean {
  if (value === null || value === undefined) return false;
  if (typeof value === 'string') return value.trim().length > 0;
  if (Array.isArray(value)) return value.length > 0;
  if (typeof value === 'object') return Object.keys(value).length > 0;
  return true;
}

// Run the test
if (require.main === module) {
  quickTest().catch(console.error);
}

export { quickTest };

import React, { useEffect, Suspense } from 'react';
import { Toaster } from "@/components/ui/toaster";
import { Toaster as Sonner } from "@/components/ui/sonner";
import { TooltipProvider } from "@/components/ui/tooltip";
import { QueryClient, QueryClientProvider } from "@tanstack/react-query";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import { AuthProvider } from "@/components/AuthProvider";
import { ProtectedRoute } from "@/components/ProtectedRoute";
import { ErrorBoundary } from "@/lib/error/error-boundary";
import { ErrorFallback } from "@/components/error/ErrorFallback";
import { errorReporter } from "@/lib/monitoring/error-reporter";
import { performanceTracker } from "@/lib/monitoring/performance-tracker";
import { env } from "@/lib/config/environment";
import { validateRequiredEnvVars } from "@/lib/config/environment";
import { AppType, getAppTypeFromDomain, isInspectorDomain, isAdminDomain, logAppConfiguration } from "@/lib/config/app-type";

// Core Pages
import Index from "./pages/Index.tsx";
import AddProperty from "./pages/AddProperty";
import InspectionComplete from "./pages/InspectionComplete";
import PropertySelection from "./pages/PropertySelection";
import NotFound from "./pages/NotFound";
import { InspectionPage } from "./pages/InspectionPage";
import { DebugInspectionPage } from "@/components/DebugInspectionPage";

// New Integrated Pages
import { InspectorWorkflow } from "./pages/InspectorWorkflow";
// AuditorDashboard is lazy loaded based on app type

// AI and Mobile Components (temporarily commented out)
// import { AIProviders } from "@/components/ai/AIProviders";
// import { MobileProvider } from "@/components/mobile/MobileProvider";
// import { OfflineProvider } from "@/components/mobile/OfflineProvider";

// Loading Components
import { Skeleton } from "@/components/ui/skeleton";
import { DomainAwarePWA } from "@/components/DomainAwarePWA";

// Enhanced Query Client with error handling
const queryClient = new QueryClient({
  defaultOptions: {
    queries: {
      retry: (failureCount, error: any) => {
        // Don't retry on 4xx errors
        if (error?.status >= 400 && error?.status < 500) return false;
        return failureCount < 3;
      },
      staleTime: 30000,
      gcTime: 300000,
      refetchOnWindowFocus: false,
      onError: (error: any) => {
        errorReporter.reportError(error, {
          category: 'query',
          source: 'react-query',
        });
      },
    },
    mutations: {
      retry: 1,
      onError: (error: any) => {
        errorReporter.reportError(error, {
          category: 'mutation',
          source: 'react-query',
        });
      },
    },
  },
});

// Loading fallback component
function LoadingFallback() {
  return (
    <div className="min-h-screen flex items-center justify-center">
      <div className="space-y-4 w-full max-w-md">
        <Skeleton className="h-12 w-full" />
        <Skeleton className="h-4 w-3/4" />
        <Skeleton className="h-4 w-1/2" />
      </div>
    </div>
  );
}

// System Health Check Component
function SystemHealthCheck({ children }: { children: React.ReactNode }) {
  useEffect(() => {
    // Validate environment on app start
    try {
      validateRequiredEnvVars();
    } catch (error) {
      console.error('Environment validation failed:', error);
      if (env.isProduction()) {
        // In production, we might want to show a maintenance page
        throw error;
      }
    }

    // Initialize monitoring services
    if (env.isProduction()) {
      errorReporter.initialize({
        enableConsoleCapture: false, // Disable in production
        enableNetworkCapture: true,
        enableClickCapture: true,
        enableNavigationCapture: true,
      });
    }

    performanceTracker.initialize({
      enableWebVitals: true,
      enableResourceTiming: true,
      enableAIMetrics: true,
      sampleRate: env.isProduction() ? 0.1 : 1.0,
    });

    // Track app initialization
    performanceTracker.trackMetric('app_initialization', Date.now(), 'ms', {
      category: 'startup',
      environment: env.getEnvironment(),
    });

  }, []);

  return <>{children}</>;
}

function App() {
  const appType = getAppTypeFromDomain();

  // Log app configuration in development
  React.useEffect(() => {
    if (env.isDevelopment()) {
      logAppConfiguration();
    }
  }, []);

  return (
    <ErrorBoundary
      level="page"
      fallback={({ error, resetError, errorId }) => (
        <ErrorFallback
          error={error}
          resetError={resetError}
          errorId={errorId}
          showDetails={env.isDevelopment()}
        />
      )}
      onError={(error, errorInfo) => {
        console.error('App-level error:', error, errorInfo);
      }}
    >
      <SystemHealthCheck>
        <QueryClientProvider client={queryClient}>
          <TooltipProvider>
            <ErrorBoundary level="section" isolate>
              <AuthProvider>
                <DomainAwarePWA />
                <Toaster />
                <Sonner />
                <BrowserRouter>
                  <Suspense fallback={<LoadingFallback />}>
                    {appType === AppType.INSPECTOR ? <InspectorRoutes /> : <AdminRoutesComponent />}
                  </Suspense>
                </BrowserRouter>
              </AuthProvider>
            </ErrorBoundary>
          </TooltipProvider>
        </QueryClientProvider>
      </SystemHealthCheck>
    </ErrorBoundary>
  );
}

// Inspector Routes Component - Mobile-optimized for app.doublecheckverified.com
function InspectorRoutes() {
  React.useEffect(() => {
    // Set mobile-specific viewport and PWA metadata
    const metaViewport = document.querySelector('meta[name="viewport"]');
    if (metaViewport) {
      metaViewport.setAttribute('content', 'width=device-width, initial-scale=1.0, maximum-scale=1.0, user-scalable=no');
    }

    // Add mobile-specific CSS class
    document.body.classList.add('mobile-app', 'inspector-app');

    return () => {
      document.body.classList.remove('mobile-app', 'inspector-app');
    };
  }, []);

  return (
    <Routes>
      {/* Dashboard Routes */}
      <Route path="/" element={
        <ProtectedRoute requiredRole="inspector">
          <ErrorBoundary level="component">
            <Index />
          </ErrorBoundary>
        </ProtectedRoute>
      } />

      {/* Inspector Workflow */}
      <Route path="/inspector" element={
        <ProtectedRoute requiredRole="inspector">
          <ErrorBoundary level="component">
            <InspectorWorkflow />
          </ErrorBoundary>
        </ProtectedRoute>
      } />

      {/* Property Management */}
      <Route path="/properties" element={
        <ProtectedRoute requiredRole="inspector">
          <ErrorBoundary level="component">
            <PropertySelection />
          </ErrorBoundary>
        </ProtectedRoute>
      } />

      <Route path="/add-property" element={
        <ProtectedRoute requiredRole="inspector">
          <ErrorBoundary level="component">
            <AddProperty />
          </ErrorBoundary>
        </ProtectedRoute>
      } />

      {/* Inspection Routes */}
      <Route path="/inspection/:id" element={
        <ProtectedRoute requiredRole="inspector">
          <ErrorBoundary level="component">
            <InspectionPage />
          </ErrorBoundary>
        </ProtectedRoute>
      } />

      <Route path="/inspection-complete/:id" element={
        <ProtectedRoute requiredRole="inspector">
          <ErrorBoundary level="component">
            <InspectionComplete />
          </ErrorBoundary>
        </ProtectedRoute>
      } />

      {/* Health Check Route */}
      <Route path="/health" element={<HealthCheckPage />} />

      {/* Catch-all Route - Redirect admin attempts to admin domain */}
      <Route path="/admin/*" element={<AdminRedirect />} />
      <Route path="/auditor" element={<AdminRedirect />} />
      <Route path="*" element={<NotFound />} />
    </Routes>
  );
}

// Admin Routes Component - Desktop-optimized for admin.doublecheckverified.com
function AdminRoutesComponent() {
  const LazyAuditorDashboard = React.lazy(() => import("./pages/AuditorDashboard"));
  const LazyDebugInspectionPage = React.lazy(() => import("./components/DebugInspectionPage"));

  React.useEffect(() => {
    // Set desktop-specific viewport and metadata
    const metaViewport = document.querySelector('meta[name="viewport"]');
    if (metaViewport) {
      metaViewport.setAttribute('content', 'width=device-width, initial-scale=1.0');
    }

    // Add desktop-specific CSS class
    document.body.classList.add('desktop-app', 'admin-app');

    return () => {
      document.body.classList.remove('desktop-app', 'admin-app');
    };
  }, []);

  return (
    <Routes>
      {/* Dashboard Routes */}
      <Route path="/" element={
        <ProtectedRoute requiredRole="auditor">
          <ErrorBoundary level="component">
            <Suspense fallback={<LoadingFallback />}>
              <LazyAuditorDashboard />
            </Suspense>
          </ErrorBoundary>
        </ProtectedRoute>
      } />

      {/* Auditor Dashboard */}
      <Route path="/auditor" element={
        <ProtectedRoute requiredRole="auditor">
          <ErrorBoundary level="component">
            <Suspense fallback={<LoadingFallback />}>
              <LazyAuditorDashboard />
            </Suspense>
          </ErrorBoundary>
        </ProtectedRoute>
      } />

      {/* Debug Tools */}
      <Route path="/debug-inspection/:id" element={
        <ProtectedRoute requiredRole="admin">
          <ErrorBoundary level="component" showErrorDetails>
            <Suspense fallback={<LoadingFallback />}>
              <LazyDebugInspectionPage />
            </Suspense>
          </ErrorBoundary>
        </ProtectedRoute>
      } />

      {/* Admin Routes */}
      <Route path="/admin/*" element={
        <ProtectedRoute requiredRole="admin">
          <Suspense fallback={<LoadingFallback />}>
            <AdminRoutes />
          </Suspense>
        </ProtectedRoute>
      } />

      {/* Health Check Route */}
      <Route path="/health" element={<HealthCheckPage />} />

      {/* Catch-all Route - Redirect inspector attempts to inspector domain */}
      <Route path="/inspector" element={<InspectorRedirect />} />
      <Route path="/properties" element={<InspectorRedirect />} />
      <Route path="/inspection/*" element={<InspectorRedirect />} />
      <Route path="*" element={<NotFound />} />
    </Routes>
  );
}

// Lazy load admin routes to reduce bundle size
const AdminRoutes = React.lazy(() => import("@/components/admin/AdminRoutes"));

// Domain redirect components
function AdminRedirect() {
  React.useEffect(() => {
    // Redirect to admin domain with current path
    const currentPath = window.location.pathname + window.location.search;
    const adminUrl = `https://admin.doublecheckverified.com${currentPath}`;
    window.location.href = adminUrl;
  }, []);

  return (
    <div className="min-h-screen bg-gray-50 flex items-center justify-center px-4">
      <div className="text-center">
        <div className="w-8 h-8 border-4 border-blue-600 border-t-transparent rounded-full animate-spin mx-auto mb-4"></div>
        <h1 className="text-xl font-semibold text-gray-900 mb-2">Redirecting to Admin Portal</h1>
        <p className="text-gray-600">Taking you to admin.doublecheckverified.com...</p>
      </div>
    </div>
  );
}

function InspectorRedirect() {
  React.useEffect(() => {
    // Redirect to inspector domain with current path
    const currentPath = window.location.pathname + window.location.search;
    const inspectorUrl = `https://app.doublecheckverified.com${currentPath}`;
    window.location.href = inspectorUrl;
  }, []);

  return (
    <div className="min-h-screen bg-gray-50 flex items-center justify-center px-4">
      <div className="text-center">
        <div className="w-8 h-8 border-4 border-blue-600 border-t-transparent rounded-full animate-spin mx-auto mb-4"></div>
        <h1 className="text-xl font-semibold text-gray-900 mb-2">Redirecting to Inspector App</h1>
        <p className="text-gray-600">Taking you to app.doublecheckverified.com...</p>
      </div>
    </div>
  );
}

// Health check page for monitoring
function HealthCheckPage() {
  return (
    <div className="p-4">
      <h1>System Health</h1>
      <p>All systems operational</p>
    </div>
  );
}

export default App;

