// Helper functions for testing
function categorizeImageByUrl(url: string): string {
  const lowerUrl = url.toLowerCase();

  if (lowerUrl.includes('exterior') || lowerUrl.includes('outside')) return 'exterior';
  if (lowerUrl.includes('kitchen')) return 'kitchen';
  if (lowerUrl.includes('bedroom') || lowerUrl.includes('bed')) return 'bedroom';
  if (lowerUrl.includes('bathroom') || lowerUrl.includes('bath')) return 'bathroom';
  if (lowerUrl.includes('living') || lowerUrl.includes('lounge')) return 'living_area';
  if (lowerUrl.includes('pool') || lowerUrl.includes('deck') || lowerUrl.includes('patio')) return 'outdoor_space';
  if (lowerUrl.includes('view') || lowerUrl.includes('scenic')) return 'view';
  if (lowerUrl.includes('amenity')) return 'amenity';

  return 'interior';
}

function deduplicateImages(images: Array<{url: string, alt: string}>): Array<{url: string, alt: string}> {
  const seen = new Set<string>();
  const unique: Array<{url: string, alt: string}> = [];

  images.forEach(img => {
    const normalizedUrl = img.url.replace(/\?.*$/, '').toLowerCase();

    if (!seen.has(normalizedUrl)) {
      seen.add(normalizedUrl);
      unique.push(img);
    }
  });

  return unique;
}

function calculateDataCompleteness(data: any): number {
  let score = 0;

  // Base property data (40%)
  if (data.title) score += 10;
  if (data.description) score += 10;
  if (data.amenities && data.amenities.length > 0) score += 10;
  if (data.specifications?.bedrooms) score += 5;
  if (data.specifications?.bathrooms) score += 5;

  // Image extraction (50%)
  if (data.images && data.images.length > 0) score += 20;
  if (data.images && data.images.length >= 10) score += 10;
  if (data.images && data.images.length >= 20) score += 10;
  if (data.images && data.images.length >= 30) score += 10;

  // Additional completeness (10%)
  if (data.specifications?.maxGuests) score += 5;
  if (data.amenities && data.amenities.length >= 5) score += 5;

  return Math.round(Math.min(score, 100));
}

import React, { useContext } from 'react';
import { AuthContext, AuthContextType } from '@/contexts/AuthContext';
import { MobileAuthLoading } from '@/components/MobileAuthLoading';
import { useAuthState } from '@/hooks/useAuthState';
import { useAuthInitialization } from '@/hooks/useAuthInitialization';
import { useAuthStateListener } from '@/hooks/useAuthStateListener';

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (context === undefined) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

export const AuthProvider: React.FC<{ children: React.ReactNode }> = ({ children }) => {
  const authState = useAuthState();
  const { initializeAuth } = useAuthInitialization(authState);

  useAuthStateListener({
    ...authState,
    initializeAuth
  });

  const value: AuthContextType = {
    user: authState.user,
    userRole: authState.userRole,
    loading: authState.loading,
    error: authState.error,
    signIn: authState.handleSignIn,
    signUp: authState.handleSignUp,
    signOut: authState.handleSignOut,
    forceRefresh: authState.forceRefresh,
    clearSession: authState.handleClearSession,
    loadUserRole: authState.loadUserRole,
  };

  // Mobile-optimized loading screen
  if (authState.loading) {
    return <MobileAuthLoading onRefresh={() => {
      authState.handleClearSession();
      authState.forceRefresh();
    }} />;
  }

  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
};

import { useEffect, useState } from "react";
import { supabase } from "@/integrations/supabase/client";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { AlertCircle, CheckCircle, AlertTriangle } from "lucide-react";

interface AuditEntry {
  id: string;
  inspection_id: string;
  operation_type: string;
  items_affected: number;
  created_at: string;
  metadata?: any;
}

interface ChecklistDiagnosticsProps {
  inspectionId: string;
}

export const ChecklistDiagnostics = ({ inspectionId }: ChecklistDiagnosticsProps) => {
  const [auditEntries, setAuditEntries] = useState<AuditEntry[]>([]);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const fetchAuditData = async () => {
      try {
        const { data, error } = await supabase
          .from('checklist_operations_audit')
          .select('*')
          .eq('inspection_id', inspectionId)
          .order('created_at', { ascending: false });

        if (error) {
          console.error('Error fetching audit data:', error);
          return;
        }

        setAuditEntries(data || []);
      } catch (error) {
        console.error('Error in audit fetch:', error);
      } finally {
        setIsLoading(false);
      }
    };

    if (inspectionId) {
      fetchAuditData();
    }
  }, [inspectionId]);

  if (isLoading) {
    return <div>Loading diagnostics...</div>;
  }

  if (auditEntries.length === 0) {
    return null; // Don't show if no audit entries
  }

  const getIcon = (operationType: string) => {
    switch (operationType) {
      case 'populate':
        return <CheckCircle className="w-4 h-4 text-green-500" />;
      case 'duplicate_detected':
        return <AlertTriangle className="w-4 h-4 text-yellow-500" />;
      case 'cleanup':
        return <AlertCircle className="w-4 h-4 text-blue-500" />;
      default:
        return <AlertCircle className="w-4 h-4 text-gray-500" />;
    }
  };

  const getVariant = (operationType: string) => {
    switch (operationType) {
      case 'populate':
        return 'default';
      case 'duplicate_detected':
        return 'destructive';
      case 'cleanup':
        return 'secondary';
      default:
        return 'outline';
    }
  };

  return (
    <Card className="mb-4">
      <CardHeader className="pb-3">
        <CardTitle className="text-sm font-medium flex items-center gap-2">
          <AlertCircle className="w-4 h-4" />
          Checklist Operations Log
        </CardTitle>
      </CardHeader>
      <CardContent className="space-y-2">
        {auditEntries.map((entry) => (
          <div key={entry.id} className="flex items-center justify-between p-2 bg-gray-50 rounded-lg">
            <div className="flex items-center gap-2">
              {getIcon(entry.operation_type)}
              <Badge variant={getVariant(entry.operation_type) as any}>
                {entry.operation_type}
              </Badge>
              <span className="text-sm text-gray-600">
                {entry.items_affected} items
              </span>
            </div>
            <span className="text-xs text-gray-500">
              {new Date(entry.created_at).toLocaleTimeString()}
            </span>
          </div>
        ))}
      </CardContent>
    </Card>
  );
};

