import { useState, useMemo } from "react";
import { MobileOptimizedLayout } from "@/components/MobileOptimizedLayout";
import { InspectionProgressTracker } from "@/components/InspectionProgressTracker";
import { InspectionFilters, type SortOption, type SortDirection } from "@/components/InspectionFilters";
import { InspectionList } from "@/components/InspectionList";
import { InspectionCompleteButton } from "@/components/InspectionCompleteButton";
import { ChecklistItemType } from "@/types/inspection";
import { debugLogger } from "@/utils/debugLogger";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { InfoIcon } from "lucide-react";

interface InspectionContentProps {
  inspectionId: string;
  checklistItems: ChecklistItemType[];
  onRefetch: () => void;
  isRefetching?: boolean;
}

export const InspectionContent = ({ 
  inspectionId, 
  checklistItems, 
  onRefetch, 
  isRefetching = false 
}: InspectionContentProps) => {
  const [showCompleted, setShowCompleted] = useState(false);
  const [selectedCategory, setSelectedCategory] = useState<string | null>(null);
  const [searchQuery, setSearchQuery] = useState("");
  const [sortBy, setSortBy] = useState<SortOption>("order");
  const [sortDirection, setSortDirection] = useState<SortDirection>("asc");

  debugLogger.info('InspectionContent', 'Rendering with data', {
    inspectionId,
    itemCount: checklistItems.length,
    isRefetching
  });

  // Defensive programming - handle null/undefined data
  const safeChecklistItems = Array.isArray(checklistItems) ? checklistItems : [];

  // Sort and filter items
  const filteredAndSortedItems = useMemo(() => {
    let items = safeChecklistItems.filter(item => {
      if (!item || !item.id) return false; // Safety check

      // Filter by completion status
      const matchesCompletedFilter = showCompleted || (!item.status || item.status === null);

      // Filter by category
      const matchesCategoryFilter = !selectedCategory || item.category === selectedCategory;

      // Filter by search query
      const matchesSearch = !searchQuery.trim() || 
        item.title?.toLowerCase().includes(searchQuery.toLowerCase()) ||
        item.description?.toLowerCase().includes(searchQuery.toLowerCase()) ||
        item.category?.toLowerCase().includes(searchQuery.toLowerCase());

      return matchesCompletedFilter && matchesCategoryFilter && matchesSearch;
    });

    // Sort items
    items.sort((a, b) => {
      let comparison = 0;

      switch (sortBy) {
        case 'title':
          comparison = (a.title || '').localeCompare(b.title || '');
          break;
        case 'category':
          comparison = (a.category || '').localeCompare(b.category || '');
          break;
        case 'status':
          const statusOrder = { 'completed': 3, 'failed': 2, 'not_applicable': 1, null: 0, undefined: 0 };
          comparison = (statusOrder[a.status as keyof typeof statusOrder] || 0) - 
                      (statusOrder[b.status as keyof typeof statusOrder] || 0);
          break;
        case 'priority':
          const priorityOrder = { 'high': 3, 'medium': 2, 'low': 1 };
          comparison = (priorityOrder[a.priority as keyof typeof priorityOrder] || 0) - 
                      (priorityOrder[b.priority as keyof typeof priorityOrder] || 0);
          break;
        case 'order':
        default:
          comparison = (a.order || 0) - (b.order || 0);
          break;
      }

      return sortDirection === 'desc' ? -comparison : comparison;
    });

    return items;
  }, [safeChecklistItems, showCompleted, selectedCategory, searchQuery, sortBy, sortDirection]);

  const filteredItems = filteredAndSortedItems;

  const completedCount = safeChecklistItems.filter(item => 
    item && (item.status === 'completed' || item.status === 'failed' || item.status === 'not_applicable')
  ).length;
  const totalCount = safeChecklistItems.length;
  const passedCount = safeChecklistItems.filter(item => item && item.status === 'completed').length;
  const failedCount = safeChecklistItems.filter(item => item && item.status === 'failed').length;
  const isAllCompleted = completedCount === totalCount && totalCount > 0;

  debugLogger.info('InspectionContent', 'Render stats', {
    totalItems: totalCount,
    completedItems: completedCount,
    filteredItems: filteredItems.length,
    isAllCompleted
  });

  return (
    <MobileOptimizedLayout
      title="Inspection Checklist"
      subtitle={totalCount > 0 ? `${completedCount}/${totalCount} items completed` : "Loading checklist..."}
      showBackButton
      backTo="/properties"
    >
      <div className="px-3 sm:px-4 py-4 space-y-4 max-w-4xl mx-auto">
        {/* Loading state during refetch */}
        {isRefetching && (
          <div className="text-center py-4">
            <div className="w-6 h-6 border-2 border-blue-600 border-t-transparent rounded-full animate-spin mx-auto mb-2" />
            <p className="text-sm text-gray-600">Refreshing checklist...</p>
          </div>
        )}

        {/* Empty state with helpful message */}
        {totalCount === 0 && !isRefetching && (
          <Alert className="bg-blue-50 border-blue-200">
            <InfoIcon className="h-4 w-4 text-blue-600" />
            <AlertDescription className="text-blue-800">
              This inspection doesn't have any checklist items yet. Checklist items are usually generated automatically when a property is added. 
              You can add items manually using the "Add Item" button below.
            </AlertDescription>
          </Alert>
        )}

        {/* Progress Tracker - only show if we have items */}
        {totalCount > 0 && (
          <InspectionProgressTracker checklistItems={safeChecklistItems} showDetailed />
        )}

        {/* Filters - only show if we have items */}
        {totalCount > 0 && (
          <InspectionFilters
            checklistItems={safeChecklistItems}
            selectedCategory={selectedCategory}
            onCategoryChange={setSelectedCategory}
            showCompleted={showCompleted}
            onToggleCompleted={() => setShowCompleted(!showCompleted)}
            onRefresh={onRefetch}
            isRefetching={isRefetching}
            searchQuery={searchQuery}
            onSearchChange={setSearchQuery}
            sortBy={sortBy}
            sortDirection={sortDirection}
            onSortChange={(newSortBy: SortOption, newDirection: SortDirection) => {
              setSortBy(newSortBy);
              setSortDirection(newDirection);
            }}
          />
        )}

        {/* Checklist Items or Empty State */}
        <InspectionList
          items={filteredItems}
          showCompleted={showCompleted}
          selectedCategory={selectedCategory}
          onComplete={onRefetch}
          onCategoryChange={setSelectedCategory}
          inspectionId={inspectionId}
        />

        {/* Complete Button - only show if we have completed items */}
        {totalCount > 0 && (
          <InspectionCompleteButton
            inspectionId={inspectionId}
            isAllCompleted={isAllCompleted}
            passedCount={passedCount}
            failedCount={failedCount}
          />
        )}
      </div>
    </MobileOptimizedLayout>
  );
};

import { useState, useEffect, useRef } from "react";
import { Badge } from "@/components/ui/badge";
import { Tooltip, TooltipContent, TooltipProvider, TooltipTrigger } from "@/components/ui/tooltip";
import { Users, Eye, Wrench, AlertCircle } from "lucide-react";
import { supabase } from "@/integrations/supabase/client";
import { useChannelManager } from "@/hooks/useChannelManager";
import type { Tables } from "@/integrations/supabase/types";

type InspectorPresence = Tables<'inspector_presence'> & {
  metadata: Record<string, any>;
};

interface InspectorPresenceIndicatorProps {
  inspectionId: string;
  currentItemId?: string;
}

export const InspectorPresenceIndicator = ({ 
  inspectionId, 
  currentItemId 
}: InspectorPresenceIndicatorProps) => {
  const [presenceData, setPresenceData] = useState<InspectorPresence[]>([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const { createChannel, subscribeChannel, cleanupChannel } = useChannelManager();
  const isMountedRef = useRef(true);
  const retryTimeoutRef = useRef<NodeJS.Timeout>();

  useEffect(() => {
    if (!inspectionId) {
      setIsLoading(false);
      return;
    }

    isMountedRef.current = true;

    const fetchPresence = async () => {
      if (!isMountedRef.current) return;

      try {
        setError(null);

        const { data, error } = await supabase
          .from('inspector_presence')
          .select('*')
          .eq('inspection_id', inspectionId)
          .neq('status', 'offline')
          .gte('last_seen', new Date(Date.now() - 5 * 60 * 1000).toISOString());

        if (error) {
          console.error('Error fetching presence:', error);
          setError(error.message);

          // Retry after 5 seconds if there's an error
          if (isMountedRef.current) {
            retryTimeoutRef.current = setTimeout(() => {
              if (isMountedRef.current) fetchPresence();
            }, 5000);
          }
          return;
        }

        // Transform the data to ensure metadata is properly typed
        const transformedData: InspectorPresence[] = (data || []).map(item => ({
          ...item,
          metadata: (item.metadata as Record<string, any>) || {}
        }));

        if (isMountedRef.current) {
          setPresenceData(transformedData);
          setIsLoading(false);
        }
      } catch (fetchError) {
        console.error('Error fetching presence:', fetchError);
        if (isMountedRef.current) {
          setError('Failed to load presence data');
          setIsLoading(false);

          // Retry after 5 seconds
          retryTimeoutRef.current = setTimeout(() => {
            if (isMountedRef.current) fetchPresence();
          }, 5000);
        }
      }
    };

    const setupRealtimeSubscription = async () => {
      try {
        // Create unique channel name
        const channelName = `presence-indicator-${inspectionId}`;
        console.log('Setting up presence channel:', channelName);

        const channel = createChannel(channelName, {
          presenceChanges: {
            filter: {
              event: '*',
              schema: 'public',
              table: 'inspector_presence',
              filter: `inspection_id=eq.${inspectionId}`
            },
            callback: (payload: any) => {
              console.log('Presence update received:', payload);
              if (isMountedRef.current) {
                fetchPresence();
              }
            }
          }
        });

        // Subscribe to the channel with async handling
        await subscribeChannel(channelName, (status: string) => {
          console.log('Presence subscription status:', status);
          if (status === 'SUBSCRIBED' && isMountedRef.current) {
            fetchPresence();
          } else if (status === 'CHANNEL_ERROR') {
            console.error('Channel subscription error');
          }
        });

      } catch (subscriptionError) {
        console.error('Error setting up presence subscription:', subscriptionError);
        // Fall back to periodic polling if realtime fails
        if (isMountedRef.current) {
          const pollInterval = setInterval(() => {
            if (isMountedRef.current) fetchPresence();
          }, 30000);

          return () => {
            clearInterval(pollInterval);
          };
        }
      }
    };

    // Initial fetch
    fetchPresence();

    // Setup realtime subscription
    setupRealtimeSubscription();

    return () => {
      isMountedRef.current = false;

      if (retryTimeoutRef.current) clearTimeout(retryTimeoutRef.current);

      // Clean up channel
      const channelName = `presence-indicator-${inspectionId}`;
      cleanupChannel(channelName);
    };
  }, [inspectionId, createChannel, subscribeChannel, cleanupChannel]);

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'viewing':
        return <Eye className="w-3 h-3" />;
      case 'working':
        return <Wrench className="w-3 h-3" />;
      default:
        return <Users className="w-3 h-3" />;
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'online':
        return 'bg-green-500';
      case 'viewing':
        return 'bg-blue-500';
      case 'working':
        return 'bg-orange-500';
      default:
        return 'bg-gray-500';
    }
  };

  // Show error state
  if (error && !isLoading) {
    return (
      <TooltipProvider>
        <Tooltip>
          <TooltipTrigger>
            <Badge variant="outline" className="flex items-center gap-1 text-red-600 border-red-200">
              <AlertCircle className="w-3 h-3" />
              Connection issue
            </Badge>
          </TooltipTrigger>
          <TooltipContent>
            <p>Unable to load presence data: {error}</p>
          </TooltipContent>
        </Tooltip>
      </TooltipProvider>
    );
  }

  // Show loading state
  if (isLoading) {
    return (
      <Badge variant="outline" className="flex items-center gap-1 animate-pulse">
        <Users className="w-3 h-3" />
        Loading...
      </Badge>
    );
  }

  const activeInspectorsOnItem = presenceData.filter(
    p => p.current_item_id === currentItemId && currentItemId
  );

  const totalActiveInspectors = presenceData.length;

  if (totalActiveInspectors === 0) return null;

  return (
    <TooltipProvider>
      <div className="flex items-center gap-2">
        <Tooltip>
          <TooltipTrigger>
            <Badge variant="outline" className="flex items-center gap-1">
              <Users className="w-3 h-3" />
              {totalActiveInspectors} active
            </Badge>
          </TooltipTrigger>
          <TooltipContent>
            <p>{totalActiveInspectors} inspector(s) currently working on this inspection</p>
          </TooltipContent>
        </Tooltip>

        {activeInspectorsOnItem.length > 0 && (
          <Tooltip>
            <TooltipTrigger>
              <Badge className={`flex items-center gap-1 ${getStatusColor(activeInspectorsOnItem[0].status)}`}>
                {getStatusIcon(activeInspectorsOnItem[0].status)}
                {activeInspectorsOnItem.length}
              </Badge>
            </TooltipTrigger>
            <TooltipContent>
              <p>{activeInspectorsOnItem.length} inspector(s) working on this item</p>
            </TooltipContent>
          </Tooltip>
        )}
      </div>
    </TooltipProvider>
  );
};

import React, { useState, useEffect } from 'react';
import { AlertTriangle, RefreshCw, Home, Wifi, WifiOff } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { useNetworkStatus } from '@/hooks/useNetworkStatus';

interface MobileErrorRecoveryProps {
  error: Error | string;
  onRetry: () => void;
  onNavigateHome: () => void;
  context?: string;
  showOfflineMode?: boolean;
}

export const MobileErrorRecovery: React.FC<MobileErrorRecoveryProps> = ({
  error,
  onRetry,
  onNavigateHome,
  context = 'operation',
  showOfflineMode = true
}) => {
  const [retryCount, setRetryCount] = useState(0);
  const isOnline = useNetworkStatus();
  const maxRetries = 3;

  const errorMessage = error instanceof Error ? error.message : error;
  const canRetry = retryCount < maxRetries;

  // Auto-retry on network reconnection
  useEffect(() => {
    if (isOnline && retryCount > 0 && canRetry) {
      console.log('üì± Network reconnected, auto-retrying...');
      setTimeout(() => {
        handleRetry();
      }, 1000);
    }
  }, [isOnline]);

  const handleRetry = () => {
    setRetryCount(prev => prev + 1);
    onRetry();
  };

  const isNetworkError = errorMessage.toLowerCase().includes('network') || 
                        errorMessage.toLowerCase().includes('fetch') ||
                        !isOnline;

  return (
    <div className="fixed inset-0 bg-gray-50 flex items-center justify-center p-4 z-50">
      <Card className="max-w-sm w-full">
        <CardHeader className="text-center pb-4">
          <div className="mx-auto w-12 h-12 bg-red-100 rounded-full flex items-center justify-center mb-3">
            <AlertTriangle className="w-6 h-6 text-red-600" />
          </div>

          <CardTitle className="text-lg text-gray-900">
            {context} Failed
          </CardTitle>

          {/* Network Status */}
          <div className={`flex items-center justify-center gap-2 px-3 py-1 rounded-full text-xs ${
            isOnline ? 'bg-green-100 text-green-700' : 'bg-red-100 text-red-700'
          }`}>
            {isOnline ? <Wifi className="w-3 h-3" /> : <WifiOff className="w-3 h-3" />}
            {isOnline ? 'Online' : 'Offline'}
          </div>
        </CardHeader>

        <CardContent className="space-y-4">
          {/* Error Message */}
          <Alert className="bg-red-50 border-red-200">
            <AlertDescription className="text-red-700 text-sm">
              {errorMessage}
            </AlertDescription>
          </Alert>

          {/* Retry Status */}
          {maxRetries > 1 && (
            <div className="flex items-center justify-between text-xs text-gray-600">
              <span>Attempt {retryCount + 1} of {maxRetries + 1}</span>
              <div className="flex gap-1">
                {Array.from({ length: maxRetries + 1 }, (_, i) => (
                  <div
                    key={i}
                    className={`w-2 h-2 rounded-full ${
                      i <= retryCount 
                        ? i === retryCount && !canRetry
                          ? 'bg-red-400'
                          : 'bg-blue-400'
                        : 'bg-gray-200'
                    }`}
                  />
                ))}
              </div>
            </div>
          )}

          {/* Mobile Tips */}
          <div className="bg-blue-50 border border-blue-200 rounded-lg p-3">
            <h4 className="text-sm font-medium text-blue-800 mb-2">üí° Quick fixes:</h4>
            <ul className="text-xs text-blue-700 space-y-1">
              {isNetworkError ? (
                <>
                  <li>‚Ä¢ Check your mobile data or WiFi</li>
                  <li>‚Ä¢ Move to an area with better signal</li>
                  <li>‚Ä¢ Try switching between WiFi and mobile data</li>
                </>
              ) : (
                <>
                  <li>‚Ä¢ Close and reopen the app</li>
                  <li>‚Ä¢ Clear browser cache</li>
                  <li>‚Ä¢ Try again in a few moments</li>
                </>
              )}
            </ul>
          </div>

          {/* Action Buttons */}
          <div className="space-y-3">
            {canRetry && (
              <Button
                onClick={handleRetry}
                className="w-full"
                disabled={!isOnline && isNetworkError}
              >
                <RefreshCw className="w-4 h-4 mr-2" />
                {!isOnline && isNetworkError ? 'Waiting for connection...' : `Try Again (${maxRetries - retryCount} left)`}
              </Button>
            )}

            <Button
              onClick={onNavigateHome}
              variant="outline"
              className="w-full"
            >
              <Home className="w-4 h-4 mr-2" />
              Return to Properties
            </Button>
          </div>

          {/* Offline Mode Info */}
          {showOfflineMode && !isOnline && (
            <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-3">
              <p className="text-xs text-yellow-700">
                üì± Some features work offline. Photos can be saved locally and will sync when reconnected.
              </p>
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
};

import React, { useContext, useEffect, useState, useRef, useCallback } from 'react';
import { User, Session } from '@supabase/supabase-js';
import { supabase } from '@/integrations/supabase/client';
import { AuthContext, AuthContextType } from '@/contexts/MobileAuthContext';
import { MobileAuthLoading } from '@/components/MobileAuthLoading';
import { useMobileAuthHooks } from '@/hooks/useMobileAuthHooks';
import { getCachedRole } from '@/utils/mobileCacheUtils';
import { useToast } from '@/hooks/use-toast';

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (context === undefined) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

export const MobileFastAuthProvider: React.FC<{ children: React.ReactNode }> = ({ children }) => {
  const [user, setUser] = useState<User | null>(null);
  const [session, setSession] = useState<Session | null>(null);
  const [userRole, setUserRole] = useState<string | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  const initializationRef = useRef<Promise<void> | null>(null);
  const { toast } = useToast();
  const { fetchUserRole, clearSession, signIn, signUp, signOut } = useMobileAuthHooks();

  const loadUserRole = useCallback(async () => {
    if (!user?.id) return;

    console.log('üì± Loading mobile user role...');
    try {
      const role = await fetchUserRole(user.id, false);
      setUserRole(role);
    } catch (error) {
      console.error('üì± Failed to load mobile role:', error);
      setUserRole('inspector');
    }
  }, [user?.id, fetchUserRole]);

  const handleClearSession = useCallback(() => {
    clearSession(user?.id);
    setUser(null);
    setSession(null);
    setUserRole(null);
    setError('Session cleared - please sign in again');
  }, [user?.id, clearSession]);

  // Mobile-optimized initialization
  const initializeAuth = useCallback(async (): Promise<void> => {
    if (initializationRef.current) {
      return initializationRef.current;
    }

    console.log('üì± Mobile auth initialization...');
    setError(null);

    const initPromise = new Promise<void>(async (resolve) => {
      try {
        // Mobile-friendly timeout (2 seconds)
        const timeoutPromise = new Promise<never>((_, reject) => {
          setTimeout(() => reject(new Error('Mobile auth timeout')), 2000);
        });

        const sessionPromise = supabase.auth.getSession();

        const { data: { session } } = await Promise.race([sessionPromise, timeoutPromise]);

        setSession(session);
        setUser(session?.user ?? null);

        if (session?.user) {
          console.log('üì± Mobile user found, setting role...');
          const cachedRole = getCachedRole(session.user.id);
          if (cachedRole) {
            setUserRole(cachedRole);
          } else {
            setUserRole('inspector');
            // Background role fetch for mobile
            fetchUserRole(session.user.id, false).then(role => {
              setUserRole(role);
            }).catch(() => {
              console.warn('üì± Background mobile role fetch failed');
            });
          }
        } else {
          setUserRole(null);
          console.log('üì± No mobile session found');
        }

        setLoading(false);
        setError(null);
        console.log('üì± Mobile auth initialization complete');
        resolve();

      } catch (error) {
        console.error('üì± Mobile auth initialization error:', error);
        setUser(null);
        setSession(null);
        setUserRole(null);
        setError('Mobile authentication failed');
        setLoading(false);
        resolve();
      }
    });

    initializationRef.current = initPromise;
    return initPromise;
  }, [fetchUserRole]);

  const forceRefresh = useCallback(async () => {
    console.log('üì± Mobile force refresh...');
    setLoading(true);
    setError(null);
    initializationRef.current = null;

    try {
      await initializeAuth();
      toast({
        title: "Mobile Session Refreshed",
        description: "Your mobile session has been refreshed.",
      });
    } catch (error) {
      console.error('üì± Mobile refresh failed:', error);
      toast({
        title: "Mobile Refresh Failed",
        description: "Please try signing in again.",
        variant: "destructive",
      });
    }
  }, [initializeAuth, toast]);

  const handleSignIn = useCallback(async (email: string, password: string) => {
    setLoading(true);
    setError(null);

    try {
      const result = await signIn(email, password);
      return result;
    } finally {
      setLoading(false);
    }
  }, [signIn]);

  const handleSignUp = useCallback(async (email: string, password: string) => {
    setLoading(true);
    setError(null);

    try {
      const result = await signUp(email, password);
      return result;
    } finally {
      setLoading(false);
    }
  }, [signUp]);

  const handleSignOut = useCallback(async () => {
    setLoading(true);
    setUserRole(null);
    setSession(null);
    setUser(null);
    setError(null);

    try {
      await signOut(user?.id);
    } finally {
      setLoading(false);
    }
  }, [signOut, user?.id]);

  // Mobile-optimized auth state listener
  useEffect(() => {
    let isMounted = true;

    const { data: { subscription } } = supabase.auth.onAuthStateChange(
      async (event, session) => {
        if (!isMounted) return;

        console.log('üì± Mobile auth state changed:', event, session?.user?.email);

        setSession(session);
        setUser(session?.user ?? null);

        if (session?.user) {
          const cachedRole = getCachedRole(session.user.id);
          if (cachedRole) {
            setUserRole(cachedRole);
          } else {
            setUserRole('inspector');

            // Background mobile role fetch
            fetchUserRole(session.user.id, false).then(role => {
              if (isMounted) {
                setUserRole(role);
              }
            }).catch(error => {
              console.error('üì± Background mobile role fetch failed:', error);
            });
          }
        } else {
          if (isMounted) {
            setUserRole(null);
          }
        }

        if (isMounted) {
          setLoading(false);
          setError(null);
        }
      }
    );

    initializeAuth().catch((error) => {
      if (isMounted) {
        console.error('üì± Failed to initialize mobile auth:', error);
        setLoading(false);
      }
    });

    return () => {
      isMounted = false;
      subscription.unsubscribe();
      initializationRef.current = null;
    };
  }, [initializeAuth, fetchUserRole]);

  const value: AuthContextType = {
    user,
    userRole,
    loading,
    error,
    signIn: handleSignIn,
    signUp: handleSignUp,
    signOut: handleSignOut,
    forceRefresh,
    clearSession: handleClearSession,
    loadUserRole,
  };

  // Mobile-optimized loading screen
  if (loading) {
    return <MobileAuthLoading onRefresh={() => {
      handleClearSession();
      forceRefresh();
    }} />;
  }

  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
};

import React from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { MapPin, Clock, Users, CheckCircle2, Edit } from "lucide-react";
import { useFastAuth } from "@/hooks/useFastAuth";

interface PropertyStatus {
  status: string;
  color: string;
  textLabel: string;
  badgeColor: string;
}

interface MobileInspectionCardProps {
  property: {
    property_id: string;
    property_name: string;
    property_address: string;
    property_vrbo_url?: string | null;
    property_airbnb_url?: string | null;
    inspection_count?: number;
    completed_inspection_count?: number;
    active_inspection_count?: number;
  };
  propertyStatus: PropertyStatus;
  isSelected: boolean;
  onSelect: (propertyId: string) => void;
  onStartInspection: (propertyId: string) => void;
  onEdit?: (propertyId: string) => void;
  isLoading?: boolean;
}

export const MobileInspectionCard: React.FC<MobileInspectionCardProps> = ({
  property,
  propertyStatus,
  isSelected,
  onSelect,
  onStartInspection,
  onEdit,
  isLoading = false
}) => {
  const { userRole, user } = useFastAuth();

  // Debug logging to help troubleshoot
  console.log('üîç MobileInspectionCard Debug:', {
    userRole,
    hasUser: !!user,
    userEmail: user?.email,
    hasOnEdit: !!onEdit,
    isAdmin: userRole === 'admin'
  });

  const isAdmin = userRole === 'admin';

  const getButtonText = () => {
    if (propertyStatus.status === 'in-progress') {
      return 'Join Inspection';
    }
    return 'Start Inspection';
  };

  const handleCardTap = () => {
    onSelect(property.property_id);
  };

  const handleInspectionStart = (e: React.MouseEvent) => {
    e.stopPropagation(); // Prevent card selection when clicking button
    onStartInspection(property.property_id);
  };

  const handleEdit = (e: React.MouseEvent) => {
    e.stopPropagation(); // Prevent card selection when clicking edit
    console.log('üîß Edit button clicked for property:', property.property_id);
    if (onEdit) {
      onEdit(property.property_id);
    }
  };

  return (
    <Card 
      className={`mobile-optimized-card cursor-pointer transition-all duration-200 ${
        isSelected 
          ? 'ring-2 ring-blue-500 bg-blue-50 border-blue-200' 
          : 'hover:shadow-md border-gray-200'
      }`}
      onClick={handleCardTap}
    >
      <CardHeader className="pb-3">
        <div className="flex items-start justify-between">
          <div className="flex-1 min-w-0">
            <CardTitle className="text-lg font-semibold text-gray-900 truncate">
              {property.property_name}
            </CardTitle>
            <div className="flex items-center mt-1 text-gray-600">
              <MapPin className="w-4 h-4 mr-1 flex-shrink-0" />
              <span className="text-sm truncate">{property.property_address}</span>
            </div>
          </div>
          <div className="flex items-center gap-2 ml-2 flex-shrink-0">
            <Badge className={`${propertyStatus.badgeColor}`}>
              {propertyStatus.textLabel}
            </Badge>
            {/* Force show edit button for debugging - remove this condition check temporarily */}
            {onEdit && (
              <Button
                variant="ghost"
                size="sm"
                onClick={handleEdit}
                className="h-8 w-8 p-0 bg-blue-50 hover:bg-blue-100"
                title={`Edit ${property.property_name} (Admin: ${isAdmin})`}
              >
                <Edit className="h-4 w-4 text-blue-600" />
              </Button>
            )}
          </div>
        </div>
      </CardHeader>

      <CardContent className="pt-0 space-y-3">
