        {user?.email?.includes('@rentresponsibly.org') && (
          <div className="text-xs text-gray-500 bg-gray-50 p-2 rounded">
            Debug: Role={userRole}, Admin={isAdmin ? 'Yes' : 'No'}, HasEdit={!!onEdit ? 'Yes' : 'No'}
