        {/* Quick Links */}
        {(property.property_vrbo_url || property.property_airbnb_url) && (
          <div className="flex gap-2 pt-2 border-t border-gray-100">
            {property.property_vrbo_url && (
              <a
                href={property.property_vrbo_url}
