import React from 'react';
import { useNavigate } from 'react-router-dom';
import { Plus, Search, RefreshCw, Settings, Zap } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Card, CardContent } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';

interface QuickAction {
  id: string;
  label: string;
  icon: React.ComponentType<any>;
  action: () => void;
  shortcut?: string;
  variant?: 'default' | 'secondary' | 'outline';
  badge?: string;
}

interface QuickActionsProps {
  context?: 'home' | 'properties' | 'inspection';
  pendingInspections?: number;
}

export const QuickActions = ({ 
  context = 'home', 
  pendingInspections = 0 
}: QuickActionsProps) => {
  const navigate = useNavigate();

  const getQuickActions = (): QuickAction[] => {
    const baseActions: QuickAction[] = [
      {
        id: 'add-property',
        label: 'Add Property',
        icon: Plus,
        action: () => navigate('/add-property'),
        shortcut: 'Ctrl+N',
        variant: 'default'
      },
      {
        id: 'refresh',
        label: 'Refresh',
        icon: RefreshCw,
        action: () => window.location.reload(),
        shortcut: 'F5',
        variant: 'outline'
      }
    ];

    if (context === 'home' || context === 'properties') {
      baseActions.unshift({
        id: 'view-properties',
        label: 'Properties',
        icon: Search,
        action: () => navigate('/properties'),
        variant: 'secondary',
        badge: pendingInspections > 0 ? `${pendingInspections}` : undefined
      });
    }

    return baseActions;
  };

  const actions = getQuickActions();

  // Keyboard shortcuts
  React.useEffect(() => {
    const handleKeyboard = (e: KeyboardEvent) => {
      if (e.ctrlKey && e.key === 'n') {
        e.preventDefault();
        navigate('/add-property');
      }
      if (e.key === 'F5') {
        e.preventDefault();
        window.location.reload();
      }
    };

    window.addEventListener('keydown', handleKeyboard);
    return () => window.removeEventListener('keydown', handleKeyboard);
  }, [navigate]);

  return (
    <Card className="shadow-sm">
      <CardContent className="p-4">
        <div className="flex items-center gap-2 mb-3">
          <Zap className="w-4 h-4 text-blue-600" />
          <h3 className="font-medium text-gray-900">Quick Actions</h3>
        </div>

        <div className="grid grid-cols-2 sm:grid-cols-3 lg:grid-cols-4 gap-2">
          {actions.map((action) => {
            const Icon = action.icon;
            return (
              <Button
                key={action.id}
                variant={action.variant || 'outline'}
                size="sm"
                onClick={action.action}
                className="flex flex-col items-center gap-1 h-auto py-3 relative"
              >
                <Icon className="w-4 h-4" />
                <span className="text-xs">{action.label}</span>
                {action.badge && (
                  <Badge 
                    variant="destructive" 
                    className="absolute -top-1 -right-1 text-xs min-w-[1.25rem] h-5 flex items-center justify-center"
                  >
                    {action.badge}
                  </Badge>
                )}
                {action.shortcut && (
                  <span className="text-xs text-gray-500 mt-1">
                    {action.shortcut}
                  </span>
                )}
              </Button>
            );
          })}
        </div>
      </CardContent>
    </Card>
  );
};

import React, { useState, useEffect } from "react";
import { PropertyFormFields } from "@/components/PropertyFormFields";
import { PropertyFormAlerts } from "@/components/PropertyFormAlerts";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { useSimplePropertySubmission } from "@/hooks/useSimplePropertySubmission";
import { useMobileAuth } from "@/hooks/useMobileAuth";
import { useNetworkStatus } from "@/hooks/useNetworkStatus";
import { supabase } from "@/integrations/supabase/client";
import { Loader2 } from "lucide-react";
import type { PropertyFormData } from "@/types/propertySubmission";

export const SimplePropertyForm = () => {
  const { user, isAuthenticated, loading: authLoading } = useMobileAuth();
  const isOnline = useNetworkStatus();
  const { isLoading, submitProperty, isEditing } = useSimplePropertySubmission();

  const [formData, setFormData] = useState<PropertyFormData>({
    name: '',
    address: '',
    vrbo_url: '',
    airbnb_url: ''
  });

  const [formErrors, setFormErrors] = useState<Record<string, string>>({});
  const [isLoadingProperty, setIsLoadingProperty] = useState(false);
  const [scrapedVRBOData, setScrapedVRBOData] = useState<any>(null);

  // Load property data for editing
  useEffect(() => {
    const loadPropertyForEdit = async () => {
      if (!isEditing) return;

      const urlParams = new URLSearchParams(window.location.search);
      const editId = urlParams.get('edit');

      if (!editId) return;

      console.log('üìù Loading property for edit:', editId);
      setIsLoadingProperty(true);

      try {
        const { data, error } = await supabase
          .from('properties')
          .select('*')
          .eq('id', editId)
          .single();

        if (error) {
          console.error('‚ùå Error loading property:', error);
          return;
        }

        if (data) {
          console.log('‚úÖ Property loaded for edit:', data);
          setFormData({
            name: data.name || '',
            address: data.address || '',
            vrbo_url: data.vrbo_url || '',
            airbnb_url: data.airbnb_url || ''
          });
        }
      } catch (error) {
        console.error('üí• Unexpected error loading property:', error);
      } finally {
        setIsLoadingProperty(false);
      }
    };

    loadPropertyForEdit();
  }, [isEditing]);

  const handleInputChange = (field: string, value: string) => {
    setFormData(prev => ({ ...prev, [field]: value }));
    // Clear field error when user starts typing
    if (formErrors[field]) {
      setFormErrors(prev => ({ ...prev, [field]: '' }));
    }
  };

  const handleVRBODataScraped = (data: any) => {
    console.log('üìä VRBO data scraped:', data);
    setScrapedVRBOData(data);
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    console.log('üìù Form submitted with data:', formData);
    console.log('üìä Scraped VRBO data:', scrapedVRBOData);

    // Clear previous errors
    setFormErrors({});

    // Include scraped VRBO data in submission
    const submissionData = {
      ...formData,
      scraped_vrbo_data: scrapedVRBOData
    };

    const success = await submitProperty(submissionData);
    if (!success) {
      console.log('‚ùå Submission failed');
    }
  };

  if (authLoading || isLoadingProperty) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <Loader2 className="h-8 w-8 animate-spin mx-auto mb-4 text-blue-600" />
          <p className="text-gray-600">
            {authLoading ? "Loading..." : "Loading property..."}
          </p>
        </div>
      </div>
    );
  }

  const submitButtonText = isLoading 
    ? (isEditing ? "Updating..." : "Adding...")
    : (isEditing ? "Update Property" : "Add Property");

  return (
    <div className="min-h-screen bg-gray-50 p-4">
      <div className="max-w-2xl mx-auto">
        <Card>
          <CardHeader>
            <CardTitle className="text-xl font-semibold text-gray-900">
              {isEditing ? "Edit Property" : "Add New Property"}
            </CardTitle>
          </CardHeader>
          <CardContent>
            <PropertyFormAlerts isOnline={isOnline} user={user} />

            <form onSubmit={handleSubmit} className="space-y-6">
              <PropertyFormFields
                formData={formData}
                formErrors={formErrors}
                onInputChange={handleInputChange}
                onVRBODataScraped={handleVRBODataScraped}
              />

              {/* Show scraped data preview */}
              {scrapedVRBOData && (
                <div className="mt-6 p-4 bg-blue-50 border border-blue-200 rounded-lg">
                  <h3 className="text-sm font-medium text-blue-900 mb-2">
                    üìä Imported Property Details
                  </h3>
                  <div className="text-xs text-blue-800 space-y-1">
                    <div>‚Ä¢ {scrapedVRBOData.specifications?.bedrooms || 0} bedrooms, {scrapedVRBOData.specifications?.bathrooms || 0} bathrooms</div>
                    <div>‚Ä¢ Max guests: {scrapedVRBOData.specifications?.maxGuests || 'N/A'}</div>
                    <div>‚Ä¢ {scrapedVRBOData.amenities?.length || 0} amenities detected</div>
                    <div>‚Ä¢ Ready for AI checklist generation</div>
                  </div>
                </div>
              )}

              <div className="flex gap-3 pt-4">
                <Button
                  type="submit"
                  disabled={isLoading || !isAuthenticated || !isOnline}
                  className="flex-1 bg-blue-600 hover:bg-blue-700"
                >
                  {isLoading && <Loader2 className="w-4 h-4 mr-2 animate-spin" />}
                  {submitButtonText}
                </Button>
              </div>
            </form>
          </CardContent>
        </Card>
      </div>
    </div>
  );
};

import { useState, useEffect, useRef } from "react";
import { Image as ImageIcon } from "lucide-react";
import { MediaUpload } from "@/types/inspection";
import { MediaLightbox } from "@/components/MediaLightbox";
import { MediaItem } from "@/components/MediaItem";
import { supabase } from "@/integrations/supabase/client";
import { useChannelManager } from "@/hooks/useChannelManager";

interface MediaUploadWithAttribution extends MediaUpload {
  user_id?: string;
  uploaded_by_name?: string;
}

interface UploadedEvidenceProps {
  checklistItemId: string;
}

export const UploadedEvidence = ({ checklistItemId }: UploadedEvidenceProps) => {
  const [selectedMedia, setSelectedMedia] = useState<MediaUploadWithAttribution | null>(null);
  const [mediaItems, setMediaItems] = useState<MediaUploadWithAttribution[]>([]);
  const [isLoading, setIsLoading] = useState(true);
  const { createChannel, subscribeChannel, cleanupChannel } = useChannelManager();
  const isMountedRef = useRef(true);

  // Load media items with user attribution
  useEffect(() => {
    isMountedRef.current = true;

    const loadMediaItems = async () => {
      if (!isMountedRef.current) return;

      try {
        const { data, error } = await supabase
          .from('media')
          .select('*')
          .eq('checklist_item_id', checklistItemId)
          .order('created_at', { ascending: false });

        if (error) throw error;

        // Transform and properly type the data
        const transformedData: MediaUploadWithAttribution[] = (data || []).map(item => ({
          id: item.id,
          checklist_item_id: item.checklist_item_id,
          type: (item.type === 'photo' || item.type === 'video') ? item.type : 'photo',
          url: item.url || '',
          user_id: item.user_id || undefined,
          uploaded_by_name: item.uploaded_by_name || undefined,
          created_at: item.created_at || new Date().toISOString()
        }));

        if (isMountedRef.current) {
          setMediaItems(transformedData);
          setIsLoading(false);
        }
      } catch (error) {
        console.error('Failed to load media items:', error);
        if (isMountedRef.current) {
          setIsLoading(false);
        }
      }
    };

    loadMediaItems();

    return () => {
      isMountedRef.current = false;
    };
  }, [checklistItemId]);

  // Real-time subscription for media updates
  useEffect(() => {
    isMountedRef.current = true;

    const setupSubscription = async () => {
      try {
        // Create unique channel name
        const channelName = `media-${checklistItemId}`;
        console.log('Setting up media channel:', channelName);

        const channel = createChannel(channelName, {
          mediaChanges: {
            filter: {
              event: '*',
              schema: 'public',
              table: 'media',
              filter: `checklist_item_id=eq.${checklistItemId}`
            },
            callback: (payload: any) => {
              console.log('Media update received:', payload);

              if (!isMountedRef.current) return;

              if (payload.eventType === 'INSERT') {
                const newItem: MediaUploadWithAttribution = {
                  id: payload.new.id,
                  checklist_item_id: payload.new.checklist_item_id,
                  type: (payload.new.type === 'photo' || payload.new.type === 'video') ? payload.new.type : 'photo',
                  url: payload.new.url || '',
                  user_id: payload.new.user_id || undefined,
                  uploaded_by_name: payload.new.uploaded_by_name || undefined,
                  created_at: payload.new.created_at || new Date().toISOString()
                };
                setMediaItems(prev => [newItem, ...prev]);
              } else if (payload.eventType === 'DELETE') {
                setMediaItems(prev => prev.filter(item => item.id !== payload.old.id));
              } else if (payload.eventType === 'UPDATE') {
                const updatedItem: MediaUploadWithAttribution = {
                  id: payload.new.id,
                  checklist_item_id: payload.new.checklist_item_id,
                  type: (payload.new.type === 'photo' || payload.new.type === 'video') ? payload.new.type : 'photo',
                  url: payload.new.url || '',
                  user_id: payload.new.user_id || undefined,
                  uploaded_by_name: payload.new.uploaded_by_name || undefined,
                  created_at: payload.new.created_at || new Date().toISOString()
                };
                setMediaItems(prev => prev.map(item => 
                  item.id === payload.new.id ? updatedItem : item
                ));
              }
            }
          }
        });

        // Subscribe to the channel with async handling
        await subscribeChannel(channelName, (status: string) => {
          console.log('Media subscription status:', status);
          if (status === 'CHANNEL_ERROR') {
            console.error('Media channel subscription error');
          }
        });

      } catch (error) {
        console.error('Error setting up media subscription:', error);
      }
    };

    setupSubscription();

    return () => {
      isMountedRef.current = false;

      // Clean up channel
      const channelName = `media-${checklistItemId}`;
      cleanupChannel(channelName);
    };
  }, [checklistItemId, createChannel, subscribeChannel, cleanupChannel]);

  const handleDownload = async (media: MediaUploadWithAttribution) => {
    try {
      const response = await fetch(media.url);
      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = `evidence-${media.id}.${media.type === 'photo' ? 'jpg' : 'mp4'}`;
      document.body.appendChild(a);
      a.click();
      window.URL.revokeObjectURL(url);
      document.body.removeChild(a);
    } catch (error) {
      console.error('Download failed:', error);
    }
  };

  if (isLoading) {
    return (
      <div className="bg-gray-50 rounded-lg p-4">
        <div className="animate-pulse">
          <div className="h-4 bg-gray-200 rounded mb-2 w-24"></div>
          <div className="h-20 bg-gray-200 rounded"></div>
        </div>
      </div>
    );
  }

  if (mediaItems.length === 0) {
    return null;
  }

  return (
    <>
      <div className="space-y-3">
        <div className="flex items-center justify-between">
          <h4 className="text-sm font-medium text-gray-700 flex items-center gap-2">
            <ImageIcon className="w-4 h-4" />
            Uploaded Evidence ({mediaItems.length})
          </h4>
          {mediaItems.length > 0 && (
            <div className="text-xs text-green-600 bg-green-50 px-2 py-1 rounded-full">
              ‚úì Complete
            </div>
          )}
        </div>

        <div className="grid grid-cols-1 gap-3">
          {mediaItems.map((media) => (
            <MediaItem
              key={media.id}
              media={media}
              onMediaClick={setSelectedMedia}
              onDownload={handleDownload}
            />
          ))}
        </div>
      </div>

      {selectedMedia && (
        <MediaLightbox
          media={selectedMedia}
          isOpen={!!selectedMedia}
          onClose={() => setSelectedMedia(null)}
        />
      )}
    </>
  );
};

