// Feedback Collection Form Component for STR Certified
// Allows auditors to provide feedback on AI predictions

import React, { useState, useEffect } from 'react';
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { Label } from '@/components/ui/label';
import { Textarea } from '@/components/ui/textarea';
import { RadioGroup, RadioGroupItem } from '@/components/ui/radio-group';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Slider } from '@/components/ui/slider';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { ScrollArea } from '@/components/ui/scroll-area';
import { Separator } from '@/components/ui/separator';
import { Checkbox } from '@/components/ui/checkbox';
import {
  MessageSquare,
  AlertTriangle,
  CheckCircle,
  XCircle,
  Info,
  Brain,
  Target,
  TrendingUp,
  Send,
  Save,
  Trash2,
  Plus,
  Edit3,
  Image as ImageIcon,
  Video,
  FileText,
  ChevronRight,
  ChevronDown,
  HelpCircle,
  Sparkles,
  ThumbsUp,
  ThumbsDown
} from 'lucide-react';
import { cn } from '@/lib/utils';
import type {
  FeedbackFormData,
  FeedbackItem,
  FeedbackCategory,
  AuditorFeedback
} from '@/types/learning';
import { createFeedbackProcessor } from '@/lib/ai/feedback-processor';
import { useMutation } from '@tanstack/react-query';

interface FeedbackCollectionFormProps {
  inspectionId: string;
  auditorId: string;
  aiPredictions: Array<{
    id: string;
    category: FeedbackCategory;
    value: any;
    confidence: number;
    context?: {
      roomType?: string;
      photoId?: string;
      videoTimestamp?: number;
      checklistItemId?: string;
    };
  }>;
  onSubmit?: (feedback: AuditorFeedback[]) => void;
  onSaveDraft?: (draft: FeedbackFormData) => void;
  initialDraft?: FeedbackFormData;
  className?: string;
}

export const FeedbackCollectionForm: React.FC<FeedbackCollectionFormProps> = ({
  inspectionId,
  auditorId,
  aiPredictions,
  onSubmit,
  onSaveDraft,
  initialDraft,
  className
}) => {
  // State
  const [feedbackItems, setFeedbackItems] = useState<FeedbackItem[]>([]);
  const [overallRating, setOverallRating] = useState(3);
  const [comments, setComments] = useState('');
  const [suggestedImprovements, setSuggestedImprovements] = useState<string[]>([]);
  const [newImprovement, setNewImprovement] = useState('');
  const [expandedItems, setExpandedItems] = useState<Set<string>>(new Set());
  const [filterCategory, setFilterCategory] = useState<FeedbackCategory | 'all'>('all');
  const [showOnlyErrors, setShowOnlyErrors] = useState(false);

  // Initialize feedback processor
  const feedbackProcessor = React.useMemo(() => createFeedbackProcessor(), []);

  // Initialize feedback items from AI predictions
  useEffect(() => {
    const items: FeedbackItem[] = aiPredictions.map(prediction => ({
      id: prediction.id,
      type: prediction.category,
      aiValue: prediction.value,
      correctValue: prediction.value, // Initially same as AI
      confidenceRating: prediction.confidence,
      severity: 'minor',
      explanation: '',
      evidence: {
        photoIds: prediction.context?.photoId ? [prediction.context.photoId] : undefined,
        videoTimestamp: prediction.context?.videoTimestamp,
        checklistItemId: prediction.context?.checklistItemId
      }
    }));

    // Apply initial draft if provided
    if (initialDraft) {
      const draftMap = new Map(initialDraft.feedbackItems.map(item => [item.id, item]));
      items.forEach(item => {
        const draft = draftMap.get(item.id);
        if (draft) {
          Object.assign(item, draft);
        }
      });
      setOverallRating(initialDraft.overallRating);
      setComments(initialDraft.comments || '');
      setSuggestedImprovements(initialDraft.suggestedImprovements || []);
    }

    setFeedbackItems(items);
  }, [aiPredictions, initialDraft]);

  // Submit mutation
  const submitMutation = useMutation({
    mutationFn: async () => {
      const formData: FeedbackFormData = {
        inspectionId,
        feedbackItems: feedbackItems.filter(item => 
          JSON.stringify(item.aiValue) !== JSON.stringify(item.correctValue) ||
          item.explanation
        ),
        overallRating,
        comments,
        suggestedImprovements
      };

      const feedback = await feedbackProcessor.collectFeedback(
        formData,
        auditorId,
        inspectionId
      );

      return feedback;
    },
    onSuccess: (feedback) => {
      onSubmit?.(feedback);
    }
  });

  // Filter items
  const filteredItems = feedbackItems.filter(item => {
    if (filterCategory !== 'all' && item.type !== filterCategory) return false;
    if (showOnlyErrors && JSON.stringify(item.aiValue) === JSON.stringify(item.correctValue)) return false;
    return true;
  });

  // Statistics
  const stats = {
    total: feedbackItems.length,
    corrections: feedbackItems.filter(item => 
      JSON.stringify(item.aiValue) !== JSON.stringify(item.correctValue)
    ).length,
    reviewed: feedbackItems.filter(item => item.explanation).length,
    highSeverity: feedbackItems.filter(item => item.severity === 'major').length
  };

  // Handle item update
  const updateFeedbackItem = (id: string, updates: Partial<FeedbackItem>) => {
    setFeedbackItems(prev => prev.map(item =>
      item.id === id ? { ...item, ...updates } : item
    ));
  };

  // Handle save draft
  const handleSaveDraft = () => {
    const draft: FeedbackFormData = {
      inspectionId,
      feedbackItems,
      overallRating,
      comments,
      suggestedImprovements
    };
    onSaveDraft?.(draft);
  };

  // Toggle item expansion
  const toggleExpanded = (id: string) => {
    setExpandedItems(prev => {
      const next = new Set(prev);
      if (next.has(id)) {
        next.delete(id);
      } else {
        next.add(id);
      }
      return next;
    });
  };

  // Add improvement suggestion
  const addImprovement = () => {
    if (newImprovement.trim()) {
      setSuggestedImprovements(prev => [...prev, newImprovement.trim()]);
      setNewImprovement('');
    }
  };

  // Remove improvement suggestion
  const removeImprovement = (index: number) => {
    setSuggestedImprovements(prev => prev.filter((_, i) => i !== index));
  };

  // Get category color
  const getCategoryColor = (category: FeedbackCategory) => {
    const colors: Record<FeedbackCategory, string> = {
      photo_quality: 'bg-blue-100 text-blue-800',
      object_detection: 'bg-purple-100 text-purple-800',
      room_classification: 'bg-green-100 text-green-800',
      damage_assessment: 'bg-red-100 text-red-800',
      completeness_check: 'bg-yellow-100 text-yellow-800',
      safety_compliance: 'bg-orange-100 text-orange-800',
      amenity_verification: 'bg-indigo-100 text-indigo-800',
      measurement_accuracy: 'bg-pink-100 text-pink-800',
      condition_rating: 'bg-gray-100 text-gray-800'
    };
    return colors[category] || 'bg-gray-100 text-gray-800';
  };

  // Render value based on type
  const renderValue = (value: any) => {
    if (value === null || value === undefined) return 'N/A';
    if (typeof value === 'boolean') return value ? 'Yes' : 'No';
    if (typeof value === 'number') return value.toFixed(2);
    if (typeof value === 'string') return value;
    if (Array.isArray(value)) return `${value.length} items`;
    if (typeof value === 'object') return JSON.stringify(value, null, 2);
    return String(value);
  };

  return (
    <div className={cn('space-y-6', className)}>
      {/* Header */}
      <Card>
        <CardHeader>
          <div className="flex items-center justify-between">
            <div>
              <CardTitle className="flex items-center space-x-2">
                <Brain className="h-5 w-5 text-purple-600" />
                <span>AI Learning Feedback</span>
              </CardTitle>
              <CardDescription>
                Help improve our AI by reviewing and correcting its predictions
              </CardDescription>
            </div>
            <div className="flex items-center space-x-2">
              <Button
                variant="outline"
                size="sm"
                onClick={handleSaveDraft}
              >
                <Save className="h-4 w-4 mr-2" />
                Save Draft
              </Button>
              <Button
                size="sm"
                onClick={() => submitMutation.mutate()}
                disabled={submitMutation.isPending}
              >
                <Send className="h-4 w-4 mr-2" />
                Submit Feedback
              </Button>
            </div>
          </div>
        </CardHeader>
      </Card>

      {/* Statistics */}
      <div className="grid grid-cols-4 gap-4">
        <Card>
          <CardContent className="pt-6">
            <div className="text-2xl font-bold">{stats.total}</div>
            <p className="text-xs text-muted-foreground">Total Predictions</p>
          </CardContent>
        </Card>
        <Card>
          <CardContent className="pt-6">
            <div className="text-2xl font-bold text-red-600">{stats.corrections}</div>
            <p className="text-xs text-muted-foreground">Corrections</p>
          </CardContent>
        </Card>
        <Card>
          <CardContent className="pt-6">
            <div className="text-2xl font-bold text-green-600">{stats.reviewed}</div>
            <p className="text-xs text-muted-foreground">Reviewed</p>
          </CardContent>
        </Card>
        <Card>
          <CardContent className="pt-6">
            <div className="text-2xl font-bold text-orange-600">{stats.highSeverity}</div>
            <p className="text-xs text-muted-foreground">High Severity</p>
          </CardContent>
        </Card>
      </div>

      {/* Main Content */}
      <Card>
        <CardHeader>
          <div className="flex items-center justify-between">
            <CardTitle>Review AI Predictions</CardTitle>
            <div className="flex items-center space-x-4">
              <Select
                value={filterCategory}
                onValueChange={(value: any) => setFilterCategory(value)}
              >
                <SelectTrigger className="w-[200px]">
                  <SelectValue placeholder="Filter by category" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Categories</SelectItem>
                  <SelectItem value="photo_quality">Photo Quality</SelectItem>
                  <SelectItem value="object_detection">Object Detection</SelectItem>
                  <SelectItem value="room_classification">Room Classification</SelectItem>
                  <SelectItem value="damage_assessment">Damage Assessment</SelectItem>
                  <SelectItem value="completeness_check">Completeness Check</SelectItem>
                  <SelectItem value="safety_compliance">Safety Compliance</SelectItem>
                  <SelectItem value="amenity_verification">Amenity Verification</SelectItem>
                  <SelectItem value="measurement_accuracy">Measurement Accuracy</SelectItem>
                  <SelectItem value="condition_rating">Condition Rating</SelectItem>
                </SelectContent>
              </Select>
              <div className="flex items-center space-x-2">
                <Checkbox
                  id="showErrors"
                  checked={showOnlyErrors}
                  onCheckedChange={(checked) => setShowOnlyErrors(!!checked)}
                />
                <Label htmlFor="showErrors" className="text-sm cursor-pointer">
                  Show only errors
                </Label>
              </div>
            </div>
          </div>
        </CardHeader>
        <CardContent>
          <ScrollArea className="h-[600px]">
            <div className="space-y-4">
              {filteredItems.map((item) => {
                const isExpanded = expandedItems.has(item.id);
                const hasError = JSON.stringify(item.aiValue) !== JSON.stringify(item.correctValue);

                return (
                  <div
                    key={item.id}
                    className={cn(
                      'border rounded-lg p-4 transition-all',
                      hasError ? 'border-red-200 bg-red-50' : 'border-gray-200',
                      isExpanded && 'ring-2 ring-primary ring-offset-2'
                    )}
                  >
                    {/* Item Header */}
                    <div className="flex items-start justify-between">
                      <div className="flex items-start space-x-3 flex-1">
                        <button
                          onClick={() => toggleExpanded(item.id)}
                          className="mt-1"
                        >
                          {isExpanded ? (
                            <ChevronDown className="h-4 w-4" />
                          ) : (
                            <ChevronRight className="h-4 w-4" />
                          )}
                        </button>
                        <div className="flex-1">
                          <div className="flex items-center space-x-2">
                            <Badge className={cn('text-xs', getCategoryColor(item.type))}>
                              {item.type.replace(/_/g, ' ')}
                            </Badge>
                            {hasError && (
                              <Badge variant="destructive" className="text-xs">
                                Correction
                              </Badge>
                            )}
                            <Badge variant="outline" className="text-xs">
                              {item.confidenceRating}% confidence
                            </Badge>
                          </div>
                          <div className="mt-2 grid grid-cols-2 gap-4">
                            <div>
                              <p className="text-xs text-muted-foreground">AI Prediction</p>
                              <p className="text-sm font-mono">{renderValue(item.aiValue)}</p>
                            </div>
                            <div>
                              <p className="text-xs text-muted-foreground">Correct Value</p>
                              <p className={cn(
                                'text-sm font-mono',
                                hasError && 'text-red-600 font-semibold'
                              )}>
                                {renderValue(item.correctValue)}
                              </p>
                            </div>
                          </div>
                        </div>
                      </div>
                      <div className="flex items-center space-x-2">
                        {item.explanation && (
                          <Badge variant="secondary" className="text-xs">
                            <MessageSquare className="h-3 w-3 mr-1" />
                            Explained
                          </Badge>
                        )}
                        {hasError ? (
                          <XCircle className="h-5 w-5 text-red-600" />
                        ) : (
                          <CheckCircle className="h-5 w-5 text-green-600" />
                        )}
                      </div>
                    </div>

                    {/* Expanded Content */}
                    {isExpanded && (
                      <div className="mt-4 space-y-4">
                        <Separator />

                        {/* Correct Value Input */}
                        <div>
                          <Label>Correct Value</Label>
                          <div className="mt-1">
                            {typeof item.aiValue === 'boolean' ? (
                              <RadioGroup
                                value={String(item.correctValue)}
                                onValueChange={(value) => 
                                  updateFeedbackItem(item.id, { correctValue: value === 'true' })
                                }
                              >
                                <div className="flex items-center space-x-4">
                                  <div className="flex items-center space-x-2">
                                    <RadioGroupItem value="true" id={`${item.id}_true`} />
                                    <Label htmlFor={`${item.id}_true`}>Yes</Label>
                                  </div>
                                  <div className="flex items-center space-x-2">
                                    <RadioGroupItem value="false" id={`${item.id}_false`} />
                                    <Label htmlFor={`${item.id}_false`}>No</Label>
                                  </div>
                                </div>
                              </RadioGroup>
                            ) : typeof item.aiValue === 'number' ? (
                              <div className="flex items-center space-x-2">
                                <Slider
                                  value={[item.correctValue]}
                                  onValueChange={([value]) => 
                                    updateFeedbackItem(item.id, { correctValue: value })
                                  }
                                  max={100}
                                  step={1}
                                  className="flex-1"
                                />
                                <span className="w-12 text-sm font-mono">{item.correctValue}</span>
                              </div>
                            ) : (
                              <Textarea
                                value={
                                  typeof item.correctValue === 'string' 
                                    ? item.correctValue 
                                    : JSON.stringify(item.correctValue, null, 2)
                                }
                                onChange={(e) => {
                                  try {
                                    const value = JSON.parse(e.target.value);
                                    updateFeedbackItem(item.id, { correctValue: value });
                                  } catch {
                                    updateFeedbackItem(item.id, { correctValue: e.target.value });
                                  }
                                }}
                                className="font-mono text-sm"
                                rows={3}
                              />
                            )}
                          </div>
                        </div>

                        {/* Severity */}
                        <div>
                          <Label>Severity</Label>
                          <RadioGroup
                            value={item.severity}
                            onValueChange={(value: any) => 
                              updateFeedbackItem(item.id, { severity: value })
                            }
                            className="flex items-center space-x-4 mt-1"
                          >
                            <div className="flex items-center space-x-2">
                              <RadioGroupItem value="minor" id={`${item.id}_minor`} />
                              <Label htmlFor={`${item.id}_minor`} className="cursor-pointer">
                                Minor
                              </Label>
                            </div>
                            <div className="flex items-center space-x-2">
                              <RadioGroupItem value="moderate" id={`${item.id}_moderate`} />
                              <Label htmlFor={`${item.id}_moderate`} className="cursor-pointer">
                                Moderate
                              </Label>
                            </div>
                            <div className="flex items-center space-x-2">
                              <RadioGroupItem value="major" id={`${item.id}_major`} />
                              <Label htmlFor={`${item.id}_major`} className="cursor-pointer">
                                Major
                              </Label>
                            </div>
                          </RadioGroup>
                        </div>

                        {/* Confidence Rating */}
                        <div>
                          <Label>Your Confidence Level</Label>
                          <div className="flex items-center space-x-2 mt-1">
                            <Slider
                              value={[item.confidenceRating]}
                              onValueChange={([value]) => 
                                updateFeedbackItem(item.id, { confidenceRating: value })
                              }
                              max={100}
                              step={5}
                              className="flex-1"
                            />
                            <span className="w-12 text-sm font-mono">{item.confidenceRating}%</span>
                          </div>
                        </div>

                        {/* Explanation */}
                        <div>
                          <Label>Explanation (Optional)</Label>
                          <Textarea
                            placeholder="Explain why this correction is needed..."
                            value={item.explanation || ''}
                            onChange={(e) => 
                              updateFeedbackItem(item.id, { explanation: e.target.value })
                            }
                            className="mt-1"
                            rows={3}
                          />
                        </div>

                        {/* Evidence */}
                        {item.evidence && (
                          <div>
                            <Label>Related Evidence</Label>
                            <div className="flex items-center space-x-4 mt-1 text-sm text-muted-foreground">
                              {item.evidence.photoIds && (
                                <div className="flex items-center space-x-1">
                                  <ImageIcon className="h-4 w-4" />
                                  <span>{item.evidence.photoIds.length} photos</span>
                                </div>
                              )}
                              {item.evidence.videoTimestamp !== undefined && (
                                <div className="flex items-center space-x-1">
                                  <Video className="h-4 w-4" />
                                  <span>Video at {formatTime(item.evidence.videoTimestamp)}</span>
                                </div>
                              )}
                              {item.evidence.checklistItemId && (
                                <div className="flex items-center space-x-1">
                                  <FileText className="h-4 w-4" />
                                  <span>Checklist item</span>
                                </div>
                              )}
                            </div>
                          </div>
                        )}
                      </div>
                    )}
                  </div>
                );
              })}
            </div>
          </ScrollArea>
        </CardContent>
      </Card>

      {/* Overall Feedback */}
      <Card>
        <CardHeader>
          <CardTitle>Overall Feedback</CardTitle>
        </CardHeader>
        <CardContent className="space-y-4">
          {/* Overall Rating */}
          <div>
            <Label>Overall AI Performance Rating</Label>
            <div className="flex items-center space-x-4 mt-2">
              {[1, 2, 3, 4, 5].map((rating) => (
                <button
                  key={rating}
                  onClick={() => setOverallRating(rating)}
                  className={cn(
                    'p-2 rounded-lg transition-all',
                    overallRating >= rating
                      ? 'bg-primary text-primary-foreground'
                      : 'bg-gray-100 text-gray-400 hover:bg-gray-200'
                  )}
                >
                  <Target className="h-6 w-6" />
                </button>
              ))}
              <span className="text-sm text-muted-foreground">
                {overallRating === 1 && 'Poor'}
                {overallRating === 2 && 'Fair'}
                {overallRating === 3 && 'Good'}
                {overallRating === 4 && 'Very Good'}
                {overallRating === 5 && 'Excellent'}
              </span>
            </div>
          </div>

          {/* Comments */}
          <div>
            <Label>Additional Comments</Label>
            <Textarea
              placeholder="Any additional feedback about the AI's performance..."
              value={comments}
              onChange={(e) => setComments(e.target.value)}
              className="mt-1"
              rows={4}
            />
          </div>

          {/* Suggested Improvements */}
          <div>
            <Label>Suggested Improvements</Label>
            <div className="space-y-2 mt-1">
              {suggestedImprovements.map((improvement, index) => (
                <div key={index} className="flex items-center justify-between p-2 bg-gray-50 rounded">
                  <span className="text-sm">{improvement}</span>
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={() => removeImprovement(index)}
                  >
                    <Trash2 className="h-4 w-4" />
                  </Button>
                </div>
              ))}
              <div className="flex items-center space-x-2">
                <input
                  type="text"
                  placeholder="Add a suggestion..."
                  value={newImprovement}
                  onChange={(e) => setNewImprovement(e.target.value)}
                  onKeyPress={(e) => e.key === 'Enter' && addImprovement()}
                  className="flex-1 px-3 py-2 border rounded-md text-sm"
                />
                <Button size="sm" onClick={addImprovement}>
                  <Plus className="h-4 w-4" />
                </Button>
              </div>
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Submit Status */}
      {submitMutation.isPending && (
        <Alert>
          <Sparkles className="h-4 w-4 animate-spin" />
          <AlertDescription>
            Submitting your feedback to improve our AI...
          </AlertDescription>
        </Alert>
      )}

      {submitMutation.isSuccess && (
        <Alert className="border-green-200 bg-green-50">
          <CheckCircle className="h-4 w-4 text-green-600" />
          <AlertDescription className="text-green-800">
            Thank you! Your feedback has been submitted and will help improve our AI.
          </AlertDescription>
        </Alert>
      )}

      {submitMutation.isError && (
        <Alert variant="destructive">
          <AlertTriangle className="h-4 w-4" />
          <AlertDescription>
            Failed to submit feedback. Please try again.
          </AlertDescription>
        </Alert>
      )}
    </div>
  );
};

// Helper function
function formatTime(seconds: number): string {
  const mins = Math.floor(seconds / 60);
  const secs = Math.floor(seconds % 60);
  return `${mins}:${secs.toString().padStart(2, '0')}`;
}

// Video Annotation Tools Component for STR Certified Auditor Interface

import React, { useState, useRef, useEffect } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Textarea } from '@/components/ui/textarea';
import { RadioGroup, RadioGroupItem } from '@/components/ui/radio-group';
import { Label } from '@/components/ui/label';
import { Popover, PopoverContent, PopoverTrigger } from '@/components/ui/popover';
import { ScrollArea } from '@/components/ui/scroll-area';
import { Alert, AlertDescription } from '@/components/ui/alert';
import {
  MessageSquare,
  Edit3,
  Square,
  Circle,
  Type,
  Check,
  X,
  AlertTriangle,
  Flag,
  Save,
  Trash2,
  ChevronDown,
  Clock,
  User,
  Palette,
  MousePointer,
  Move,
  ZoomIn,
  Undo,
  Redo,
  Download,
  Upload
} from 'lucide-react';
import { cn } from '@/lib/utils';

export interface VideoAnnotation {
  id: string;
  timestamp: number;
  type: 'comment' | 'drawing' | 'flag';
  status?: 'pass' | 'fail' | 'needs_review';
  content: string;
  drawing?: DrawingData;
  author: string;
  createdAt: Date;
  position?: { x: number; y: number };
  color?: string;
}

export interface DrawingData {
  tool: 'pen' | 'rectangle' | 'circle' | 'arrow' | 'text';
  points: { x: number; y: number }[];
  color: string;
  strokeWidth: number;
  text?: string;
}

interface VideoAnnotationToolsProps {
  currentTime: number;
  videoElement?: HTMLVideoElement | null;
  annotations: VideoAnnotation[];
  onAddAnnotation: (annotation: Omit<VideoAnnotation, 'id' | 'createdAt'>) => void;
  onUpdateAnnotation: (id: string, annotation: Partial<VideoAnnotation>) => void;
  onDeleteAnnotation: (id: string) => void;
  currentUser: string;
  className?: string;
}

export const VideoAnnotationTools: React.FC<VideoAnnotationToolsProps> = ({
  currentTime,
  videoElement,
  annotations,
  onAddAnnotation,
  onUpdateAnnotation,
  onDeleteAnnotation,
  currentUser,
  className
}) => {
  // State
  const [activeTab, setActiveTab] = useState<'comment' | 'drawing' | 'flag'>('comment');
  const [commentText, setCommentText] = useState('');
  const [flagStatus, setFlagStatus] = useState<'pass' | 'fail' | 'needs_review'>('needs_review');
  const [drawingTool, setDrawingTool] = useState<DrawingData['tool']>('pen');
  const [drawingColor, setDrawingColor] = useState('#ff0000');
  const [isDrawing, setIsDrawing] = useState(false);
  const [showAnnotations, setShowAnnotations] = useState(true);
  const [selectedAnnotation, setSelectedAnnotation] = useState<string | null>(null);

  // Refs
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const drawingRef = useRef<DrawingData | null>(null);

  // Get annotations for current time (within 2 seconds)
  const currentAnnotations = annotations.filter(
    ann => Math.abs(ann.timestamp - currentTime) < 2
  );

  // Format time
  const formatTime = (seconds: number): string => {
    const mins = Math.floor(seconds / 60);
    const secs = Math.floor(seconds % 60);
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  // Handle comment submission
  const handleAddComment = () => {
    if (!commentText.trim()) return;

    onAddAnnotation({
      timestamp: currentTime,
      type: 'comment',
      content: commentText,
      author: currentUser
    });

    setCommentText('');
  };

  // Handle flag submission
  const handleAddFlag = () => {
    onAddAnnotation({
      timestamp: currentTime,
      type: 'flag',
      status: flagStatus,
      content: `Flagged as ${flagStatus.replace('_', ' ')}`,
      author: currentUser
    });
  };

  // Initialize canvas for drawing
  useEffect(() => {
    if (activeTab === 'drawing' && canvasRef.current && videoElement) {
      const canvas = canvasRef.current;
      const rect = videoElement.getBoundingClientRect();
      canvas.width = rect.width;
      canvas.height = rect.height;

      // Clear canvas
      const ctx = canvas.getContext('2d');
      if (ctx) {
        ctx.clearRect(0, 0, canvas.width, canvas.height);
      }
    }
  }, [activeTab, videoElement]);

  // Handle drawing
  const startDrawing = (e: React.MouseEvent<HTMLCanvasElement>) => {
    if (!canvasRef.current) return;

    const rect = canvasRef.current.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;

    setIsDrawing(true);
    drawingRef.current = {
      tool: drawingTool,
      points: [{ x, y }],
      color: drawingColor,
      strokeWidth: 3
    };
  };

  const draw = (e: React.MouseEvent<HTMLCanvasElement>) => {
    if (!isDrawing || !canvasRef.current || !drawingRef.current) return;

    const rect = canvasRef.current.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;

    drawingRef.current.points.push({ x, y });

    // Draw on canvas
    const ctx = canvasRef.current.getContext('2d');
    if (ctx) {
      ctx.strokeStyle = drawingColor;
      ctx.lineWidth = 3;
      ctx.lineCap = 'round';
      ctx.lineJoin = 'round';

      const points = drawingRef.current.points;
      if (points.length > 1) {
        const lastPoint = points[points.length - 2];
        const currentPoint = points[points.length - 1];

        ctx.beginPath();
        ctx.moveTo(lastPoint.x, lastPoint.y);
        ctx.lineTo(currentPoint.x, currentPoint.y);
        ctx.stroke();
      }
    }
  };

  const stopDrawing = () => {
    if (!isDrawing || !drawingRef.current) return;

    setIsDrawing(false);

    // Save drawing annotation
    onAddAnnotation({
      timestamp: currentTime,
      type: 'drawing',
      content: `Drawing annotation with ${drawingTool}`,
      drawing: drawingRef.current,
      author: currentUser
    });

    // Clear canvas
    if (canvasRef.current) {
      const ctx = canvasRef.current.getContext('2d');
      if (ctx) {
        ctx.clearRect(0, 0, canvasRef.current.width, canvasRef.current.height);
      }
    }

    drawingRef.current = null;
  };

  // Status badge color
  const getStatusColor = (status: string) => {
    switch (status) {
      case 'pass':
        return 'bg-green-500';
      case 'fail':
        return 'bg-red-500';
      case 'needs_review':
        return 'bg-yellow-500';
      default:
        return 'bg-gray-500';
    }
  };

  return (
    <div className={cn('space-y-4', className)}>
      {/* Annotation Tools Card */}
      <Card>
        <CardHeader>
          <div className="flex items-center justify-between">
            <CardTitle className="text-lg">Annotation Tools</CardTitle>
            <Badge variant="secondary">
              {formatTime(currentTime)}
            </Badge>
          </div>
        </CardHeader>
        <CardContent>
          {/* Tool Tabs */}
          <div className="flex space-x-2 mb-4">
            <Button
              variant={activeTab === 'comment' ? 'default' : 'outline'}
              size="sm"
              onClick={() => setActiveTab('comment')}
            >
              <MessageSquare className="h-4 w-4 mr-1" />
              Comment
            </Button>
            <Button
              variant={activeTab === 'drawing' ? 'default' : 'outline'}
              size="sm"
              onClick={() => setActiveTab('drawing')}
            >
              <Edit3 className="h-4 w-4 mr-1" />
              Draw
            </Button>
            <Button
              variant={activeTab === 'flag' ? 'default' : 'outline'}
              size="sm"
              onClick={() => setActiveTab('flag')}
            >
              <Flag className="h-4 w-4 mr-1" />
              Flag
            </Button>
          </div>

          {/* Comment Tab */}
          {activeTab === 'comment' && (
            <div className="space-y-3">
              <Textarea
                placeholder="Add a comment about this moment..."
                value={commentText}
                onChange={(e) => setCommentText(e.target.value)}
                className="min-h-[80px]"
              />
              <Button 
                onClick={handleAddComment}
                disabled={!commentText.trim()}
                className="w-full"
              >
                <MessageSquare className="h-4 w-4 mr-2" />
                Add Comment
              </Button>
            </div>
          )}

          {/* Drawing Tab */}
          {activeTab === 'drawing' && (
            <div className="space-y-3">
              {/* Drawing Tools */}
              <div className="flex items-center space-x-2">
                <Button
                  variant={drawingTool === 'pen' ? 'default' : 'outline'}
                  size="icon"
                  onClick={() => setDrawingTool('pen')}
                >
                  <Edit3 className="h-4 w-4" />
                </Button>
                <Button
                  variant={drawingTool === 'rectangle' ? 'default' : 'outline'}
                  size="icon"
                  onClick={() => setDrawingTool('rectangle')}
                >
                  <Square className="h-4 w-4" />
                </Button>
                <Button
                  variant={drawingTool === 'circle' ? 'default' : 'outline'}
                  size="icon"
                  onClick={() => setDrawingTool('circle')}
                >
                  <Circle className="h-4 w-4" />
                </Button>
                <Button
                  variant={drawingTool === 'text' ? 'default' : 'outline'}
                  size="icon"
                  onClick={() => setDrawingTool('text')}
                >
                  <Type className="h-4 w-4" />
                </Button>

                <div className="flex-1" />

                {/* Color Picker */}
                <Popover>
                  <PopoverTrigger asChild>
                    <Button variant="outline" size="icon">
                      <div
                        className="w-4 h-4 rounded"
                        style={{ backgroundColor: drawingColor }}
                      />
                    </Button>
                  </PopoverTrigger>
                  <PopoverContent className="w-48">
                    <div className="grid grid-cols-4 gap-2">
                      {['#ff0000', '#00ff00', '#0000ff', '#ffff00', '#ff00ff', '#00ffff', '#000000', '#ffffff'].map(color => (
                        <button
                          key={color}
                          className={cn(
                            'w-8 h-8 rounded border-2',
                            drawingColor === color ? 'border-primary' : 'border-transparent'
                          )}
                          style={{ backgroundColor: color }}
                          onClick={() => setDrawingColor(color)}
                        />
                      ))}
                    </div>
                  </PopoverContent>
                </Popover>
              </div>

              {/* Drawing Canvas */}
              {videoElement && (
                <div className="relative bg-gray-100 rounded-lg overflow-hidden">
                  <canvas
                    ref={canvasRef}
                    className="absolute inset-0 cursor-crosshair"
                    onMouseDown={startDrawing}
                    onMouseMove={draw}
                    onMouseUp={stopDrawing}
                    onMouseLeave={stopDrawing}
                  />
                  <Alert className="m-2">
                    <AlertDescription>
                      Draw on the video to highlight areas. Your drawing will be saved at the current timestamp.
                    </AlertDescription>
                  </Alert>
                </div>
              )}
            </div>
          )}

          {/* Flag Tab */}
          {activeTab === 'flag' && (
            <div className="space-y-3">
              <RadioGroup value={flagStatus} onValueChange={(value: any) => setFlagStatus(value)}>
                <div className="flex items-center space-x-2">
                  <RadioGroupItem value="pass" id="pass" />
                  <Label htmlFor="pass" className="flex items-center cursor-pointer">
                    <Check className="h-4 w-4 mr-1 text-green-600" />
                    Pass
                  </Label>
                </div>
                <div className="flex items-center space-x-2">
                  <RadioGroupItem value="fail" id="fail" />
                  <Label htmlFor="fail" className="flex items-center cursor-pointer">
                    <X className="h-4 w-4 mr-1 text-red-600" />
                    Fail
                  </Label>
                </div>
                <div className="flex items-center space-x-2">
                  <RadioGroupItem value="needs_review" id="needs_review" />
                  <Label htmlFor="needs_review" className="flex items-center cursor-pointer">
                    <AlertTriangle className="h-4 w-4 mr-1 text-yellow-600" />
                    Needs Review
                  </Label>
                </div>
              </RadioGroup>
              <Button onClick={handleAddFlag} className="w-full">
                <Flag className="h-4 w-4 mr-2" />
                Add Flag
              </Button>
            </div>
          )}
        </CardContent>
      </Card>

      {/* Current Annotations */}
      {currentAnnotations.length > 0 && (
        <Card>
          <CardHeader>
            <CardTitle className="text-lg">Annotations at {formatTime(currentTime)}</CardTitle>
          </CardHeader>
          <CardContent>
            <ScrollArea className="h-48">
              <div className="space-y-2">
                {currentAnnotations.map((annotation) => (
                  <AnnotationItem
                    key={annotation.id}
                    annotation={annotation}
                    isSelected={selectedAnnotation === annotation.id}
                    onSelect={() => setSelectedAnnotation(annotation.id)}
                    onDelete={() => onDeleteAnnotation(annotation.id)}
                  />
                ))}
              </div>
            </ScrollArea>
          </CardContent>
        </Card>
      )}

      {/* All Annotations List */}
      <Card>
        <CardHeader>
          <div className="flex items-center justify-between">
            <CardTitle className="text-lg">All Annotations</CardTitle>
            <Badge variant="secondary">
              {annotations.length} total
            </Badge>
          </div>
        </CardHeader>
        <CardContent>
          <ScrollArea className="h-64">
            <div className="space-y-2">
              {annotations.map((annotation) => (
                <AnnotationItem
                  key={annotation.id}
                  annotation={annotation}
                  isSelected={selectedAnnotation === annotation.id}
                  onSelect={() => setSelectedAnnotation(annotation.id)}
                  onDelete={() => onDeleteAnnotation(annotation.id)}
                  showTimestamp
                />
              ))}
            </div>
          </ScrollArea>
        </CardContent>
      </Card>
    </div>
  );
};

// Annotation item component
const AnnotationItem: React.FC<{
  annotation: VideoAnnotation;
  isSelected: boolean;
  onSelect: () => void;
  onDelete: () => void;
  showTimestamp?: boolean;
}> = ({ annotation, isSelected, onSelect, onDelete, showTimestamp }) => {
  const getIcon = () => {
    switch (annotation.type) {
      case 'comment':
        return <MessageSquare className="h-4 w-4" />;
      case 'drawing':
        return <Edit3 className="h-4 w-4" />;
      case 'flag':
        return <Flag className="h-4 w-4" />;
    }
  };

  const getStatusBadge = () => {
    if (annotation.status) {
      const colors = {
        pass: 'bg-green-500',
        fail: 'bg-red-500',
        needs_review: 'bg-yellow-500'
      };

      return (
        <Badge className={cn('text-xs', colors[annotation.status])}>
          {annotation.status.replace('_', ' ')}
        </Badge>
      );
    }
    return null;
  };

  return (
    <div
      className={cn(
        'p-3 rounded-lg border cursor-pointer transition-colors',
        isSelected ? 'border-primary bg-primary/5' : 'border-border hover:bg-muted/50'
      )}
      onClick={onSelect}
    >
      <div className="flex items-start justify-between">
        <div className="flex items-start space-x-2">
          <div className="mt-0.5">{getIcon()}</div>
          <div className="flex-1">
            <div className="flex items-center space-x-2">
              <span className="text-sm font-medium">{annotation.author}</span>
              {getStatusBadge()}
              {showTimestamp && (
                <span className="text-xs text-muted-foreground">
                  at {formatTime(annotation.timestamp)}
                </span>
              )}
            </div>
            <p className="text-sm text-muted-foreground mt-1">
              {annotation.content}
            </p>
          </div>
        </div>
        <Button
          variant="ghost"
          size="icon"
          onClick={(e) => {
            e.stopPropagation();
            onDelete();
          }}
          className="h-6 w-6"
        >
          <Trash2 className="h-3 w-3" />
        </Button>
      </div>
    </div>
  );
};

// Helper function
function formatTime(seconds: number): string {
  const mins = Math.floor(seconds / 60);
  const secs = Math.floor(seconds % 60);
  return `${mins}:${secs.toString().padStart(2, '0')}`;
}

// Video Comparison Panel Component for STR Certified Auditor Interface

import React, { useState, useEffect } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Alert, AlertDescription, AlertTitle } from '@/components/ui/alert';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { ScrollArea } from '@/components/ui/scroll-area';
import { Separator } from '@/components/ui/separator';
import { RadioGroup, RadioGroupItem } from '@/components/ui/radio-group';
import { Label } from '@/components/ui/label';
import { Textarea } from '@/components/ui/textarea';
import {
  Check,
  X,
  AlertTriangle,
  Eye,
  EyeOff,
  ChevronRight,
  ZoomIn,
  RotateCw,
  Flag,
  ThumbsUp,
  ThumbsDown,
  MessageSquare,
  Sparkles,
  Image as ImageIcon,
  Video,
  Info,
  ArrowRight,
  CheckCircle,
  XCircle
} from 'lucide-react';
import { cn } from '@/lib/utils';
import type { VideoTimestamp, VideoAnalysisResult, DiscrepancyReport } from '@/types/video';
import type { PhotoData } from '@/types/property';

interface AIFinding {
  id: string;
  timestamp: number;
  type: 'match' | 'discrepancy' | 'missing' | 'extra';
  category: string;
  description: string;
  confidence: number;
  videoEvidence?: string; // Frame URL
  listingEvidence?: string; // Photo URL
  severity: 'low' | 'medium' | 'high';
  auditorStatus?: 'pending' | 'confirmed' | 'rejected';
  auditorNotes?: string;
}

interface VideoComparisonPanelProps {
  videoAnalysis: VideoAnalysisResult;
  listingPhotos: PhotoData[];
  currentTime: number;
  onJumpToTime?: (time: number) => void;
  onFindingUpdate?: (findingId: string, status: 'confirmed' | 'rejected', notes?: string) => void;
  className?: string;
}

export const VideoComparisonPanel: React.FC<VideoComparisonPanelProps> = ({
  videoAnalysis,
  listingPhotos,
  currentTime,
  onJumpToTime,
  onFindingUpdate,
  className
}) => {
  // State
  const [selectedFinding, setSelectedFinding] = useState<AIFinding | null>(null);
  const [showOnlyUnreviewed, setShowOnlyUnreviewed] = useState(false);
  const [filterSeverity, setFilterSeverity] = useState<'all' | 'low' | 'medium' | 'high'>('all');
  const [auditorNotes, setAuditorNotes] = useState('');
  const [comparisonMode, setComparisonMode] = useState<'side-by-side' | 'overlay'>('side-by-side');

  // Generate AI findings from video analysis
  const aiFindings: AIFinding[] = React.useMemo(() => {
    const findings: AIFinding[] = [];

    // Convert discrepancies to findings
    videoAnalysis.issues.forEach((issue, index) => {
      issue.affectedTimestamps.forEach((timestamp) => {
        findings.push({
          id: `issue_${index}_${timestamp}`,
          timestamp,
          type: 'discrepancy',
          category: issue.type,
          description: issue.description,
          confidence: 85 + Math.random() * 10,
          severity: issue.severity,
          auditorStatus: 'pending'
        });
      });
    });

    // Add room-based findings
    videoAnalysis.roomSequence.forEach((room) => {
      // Check if room exists in listing
      const listingHasRoom = listingPhotos.some(photo => 
        photo.roomType?.toLowerCase() === room.roomType.toLowerCase()
      );

      if (!listingHasRoom && room.coverage !== 'minimal') {
        findings.push({
          id: `extra_room_${room.roomId}`,
          timestamp: room.startTime,
          type: 'extra',
          category: 'room_not_in_listing',
          description: `${room.roomType} found in video but not shown in listing photos`,
          confidence: 75,
          severity: 'medium',
          auditorStatus: 'pending'
        });
      }
    });

    // Check for missing rooms
    const coveredRooms = videoAnalysis.roomSequence.map(r => r.roomType.toLowerCase());
    const listingRooms = [...new Set(listingPhotos.map(p => p.roomType?.toLowerCase()).filter(Boolean))];

    listingRooms.forEach((roomType) => {
      if (!coveredRooms.includes(roomType!)) {
        findings.push({
          id: `missing_room_${roomType}`,
          timestamp: 0,
          type: 'missing',
          category: 'room_not_covered',
          description: `${roomType} shown in listing but not found in video`,
          confidence: 90,
          severity: 'high',
          auditorStatus: 'pending'
        });
      }
    });

    // Add feature-based findings
    videoAnalysis.featureDetection.forEach((feature) => {
      if (!feature.detected) {
        findings.push({
          id: `missing_feature_${feature.feature}`,
          timestamp: 0,
          type: 'missing',
          category: 'amenity_missing',
          description: `${feature.feature} not detected in video`,
          confidence: feature.confidence,
          severity: 'medium',
          auditorStatus: 'pending'
        });
      }
    });

    return findings.sort((a, b) => a.timestamp - b.timestamp);
  }, [videoAnalysis, listingPhotos]);

  // Filter findings
  const filteredFindings = aiFindings.filter(finding => {
    if (showOnlyUnreviewed && finding.auditorStatus !== 'pending') return false;
    if (filterSeverity !== 'all' && finding.severity !== filterSeverity) return false;
    return true;
  });

  // Get findings at current time
  const currentFindings = filteredFindings.filter(
    f => Math.abs(f.timestamp - currentTime) < 5
  );

  // Statistics
  const stats = {
    total: aiFindings.length,
    confirmed: aiFindings.filter(f => f.auditorStatus === 'confirmed').length,
    rejected: aiFindings.filter(f => f.auditorStatus === 'rejected').length,
    pending: aiFindings.filter(f => f.auditorStatus === 'pending').length,
    high: aiFindings.filter(f => f.severity === 'high').length,
    medium: aiFindings.filter(f => f.severity === 'medium').length,
    low: aiFindings.filter(f => f.severity === 'low').length
  };

  // Handle finding review
  const handleFindingReview = (status: 'confirmed' | 'rejected') => {
    if (!selectedFinding) return;

    onFindingUpdate?.(selectedFinding.id, status, auditorNotes);

    // Update local state
    selectedFinding.auditorStatus = status;
    selectedFinding.auditorNotes = auditorNotes;

    // Clear form
    setAuditorNotes('');

    // Move to next unreviewed finding
    const nextUnreviewed = filteredFindings.find(
      f => f.auditorStatus === 'pending' && f.id !== selectedFinding.id
    );
    if (nextUnreviewed) {
      setSelectedFinding(nextUnreviewed);
    }
  };

  // Get severity color
  const getSeverityColor = (severity: string) => {
    switch (severity) {
      case 'high': return 'text-red-600 bg-red-50';
      case 'medium': return 'text-yellow-600 bg-yellow-50';
      case 'low': return 'text-blue-600 bg-blue-50';
      default: return 'text-gray-600 bg-gray-50';
    }
  };

  // Get finding icon
  const getFindingIcon = (type: string) => {
    switch (type) {
      case 'match': return <CheckCircle className="h-4 w-4 text-green-600" />;
      case 'discrepancy': return <AlertTriangle className="h-4 w-4 text-yellow-600" />;
      case 'missing': return <XCircle className="h-4 w-4 text-red-600" />;
      case 'extra': return <Info className="h-4 w-4 text-blue-600" />;
      default: return <Flag className="h-4 w-4" />;
    }
  };

  return (
    <div className={cn('space-y-4', className)}>
      {/* Overview Card */}
      <Card>
        <CardHeader>
          <div className="flex items-center justify-between">
            <CardTitle className="flex items-center space-x-2">
              <Sparkles className="h-5 w-5 text-purple-600" />
              <span>AI Comparison Analysis</span>
            </CardTitle>
            <Badge variant="secondary">
              {videoAnalysis.aiConfidence.toFixed(0)}% Confidence
            </Badge>
          </div>
        </CardHeader>
        <CardContent>
          {/* Statistics */}
          <div className="grid grid-cols-3 gap-4 mb-4">
            <div className="text-center">
              <div className="text-2xl font-bold">{stats.total}</div>
              <div className="text-xs text-muted-foreground">Total Findings</div>
            </div>
            <div className="text-center">
              <div className="text-2xl font-bold text-green-600">{stats.confirmed}</div>
              <div className="text-xs text-muted-foreground">Confirmed</div>
            </div>
            <div className="text-center">
              <div className="text-2xl font-bold text-red-600">{stats.rejected}</div>
              <div className="text-xs text-muted-foreground">Rejected</div>
            </div>
          </div>

          <Separator className="my-4" />

          {/* Severity Breakdown */}
          <div className="flex items-center justify-between text-sm">
            <div className="flex items-center space-x-4">
              <span className="text-muted-foreground">Severity:</span>
              <Badge variant="destructive">{stats.high} High</Badge>
              <Badge variant="secondary" className="bg-yellow-100">{stats.medium} Medium</Badge>
              <Badge variant="secondary">{stats.low} Low</Badge>
            </div>
            <div className="text-muted-foreground">
              {stats.pending} pending review
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Findings List */}
      <Card>
        <CardHeader>
          <div className="flex items-center justify-between">
            <CardTitle>Comparison Findings</CardTitle>
            <div className="flex items-center space-x-2">
              <Button
                variant={showOnlyUnreviewed ? 'default' : 'outline'}
                size="sm"
                onClick={() => setShowOnlyUnreviewed(!showOnlyUnreviewed)}
              >
                <Eye className="h-4 w-4 mr-1" />
                Unreviewed Only
              </Button>
              <RadioGroup
                value={filterSeverity}
                onValueChange={(value: any) => setFilterSeverity(value)}
                className="flex items-center space-x-2"
              >
                <div className="flex items-center space-x-1">
                  <RadioGroupItem value="all" id="all" className="h-3 w-3" />
                  <Label htmlFor="all" className="text-xs cursor-pointer">All</Label>
                </div>
                <div className="flex items-center space-x-1">
                  <RadioGroupItem value="high" id="high" className="h-3 w-3" />
                  <Label htmlFor="high" className="text-xs cursor-pointer">High</Label>
                </div>
                <div className="flex items-center space-x-1">
                  <RadioGroupItem value="medium" id="medium" className="h-3 w-3" />
                  <Label htmlFor="medium" className="text-xs cursor-pointer">Medium</Label>
                </div>
                <div className="flex items-center space-x-1">
                  <RadioGroupItem value="low" id="low" className="h-3 w-3" />
                  <Label htmlFor="low" className="text-xs cursor-pointer">Low</Label>
                </div>
              </RadioGroup>
            </div>
          </div>
        </CardHeader>
        <CardContent>
          <ScrollArea className="h-96">
            <div className="space-y-2">
              {filteredFindings.map((finding) => {
                const isSelected = selectedFinding?.id === finding.id;
                const isAtCurrentTime = Math.abs(finding.timestamp - currentTime) < 5;

                return (
                  <div
                    key={finding.id}
                    className={cn(
                      'p-3 rounded-lg border cursor-pointer transition-all',
                      isSelected ? 'border-primary bg-primary/5' : 'border-border hover:bg-muted/50',
                      isAtCurrentTime && 'ring-2 ring-primary ring-offset-2'
                    )}
                    onClick={() => setSelectedFinding(finding)}
                  >
                    <div className="flex items-start justify-between">
                      <div className="flex items-start space-x-2">
                        {getFindingIcon(finding.type)}
                        <div className="flex-1">
                          <div className="flex items-center space-x-2">
                            <span className="font-medium text-sm">
                              {finding.category.replace(/_/g, ' ')}
                            </span>
                            <Badge className={cn('text-xs', getSeverityColor(finding.severity))}>
                              {finding.severity}
                            </Badge>
                            {finding.auditorStatus !== 'pending' && (
                              <Badge
                                variant={finding.auditorStatus === 'confirmed' ? 'default' : 'secondary'}
                                className="text-xs"
                              >
                                {finding.auditorStatus === 'confirmed' ? (
                                  <Check className="h-3 w-3 mr-1" />
                                ) : (
                                  <X className="h-3 w-3 mr-1" />
                                )}
                                {finding.auditorStatus}
                              </Badge>
                            )}
                          </div>
                          <p className="text-sm text-muted-foreground mt-1">
                            {finding.description}
                          </p>
                          <div className="flex items-center space-x-3 mt-1 text-xs text-muted-foreground">
                            <span>At {formatTime(finding.timestamp)}</span>
                            <span>‚Ä¢</span>
                            <span>{finding.confidence.toFixed(0)}% confidence</span>
                          </div>
                        </div>
                      </div>
                      {finding.timestamp > 0 && (
                        <Button
                          variant="ghost"
                          size="sm"
                          onClick={(e) => {
                            e.stopPropagation();
                            onJumpToTime?.(finding.timestamp);
                          }}
                        >
                          <ArrowRight className="h-4 w-4" />
                        </Button>
                      )}
                    </div>
                  </div>
                );
              })}
            </div>
          </ScrollArea>
        </CardContent>
      </Card>

      {/* Selected Finding Detail */}
      {selectedFinding && (
        <Card>
          <CardHeader>
            <CardTitle>Finding Review</CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            {/* Evidence Comparison */}
            <div>
              <h4 className="text-sm font-medium mb-2">Evidence Comparison</h4>
              <Tabs value={comparisonMode} onValueChange={(v: any) => setComparisonMode(v)}>
                <TabsList className="grid w-full grid-cols-2">
                  <TabsTrigger value="side-by-side">Side by Side</TabsTrigger>
                  <TabsTrigger value="overlay">Overlay</TabsTrigger>
                </TabsList>
                <TabsContent value="side-by-side" className="mt-4">
                  <div className="grid grid-cols-2 gap-4">
                    <div>
                      <p className="text-sm font-medium mb-2">Video Frame</p>
                      <div className="aspect-video bg-gray-100 rounded-lg overflow-hidden">
                        {selectedFinding.videoEvidence ? (
                          <img
                            src={selectedFinding.videoEvidence}
                            alt="Video evidence"
                            className="w-full h-full object-cover"
                          />
                        ) : (
                          <div className="flex items-center justify-center h-full text-muted-foreground">
                            <Video className="h-8 w-8" />
                          </div>
                        )}
                      </div>
                    </div>
                    <div>
                      <p className="text-sm font-medium mb-2">Listing Photo</p>
                      <div className="aspect-video bg-gray-100 rounded-lg overflow-hidden">
                        {selectedFinding.listingEvidence ? (
                          <img
                            src={selectedFinding.listingEvidence}
                            alt="Listing evidence"
                            className="w-full h-full object-cover"
                          />
                        ) : (
                          <div className="flex items-center justify-center h-full text-muted-foreground">
                            <ImageIcon className="h-8 w-8" />
                          </div>
                        )}
                      </div>
                    </div>
                  </div>
                </TabsContent>
                <TabsContent value="overlay" className="mt-4">
                  <div className="relative aspect-video bg-gray-100 rounded-lg overflow-hidden">
                    {selectedFinding.listingEvidence && (
                      <img
                        src={selectedFinding.listingEvidence}
                        alt="Listing evidence"
                        className="absolute inset-0 w-full h-full object-cover opacity-50"
                      />
                    )}
                    {selectedFinding.videoEvidence && (
                      <img
                        src={selectedFinding.videoEvidence}
                        alt="Video evidence"
                        className="absolute inset-0 w-full h-full object-cover opacity-50 mix-blend-difference"
                      />
                    )}
                  </div>
                </TabsContent>
              </Tabs>
            </div>

            {/* AI Analysis */}
            <Alert>
              <Sparkles className="h-4 w-4" />
              <AlertTitle>AI Analysis</AlertTitle>
              <AlertDescription>
                <p className="mt-2">{selectedFinding.description}</p>
                <div className="flex items-center space-x-4 mt-2 text-sm">
                  <span>Type: {selectedFinding.type}</span>
                  <span>Category: {selectedFinding.category.replace(/_/g, ' ')}</span>
                  <span>Confidence: {selectedFinding.confidence.toFixed(0)}%</span>
                </div>
              </AlertDescription>
            </Alert>

            {/* Auditor Review */}
            <div className="space-y-3">
              <Label>Auditor Notes</Label>
              <Textarea
                placeholder="Add notes about this finding..."
                value={auditorNotes}
                onChange={(e) => setAuditorNotes(e.target.value)}
                className="min-h-[80px]"
              />

              <div className="flex items-center space-x-2">
                <Button
                  onClick={() => handleFindingReview('confirmed')}
                  disabled={selectedFinding.auditorStatus !== 'pending'}
                  className="flex-1"
                  variant="default"
                >
                  <ThumbsUp className="h-4 w-4 mr-2" />
                  Confirm Finding
                </Button>
                <Button
                  onClick={() => handleFindingReview('rejected')}
                  disabled={selectedFinding.auditorStatus !== 'pending'}
                  className="flex-1"
                  variant="destructive"
                >
                  <ThumbsDown className="h-4 w-4 mr-2" />
                  Reject Finding
                </Button>
              </div>

              {selectedFinding.auditorStatus !== 'pending' && selectedFinding.auditorNotes && (
                <Alert>
                  <MessageSquare className="h-4 w-4" />
                  <AlertDescription>
                    <p className="font-medium">Previous Notes:</p>
                    <p className="mt-1">{selectedFinding.auditorNotes}</p>
                  </AlertDescription>
                </Alert>
              )}
            </div>
          </CardContent>
        </Card>
      )}

      {/* Current Time Findings */}
      {currentFindings.length > 0 && (
        <Alert>
          <Info className="h-4 w-4" />
          <AlertTitle>Findings at Current Time</AlertTitle>
          <AlertDescription>
            <p className="mt-1">
              {currentFindings.length} finding{currentFindings.length > 1 ? 's' : ''} detected at {formatTime(currentTime)}
            </p>
          </AlertDescription>
        </Alert>
      )}
    </div>
  );
};

// Helper function
function formatTime(seconds: number): string {
  const mins = Math.floor(seconds / 60);
  const secs = Math.floor(seconds % 60);
  return `${mins}:${secs.toString().padStart(2, '0')}`;
}

// Video Review Player Component for STR Certified Auditor Interface

import React, { useState, useRef, useEffect } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Slider } from '@/components/ui/slider';
import { Badge } from '@/components/ui/badge';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Popover, PopoverContent, PopoverTrigger } from '@/components/ui/popover';
import {
  Play,
  Pause,
  SkipBack,
  SkipForward,
  Volume2,
  VolumeX,
  Maximize2,
  Minimize2,
  Settings,
  Bookmark,
  Flag,
  ChevronLeft,
  ChevronRight,
  Info,
  Clock,
  MapPin,
  Camera,
  Home,
  Zap
} from 'lucide-react';
import { VideoTimeline } from './VideoTimeline';
import { useVideoReview } from '@/hooks/useVideoReview';
import { cn } from '@/lib/utils';
import type { VideoRecording, VideoTimestamp, SceneType } from '@/types/video';

interface VideoReviewPlayerProps {
  video: VideoRecording;
  onTimestampClick?: (timestamp: VideoTimestamp) => void;
  onAnnotation?: (timestamp: number, annotation: any) => void;
  showSceneInfo?: boolean;
  className?: string;
}

export const VideoReviewPlayer: React.FC<VideoReviewPlayerProps> = ({
  video,
  onTimestampClick,
  onAnnotation,
  showSceneInfo = true,
  className
}) => {
  // Refs
  const videoRef = useRef<HTMLVideoElement>(null);
  const containerRef = useRef<HTMLDivElement>(null);

  // State
  const [isFullscreen, setIsFullscreen] = useState(false);
  const [showControls, setShowControls] = useState(true);
  const [volume, setVolume] = useState(1);
  const [isMuted, setIsMuted] = useState(false);
  const [playbackRate, setPlaybackRate] = useState(1);
  const [showSettings, setShowSettings] = useState(false);
  const controlsTimeoutRef = useRef<NodeJS.Timeout | null>(null);

  // Custom hook for video management
  const {
    isPlaying,
    currentTime,
    duration,
    buffered,
    currentScene,
    nearestTimestamp,
    play,
    pause,
    seek,
    jumpToTimestamp,
    jumpToNextScene,
    jumpToPreviousScene,
    addBookmark,
    removeBookmark,
    bookmarks,
    isBookmarked
  } = useVideoReview(video, videoRef);

  // Format time display
  const formatTime = (seconds: number): string => {
    const hours = Math.floor(seconds / 3600);
    const minutes = Math.floor((seconds % 3600) / 60);
    const secs = Math.floor(seconds % 60);

    if (hours > 0) {
      return `${hours}:${minutes.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
    }
    return `${minutes}:${secs.toString().padStart(2, '0')}`;
  };

  // Get scene icon
  const getSceneIcon = (sceneType: SceneType) => {
    switch (sceneType) {
      case 'room_entry':
        return <Home className="h-4 w-4" />;
      case 'room_overview':
        return <Camera className="h-4 w-4" />;
      case 'detail_shot':
        return <ZoomIn className="h-4 w-4" />;
      case 'amenity_focus':
        return <Zap className="h-4 w-4" />;
      case 'issue_documentation':
        return <Flag className="h-4 w-4" />;
      default:
        return <MapPin className="h-4 w-4" />;
    }
  };

  // Get scene color
  const getSceneColor = (sceneType: SceneType) => {
    switch (sceneType) {
      case 'room_entry':
        return 'bg-blue-500';
      case 'room_overview':
        return 'bg-green-500';
      case 'detail_shot':
        return 'bg-purple-500';
      case 'amenity_focus':
        return 'bg-yellow-500';
      case 'issue_documentation':
        return 'bg-red-500';
      default:
        return 'bg-gray-500';
    }
  };

  // Handle play/pause toggle
  const togglePlayPause = () => {
    if (isPlaying) {
      pause();
    } else {
      play();
    }
  };

  // Handle volume change
  const handleVolumeChange = (value: number[]) => {
    const newVolume = value[0];
    setVolume(newVolume);
    if (videoRef.current) {
      videoRef.current.volume = newVolume;
    }
    if (newVolume > 0 && isMuted) {
      setIsMuted(false);
    }
  };

  // Toggle mute
  const toggleMute = () => {
    if (videoRef.current) {
      videoRef.current.muted = !isMuted;
      setIsMuted(!isMuted);
    }
  };

  // Handle playback rate change
  const handlePlaybackRateChange = (rate: string) => {
    const newRate = parseFloat(rate);
    setPlaybackRate(newRate);
    if (videoRef.current) {
      videoRef.current.playbackRate = newRate;
    }
  };

  // Toggle fullscreen
  const toggleFullscreen = async () => {
    if (!containerRef.current) return;

    if (!isFullscreen) {
      if (containerRef.current.requestFullscreen) {
        await containerRef.current.requestFullscreen();
      }
    } else {
      if (document.exitFullscreen) {
        await document.exitFullscreen();
      }
    }
    setIsFullscreen(!isFullscreen);
  };

  // Handle keyboard shortcuts
  useEffect(() => {
    const handleKeyPress = (e: KeyboardEvent) => {
      if (e.target instanceof HTMLInputElement || e.target instanceof HTMLTextAreaElement) {
        return;
      }

      switch (e.key) {
        case ' ':
          e.preventDefault();
          togglePlayPause();
          break;
        case 'ArrowLeft':
          e.preventDefault();
          seek(currentTime - 10);
          break;
        case 'ArrowRight':
          e.preventDefault();
          seek(currentTime + 10);
          break;
        case 'ArrowUp':
          e.preventDefault();
          handleVolumeChange([Math.min(1, volume + 0.1)]);
          break;
        case 'ArrowDown':
          e.preventDefault();
          handleVolumeChange([Math.max(0, volume - 0.1)]);
          break;
        case 'm':
          toggleMute();
          break;
        case 'f':
          toggleFullscreen();
          break;
        case 'b':
          if (nearestTimestamp) {
            addBookmark(currentTime, nearestTimestamp.description);
          }
          break;
      }
    };

    window.addEventListener('keydown', handleKeyPress);
    return () => window.removeEventListener('keydown', handleKeyPress);
  }, [currentTime, volume, isPlaying, nearestTimestamp]);

  // Auto-hide controls
  useEffect(() => {
    const handleMouseMove = () => {
      setShowControls(true);

      if (controlsTimeoutRef.current) {
        clearTimeout(controlsTimeoutRef.current);
      }

      controlsTimeoutRef.current = setTimeout(() => {
        if (isPlaying) {
          setShowControls(false);
        }
      }, 3000);
    };

    const container = containerRef.current;
    if (container) {
      container.addEventListener('mousemove', handleMouseMove);
      return () => container.removeEventListener('mousemove', handleMouseMove);
    }
  }, [isPlaying]);

  // Handle timeline click
  const handleTimelineClick = (timestamp: VideoTimestamp) => {
    jumpToTimestamp(timestamp);
    onTimestampClick?.(timestamp);
  };

  // Handle bookmark toggle
  const handleBookmarkToggle = () => {
    if (isBookmarked(currentTime)) {
      const bookmark = bookmarks.find(b => Math.abs(b.time - currentTime) < 1);
      if (bookmark) {
        removeBookmark(bookmark.id);
      }
    } else {
      const description = nearestTimestamp?.description || `Bookmark at ${formatTime(currentTime)}`;
      addBookmark(currentTime, description);
    }
  };

  return (
    <Card className={cn('w-full overflow-hidden', className)}>
      <CardHeader className="pb-3">
        <div className="flex items-center justify-between">
          <CardTitle className="flex items-center space-x-2">
            <span>Video Review</span>
            <Badge variant="secondary">
              {video.duration}s ‚Ä¢ {video.resolution.width}x{video.resolution.height}
            </Badge>
          </CardTitle>
          {showSceneInfo && currentScene && (
            <div className="flex items-center space-x-2">
              <div className={cn('p-1 rounded', getSceneColor(currentScene.sceneType), 'bg-opacity-20')}>
                {getSceneIcon(currentScene.sceneType)}
              </div>
              <div className="text-sm">
                <p className="font-medium">{currentScene.roomDetected || 'Unknown Room'}</p>
                <p className="text-xs text-muted-foreground">{currentScene.description}</p>
              </div>
            </div>
          )}
        </div>
      </CardHeader>

      <CardContent className="p-0">
        <div
          ref={containerRef}
          className={cn(
            'relative bg-black',
            isFullscreen && 'fixed inset-0 z-50'
          )}
        >
          {/* Video Element */}
          <video
            ref={videoRef}
            src={URL.createObjectURL(video.file)}
            className="w-full h-full"
            onClick={togglePlayPause}
          />

          {/* Video Controls Overlay */}
          <div
            className={cn(
              'absolute inset-x-0 bottom-0 bg-gradient-to-t from-black/80 to-transparent transition-opacity duration-300',
              showControls ? 'opacity-100' : 'opacity-0 pointer-events-none'
            )}
          >
            {/* Timeline */}
            <div className="px-4 pb-2">
              <VideoTimeline
                duration={duration}
                currentTime={currentTime}
                timestamps={video.timestamps}
                bookmarks={bookmarks}
                buffered={buffered}
                onSeek={seek}
                onTimestampClick={handleTimelineClick}
                className="mb-4"
              />
            </div>

            {/* Control Buttons */}
            <div className="px-4 pb-4">
              <div className="flex items-center justify-between">
                {/* Left Controls */}
                <div className="flex items-center space-x-2">
                  {/* Play/Pause */}
                  <Button
                    variant="ghost"
                    size="icon"
                    onClick={togglePlayPause}
                    className="text-white hover:bg-white/20"
                  >
                    {isPlaying ? (
                      <Pause className="h-5 w-5" />
                    ) : (
                      <Play className="h-5 w-5" />
                    )}
                  </Button>

                  {/* Previous/Next Scene */}
                  <Button
                    variant="ghost"
                    size="icon"
                    onClick={jumpToPreviousScene}
                    className="text-white hover:bg-white/20"
                  >
                    <SkipBack className="h-4 w-4" />
                  </Button>
                  <Button
                    variant="ghost"
                    size="icon"
                    onClick={jumpToNextScene}
                    className="text-white hover:bg-white/20"
                  >
                    <SkipForward className="h-4 w-4" />
                  </Button>

                  {/* Volume */}
                  <div className="flex items-center space-x-2">
                    <Button
                      variant="ghost"
                      size="icon"
                      onClick={toggleMute}
                      className="text-white hover:bg-white/20"
                    >
                      {isMuted || volume === 0 ? (
                        <VolumeX className="h-4 w-4" />
                      ) : (
                        <Volume2 className="h-4 w-4" />
                      )}
                    </Button>
                    <div className="w-24">
                      <Slider
                        value={[isMuted ? 0 : volume]}
                        onValueChange={handleVolumeChange}
                        min={0}
                        max={1}
                        step={0.1}
                        className="cursor-pointer"
                      />
                    </div>
                  </div>

                  {/* Time Display */}
                  <div className="text-white text-sm font-mono ml-4">
                    {formatTime(currentTime)} / {formatTime(duration)}
                  </div>
                </div>

                {/* Right Controls */}
                <div className="flex items-center space-x-2">
                  {/* Bookmark */}
                  <Button
                    variant="ghost"
                    size="icon"
                    onClick={handleBookmarkToggle}
                    className={cn(
                      'text-white hover:bg-white/20',
                      isBookmarked(currentTime) && 'text-yellow-400'
                    )}
                  >
                    <Bookmark className="h-4 w-4" />
                  </Button>

                  {/* Settings */}
                  <Popover open={showSettings} onOpenChange={setShowSettings}>
                    <PopoverTrigger asChild>
                      <Button
                        variant="ghost"
                        size="icon"
                        className="text-white hover:bg-white/20"
                      >
                        <Settings className="h-4 w-4" />
                      </Button>
