// Offline Sync Manager Component for STR Certified
// Handles offline data storage, sync, and conflict resolution

import React, { useState, useEffect, useCallback, useRef } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Alert, AlertDescription, AlertTitle } from '@/components/ui/alert';
import { Progress } from '@/components/ui/progress';
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogFooter } from '@/components/ui/dialog';
import { RadioGroup, RadioGroupItem } from '@/components/ui/radio-group';
import { Label } from '@/components/ui/label';
import { ScrollArea } from '@/components/ui/scroll-area';
import { Separator } from '@/components/ui/separator';
import {
  Cloud,
  CloudOff,
  Download,
  Upload,
  RefreshCw,
  Wifi,
  WifiOff,
  Check,
  X,
  AlertTriangle,
  Database,
  HardDrive,
  Smartphone,
  Clock,
  ArrowUpDown,
  FileWarning,
  CheckCircle,
  Info,
  Loader2,
  Zap,
  Shield
} from 'lucide-react';
import { cn } from '@/lib/utils';
import { useQuery, useMutation } from '@tanstack/react-query';

interface OfflineSyncManagerProps {
  className?: string;
}

interface SyncItem {
  id: string;
  type: 'inspection' | 'photo' | 'video' | 'checklist' | 'report';
  action: 'create' | 'update' | 'delete';
  data: any;
  size: number;
  timestamp: Date;
  retries: number;
  status: 'pending' | 'syncing' | 'completed' | 'failed' | 'conflict';
  error?: string;
  conflictData?: any;
}

interface SyncConflict {
  id: string;
  item: SyncItem;
  localVersion: any;
  remoteVersion: any;
  detectedAt: Date;
  resolution?: 'local' | 'remote' | 'merge';
}

interface SyncStatus {
  isOnline: boolean;
  isSyncing: boolean;
  pendingItems: number;
  failedItems: number;
  conflicts: number;
  lastSync: Date | null;
  dataUsage: {
    uploaded: number;
    downloaded: number;
  };
}

export const OfflineSyncManager: React.FC<OfflineSyncManagerProps> = ({
  className
}) => {
  // State
  const [syncStatus, setSyncStatus] = useState<SyncStatus>({
    isOnline: navigator.onLine,
    isSyncing: false,
    pendingItems: 0,
    failedItems: 0,
    conflicts: 0,
    lastSync: null,
    dataUsage: {
      uploaded: 0,
      downloaded: 0
    }
  });
  const [syncQueue, setSyncQueue] = useState<SyncItem[]>([]);
  const [conflicts, setConflicts] = useState<SyncConflict[]>([]);
  const [selectedConflict, setSelectedConflict] = useState<SyncConflict | null>(null);
  const [syncProgress, setSyncProgress] = useState(0);
  const [autoSync, setAutoSync] = useState(true);
  const [showDetails, setShowDetails] = useState(false);

  // Refs
  const syncWorker = useRef<Worker | null>(null);
  const syncInterval = useRef<NodeJS.Timeout | null>(null);

  // Initialize sync worker
  useEffect(() => {
    // In production, would use actual Web Worker
    // syncWorker.current = new Worker('/workers/sync-worker.js');

    // Monitor online status
    const handleOnline = () => {
      setSyncStatus(prev => ({ ...prev, isOnline: true }));
      if (autoSync) {
        startSync();
      }
    };

    const handleOffline = () => {
      setSyncStatus(prev => ({ ...prev, isOnline: false }));
    };

    window.addEventListener('online', handleOnline);
    window.addEventListener('offline', handleOffline);

    // Load saved sync queue
    loadSyncQueue();

    // Start auto-sync if online
    if (navigator.onLine && autoSync) {
      startAutoSync();
    }

    return () => {
      window.removeEventListener('online', handleOnline);
      window.removeEventListener('offline', handleOffline);
      if (syncInterval.current) {
        clearInterval(syncInterval.current);
      }
    };
  }, [autoSync]);

  // Load sync queue from storage
  const loadSyncQueue = async () => {
    try {
      const stored = localStorage.getItem('syncQueue');
      if (stored) {
        const queue = JSON.parse(stored);
        setSyncQueue(queue);
        updateSyncStatus(queue);
      }
    } catch (error) {
      console.error('Failed to load sync queue:', error);
    }
  };

  // Save sync queue to storage
  const saveSyncQueue = useCallback((queue: SyncItem[]) => {
    try {
      localStorage.setItem('syncQueue', JSON.stringify(queue));
      setSyncQueue(queue);
      updateSyncStatus(queue);
    } catch (error) {
      console.error('Failed to save sync queue:', error);
    }
  }, []);

  // Update sync status based on queue
  const updateSyncStatus = (queue: SyncItem[]) => {
    const pending = queue.filter(item => item.status === 'pending').length;
    const failed = queue.filter(item => item.status === 'failed').length;
    const conflictCount = queue.filter(item => item.status === 'conflict').length;

    setSyncStatus(prev => ({
      ...prev,
      pendingItems: pending,
      failedItems: failed,
      conflicts: conflictCount
    }));
  };

  // Start auto-sync
  const startAutoSync = () => {
    if (syncInterval.current) {
      clearInterval(syncInterval.current);
    }

    syncInterval.current = setInterval(() => {
      if (navigator.onLine && !syncStatus.isSyncing) {
        startSync();
      }
    }, 30000); // Every 30 seconds
  };

  // Start manual sync
  const startSync = async () => {
    if (!navigator.onLine || syncStatus.isSyncing) return;

    setSyncStatus(prev => ({ ...prev, isSyncing: true }));
    setSyncProgress(0);

    try {
      const queue = [...syncQueue];
      const totalItems = queue.filter(item => 
        item.status === 'pending' || item.status === 'failed'
      ).length;

      let processed = 0;

      for (const item of queue) {
        if (item.status === 'pending' || item.status === 'failed') {
          const result = await syncItem(item);

          if (result.success) {
            item.status = 'completed';
            setSyncStatus(prev => ({
              ...prev,
              dataUsage: {
                uploaded: prev.dataUsage.uploaded + (item.size || 0),
                downloaded: prev.dataUsage.downloaded
              }
            }));
          } else if (result.conflict) {
            item.status = 'conflict';
            const conflict: SyncConflict = {
              id: `conflict_${Date.now()}`,
              item,
              localVersion: item.data,
              remoteVersion: result.remoteData,
              detectedAt: new Date()
            };
            setConflicts(prev => [...prev, conflict]);
          } else {
            item.status = 'failed';
            item.error = result.error;
            item.retries++;
          }

          processed++;
          setSyncProgress((processed / totalItems) * 100);
        }
      }

      // Clean up completed items
      const updatedQueue = queue.filter(item => 
        item.status !== 'completed' || 
        (Date.now() - item.timestamp.getTime()) < 24 * 60 * 60 * 1000 // Keep for 24h
      );

      saveSyncQueue(updatedQueue);
      setSyncStatus(prev => ({
        ...prev,
        isSyncing: false,
        lastSync: new Date()
      }));

    } catch (error) {
      console.error('Sync failed:', error);
      setSyncStatus(prev => ({ ...prev, isSyncing: false }));
    }
  };

  // Sync individual item
  const syncItem = async (item: SyncItem): Promise<{
    success: boolean;
    conflict?: boolean;
    remoteData?: any;
    error?: string;
  }> => {
    // Simulate API call
    return new Promise((resolve) => {
      setTimeout(() => {
        // Mock sync logic
        const random = Math.random();
        if (random > 0.9) {
          // Simulate conflict
          resolve({
            success: false,
            conflict: true,
            remoteData: { ...item.data, updated: 'remote' }
          });
        } else if (random > 0.8) {
          // Simulate failure
          resolve({
            success: false,
            error: 'Network timeout'
          });
        } else {
          // Success
          resolve({ success: true });
        }
      }, 500 + Math.random() * 1000);
    });
  };

  // Add item to sync queue
  const addToSyncQueue = (item: Omit<SyncItem, 'id' | 'timestamp' | 'retries' | 'status'>) => {
    const newItem: SyncItem = {
      ...item,
      id: `sync_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
      timestamp: new Date(),
      retries: 0,
      status: 'pending'
    };

    const updatedQueue = [...syncQueue, newItem];
    saveSyncQueue(updatedQueue);

    // Auto-sync if online
    if (navigator.onLine && autoSync) {
      setTimeout(() => startSync(), 1000);
    }
  };

  // Resolve conflict
  const resolveConflict = async (conflictId: string, resolution: 'local' | 'remote' | 'merge') => {
    const conflict = conflicts.find(c => c.id === conflictId);
    if (!conflict) return;

    conflict.resolution = resolution;

    // Apply resolution
    let resolvedData;
    switch (resolution) {
      case 'local':
        resolvedData = conflict.localVersion;
        break;
      case 'remote':
        resolvedData = conflict.remoteVersion;
        break;
      case 'merge':
        // Simple merge - in production would be more sophisticated
        resolvedData = { ...conflict.remoteVersion, ...conflict.localVersion };
        break;
    }

    // Update sync item
    const queueIndex = syncQueue.findIndex(item => item.id === conflict.item.id);
    if (queueIndex !== -1) {
      syncQueue[queueIndex].data = resolvedData;
      syncQueue[queueIndex].status = 'pending';
      saveSyncQueue(syncQueue);
    }

    // Remove conflict
    setConflicts(prev => prev.filter(c => c.id !== conflictId));
    setSelectedConflict(null);

    // Retry sync
    if (navigator.onLine) {
      startSync();
    }
  };

  // Clear sync queue
  const clearSyncQueue = () => {
    if (confirm('Are you sure you want to clear all pending sync items? This action cannot be undone.')) {
      setSyncQueue([]);
      localStorage.removeItem('syncQueue');
      updateSyncStatus([]);
    }
  };

  // Get storage info
  const getStorageInfo = async (): Promise<{
    used: number;
    quota: number;
    percentage: number;
  }> => {
    if ('storage' in navigator && 'estimate' in navigator.storage) {
      const estimate = await navigator.storage.estimate();
      const used = estimate.usage || 0;
      const quota = estimate.quota || 0;
      return {
        used,
        quota,
        percentage: quota > 0 ? (used / quota) * 100 : 0
      };
    }
    return { used: 0, quota: 0, percentage: 0 };
  };

  // Format bytes
  const formatBytes = (bytes: number): string => {
    if (bytes === 0) return '0 B';
    const k = 1024;
    const sizes = ['B', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  };

  // Format time ago
  const formatTimeAgo = (date: Date | null): string => {
    if (!date) return 'Never';
    const seconds = Math.floor((Date.now() - date.getTime()) / 1000);
    if (seconds < 60) return 'Just now';
    if (seconds < 3600) return `${Math.floor(seconds / 60)}m ago`;
    if (seconds < 86400) return `${Math.floor(seconds / 3600)}h ago`;
    return `${Math.floor(seconds / 86400)}d ago`;
  };

  // Get status color
  const getStatusColor = (status: string) => {
    switch (status) {
      case 'completed': return 'text-green-600 bg-green-50';
      case 'syncing': return 'text-blue-600 bg-blue-50';
      case 'failed': return 'text-red-600 bg-red-50';
      case 'conflict': return 'text-orange-600 bg-orange-50';
      default: return 'text-gray-600 bg-gray-50';
    }
  };

  // Get type icon
  const getTypeIcon = (type: string) => {
    switch (type) {
      case 'inspection': return <FileWarning className="h-4 w-4" />;
      case 'photo': return <Database className="h-4 w-4" />;
      case 'video': return <HardDrive className="h-4 w-4" />;
      case 'checklist': return <CheckCircle className="h-4 w-4" />;
      case 'report': return <Info className="h-4 w-4" />;
      default: return <Database className="h-4 w-4" />;
    }
  };

  return (
    <div className={cn('space-y-4', className)}>
      {/* Main Status Card */}
      <Card>
        <CardHeader>
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-2">
              <CardTitle>Offline Sync Manager</CardTitle>
              <Badge variant={syncStatus.isOnline ? 'default' : 'secondary'}>
                {syncStatus.isOnline ? (
                  <>
                    <Wifi className="h-3 w-3 mr-1" />
                    Online
                  </>
                ) : (
                  <>
                    <WifiOff className="h-3 w-3 mr-1" />
                    Offline
                  </>
                )}
              </Badge>
            </div>
            <div className="flex items-center space-x-2">
              <Button
                variant="outline"
                size="sm"
                onClick={() => setShowDetails(!showDetails)}
              >
                {showDetails ? 'Hide' : 'Show'} Details
              </Button>
              <Button
                size="sm"
                onClick={() => startSync()}
                disabled={!syncStatus.isOnline || syncStatus.isSyncing}
              >
                {syncStatus.isSyncing ? (
                  <>
                    <Loader2 className="h-4 w-4 mr-2 animate-spin" />
                    Syncing...
                  </>
                ) : (
                  <>
                    <RefreshCw className="h-4 w-4 mr-2" />
                    Sync Now
                  </>
                )}
              </Button>
            </div>
          </div>
        </CardHeader>
        <CardContent>
          {/* Sync Progress */}
          {syncStatus.isSyncing && (
            <div className="mb-4">
              <div className="flex items-center justify-between text-sm mb-1">
                <span>Syncing...</span>
                <span>{Math.round(syncProgress)}%</span>
              </div>
              <Progress value={syncProgress} className="h-2" />
            </div>
          )}

          {/* Status Overview */}
          <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
            <div className="text-center">
              <div className="text-2xl font-bold">{syncStatus.pendingItems}</div>
              <p className="text-xs text-muted-foreground">Pending</p>
            </div>
            <div className="text-center">
              <div className="text-2xl font-bold text-red-600">{syncStatus.failedItems}</div>
              <p className="text-xs text-muted-foreground">Failed</p>
            </div>
            <div className="text-center">
              <div className="text-2xl font-bold text-orange-600">{syncStatus.conflicts}</div>
              <p className="text-xs text-muted-foreground">Conflicts</p>
            </div>
            <div className="text-center">
              <div className="text-sm font-medium">
                {formatTimeAgo(syncStatus.lastSync)}
              </div>
              <p className="text-xs text-muted-foreground">Last Sync</p>
            </div>
          </div>

          {/* Data Usage */}
          <div className="mt-4 pt-4 border-t">
            <div className="flex items-center justify-between text-sm">
              <div className="flex items-center space-x-2">
                <Upload className="h-4 w-4 text-muted-foreground" />
                <span>Uploaded: {formatBytes(syncStatus.dataUsage.uploaded)}</span>
              </div>
              <div className="flex items-center space-x-2">
                <Download className="h-4 w-4 text-muted-foreground" />
                <span>Downloaded: {formatBytes(syncStatus.dataUsage.downloaded)}</span>
              </div>
            </div>
          </div>

          {/* Auto-sync Toggle */}
          <div className="mt-4 flex items-center justify-between">
            <div className="flex items-center space-x-2">
              <Zap className="h-4 w-4 text-muted-foreground" />
              <span className="text-sm font-medium">Auto-sync when online</span>
            </div>
            <Button
              variant={autoSync ? 'default' : 'outline'}
              size="sm"
              onClick={() => setAutoSync(!autoSync)}
            >
              {autoSync ? 'Enabled' : 'Disabled'}
            </Button>
          </div>
        </CardContent>
      </Card>

      {/* Alerts */}
      {syncStatus.conflicts > 0 && (
        <Alert>
          <AlertTriangle className="h-4 w-4" />
          <AlertTitle>Sync Conflicts Detected</AlertTitle>
          <AlertDescription>
            {syncStatus.conflicts} items have conflicts that need resolution.
            <Button
              variant="link"
              size="sm"
              className="ml-2"
              onClick={() => setShowDetails(true)}
            >
              View Conflicts
            </Button>
          </AlertDescription>
        </Alert>
      )}

      {!syncStatus.isOnline && (
        <Alert>
          <WifiOff className="h-4 w-4" />
          <AlertTitle>Working Offline</AlertTitle>
          <AlertDescription>
            Your changes are being saved locally and will sync when you're back online.
          </AlertDescription>
        </Alert>
      )}

      {/* Detailed View */}
      {showDetails && (
        <Card>
          <CardHeader>
            <CardTitle>Sync Queue Details</CardTitle>
          </CardHeader>
          <CardContent>
            <ScrollArea className="h-96">
              <div className="space-y-2">
                {syncQueue.length === 0 ? (
                  <p className="text-center text-muted-foreground py-8">
                    No items in sync queue
                  </p>
                ) : (
                  syncQueue.map((item) => (
                    <div
                      key={item.id}
                      className="flex items-center justify-between p-3 border rounded-lg"
                    >
                      <div className="flex items-center space-x-3">
                        {getTypeIcon(item.type)}
                        <div>
                          <p className="text-sm font-medium">
                            {item.type.charAt(0).toUpperCase() + item.type.slice(1)} - {item.action}
                          </p>
                          <p className="text-xs text-muted-foreground">
                            {formatBytes(item.size)} ‚Ä¢ {formatTimeAgo(item.timestamp)}
                          </p>
                        </div>
                      </div>
                      <div className="flex items-center space-x-2">
                        <Badge className={cn('text-xs', getStatusColor(item.status))}>
                          {item.status}
                        </Badge>
                        {item.status === 'conflict' && (
                          <Button
                            variant="outline"
                            size="sm"
                            onClick={() => {
                              const conflict = conflicts.find(c => c.item.id === item.id);
                              if (conflict) setSelectedConflict(conflict);
                            }}
                          >
                            Resolve
                          </Button>
                        )}
                      </div>
                    </div>
                  ))
                )}
              </div>
            </ScrollArea>

            {syncQueue.length > 0 && (
              <div className="mt-4 pt-4 border-t">
                <Button
                  variant="destructive"
                  size="sm"
                  onClick={clearSyncQueue}
                >
                  Clear Sync Queue
                </Button>
              </div>
            )}
          </CardContent>
        </Card>
      )}

      {/* Storage Info */}
      <Card>
        <CardHeader>
          <CardTitle>Local Storage</CardTitle>
        </CardHeader>
        <CardContent>
          <StorageInfo />
        </CardContent>
      </Card>

      {/* Conflict Resolution Dialog */}
      {selectedConflict && (
        <Dialog open={!!selectedConflict} onOpenChange={() => setSelectedConflict(null)}>
          <DialogContent className="max-w-2xl">
            <DialogHeader>
              <DialogTitle>Resolve Sync Conflict</DialogTitle>
              <DialogDescription>
                Choose which version to keep or merge the changes.
              </DialogDescription>
            </DialogHeader>

            <div className="space-y-4">
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <h4 className="text-sm font-medium mb-2">Local Version</h4>
                  <div className="p-3 border rounded-lg bg-gray-50">
                    <pre className="text-xs overflow-auto">
                      {JSON.stringify(selectedConflict.localVersion, null, 2)}
                    </pre>
                  </div>
                </div>
                <div>
                  <h4 className="text-sm font-medium mb-2">Remote Version</h4>
                  <div className="p-3 border rounded-lg bg-gray-50">
                    <pre className="text-xs overflow-auto">
                      {JSON.stringify(selectedConflict.remoteVersion, null, 2)}
                    </pre>
                  </div>
                </div>
              </div>

              <RadioGroup defaultValue="local" className="space-y-2">
                <div className="flex items-center space-x-2">
                  <RadioGroupItem value="local" id="local" />
                  <Label htmlFor="local">Keep local version</Label>
                </div>
                <div className="flex items-center space-x-2">
                  <RadioGroupItem value="remote" id="remote" />
                  <Label htmlFor="remote">Keep remote version</Label>
                </div>
                <div className="flex items-center space-x-2">
                  <RadioGroupItem value="merge" id="merge" />
                  <Label htmlFor="merge">Merge both versions</Label>
                </div>
              </RadioGroup>
            </div>

            <DialogFooter>
              <Button variant="outline" onClick={() => setSelectedConflict(null)}>
                Cancel
              </Button>
              <Button
                onClick={() => {
                  const resolution = (document.querySelector('input[name="radio-group"]:checked') as HTMLInputElement)?.value as any;
                  resolveConflict(selectedConflict.id, resolution || 'local');
                }}
              >
                Resolve Conflict
              </Button>
            </DialogFooter>
          </DialogContent>
        </Dialog>
      )}
    </div>
  );
};

// Storage Info Component
const StorageInfo: React.FC = () => {
  const [storage, setStorage] = useState<{
    used: number;
    quota: number;
    percentage: number;
  }>({ used: 0, quota: 0, percentage: 0 });

  useEffect(() => {
    const checkStorage = async () => {
      if ('storage' in navigator && 'estimate' in navigator.storage) {
        const estimate = await navigator.storage.estimate();
        setStorage({
          used: estimate.usage || 0,
          quota: estimate.quota || 0,
          percentage: estimate.quota ? ((estimate.usage || 0) / estimate.quota) * 100 : 0
        });
      }
    };

    checkStorage();
    const interval = setInterval(checkStorage, 10000); // Update every 10 seconds

    return () => clearInterval(interval);
  }, []);

  const formatBytes = (bytes: number): string => {
    if (bytes === 0) return '0 B';
    const k = 1024;
    const sizes = ['B', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  };

  return (
    <div className="space-y-4">
      <div className="flex items-center justify-between">
        <div className="flex items-center space-x-2">
          <HardDrive className="h-4 w-4 text-muted-foreground" />
          <span className="text-sm">Storage Usage</span>
        </div>
        <span className="text-sm font-medium">
          {formatBytes(storage.used)} / {formatBytes(storage.quota)}
        </span>
      </div>
      <Progress value={storage.percentage} className="h-2" />
      <div className="grid grid-cols-3 gap-4 text-sm">
        <div>
          <p className="text-muted-foreground">Used</p>
          <p className="font-medium">{formatBytes(storage.used)}</p>
        </div>
        <div>
          <p className="text-muted-foreground">Available</p>
          <p className="font-medium">{formatBytes(storage.quota - storage.used)}</p>
        </div>
        <div>
          <p className="text-muted-foreground">Percentage</p>
          <p className="font-medium">{storage.percentage.toFixed(1)}%</p>
        </div>
      </div>
      {storage.percentage > 80 && (
        <Alert>
          <AlertTriangle className="h-4 w-4" />
          <AlertDescription>
            Storage is {storage.percentage.toFixed(0)}% full. Consider clearing old data.
          </AlertDescription>
        </Alert>
      )}
    </div>
  );
};

// Mobile-Optimized Photo Capture Interface for STR Certified

import React, { useState, useRef, useEffect } from 'react';
import { Button } from '@/components/ui/button';
import { Card } from '@/components/ui/card';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { Progress } from '@/components/ui/progress';
import {
  Camera,
  CameraOff,
  Check,
  RefreshCw,
  X,
  ZoomIn,
  FlipHorizontal,
  Maximize2,
  Eye,
  EyeOff,
  Settings,
  Download,
  ChevronUp,
  ChevronDown,
  Loader2
} from 'lucide-react';
import { useCamera } from '@/hooks/useCamera';
import { usePhotoGuidance } from '@/hooks/usePhotoGuidance';
import { usePhotoComparison } from '@/hooks/usePhotoComparison';
import { PhotoQualityIndicator } from './PhotoQualityIndicator';
import { ReferencePhotoOverlay } from '@/components/photo/ReferencePhotoOverlay';
import { PhotoGuidanceOverlay } from '@/components/photo/PhotoGuidanceOverlay';
import { cn } from '@/lib/utils';
import type { PhotoGuidance, ChecklistItem } from '@/types/photo';

interface PhotoCaptureInterfaceProps {
  checklistItem: ChecklistItem;
  referencePhotoUrl?: string;
  onPhotoCapture: (photo: File, metadata: PhotoCaptureMetadata) => void;
  onCancel?: () => void;
  className?: string;
}

export interface PhotoCaptureMetadata {
  checklistItemId: string;
  roomName: string;
  captureTime: Date;
  deviceInfo: {
    userAgent: string;
    screenResolution: string;
    cameraCapabilities: MediaTrackCapabilities | null;
  };
  qualityScore: number;
  guidanceFollowed: boolean;
}

export const PhotoCaptureInterface: React.FC<PhotoCaptureInterfaceProps> = ({
  checklistItem,
  referencePhotoUrl,
  onPhotoCapture,
  onCancel,
  className
}) => {
  // State management
  const [capturedPhoto, setCapturedPhoto] = useState<string | null>(null);
  const [isProcessing, setIsProcessing] = useState(false);
  const [showReference, setShowReference] = useState(true);
  const [referenceOpacity, setReferenceOpacity] = useState(30);
  const [showGuidance, setShowGuidance] = useState(true);
  const [expandedView, setExpandedView] = useState(false);
  const [currentQuality, setCurrentQuality] = useState<PhotoGuidance | null>(null);

  // Refs
  const videoRef = useRef<HTMLVideoElement>(null);
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const captureTimeoutRef = useRef<NodeJS.Timeout | null>(null);

  // Custom hooks
  const {
    stream,
    isReady,
    error: cameraError,
    hasPermission,
    isLoading: cameraLoading,
    requestPermission,
    switchCamera,
    getCurrentCamera,
    getCameraCapabilities,
    stopCamera
  } = useCamera({
    videoRef,
    facingMode: 'environment',
    resolution: { width: 1920, height: 1080 }
  });

  const {
    currentStep,
    guidance,
    isComplete,
    nextStep,
    previousStep,
    resetGuidance,
    getProgress
  } = usePhotoGuidance({
    checklistItem,
    referencePhoto: referencePhotoUrl
  });

  const {
    checkPhotoQuality,
    qualityGuidance,
    isCheckingQuality,
    startRealTimeFeedback,
    stopRealTimeFeedback,
    realTimeGuidance
  } = usePhotoComparison({
    enableHistory: false
  });

  // Start real-time feedback when camera is ready
  useEffect(() => {
    if (stream && isReady && showGuidance) {
      startRealTimeFeedback(stream);
      return () => {
        stopRealTimeFeedback();
      };
    }
  }, [stream, isReady, showGuidance, startRealTimeFeedback, stopRealTimeFeedback]);

  // Update current quality from real-time guidance
  useEffect(() => {
    if (realTimeGuidance) {
      setCurrentQuality(realTimeGuidance);
    }
  }, [realTimeGuidance]);

  // Handle photo capture
  const handleCapture = async () => {
    if (!videoRef.current || !canvasRef.current || !currentQuality) return;

    setIsProcessing(true);

    try {
      // Set canvas dimensions to match video
      const video = videoRef.current;
      const canvas = canvasRef.current;
      canvas.width = video.videoWidth;
      canvas.height = video.videoHeight;

      // Draw video frame to canvas
      const ctx = canvas.getContext('2d');
      if (!ctx) throw new Error('Failed to get canvas context');

      ctx.drawImage(video, 0, 0);

      // Convert to blob
      const blob = await new Promise<Blob>((resolve, reject) => {
        canvas.toBlob(
          (blob) => {
            if (blob) resolve(blob);
            else reject(new Error('Failed to create blob'));
          },
          'image/jpeg',
          0.95
        );
      });

      // Create File object
      const file = new File([blob], `inspection_${Date.now()}.jpg`, {
        type: 'image/jpeg'
      });

      // Get device capabilities
      const capabilities = getCameraCapabilities();

      // Create metadata
      const metadata: PhotoCaptureMetadata = {
        checklistItemId: checklistItem.id,
        roomName: checklistItem.roomName || 'Unknown Room',
        captureTime: new Date(),
        deviceInfo: {
          userAgent: navigator.userAgent,
          screenResolution: `${window.screen.width}x${window.screen.height}`,
          cameraCapabilities: capabilities
        },
        qualityScore: currentQuality.qualityScore,
        guidanceFollowed: guidance.stepsCompleted >= guidance.totalSteps * 0.8
      };

      // Set captured photo for preview
      setCapturedPhoto(URL.createObjectURL(blob));

      // Delay to show preview
      setTimeout(() => {
        onPhotoCapture(file, metadata);
      }, 1500);

    } catch (error) {
      console.error('Capture error:', error);
      setIsProcessing(false);
    }
  };

  // Handle retake
  const handleRetake = () => {
    if (capturedPhoto) {
      URL.revokeObjectURL(capturedPhoto);
    }
    setCapturedPhoto(null);
    setIsProcessing(false);
    resetGuidance();
  };

  // Handle permission request
  const handlePermissionRequest = async () => {
    await requestPermission();
  };

  // Cleanup on unmount
  useEffect(() => {
    return () => {
      if (capturedPhoto) {
        URL.revokeObjectURL(capturedPhoto);
      }
      if (captureTimeoutRef.current) {
        clearTimeout(captureTimeoutRef.current);
      }
      stopCamera();
    };
  }, [capturedPhoto, stopCamera]);

  // Render permission screen
  if (!hasPermission) {
    return (
      <div className={cn(
        'fixed inset-0 bg-background flex flex-col items-center justify-center p-4',
        className
      )}>
        <Card className="w-full max-w-md p-6 space-y-4">
          <div className="text-center space-y-2">
            <CameraOff className="h-12 w-12 text-muted-foreground mx-auto" />
            <h2 className="text-xl font-semibold">Camera Permission Required</h2>
            <p className="text-sm text-muted-foreground">
              STR Certified needs camera access to capture inspection photos
            </p>
          </div>

          <Button
            onClick={handlePermissionRequest}
            className="w-full"
            size="lg"
          >
            <Camera className="h-5 w-5 mr-2" />
            Grant Camera Access
          </Button>

          {cameraError && (
            <Alert variant="destructive">
              <AlertDescription>{cameraError}</AlertDescription>
            </Alert>
          )}

          {onCancel && (
            <Button
              variant="outline"
              onClick={onCancel}
              className="w-full"
            >
              Cancel
            </Button>
          )}
        </Card>
      </div>
    );
  }

  // Render loading screen
  if (cameraLoading || !isReady) {
    return (
      <div className={cn(
        'fixed inset-0 bg-background flex items-center justify-center',
        className
      )}>
        <div className="text-center space-y-4">
          <Loader2 className="h-8 w-8 animate-spin text-primary mx-auto" />
          <p className="text-muted-foreground">Initializing camera...</p>
        </div>
      </div>
    );
  }

  // Main capture interface
  return (
    <div className={cn(
      'fixed inset-0 bg-black flex flex-col',
      expandedView && 'z-50',
      className
    )}>
      {/* Header */}
      <div className="relative z-20 bg-gradient-to-b from-black/70 to-transparent p-4">
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-2">
            <Button
              variant="ghost"
              size="icon"
              onClick={onCancel}
              className="text-white hover:bg-white/20"
            >
              <X className="h-5 w-5" />
            </Button>
            <div className="text-white">
              <h3 className="font-medium">{checklistItem.title}</h3>
              <p className="text-xs opacity-80">{checklistItem.roomName}</p>
            </div>
          </div>

          <div className="flex items-center space-x-2">
            <Button
              variant="ghost"
              size="icon"
              onClick={() => setExpandedView(!expandedView)}
              className="text-white hover:bg-white/20"
            >
              <Maximize2 className="h-5 w-5" />
            </Button>
            <Button
              variant="ghost"
              size="icon"
              onClick={switchCamera}
              className="text-white hover:bg-white/20"
            >
              <FlipHorizontal className="h-5 w-5" />
            </Button>
          </div>
        </div>

        {/* Guidance Progress */}
        {showGuidance && (
          <div className="mt-4 space-y-2">
            <div className="flex items-center justify-between text-white text-xs">
              <span>Step {currentStep + 1} of {guidance.totalSteps}</span>
              <span>{Math.round(getProgress())}% Complete</span>
            </div>
            <Progress value={getProgress()} className="h-1 bg-white/20" />
          </div>
        )}
      </div>

      {/* Camera View */}
      <div className="relative flex-1 overflow-hidden">
        {/* Video Preview */}
        <video
          ref={videoRef}
          autoPlay
          playsInline
          muted
          className="absolute inset-0 w-full h-full object-cover"
        />

        {/* Reference Photo Overlay */}
        {referencePhotoUrl && showReference && (
          <ReferencePhotoOverlay
            photoUrl={referencePhotoUrl}
            opacity={referenceOpacity}
            onOpacityChange={setReferenceOpacity}
            onClose={() => setShowReference(false)}
          />
        )}

        {/* Quality Indicator */}
        {currentQuality && (
          <div className="absolute top-4 right-4 z-10">
            <PhotoQualityIndicator
              quality={currentQuality}
              compact={!expandedView}
            />
          </div>
        )}

        {/* Guidance Overlay */}
        {showGuidance && currentQuality && (
          <PhotoGuidanceOverlay
            guidance={currentQuality}
            referencePhotoUrl={referencePhotoUrl}
            onCapture={handleCapture}
            onRetake={handleRetake}
            isCapturing={isProcessing}
            showReferencePhoto={false}
            className="absolute inset-0"
          />
        )}

        {/* Captured Photo Preview */}
        {capturedPhoto && (
          <div className="absolute inset-0 bg-black/90 flex items-center justify-center z-30">
            <div className="relative max-w-full max-h-full">
              <img
                src={capturedPhoto}
                alt="Captured photo"
                className="max-w-full max-h-full object-contain"
              />
              <div className="absolute inset-0 flex items-center justify-center">
                <div className="bg-green-500 text-white p-4 rounded-full animate-pulse">
                  <Check className="h-8 w-8" />
                </div>
              </div>
            </div>
          </div>
        )}

        {/* Canvas for capture (hidden) */}
        <canvas ref={canvasRef} className="hidden" />
      </div>

      {/* Bottom Controls */}
      <div className="relative z-20 bg-gradient-to-t from-black/70 to-transparent p-4">
        {/* Quick Settings */}
        <div className="flex items-center justify-center space-x-4 mb-4">
          <Button
            variant="ghost"
            size="sm"
            onClick={() => setShowReference(!showReference)}
            className="text-white hover:bg-white/20"
            disabled={!referencePhotoUrl}
          >
            {showReference ? <EyeOff className="h-4 w-4" /> : <Eye className="h-4 w-4" />}
            <span className="ml-1 text-xs">Reference</span>
          </Button>

          <Button
            variant="ghost"
            size="sm"
            onClick={() => setShowGuidance(!showGuidance)}
            className="text-white hover:bg-white/20"
          >
            <Settings className="h-4 w-4" />
            <span className="ml-1 text-xs">Guidance</span>
          </Button>
        </div>

        {/* Capture Controls */}
        <div className="flex items-center justify-center space-x-4">
          {/* Previous Step */}
          <Button
            variant="ghost"
            size="icon"
            onClick={previousStep}
            disabled={currentStep === 0 || isProcessing}
            className="text-white hover:bg-white/20"
          >
            <ChevronDown className="h-5 w-5" />
          </Button>

          {/* Capture Button */}
          <Button
            size="lg"
            onClick={handleCapture}
            disabled={isProcessing || !currentQuality?.isAcceptable}
            className={cn(
              'h-16 w-16 rounded-full shadow-xl transition-all',
              currentQuality?.isAcceptable
                ? 'bg-white hover:bg-gray-100'
                : 'bg-red-500 hover:bg-red-600'
            )}
          >
            {isProcessing ? (
              <Loader2 className="h-6 w-6 animate-spin text-black" />
            ) : (
              <Camera className="h-6 w-6 text-black" />
            )}
          </Button>

          {/* Next Step */}
          <Button
            variant="ghost"
            size="icon"
            onClick={nextStep}
            disabled={currentStep >= guidance.totalSteps - 1 || isProcessing}
            className="text-white hover:bg-white/20"
          >
            <ChevronUp className="h-5 w-5" />
          </Button>
        </div>

        {/* Current Guidance Text */}
        {guidance.currentGuidance && (
          <div className="mt-4 text-center">
            <p className="text-white text-sm">
              {guidance.currentGuidance}
            </p>
          </div>
        )}
      </div>
    </div>
  );
};

// Simplified Mobile Capture for Quick Photos
export const QuickPhotoCapture: React.FC<{
  onCapture: (photo: File) => void;
  onCancel: () => void;
}> = ({ onCapture, onCancel }) => {
  const videoRef = useRef<HTMLVideoElement>(null);
  const canvasRef = useRef<HTMLCanvasElement>(null);

  const { stream, isReady, hasPermission, requestPermission } = useCamera({
    videoRef,
    facingMode: 'environment'
  });

  const handleQuickCapture = async () => {
    if (!videoRef.current || !canvasRef.current) return;

    const video = videoRef.current;
    const canvas = canvasRef.current;
    canvas.width = video.videoWidth;
    canvas.height = video.videoHeight;

    const ctx = canvas.getContext('2d');
    if (!ctx) return;

    ctx.drawImage(video, 0, 0);

    canvas.toBlob((blob) => {
      if (blob) {
        const file = new File([blob], `quick_${Date.now()}.jpg`, {
          type: 'image/jpeg'
        });
        onCapture(file);
      }
    }, 'image/jpeg', 0.9);
  };

  if (!hasPermission) {
    return (
      <div className="fixed inset-0 bg-black flex items-center justify-center p-4">
        <Button onClick={requestPermission} size="lg" variant="outline">
          <Camera className="h-5 w-5 mr-2" />
          Enable Camera
        </Button>
      </div>
    );
  }

  return (
    <div className="fixed inset-0 bg-black">
      <video
        ref={videoRef}
        autoPlay
        playsInline
        muted
        className="w-full h-full object-cover"
      />
      <canvas ref={canvasRef} className="hidden" />

      <div className="absolute bottom-0 inset-x-0 p-6 flex items-center justify-center space-x-4">
        <Button
          variant="outline"
          size="lg"
          onClick={onCancel}
          className="bg-white/10 backdrop-blur text-white border-white/20"
        >
          Cancel
        </Button>
        <Button
          size="lg"
          onClick={handleQuickCapture}
          disabled={!isReady}
          className="h-16 w-16 rounded-full bg-white shadow-xl"
        >
          <Camera className="h-6 w-6 text-black" />
        </Button>
      </div>
    </div>
  );
};

import React, { useState, useRef, useCallback, useEffect } from 'react';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Alert, AlertDescription, AlertTitle } from '@/components/ui/alert';
import { Progress } from '@/components/ui/progress';
import { 
  Camera, 
  RotateCcw, 
  CheckCircle, 
  X, 
  AlertTriangle,
  Zap,
  Eye,
  Target,
  Lightbulb,
  RefreshCw
} from 'lucide-react';
import { useCamera } from '@/hooks/useCamera';
import type { DynamicChecklistItem } from '@/lib/ai/dynamic-checklist-generator';
import { STRCertifiedAIService } from '@/lib/ai/openai-service';
import { createPhotoQualityChecker } from '@/lib/ai/photo-quality-checker';
import { checklistService } from '@/services/checklistService';
import { mediaCompressionService } from '@/services/mediaCompressionService';
import { logger } from '@/utils/logger';

// Checklist interface for workflow compatibility  
interface ChecklistData {
  items: DynamicChecklistItem[];
  estimatedTime: number;
  totalItems: number;
}

interface PhotoResult {
  photo: File;
  analysis: {
    score: number;
    issues: string[];
    suggestions: string[];
  };
}

interface PhotoGuidanceProps {
  checklist: ChecklistData;
  onPhotoCapture: (roomType: string) => Promise<PhotoResult>;
  onAllPhotosComplete: () => void;
  onPhotoStored?: (itemId: string, photoFile: File, analysis: any) => void;
  inspectionId?: string;
}

export function PhotoGuidance({ 
  checklist, 
  onPhotoCapture, 
  onAllPhotosComplete,
  onPhotoStored,
  inspectionId
}: PhotoGuidanceProps) {
  const videoRef = useRef<HTMLVideoElement | null>(null);
  const [currentItemIndex, setCurrentItemIndex] = useState(0);
  const [capturedPhotos, setCapturedPhotos] = useState<Record<string, PhotoResult>>({});
  const [isCapturing, setIsCapturing] = useState(false);
  const [captureProgress, setCaptureProgress] = useState(0);

  const {
    isReady,
    error: cameraError,
    hasPermission,
    isLoading: cameraLoading,
    availableDevices,
    requestPermission,
    startCamera,
    stopCamera,
    switchCamera,
    takePhoto
  } = useCamera({
    videoRef,
    facingMode: 'environment',
    resolution: { width: 1920, height: 1080 },
    autoStart: true
  });

  const currentItem = checklist?.items[currentItemIndex];
  const totalItems = checklist?.items.length || 0;
  const completedPhotos = Object.keys(capturedPhotos).length;

  // Photo capture handler
  const handlePhotoCapture = useCallback(async () => {
    if (!currentItem || !isReady) return;

    setIsCapturing(true);
    setCaptureProgress(0);

    try {
      // Simulate progress updates
      const progressInterval = setInterval(() => {
        setCaptureProgress(prev => Math.min(prev + 10, 90));
      }, 150);

      // Take photo using camera hook
      const photoBlob = await takePhoto();
      if (!photoBlob) {
        throw new Error('Failed to capture photo');
      }

      // Convert blob to file
      const originalPhotoFile = new File([photoBlob], `${currentItem.id}_${Date.now()}.jpg`, {
        type: 'image/jpeg',
        lastModified: Date.now(),
      });

      // Compress photo for mobile optimization
      let photoFile = originalPhotoFile;
      try {
        setCaptureProgress(20);

        // Check if compression is needed
        if (mediaCompressionService.shouldCompressFile(originalPhotoFile)) {
          const compressionResult = await mediaCompressionService.compressPhoto(originalPhotoFile, {
            quality: 0.8,
            maxWidth: 1920,
            maxHeight: 1080,
            outputFormat: 'jpeg',
            stripMetadata: true
          });

          photoFile = compressionResult.compressedFile;

          logger.info('Photo compressed successfully', {
            originalSize: compressionResult.originalSize,
            compressedSize: compressionResult.compressedSize,
            compressionRatio: compressionResult.compressionRatio,
            timeTaken: compressionResult.timeTaken,
            savedBytes: compressionResult.originalSize - compressionResult.compressedSize
          }, 'PHOTO_GUIDANCE');
        }
      } catch (compressionError) {
        logger.warn('Photo compression failed, using original', compressionError, 'PHOTO_GUIDANCE');
        photoFile = originalPhotoFile;
      }

      // Real AI analysis using OpenAI service
      const aiService = new STRCertifiedAIService();
      const qualityChecker = createPhotoQualityChecker();

      try {
        // First check photo quality
        const qualityGuidance = await qualityChecker.analyzePhotoWithGuidance(photoFile);

        if (!qualityGuidance.isAcceptable) {
          clearInterval(progressInterval);
          setCaptureProgress(100);

          // Return quality issues to user
          const qualityResult = {
            photo: photoFile,
            analysis: {
              score: qualityGuidance.qualityScore,
              issues: qualityGuidance.messages.map(m => m.message),
              suggestions: qualityGuidance.messages.filter(m => m.action).map(m => m.action || '')
            }
          };

          setCapturedPhotos(prev => ({
            ...prev,
            [currentItem.id]: qualityResult
          }));

          // Store photo even if quality is poor for improvement
          if (onPhotoStored) {
            onPhotoStored(currentItem.id, photoFile, qualityResult.analysis);
          }

          // Update checklist item with quality issues
          if (inspectionId) {
            try {
              await checklistService.completeChecklistItem(
                currentItem.id,
                {
                  id: currentItem.id,
                  aiScore: qualityResult.analysis.score,
                  aiConfidence: qualityGuidance.qualityScore / 100,
                  aiReasoning: qualityGuidance.messages.map(m => m.message).join('; '),
                  suggestions: qualityGuidance.messages.filter(m => m.action).map(m => m.action || ''),
                  issues: qualityGuidance.messages.map(m => m.message),
                  passed: false, // Failed quality check
                  requiresReview: true // Always require review for quality issues
                },
                [photoFile], // photos array
                undefined, // no videos
                'Photo quality issues detected - retake recommended', // notes
                false // no user override
              );

              logger.info('Checklist item completed with quality issues', {
                itemId: currentItem.id,
                qualityScore: qualityGuidance.qualityScore,
                issues: qualityGuidance.messages.length
              }, 'PHOTO_GUIDANCE');
            } catch (error) {
              logger.error('Failed to complete checklist item with quality issues', error, 'PHOTO_GUIDANCE');
              // Don't block the workflow - continue with photo capture
            }
          }

          return;
        }

        // If quality is acceptable, proceed with AI analysis
        const aiResult = await aiService.analyzeInspectionPhoto(
          photoFile,
          currentItem.gptPrompt || currentItem.description,
          {
            includeQualityMetrics: true,
            includeImprovementSuggestions: true,
            roomType: currentItem.roomType || currentItem.category
          }
        );

        clearInterval(progressInterval);
        setCaptureProgress(100);

        // Create result with real AI analysis
        const analysisResult = {
          photo: photoFile,
          analysis: {
            score: Math.round(aiResult.confidence * 100),
            issues: aiResult.issues || [],
            suggestions: aiResult.suggestions || ['Analysis completed successfully']
          }
        };

        setCapturedPhotos(prev => ({
          ...prev,
          [currentItem.id]: analysisResult
        }));

        // Store photo data in parent component for database sync
        if (onPhotoStored) {
          onPhotoStored(currentItem.id, photoFile, analysisResult.analysis);
        }

        // Update checklist item status using the service
        if (inspectionId) {
          try {
            await checklistService.completeChecklistItem(
              currentItem.id,
              {
                id: currentItem.id,
                aiScore: analysisResult.analysis.score,
                aiConfidence: aiResult.confidence,
                aiReasoning: aiResult.reasoning || 'Photo analysis completed',
                suggestions: aiResult.suggestions || [],
                issues: aiResult.issues || [],
                passed: analysisResult.analysis.score > 80,
                requiresReview: analysisResult.analysis.score < 60
              },
              [photoFile], // photos array
              undefined, // no videos
              undefined, // no notes
              false // no user override
            );

            logger.info('Checklist item completed successfully', {
              itemId: currentItem.id,
              score: analysisResult.analysis.score,
              confidence: aiResult.confidence
            }, 'PHOTO_GUIDANCE');
          } catch (error) {
            logger.error('Failed to complete checklist item', error, 'PHOTO_GUIDANCE');
            // Don't block the workflow - continue with photo capture
          }
        }

        // Also call the parent handler for workflow integration
        await onPhotoCapture(currentItem.roomType || currentItem.category);

      } catch (error) {
        console.error('AI analysis failed:', error);
        clearInterval(progressInterval);
        setCaptureProgress(100);

        // Fallback to basic analysis
        const fallbackResult = {
          photo: photoFile,
          analysis: {
            score: 70,
            issues: ['AI analysis unavailable'],
            suggestions: ['Photo captured, manual review recommended']
          }
        };

        setCapturedPhotos(prev => ({
          ...prev,
          [currentItem.id]: fallbackResult
        }));

        // Store photo data in parent component for database sync
        if (onPhotoStored) {
          onPhotoStored(currentItem.id, photoFile, fallbackResult.analysis);
        }

        // Update checklist item status with fallback analysis
        if (inspectionId) {
          try {
            await checklistService.completeChecklistItem(
              currentItem.id,
              {
                id: currentItem.id,
                aiScore: fallbackResult.analysis.score,
                aiConfidence: 0.5, // Low confidence for fallback
                aiReasoning: 'AI analysis unavailable, manual review recommended',
                suggestions: fallbackResult.analysis.suggestions,
                issues: fallbackResult.analysis.issues,
                passed: false, // Conservative approach when AI fails
                requiresReview: true // Always require review when AI fails
              },
              [photoFile], // photos array
              undefined, // no videos
              'AI analysis failed - manual review required', // notes
              false // no user override
            );

            logger.info('Checklist item completed with fallback analysis', {
              itemId: currentItem.id,
              score: fallbackResult.analysis.score
            }, 'PHOTO_GUIDANCE');
          } catch (error) {
            logger.error('Failed to complete checklist item with fallback', error, 'PHOTO_GUIDANCE');
            // Don't block the workflow - continue with photo capture
          }
        }

        // Still call parent handler
        await onPhotoCapture(currentItem.roomType || currentItem.category);
      }

      // Result is already stored in the AI analysis section above

      // Move to next item or complete
      if (currentItemIndex < totalItems - 1) {
        setCurrentItemIndex(prev => prev + 1);
      } else {
        // All photos captured
        onAllPhotosComplete();
      }

    } catch (error) {
      console.error('Photo capture failed:', error);
    } finally {
      setIsCapturing(false);
      setCaptureProgress(0);
    }
  }, [currentItem, isReady, takePhoto, onPhotoCapture, currentItemIndex, totalItems, onAllPhotosComplete]);

  const handleRetake = useCallback(() => {
    if (currentItem) {
      const newCapturedPhotos = { ...capturedPhotos };
      delete newCapturedPhotos[currentItem.id];
      setCapturedPhotos(newCapturedPhotos);
    }
  }, [currentItem, capturedPhotos]);

  const handleSkipItem = useCallback(() => {
    if (currentItemIndex < totalItems - 1) {
      setCurrentItemIndex(prev => prev + 1);
    } else {
      onAllPhotosComplete();
    }
  }, [currentItemIndex, totalItems, onAllPhotosComplete]);

  const handlePreviousItem = useCallback(() => {
    if (currentItemIndex > 0) {
      setCurrentItemIndex(prev => prev - 1);
    }
  }, [currentItemIndex]);

  const getCategoryIcon = (category: string) => {
    const icons: Record<string, React.ReactNode> = {
      safety: <AlertTriangle className="h-4 w-4" />,
      bedrooms: <Eye className="h-4 w-4" />,
      bathrooms: <Eye className="h-4 w-4" />,
      kitchen: <Eye className="h-4 w-4" />,
      general: <Target className="h-4 w-4" />
    };

    return icons[category] || <Camera className="h-4 w-4" />;
  };

  const getPhotoGuidelines = (item: DynamicChecklistItem): string[] => {
    const baseGuidelines = [
      'Ensure good lighting and clear visibility',
      'Hold the camera steady to avoid blur',
      'Capture the entire subject in frame',
      'Avoid shadows and reflections when possible'
    ];

    const categoryGuidelines: Record<string, string[]> = {
      safety: [
        'Focus on safety equipment and hazards',
        'Show condition and accessibility clearly',
        'Include any warning labels or signs'
      ],
      kitchen: [
        'Show appliances and their condition',
        'Capture cleanliness of surfaces',
        'Include storage and organization'
      ],
      bathrooms: [
        'Test and show water pressure',
        'Check for leaks or damage',
        'Show cleanliness and supplies'
      ],
      bedrooms: [
        'Show bed quality and linens',
        'Capture overall room condition',
        'Include lighting and furniture'
      ]
    };

    return [...baseGuidelines, ...(categoryGuidelines[item.category] || [])];
  };

  // Camera permission handling
  if (!hasPermission && !cameraLoading) {
    return (
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Camera className="h-5 w-5" />
            Camera Permission Required
          </CardTitle>
          <CardDescription>
            We need access to your camera to capture inspection photos
          </CardDescription>
        </CardHeader>
        <CardContent className="space-y-4">
          <Alert>
            <AlertTriangle className="h-4 w-4" />
            <AlertTitle>Camera Access Needed</AlertTitle>
            <AlertDescription>
              To continue with the photo documentation step, please allow camera access when prompted.
            </AlertDescription>
          </Alert>
          <Button onClick={requestPermission} className="w-full">
            <Camera className="h-4 w-4 mr-2" />
            Enable Camera
          </Button>
        </CardContent>
      </Card>
    );
  }

  if (cameraError) {
    return (
      <Alert variant="destructive">
        <AlertTriangle className="h-4 w-4" />
        <AlertTitle>Camera Error</AlertTitle>
        <AlertDescription className="mt-2">
          {cameraError}
          <Button 
            variant="outline" 
            size="sm" 
            className="mt-2 ml-2" 
            onClick={() => window.location.reload()}
          >
            <RefreshCw className="h-4 w-4 mr-2" />
            Retry
          </Button>
        </AlertDescription>
      </Alert>
    );
  }

  if (!currentItem) {
    return (
      <Card>
        <CardContent className="text-center py-12">
          <CheckCircle className="h-12 w-12 mx-auto text-green-500 mb-4" />
          <h3 className="text-lg font-medium text-gray-900 dark:text-gray-100 mb-2">
            All Photos Captured!
          </h3>
          <p className="text-gray-600 dark:text-gray-400">
            You've completed the photo documentation step.
          </p>
        </CardContent>
      </Card>
    );
  }

  const isCurrentItemCompleted = capturedPhotos[currentItem.id];
  const currentPhotoResult = capturedPhotos[currentItem.id];

  return (
    <div className="space-y-4 pb-6">
      {/* Progress Header */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Camera className="h-5 w-5" />
            Photo Documentation
          </CardTitle>
          <CardDescription>
            Capture photos for each checklist item ({completedPhotos}/{totalItems} completed)
          </CardDescription>
        </CardHeader>
        <CardContent>
          <div className="space-y-2">
            <div className="flex items-center justify-between text-sm">
              <span>Progress</span>
              <span>{Math.round((completedPhotos / totalItems) * 100)}%</span>
