import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import { Progress } from '@/components/ui/progress';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Alert, AlertDescription, AlertTitle } from '@/components/ui/alert';
import { Separator } from '@/components/ui/separator';
import {
  CheckCircle,
  XCircle,
  AlertTriangle,
  RefreshCw,
  Home,
  MapPin,
  Users,
  Star,
  Wifi,
  Car,
  Utensils,
  Waves,
  Flame,
  Snowflake,
  Shield,
  Camera,
  Grid3X3,
  Image,
  Filter,
  Eye,
  Calendar,
  DollarSign
} from 'lucide-react';
import type {
  VRBOPropertyData,
  PropertyAmenity,
  PropertyRoom,
  PhotoData,
  PhotoDeduplicationResult,
  AmenityCategory,
  ScrapingState
} from '@/lib/scrapers/types';
import { cn } from '@/lib/utils';

interface PropertyDataDisplayProps {
  propertyData: VRBOPropertyData;
  photos?: PhotoData[];
  deduplicatedPhotos?: PhotoDeduplicationResult;
  scrapingState?: ScrapingState;
  className?: string;
  onRefreshData?: () => void;
  showPhotos?: boolean;
  showDetailedBreakdown?: boolean;
}

export const PropertyDataDisplay: React.FC<PropertyDataDisplayProps> = ({
  propertyData,
  photos = [],
  deduplicatedPhotos,
  scrapingState,
  className,
  onRefreshData,
  showPhotos = true,
  showDetailedBreakdown = true
}) => {
  const [selectedPhotoCategory, setSelectedPhotoCategory] = useState<string>('all');

  const getAmenityIcon = (category: AmenityCategory) => {
    const iconMap = {
      kitchen: Utensils,
      bathroom: Waves,
      bedroom: Home,
      entertainment: Grid3X3,
      outdoor: MapPin,
      safety: Shield,
      accessibility: Users,
      connectivity: Wifi,
      climate: Snowflake,
      parking: Car,
      laundry: RefreshCw,
      general: Home
    };

    return iconMap[category] || Home;
  };

  const getCompletionPercentage = () => {
    const requiredFields = ['title', 'description', 'amenities', 'photos', 'rooms'];
    const completedFields = requiredFields.filter(field => {
      const value = propertyData[field as keyof VRBOPropertyData];
      if (Array.isArray(value)) return value.length > 0;
      if (typeof value === 'string') return value.trim().length > 0;
      return !!value;
    });

    return Math.round((completedFields.length / requiredFields.length) * 100);
  };

  const filteredPhotos = selectedPhotoCategory === 'all' 
    ? photos 
    : photos.filter(photo => photo.category === selectedPhotoCategory);

  const photoCategories = Array.from(new Set(photos.map(photo => photo.category)));

  return (
    <div className={cn('w-full space-y-6', className)}>
      {/* Header with Status */}
      <Card>
        <CardHeader>
          <div className="flex items-center justify-between">
            <div className="space-y-1">
              <CardTitle className="flex items-center space-x-2">
                <Home className="h-5 w-5 text-blue-600" />
                <span>Property Data</span>
              </CardTitle>
              <CardDescription>
                Scraped from VRBO ‚Ä¢ Last updated {propertyData.lastUpdated.toLocaleString()}
              </CardDescription>
            </div>
            {onRefreshData && (
              <Button
                variant="outline"
                size="sm"
                onClick={onRefreshData}
                disabled={scrapingState?.status === 'scraping'}
                className="flex items-center space-x-1"
              >
                <RefreshCw className={cn(
