const getAmenityIcon = (category: AmenityCategory) => {
  const iconMap = {
    kitchen: Utensils,
    bathroom: Waves,
    bedroom: Home,
    entertainment: Grid3X3,
    outdoor: MapPin,
    safety: Shield,
    accessibility: Users,
    connectivity: Wifi,
    climate: Snowflake,
    parking: Car,
    laundry: RefreshCw,
    general: Home
  };

  return iconMap[category] || Home;
};

