import { useCallback } from 'react';
import { supabase } from '@/integrations/supabase/client';
import { getCachedRole } from '@/utils/mobileCacheUtils';

interface UseAuthInitializationProps {
  setSession: (session: any) => void;
  setUser: (user: any) => void;
  setUserRole: (role: string | null) => void;
  setLoading: (loading: boolean) => void;
  setError: (error: string | null) => void;
  fetchUserRole: (userId: string, useCache?: boolean) => Promise<string>;
  initializationRef: React.MutableRefObject<Promise<void> | null>;
}

export const useAuthInitialization = ({
  setSession,
  setUser,
  setUserRole,
  setLoading,
  setError,
  fetchUserRole,
  initializationRef
}: UseAuthInitializationProps) => {

  const initializeAuth = useCallback(async (): Promise<void> => {
    if (initializationRef.current) {
      return initializationRef.current;
    }

    console.log('üì± Mobile auth initialization...');
    setError(null);

    const initPromise = new Promise<void>(async (resolve) => {
      try {
        // Mobile-friendly timeout (2 seconds)
        const timeoutPromise = new Promise<never>((_, reject) => {
          setTimeout(() => reject(new Error('Mobile auth timeout')), 2000);
        });

        const sessionPromise = supabase.auth.getSession();

        const { data: { session } } = await Promise.race([sessionPromise, timeoutPromise]);

        setSession(session);
        setUser(session?.user ?? null);

        if (session?.user) {
          console.log('üì± Mobile user found, setting role...');
          const cachedRole = getCachedRole(session.user.id);
          if (cachedRole) {
            setUserRole(cachedRole);
          } else {
            setUserRole('inspector');
            // Background role fetch for mobile
            fetchUserRole(session.user.id, false).then(role => {
              setUserRole(role);
            }).catch(() => {
              console.warn('üì± Background mobile role fetch failed');
            });
          }
        } else {
          setUserRole(null);
          console.log('üì± No mobile session found');
        }

        setLoading(false);
        setError(null);
        console.log('üì± Mobile auth initialization complete');
        resolve();

      } catch (error) {
        console.error('üì± Mobile auth initialization error:', error);
        setUser(null);
        setSession(null);
        setUserRole(null);
        setError('Mobile authentication failed');
        setLoading(false);
        resolve();
      }
    });

    initializationRef.current = initPromise;
    return initPromise;
  }, [setSession, setUser, setUserRole, setLoading, setError, fetchUserRole, initializationRef]);

  return { initializeAuth };
};

import { useEffect } from 'react';
import { supabase } from '@/integrations/supabase/client';
import { getCachedRole } from '@/utils/mobileCacheUtils';

interface UseAuthStateListenerProps {
  setSession: (session: any) => void;
  setUser: (user: any) => void;
  setUserRole: (role: string | null) => void;
  setLoading: (loading: boolean) => void;
  setError: (error: string | null) => void;
  fetchUserRole: (userId: string, useCache?: boolean) => Promise<string>;
  initializeAuth: () => Promise<void>;
}

export const useAuthStateListener = ({
  setSession,
  setUser,
  setUserRole,
  setLoading,
  setError,
  fetchUserRole,
  initializeAuth
}: UseAuthStateListenerProps) => {

  useEffect(() => {
    let isMounted = true;

    const { data: { subscription } } = supabase.auth.onAuthStateChange(
      async (event, session) => {
        if (!isMounted) return;

        console.log('üì± Mobile auth state changed:', event, session?.user?.email);

        setSession(session);
        setUser(session?.user ?? null);

        if (session?.user) {
          const cachedRole = getCachedRole(session.user.id);
          if (cachedRole) {
            setUserRole(cachedRole);
          } else {
            setUserRole('inspector');

            // Background mobile role fetch
            fetchUserRole(session.user.id, false).then(role => {
              if (isMounted) {
                setUserRole(role);
              }
            }).catch(error => {
              console.error('üì± Background mobile role fetch failed:', error);
            });
          }
        } else {
          if (isMounted) {
            setUserRole(null);
          }
        }

        if (isMounted) {
          setLoading(false);
          setError(null);
        }
      }
    );

    initializeAuth().catch((error) => {
      if (isMounted) {
        console.error('üì± Failed to initialize mobile auth:', error);
        setLoading(false);
      }
    });

    return () => {
      isMounted = false;
      subscription.unsubscribe();
    };
  }, [setSession, setUser, setUserRole, setLoading, setError, fetchUserRole, initializeAuth]);
};

// Camera Hook for STR Certified Mobile Photo Capture

import { useState, useEffect, useCallback, useRef, MutableRefObject } from 'react';

export interface CameraOptions {
  videoRef: MutableRefObject<HTMLVideoElement | null>;
  facingMode?: 'user' | 'environment';
  resolution?: {
    width: number;
    height: number;
  };
  autoStart?: boolean;
}

export interface CameraState {
  stream: MediaStream | null;
  isReady: boolean;
  error: string | null;
  hasPermission: boolean;
  isLoading: boolean;
  currentDeviceId: string | null;
  availableDevices: MediaDeviceInfo[];
}

export interface UseCameraReturn extends CameraState {
  requestPermission: () => Promise<void>;
  startCamera: () => Promise<void>;
  stopCamera: () => void;
  switchCamera: () => Promise<void>;
  takePhoto: () => Promise<Blob | null>;
  getCurrentCamera: () => MediaDeviceInfo | null;
  getCameraCapabilities: () => MediaTrackCapabilities | null;
}

export const useCamera = (options: CameraOptions): UseCameraReturn => {
  const {
    videoRef,
    facingMode = 'environment',
    resolution = { width: 1920, height: 1080 },
    autoStart = true
  } = options;

  // State
  const [state, setState] = useState<CameraState>({
    stream: null,
    isReady: false,
    error: null,
    hasPermission: false,
    isLoading: true,
    currentDeviceId: null,
    availableDevices: []
  });

  // Refs
  const streamRef = useRef<MediaStream | null>(null);
  const permissionCheckRef = useRef<boolean>(false);

  // Check if media devices are supported
  const isSupported = useCallback(() => {
    return !!(navigator.mediaDevices && navigator.mediaDevices.getUserMedia);
  }, []);

  // Get available camera devices
  const getAvailableDevices = useCallback(async (): Promise<MediaDeviceInfo[]> => {
    try {
      const devices = await navigator.mediaDevices.enumerateDevices();
      return devices.filter(device => device.kind === 'videoinput');
    } catch (error) {
      console.error('Failed to enumerate devices:', error);
      return [];
    }
  }, []);

  // Check camera permission
  const checkPermission = useCallback(async (): Promise<boolean> => {
    if (!isSupported()) {
      setState(prev => ({
        ...prev,
        error: 'Camera not supported on this device',
        hasPermission: false,
        isLoading: false
      }));
      return false;
    }

    try {
      // Check if we already have permission by looking at device labels
      const devices = await getAvailableDevices();
      const hasLabels = devices.some(device => device.label !== '');

      if (hasLabels) {
        setState(prev => ({
          ...prev,
          hasPermission: true,
          availableDevices: devices
        }));
        return true;
      }

      // Try to check permission state if available (Chrome 64+)
      if (navigator.permissions && navigator.permissions.query) {
        try {
          const result = await navigator.permissions.query({ name: 'camera' as PermissionName });
          const granted = result.state === 'granted';
          setState(prev => ({
            ...prev,
            hasPermission: granted
          }));
          return granted;
        } catch (e) {
          // Permissions API not fully supported
        }
      }

      return false;
    } catch (error) {
      console.error('Permission check failed:', error);
      return false;
    }
  }, [isSupported, getAvailableDevices]);

  // Request camera permission
  const requestPermission = useCallback(async (): Promise<void> => {
    if (!isSupported()) {
      setState(prev => ({
        ...prev,
        error: 'Camera not supported on this device',
        hasPermission: false,
        isLoading: false
      }));
      return;
    }

    setState(prev => ({ ...prev, isLoading: true, error: null }));

    try {
      // Request a temporary stream to trigger permission prompt
      const tempStream = await navigator.mediaDevices.getUserMedia({
        video: true,
        audio: false
      });

      // Stop the temporary stream
      tempStream.getTracks().forEach(track => track.stop());

      // Update available devices
      const devices = await getAvailableDevices();

      setState(prev => ({
        ...prev,
        hasPermission: true,
        availableDevices: devices,
        isLoading: false
      }));

      // Auto-start camera if enabled
      if (autoStart) {
        await startCamera();
      }
    } catch (error: any) {
      let errorMessage = 'Failed to access camera';

      if (error.name === 'NotAllowedError' || error.name === 'PermissionDeniedError') {
        errorMessage = 'Camera permission denied';
      } else if (error.name === 'NotFoundError' || error.name === 'DevicesNotFoundError') {
        errorMessage = 'No camera found on this device';
      } else if (error.name === 'NotReadableError' || error.name === 'TrackStartError') {
        errorMessage = 'Camera is already in use by another application';
      } else if (error.name === 'OverconstrainedError' || error.name === 'ConstraintNotSatisfiedError') {
        errorMessage = 'Camera does not support the requested resolution';
      }

      setState(prev => ({
        ...prev,
        error: errorMessage,
        hasPermission: false,
        isLoading: false
      }));
    }
  }, [isSupported, getAvailableDevices, autoStart]);

  // Start camera with specified constraints
  const startCamera = useCallback(async (): Promise<void> => {
    if (!state.hasPermission) {
      await requestPermission();
      return;
    }

    setState(prev => ({ ...prev, isLoading: true, error: null }));

    try {
      // Stop existing stream if any
      if (streamRef.current) {
        streamRef.current.getTracks().forEach(track => track.stop());
      }

      // Build constraints
      const constraints: MediaStreamConstraints = {
        audio: false,
        video: {
          facingMode: state.currentDeviceId ? undefined : facingMode,
          deviceId: state.currentDeviceId ? { exact: state.currentDeviceId } : undefined,
          width: { ideal: resolution.width },
          height: { ideal: resolution.height }
        }
      };

      // Get stream
      const stream = await navigator.mediaDevices.getUserMedia(constraints);
      streamRef.current = stream;

      // Attach to video element
      if (videoRef.current) {
        videoRef.current.srcObject = stream;

        // Wait for video to be ready
        await new Promise<void>((resolve) => {
          if (videoRef.current) {
            videoRef.current.onloadedmetadata = () => {
              videoRef.current?.play();
              resolve();
            };
          }
        });
      }

      // Get current device ID
      const videoTrack = stream.getVideoTracks()[0];
      const settings = videoTrack.getSettings();

      setState(prev => ({
        ...prev,
        stream,
        isReady: true,
        isLoading: false,
        currentDeviceId: settings.deviceId || null
      }));
    } catch (error: any) {
      console.error('Failed to start camera:', error);

      setState(prev => ({
        ...prev,
        error: error.message || 'Failed to start camera',
        isReady: false,
        isLoading: false
      }));
    }
  }, [state.hasPermission, state.currentDeviceId, facingMode, resolution, videoRef, requestPermission]);

  // Stop camera
  const stopCamera = useCallback((): void => {
    if (streamRef.current) {
      streamRef.current.getTracks().forEach(track => track.stop());
      streamRef.current = null;
    }

    if (videoRef.current) {
      videoRef.current.srcObject = null;
    }

    setState(prev => ({
      ...prev,
      stream: null,
      isReady: false
    }));
  }, [videoRef]);

  // Switch between front and back camera
  const switchCamera = useCallback(async (): Promise<void> => {
    if (state.availableDevices.length < 2) {
      setState(prev => ({
        ...prev,
        error: 'Only one camera available'
      }));
      return;
    }

    // Find next device
    const currentIndex = state.availableDevices.findIndex(
      device => device.deviceId === state.currentDeviceId
    );
    const nextIndex = (currentIndex + 1) % state.availableDevices.length;
    const nextDevice = state.availableDevices[nextIndex];

    setState(prev => ({
      ...prev,
      currentDeviceId: nextDevice.deviceId
    }));

    // Restart camera with new device
    await startCamera();
  }, [state.availableDevices, state.currentDeviceId, startCamera]);

  // Take a photo
  const takePhoto = useCallback(async (): Promise<Blob | null> => {
    if (!videoRef.current || !state.isReady) {
      return null;
    }

    try {
      // Create canvas
      const canvas = document.createElement('canvas');
      const video = videoRef.current;

      canvas.width = video.videoWidth;
      canvas.height = video.videoHeight;

      const ctx = canvas.getContext('2d');
      if (!ctx) return null;

      // Draw video frame
      ctx.drawImage(video, 0, 0);

      // Convert to blob
      return new Promise<Blob | null>((resolve) => {
        canvas.toBlob(
          (blob) => resolve(blob),
          'image/jpeg',
          0.95
        );
      });
    } catch (error) {
      console.error('Failed to take photo:', error);
      return null;
    }
  }, [videoRef, state.isReady]);

  // Get current camera info
  const getCurrentCamera = useCallback((): MediaDeviceInfo | null => {
    if (!state.currentDeviceId) return null;
    return state.availableDevices.find(device => device.deviceId === state.currentDeviceId) || null;
  }, [state.currentDeviceId, state.availableDevices]);

  // Get camera capabilities
  const getCameraCapabilities = useCallback((): MediaTrackCapabilities | null => {
    if (!state.stream) return null;

    try {
      const videoTrack = state.stream.getVideoTracks()[0];
      if (videoTrack && 'getCapabilities' in videoTrack) {
        return videoTrack.getCapabilities();
      }
    } catch (error) {
      console.error('Failed to get capabilities:', error);
    }

    return null;
  }, [state.stream]);

  // Initial permission check
  useEffect(() => {
    if (!permissionCheckRef.current) {
      permissionCheckRef.current = true;
      checkPermission().then(hasPermission => {
        setState(prev => ({ ...prev, isLoading: false }));
        if (hasPermission && autoStart) {
          startCamera();
        }
      });
    }
  }, [checkPermission, autoStart, startCamera]);

  // Cleanup on unmount
  useEffect(() => {
    return () => {
      stopCamera();
    };
  }, [stopCamera]);

  return {
    ...state,
    requestPermission,
    startCamera,
    stopCamera,
    switchCamera,
    takePhoto,
    getCurrentCamera,
    getCameraCapabilities
  };
};

// Utility hook for checking camera support
export const useCameraSupport = () => {
  const [isSupported, setIsSupported] = useState(false);
  const [checkedSupport, setCheckedSupport] = useState(false);

  useEffect(() => {
    const checkSupport = async () => {
      const supported = !!(navigator.mediaDevices && navigator.mediaDevices.getUserMedia);
      setIsSupported(supported);
      setCheckedSupport(true);
    };

    checkSupport();
  }, []);

  return { isSupported, checkedSupport };
};

import { useRef, useCallback, useEffect } from 'react';
import { supabase } from '@/integrations/supabase/client';
import { debugLogger } from '@/utils/debugLogger';

// Global channel registry to prevent duplicate channels
const channelRegistry = new Map<string, any>();
const subscriptionRegistry = new Map<string, 'pending' | 'subscribed' | 'error'>();
const subscriptionPromises = new Map<string, Promise<void>>();

export const useChannelManager = () => {
  const channelsRef = useRef<Map<string, any>>(new Map());
  const isMountedRef = useRef(true);

  const createChannel = useCallback((channelName: string, config: any) => {
    debugLogger.debug('ChannelManager', 'Creating channel', { channelName });

    // Check if channel already exists globally
    if (channelRegistry.has(channelName)) {
      const existingChannel = channelRegistry.get(channelName);
      debugLogger.info('ChannelManager', 'Reusing existing channel', { channelName });

      // Store reference locally too
      if (!channelsRef.current.has(channelName)) {
        channelsRef.current.set(channelName, existingChannel);
      }

      return existingChannel;
    }

    // Clean up any existing channel with this name (defensive)
    if (channelsRef.current.has(channelName)) {
      const existingChannel = channelsRef.current.get(channelName);
      try {
        supabase.removeChannel(existingChannel);
        debugLogger.debug('ChannelManager', 'Cleaned up existing channel', { channelName });
      } catch (error) {
        debugLogger.warn('ChannelManager', 'Error removing existing channel', { channelName, error });
      }
    }

    debugLogger.info('ChannelManager', 'Creating new channel', { channelName });

    // Create new channel
    const channel = supabase.channel(channelName);

    // Apply configuration
    Object.keys(config).forEach(key => {
      if (typeof config[key] === 'function') {
        channel.on('postgres_changes', config[key].filter, config[key].callback);
      }
    });

    // Store in registries
    channelsRef.current.set(channelName, channel);
    channelRegistry.set(channelName, channel);
    subscriptionRegistry.set(channelName, 'pending');

    debugLogger.debug('ChannelManager', 'Channel created and stored', { channelName });
    return channel;
  }, []);

  const subscribeChannel = useCallback(async (channelName: string, onStatusChange?: (status: string) => void) => {
    if (!isMountedRef.current) {
      debugLogger.warn('ChannelManager', 'Attempted to subscribe to unmounted component', { channelName });
      return;
    }

    debugLogger.debug('ChannelManager', 'Subscribing to channel', { channelName });

    // Check if subscription is already in progress or completed
    const currentStatus = subscriptionRegistry.get(channelName);
    if (currentStatus === 'subscribed') {
      debugLogger.info('ChannelManager', 'Channel already subscribed', { channelName });
      onStatusChange?.('SUBSCRIBED');
      return;
    }

    if (currentStatus === 'pending') {
      debugLogger.info('ChannelManager', 'Subscription already pending', { channelName });
      // Wait for existing subscription to complete
      const existingPromise = subscriptionPromises.get(channelName);
      if (existingPromise) {
        await existingPromise;
        onStatusChange?.('SUBSCRIBED');
        return;
      }
    }

    const channel = channelsRef.current.get(channelName) || channelRegistry.get(channelName);
    if (!channel) {
      debugLogger.error('ChannelManager', 'Channel not found for subscription', { channelName });
      return;
    }

    // Create subscription promise to prevent race conditions
    const subscriptionPromise = new Promise<void>((resolve, reject) => {
      try {
        debugLogger.info('ChannelManager', 'Starting subscription', { channelName });
        subscriptionRegistry.set(channelName, 'pending');

        channel.subscribe((status: string) => {
          debugLogger.info('ChannelManager', 'Channel subscription status', { channelName, status });

          if (status === 'SUBSCRIBED') {
            subscriptionRegistry.set(channelName, 'subscribed');
            resolve();
          } else if (status === 'CHANNEL_ERROR') {
            subscriptionRegistry.set(channelName, 'error');
            reject(new Error('Channel subscription error'));
          }

          onStatusChange?.(status);
        });
      } catch (error) {
        debugLogger.error('ChannelManager', 'Error subscribing to channel', { channelName, error });
        subscriptionRegistry.set(channelName, 'error');
        reject(error);
      }
    });

    subscriptionPromises.set(channelName, subscriptionPromise);

    try {
      await subscriptionPromise;
      debugLogger.info('ChannelManager', 'Channel subscription successful', { channelName });
    } catch (error) {
      debugLogger.error('ChannelManager', 'Subscription failed', { channelName, error });
    } finally {
      subscriptionPromises.delete(channelName);
    }
  }, []);

  const cleanupChannel = useCallback((channelName: string) => {
    debugLogger.info('ChannelManager', 'Cleaning up channel', { channelName });

    const channel = channelsRef.current.get(channelName);
    if (channel) {
      try {
        supabase.removeChannel(channel);
        debugLogger.debug('ChannelManager', 'Channel removed from Supabase', { channelName });
      } catch (error) {
        debugLogger.warn('ChannelManager', 'Error removing channel', { channelName, error });
      }
    }

    // Clean up all references
    channelsRef.current.delete(channelName);
    channelRegistry.delete(channelName);
    subscriptionRegistry.delete(channelName);
    subscriptionPromises.delete(channelName);

    debugLogger.debug('ChannelManager', 'Channel cleanup complete', { channelName });
  }, []);

  const cleanupAllChannels = useCallback(() => {
    debugLogger.info('ChannelManager', 'Cleaning up all channels');

    const channelNames = Array.from(channelsRef.current.keys());
    channelNames.forEach(channelName => cleanupChannel(channelName));

    debugLogger.info('ChannelManager', 'All channels cleaned up', { count: channelNames.length });
  }, [cleanupChannel]);

  useEffect(() => {
    isMountedRef.current = true;
    debugLogger.debug('ChannelManager', 'Hook mounted');

    return () => {
      isMountedRef.current = false;
      debugLogger.debug('ChannelManager', 'Hook unmounting, cleaning up');
      cleanupAllChannels();
    };
  }, [cleanupAllChannels]);

  return {
    createChannel,
    subscribeChannel,
    cleanupChannel,
    cleanupAllChannels
  };
};

import { useState, useEffect } from 'react';
import { User } from '@supabase/supabase-js';
import { supabase } from '@/integrations/supabase/client';
import { debugLogger } from '@/utils/debugLogger';

export const useCleanAuth = () => {
  const [user, setUser] = useState<User | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    debugLogger.info('CleanAuth', 'Initializing clean auth system');

    let isMounted = true;

    const initializeAuth = async () => {
      try {
        debugLogger.debug('CleanAuth', 'Fetching session');
        const { data: { session }, error: sessionError } = await supabase.auth.getSession();

        if (!isMounted) return;

        if (sessionError) {
          debugLogger.error('CleanAuth', 'Session error', sessionError);
          setError(sessionError.message);
          setUser(null);
        } else {
          debugLogger.info('CleanAuth', 'Session fetched successfully', { 
            hasSession: !!session,
            userId: session?.user?.id,
            email: session?.user?.email 
          });
          setUser(session?.user ?? null);
          setError(null);
        }
      } catch (err) {
        if (!isMounted) return;

        debugLogger.error('CleanAuth', 'Auth initialization failed', err);
        const errorMessage = err instanceof Error ? err.message : 'Authentication failed';
        setError(errorMessage);
        setUser(null);
      } finally {
        if (isMounted) {
          setLoading(false);
        }
      }
    };

    initializeAuth();

    // Set up auth state listener
    const { data: { subscription } } = supabase.auth.onAuthStateChange(
      (event, session) => {
        if (!isMounted) return;

        debugLogger.info('CleanAuth', 'Auth state changed', { 
          event, 
          hasSession: !!session,
          userId: session?.user?.id 
        });

        setUser(session?.user ?? null);
        setError(null);

        if (loading) {
          setLoading(false);
        }
      }
    );

    return () => {
      isMounted = false;
      debugLogger.debug('CleanAuth', 'Cleaning up auth subscription');
      subscription.unsubscribe();
    };
  }, []);

  return {
    user,
    loading,
    error,
    isAuthenticated: !!user && !error
  };
};

import { useState, useEffect } from 'react';
import { useToast } from '@/hooks/use-toast';
import { supabase } from '@/integrations/supabase/client';

interface DataIntegrityCheck {
  id: string;
  type: 'missing_data' | 'orphaned_record' | 'duplicate_data' | 'invalid_state';
  severity: 'low' | 'medium' | 'high';
  message: string;
  data?: any;
}

export const useDataIntegrity = (inspectionId?: string) => {
  const [checks, setChecks] = useState<DataIntegrityCheck[]>([]);
  const [isChecking, setIsChecking] = useState(false);
  const { toast } = useToast();

  const runIntegrityChecks = async () => {
    if (!inspectionId) return;

    setIsChecking(true);
    const issues: DataIntegrityCheck[] = [];

    try {
      // Check for orphaned media items
      const { data: orphanedMedia } = await supabase
        .from('media')
        .select('id, checklist_item_id')
        .not('checklist_item_id', 'in', 
          `(SELECT id FROM checklist_items WHERE inspection_id = '${inspectionId}')`
        );

      if (orphanedMedia && orphanedMedia.length > 0) {
        issues.push({
          id: 'orphaned-media',
          type: 'orphaned_record',
          severity: 'medium',
          message: `Found ${orphanedMedia.length} orphaned media items`,
          data: orphanedMedia
        });
      }

      // Check for checklist items without required evidence
      const { data: itemsWithoutEvidence } = await supabase
        .from('checklist_items')
        .select('id, label, status')
        .eq('inspection_id', inspectionId)
        .in('status', ['completed', 'failed'])
        .not('id', 'in', 
          `(SELECT DISTINCT checklist_item_id FROM media)`
        );

      if (itemsWithoutEvidence && itemsWithoutEvidence.length > 0) {
        issues.push({
          id: 'missing-evidence',
          type: 'missing_data',
          severity: 'high',
          message: `${itemsWithoutEvidence.length} completed items missing evidence`,
          data: itemsWithoutEvidence
        });
      }

      // Check for duplicate checklist items
      const { data: duplicates } = await supabase
        .rpc('analyze_checklist_duplicates');

      if (duplicates && duplicates.length > 0) {
        const inspectionDuplicates = duplicates.filter(d => d.inspection_id === inspectionId);
        if (inspectionDuplicates.length > 0) {
          issues.push({
            id: 'duplicate-items',
            type: 'duplicate_data',
            severity: 'high',
            message: `Found duplicate checklist items`,
            data: inspectionDuplicates
          });
        }
      }

      setChecks(issues);

      if (issues.length > 0) {
        const highSeverityIssues = issues.filter(i => i.severity === 'high').length;
        if (highSeverityIssues > 0) {
          toast({
            title: "Data Integrity Issues Found",
            description: `Found ${highSeverityIssues} high-priority issues that need attention.`,
            variant: "destructive",
          });
        }
      }

    } catch (error) {
      console.error('Data integrity check failed:', error);
      toast({
        title: "Integrity Check Failed",
        description: "Unable to complete data integrity checks.",
        variant: "destructive",
      });
    } finally {
      setIsChecking(false);
    }
  };

  const fixIssue = async (issueId: string) => {
    const issue = checks.find(c => c.id === issueId);
    if (!issue) return;

    try {
      switch (issue.type) {
        case 'duplicate_data':
          if (issueId === 'duplicate-items') {
            await supabase.rpc('cleanup_duplicate_checklist_items');
            toast({
              title: "Duplicates Cleaned",
              description: "Duplicate checklist items have been removed.",
            });
          }
          break;

        case 'orphaned_record':
          if (issueId === 'orphaned-media' && issue.data) {
            const mediaIds = issue.data.map((item: any) => item.id);
            await supabase
              .from('media')
              .delete()
              .in('id', mediaIds);
            toast({
              title: "Orphaned Records Cleaned",
              description: "Orphaned media items have been removed.",
            });
          }
          break;
      }

      // Remove the fixed issue from the list
      setChecks(prev => prev.filter(c => c.id !== issueId));

    } catch (error) {
      console.error('Failed to fix issue:', error);
      toast({
        title: "Fix Failed",
        description: "Unable to fix the data integrity issue.",
        variant: "destructive",
      });
    }
  };

  useEffect(() => {
    if (inspectionId) {
      runIntegrityChecks();
    }
  }, [inspectionId]);

  return {
    checks,
    isChecking,
    runIntegrityChecks,
    fixIssue,
    hasIssues: checks.length > 0,
    highPriorityIssues: checks.filter(c => c.severity === 'high').length
  };
};

