import { useState, useEffect, useCallback } from "react";
import { useToast } from "@/hooks/use-toast";
import { useNetworkStatus } from "@/hooks/useNetworkStatus";

interface OfflinePhoto {
  id: string;
  checklistItemId: string;
  inspectionId: string;
  file: File;
  timestamp: number;
  uploaded: boolean;
  retryCount: number;
  lastError?: string;
}

interface OfflineStorageState {
  photos: OfflinePhoto[];
  syncInProgress: boolean;
  lastSyncAttempt: Date | null;
  totalPendingSize: number;
}

export const useEnhancedOfflineStorage = () => {
  const [storageState, setStorageState] = useState<OfflineStorageState>({
    photos: [],
    syncInProgress: false,
    lastSyncAttempt: null,
    totalPendingSize: 0
  });

  const isOnline = useNetworkStatus();
  const { toast } = useToast();

  // Calculate storage metrics
  const calculateStorageMetrics = useCallback((photos: OfflinePhoto[]) => {
    const totalSize = photos.reduce((acc, photo) => acc + photo.file.size, 0);
    return {
      totalPendingSize: totalSize,
      pendingCount: photos.filter(p => !p.uploaded).length,
      failedCount: photos.filter(p => p.retryCount > 0 && !p.uploaded).length
    };
  }, []);

  // Load offline photos on mount
  useEffect(() => {
    const loadOfflinePhotos = () => {
      try {
        const stored = localStorage.getItem('doublecheck_offline_photos_v2');
        if (stored) {
          const photos = JSON.parse(stored);
          const metrics = calculateStorageMetrics(photos);

          setStorageState(prev => ({
            ...prev,
            photos,
            totalPendingSize: metrics.totalPendingSize
          }));

          console.log('üì± Loaded offline photos:', {
            total: photos.length,
            pending: metrics.pendingCount,
            failed: metrics.failedCount,
            totalSize: `${(metrics.totalPendingSize / 1024 / 1024).toFixed(2)}MB`
          });
        }
      } catch (error) {
        console.error('üì± Error loading offline photos:', error);
        toast({
          title: "Storage Error",
          description: "Failed to load offline photos from storage.",
          variant: "destructive",
        });
      }
    };

    loadOfflinePhotos();
  }, [calculateStorageMetrics, toast]);

  // Auto-sync when coming back online
  useEffect(() => {
    if (isOnline && storageState.photos.some(p => !p.uploaded)) {
      console.log('üì± Network restored, starting auto-sync...');
      setTimeout(() => {
        syncOfflinePhotos();
      }, 2000); // Wait 2 seconds for network to stabilize
    }
  }, [isOnline]);

  const savePhotoOffline = useCallback(async (
    file: File,
    checklistItemId: string,
    inspectionId: string
  ): Promise<string> => {
    console.log('üì± Saving photo offline:', { 
      size: `${(file.size / 1024).toFixed(2)}KB`,
      type: file.type 
    });

    const photoId = `offline_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;

    const offlinePhoto: OfflinePhoto = {
      id: photoId,
      checklistItemId,
      inspectionId,
      file,
      timestamp: Date.now(),
      uploaded: false,
      retryCount: 0
    };

    const updatedPhotos = [...storageState.photos, offlinePhoto];
    const metrics = calculateStorageMetrics(updatedPhotos);

    setStorageState(prev => ({
      ...prev,
      photos: updatedPhotos,
      totalPendingSize: metrics.totalPendingSize
    }));

    // Save metadata to localStorage (without file data for size limits)
    try {
      const photoMetadata = updatedPhotos.map(photo => ({
        ...photo,
        file: null // Don't store file in localStorage
      }));

      localStorage.setItem('doublecheck_offline_photos_v2', JSON.stringify(photoMetadata));

      toast({
        title: "Photo saved offline",
        description: `Photo will sync when online. ${metrics.pendingCount + 1} pending.`,
      });
    } catch (error) {
      console.error('üì± Error saving to localStorage:', error);

      // If storage is full, try to clean up old uploaded photos
      cleanupUploadedPhotos();

      toast({
        title: "Storage Warning",
        description: "Local storage is getting full. Some photos may not be saved.",
        variant: "destructive",
      });
    }

    return photoId;
  }, [storageState.photos, calculateStorageMetrics, toast]);

  const syncOfflinePhotos = useCallback(async () => {
    if (storageState.syncInProgress || !isOnline) {
      console.log('üì± Sync skipped:', { 
        syncInProgress: storageState.syncInProgress, 
        isOnline 
      });
      return;
    }

    const pendingPhotos = storageState.photos.filter(p => !p.uploaded && p.retryCount < 3);

    if (pendingPhotos.length === 0) {
      console.log('üì± No photos to sync');
      return;
    }

    console.log(`üì± Starting sync of ${pendingPhotos.length} photos...`);

    setStorageState(prev => ({
      ...prev,
      syncInProgress: true,
      lastSyncAttempt: new Date()
    }));

    let successCount = 0;
    let failureCount = 0;

    for (const photo of pendingPhotos) {
      try {
        // Simulate upload - replace with actual upload logic
        await new Promise(resolve => setTimeout(resolve, 1000));

        // Mark as uploaded
        setStorageState(prev => ({
          ...prev,
          photos: prev.photos.map(p => 
            p.id === photo.id ? { ...p, uploaded: true } : p
          )
        }));

        successCount++;
        console.log(`üì± Uploaded photo ${photo.id}`);

      } catch (error) {
        console.error(`üì± Failed to upload photo ${photo.id}:`, error);

        // Increment retry count
        setStorageState(prev => ({
          ...prev,
          photos: prev.photos.map(p => 
            p.id === photo.id 
              ? { 
                  ...p, 
                  retryCount: p.retryCount + 1,
                  lastError: error instanceof Error ? error.message : 'Upload failed'
                } 
              : p
          )
        }));

        failureCount++;
      }
    }

    setStorageState(prev => ({
      ...prev,
      syncInProgress: false
    }));

    if (successCount > 0) {
      toast({
        title: "Sync Complete",
        description: `${successCount} photos uploaded successfully.`,
      });
    }

    if (failureCount > 0) {
      toast({
        title: "Sync Issues",
        description: `${failureCount} photos failed to upload. Will retry later.`,
        variant: "destructive",
      });
    }

    console.log('üì± Sync completed:', { successCount, failureCount });
  }, [storageState.syncInProgress, storageState.photos, isOnline, toast]);

  const cleanupUploadedPhotos = useCallback(() => {
    const remainingPhotos = storageState.photos.filter(p => !p.uploaded);
    const metrics = calculateStorageMetrics(remainingPhotos);

    setStorageState(prev => ({
      ...prev,
      photos: remainingPhotos,
      totalPendingSize: metrics.totalPendingSize
    }));

    localStorage.setItem('doublecheck_offline_photos_v2', JSON.stringify(
      remainingPhotos.map(photo => ({ ...photo, file: null }))
    ));

    console.log('üì± Cleaned up uploaded photos');
  }, [storageState.photos, calculateStorageMetrics]);

  const forceRetryFailed = useCallback(() => {
    setStorageState(prev => ({
      ...prev,
      photos: prev.photos.map(p => 
        p.retryCount > 0 && !p.uploaded 
          ? { ...p, retryCount: 0, lastError: undefined }
          : p
      )
    }));

    if (isOnline) {
      syncOfflinePhotos();
    }
  }, [isOnline, syncOfflinePhotos]);

  const getStorageStats = useCallback(() => {
    const metrics = calculateStorageMetrics(storageState.photos);
    return {
      ...metrics,
      isOnline,
      syncInProgress: storageState.syncInProgress,
      lastSyncAttempt: storageState.lastSyncAttempt,
      storageUsageMB: (metrics.totalPendingSize / 1024 / 1024).toFixed(2)
    };
  }, [storageState, isOnline, calculateStorageMetrics]);

  return {
    isOnline,
    photos: storageState.photos,
    syncInProgress: storageState.syncInProgress,
    savePhotoOffline,
    syncOfflinePhotos,
    cleanupUploadedPhotos,
    forceRetryFailed,
    getStorageStats
  };
};

import { useNavigate, useSearchParams } from "react-router-dom";
import { usePropertyMutation } from "./usePropertyMutation";
import { usePropertySubmissionState } from "./usePropertySubmissionState";
import { usePropertyErrorHandler } from "@/utils/propertySubmissionErrors";
import { useSimplePropertyValidation } from "./useSimplePropertyValidation";
import type { PropertyFormData } from "@/types/propertySubmission";

export const useEnhancedPropertySubmission = (user: any, userRole: string) => {
  const navigate = useNavigate();
  const [searchParams] = useSearchParams();
  const editId = searchParams.get('edit');
  const isEditing = !!editId;

  // Use the simplified validation hook
  const { validateSubmission } = useSimplePropertyValidation();
  const { executePropertyMutation } = usePropertyMutation();
  const { 
    isLoading, 
    setIsLoading, 
    submissionDebugInfo, 
    updateDebugInfo 
  } = usePropertySubmissionState();
  const { 
    handleSubmissionError, 
    handleUnexpectedError, 
    handleSuccess 
  } = usePropertyErrorHandler();

  const submitProperty = async (formData: PropertyFormData, isOnline: boolean) => {
    console.log('üöÄ Starting enhanced property submission process...');

    // Pre-submission validation
    if (!validateSubmission(user, isOnline, formData)) {
      return false;
    }

    setIsLoading(true);
    const submitStartTime = Date.now();

    try {
      const result = await executePropertyMutation(formData, user, isEditing, editId);
      const { data, error } = result;
      const submitDuration = Date.now() - submitStartTime;

      if (error) {
        console.error(`‚ùå Database error during ${isEditing ? 'update' : 'insert'}:`, {
          error: {
            message: error.message,
            code: error.code,
            details: error.details,
            hint: error.hint
          },
          operation: isEditing ? 'update' : 'insert',
          userId: user.id,
          duration: submitDuration,
          timestamp: new Date().toISOString()
        });

        updateDebugInfo({
          submitError: {
            operation: isEditing ? 'update' : 'insert',
            error: error.message,
            code: error.code,
            details: error.details,
            hint: error.hint,
            duration: submitDuration,
            timestamp: new Date().toISOString()
          }
        });

        handleSubmissionError(error, isEditing);
        return false;
      }

      console.log(`‚úÖ Property ${isEditing ? 'updated' : 'created'} successfully:`, {
        data,
        duration: submitDuration,
        timestamp: new Date().toISOString()
      });

      updateDebugInfo({
        submitSuccess: {
          operation: isEditing ? 'update' : 'insert',
          propertyId: data?.id,
          duration: submitDuration,
          timestamp: new Date().toISOString()
        }
      });

      handleSuccess(formData.name.trim(), isEditing);

      // Small delay to ensure UI feedback is seen
      setTimeout(() => {
        navigate('/properties');
      }, 500);

      return true;
    } catch (error) {
      const submitDuration = Date.now() - submitStartTime;
      console.error(`üí• Unexpected error during ${isEditing ? 'update' : 'create'}:`, {
        error: error instanceof Error ? {
          message: error.message,
          stack: error.stack,
          name: error.name
        } : error,
        duration: submitDuration,
        timestamp: new Date().toISOString()
      });

      updateDebugInfo({
        unexpectedSubmitError: {
          error: error instanceof Error ? error.message : 'Unknown error',
          stack: error instanceof Error ? error.stack : undefined,
          duration: submitDuration,
          timestamp: new Date().toISOString()
        }
      });

      handleUnexpectedError();
      return false;
    } finally {
      setIsLoading(false);
    }
  };

  const getSubmissionStats = () => {
    return {
      isEditing,
      userId: user?.id,
      userRole,
      timestamp: new Date().toISOString()
    };
  };

  return {
    isLoading,
    submitProperty,
    submissionDebugInfo,
    getSubmissionStats
  };
};

import { usePropertyFormValidation } from "./usePropertyFormValidation";
import { usePropertyMutation } from "./usePropertyMutation";
import { usePropertySubmissionState } from "./usePropertySubmissionState";
import { usePropertySubmissionMonitoring } from "./usePropertySubmissionMonitoring";
import { usePropertySubmissionRetry } from "./usePropertySubmissionRetry";
import { usePropertyErrorHandler } from "@/utils/propertySubmissionErrors";
import type { PropertyFormData } from "@/types/propertySubmission";

export const useEnhancedPropertySubmissionLogic = (user: any, isEditing: boolean, editId: string | null) => {
  const { validateSubmission } = usePropertyFormValidation();
  const { executePropertyMutation } = usePropertyMutation();
  const { 
    isLoading, 
    setIsLoading, 
    submissionDebugInfo, 
    updateDebugInfo 
  } = usePropertySubmissionState();
  const {
    startSubmissionTracking,
    recordError,
    recordRetry,
    completeSubmission,
    getSubmissionStats
  } = usePropertySubmissionMonitoring();
  const { 
    isRetryable,
    showRetryToast,
    createBackoffDelay
  } = usePropertySubmissionRetry();
  const { 
    handleSubmissionError, 
    handleUnexpectedError, 
    handleSuccess 
  } = usePropertyErrorHandler();

  const executeSubmissionWithRetry = async (formData: PropertyFormData): Promise<boolean> => {
    const submitStartTime = Date.now();
    let retryCount = 0;
    const maxRetries = 2;

    // Retry logic with exponential backoff
    while (retryCount <= maxRetries) {
      try {
        if (retryCount > 0) {
          recordRetry();
          console.log(`üîÑ Retry attempt ${retryCount}/${maxRetries}`);

          // Exponential backoff delay
          const backoffDelay = createBackoffDelay(retryCount);
          await new Promise(resolve => setTimeout(resolve, backoffDelay));
        }

        const result = await executePropertyMutation(formData, user, isEditing, editId);
        const { data, error } = result;

        if (error) {
          const submitDuration = Date.now() - submitStartTime;
          console.error(`‚ùå Database error on attempt ${retryCount + 1}:`, {
            error: {
              message: error.message,
              code: error.code,
              details: error.details,
              hint: error.hint
            },
            operation: isEditing ? 'update' : 'insert',
            userId: user.id,
            attempt: retryCount + 1
          });

          recordError(error);

          // Check if error is retryable
          const canRetry = isRetryable(error);

          if (canRetry && retryCount < maxRetries) {
            retryCount++;
            showRetryToast(retryCount, maxRetries);
            continue; // Retry
          } else {
            // Non-retryable error or max retries exceeded
            updateDebugInfo({
              submitError: {
                operation: isEditing ? 'update' : 'insert',
                error: error.message,
                code: error.code,
                details: error.details,
                hint: error.hint,
                attempts: retryCount + 1,
                duration: submitDuration,
                timestamp: new Date().toISOString()
              }
            });

            handleSubmissionError(error, isEditing);
            return false;
          }
        }

        // Success!
        const submitDuration = Date.now() - submitStartTime;
        console.log(`‚úÖ Property ${isEditing ? 'updated' : 'created'} successfully:`, {
          data,
          attempts: retryCount + 1
        });

        updateDebugInfo({
          submitSuccess: {
            operation: isEditing ? 'update' : 'insert',
            propertyId: data?.id,
            attempts: retryCount + 1,
            duration: submitDuration,
            timestamp: new Date().toISOString()
          }
        });

        handleSuccess(formData.name.trim(), isEditing);
        return true;

      } catch (error) {
        const submitDuration = Date.now() - submitStartTime;
        console.error(`üí• Unexpected error on attempt ${retryCount + 1}:`, {
          error: error instanceof Error ? {
            message: error.message,
            stack: error.stack,
            name: error.name
          } : error,
          attempt: retryCount + 1
        });

        recordError(error);

        if (retryCount < maxRetries) {
          retryCount++;
          showRetryToast(retryCount, maxRetries, true);
          continue; // Retry
        } else {
          updateDebugInfo({
            unexpectedSubmitError: {
              error: error instanceof Error ? error.message : 'Unknown error',
              stack: error instanceof Error ? error.stack : undefined,
              attempts: retryCount + 1,
              duration: submitDuration,
              timestamp: new Date().toISOString()
            }
          });

          handleUnexpectedError();
          return false;
        }
      }
    }

    return false;
  };

  return {
    isLoading,
    setIsLoading,
    submissionDebugInfo,
    validateSubmission,
    executeSubmissionWithRetry,
    startSubmissionTracking,
    completeSubmission,
    getSubmissionStats
  };
};

import { useState, useCallback, useRef, useEffect } from 'react';
import { useToast } from '@/components/ui/use-toast';
import { apiErrorHandler, ApiError, ApiErrorContext } from '@/lib/error/api-error-handler';
import { errorReporter } from '@/lib/monitoring/error-reporter';
import { env } from '@/lib/config/environment';

export interface ErrorState {
  error: Error | ApiError | null;
  isError: boolean;
  errorMessage: string | null;
  errorDetails: Record<string, any> | null;
  errorId: string | null;
  retryCount: number;
  isRetrying: boolean;
}

export interface UseErrorHandlingOptions {
  showToast?: boolean;
  autoRetry?: boolean;
  maxRetries?: number;
  retryDelay?: number;
  onError?: (error: Error | ApiError) => void;
  onRetry?: () => void;
  onSuccess?: () => void;
  context?: Record<string, any>;
  resetOnUnmount?: boolean;
}

export interface ErrorHandlingUtils {
  error: ErrorState;
  setError: (error: Error | ApiError | string | null) => void;
  clearError: () => void;
  handleError: (error: any, context?: ApiErrorContext | Record<string, any>) => void;
  handleApiError: (error: any, apiContext: ApiErrorContext) => Promise<void>;
  retry: () => Promise<void>;
  isLoading: boolean;
  setIsLoading: (loading: boolean) => void;
  withErrorHandling: <T>(
    asyncFn: () => Promise<T>,
    options?: { showLoading?: boolean; context?: Record<string, any> }
  ) => Promise<T | null>;
}

const DEFAULT_OPTIONS: UseErrorHandlingOptions = {
  showToast: true,
  autoRetry: false,
  maxRetries: 3,
  retryDelay: 1000,
  resetOnUnmount: true,
};

export function useErrorHandling(options?: UseErrorHandlingOptions): ErrorHandlingUtils {
  const { toast } = useToast();
  const config = { ...DEFAULT_OPTIONS, ...options };
  const retryFnRef = useRef<(() => Promise<void>) | null>(null);
  const retryTimeoutRef = useRef<NodeJS.Timeout | null>(null);

  const [errorState, setErrorState] = useState<ErrorState>({
    error: null,
    isError: false,
    errorMessage: null,
    errorDetails: null,
    errorId: null,
    retryCount: 0,
    isRetrying: false,
  });

  const [isLoading, setIsLoading] = useState(false);

  // Cleanup on unmount
  useEffect(() => {
    return () => {
      if (config.resetOnUnmount) {
        if (retryTimeoutRef.current) {
          clearTimeout(retryTimeoutRef.current);
        }
      }
    };
  }, [config.resetOnUnmount]);

  /**
   * Set error state
   */
  const setError = useCallback((error: Error | ApiError | string | null) => {
    if (!error) {
      clearError();
      return;
    }

    const errorObj = typeof error === 'string' ? new Error(error) : error;
    const errorId = errorReporter.reportError(errorObj, {
      ...config.context,
      source: 'useErrorHandling',
    });

    const errorMessage = 'userMessage' in errorObj && errorObj.userMessage
      ? errorObj.userMessage
      : errorObj.message;

    setErrorState({
      error: errorObj,
      isError: true,
      errorMessage,
      errorDetails: 'details' in errorObj ? errorObj.details : null,
      errorId,
      retryCount: 0,
      isRetrying: false,
    });

    // Show toast if enabled
    if (config.showToast) {
      toast({
        title: 'Error',
        description: errorMessage,
        variant: 'destructive',
      });
    }

    // Call error callback
    config.onError?.(errorObj);

    // Log in development
    if (env.isDevelopment()) {
      console.error('[useErrorHandling]', errorObj);
    }
  }, [config, toast]);

  /**
   * Clear error state
   */
  const clearError = useCallback(() => {
    setErrorState({
      error: null,
      isError: false,
      errorMessage: null,
      errorDetails: null,
      errorId: null,
      retryCount: 0,
      isRetrying: false,
    });

    if (retryTimeoutRef.current) {
      clearTimeout(retryTimeoutRef.current);
      retryTimeoutRef.current = null;
    }
  }, []);

  /**
   * Handle generic errors
   */
  const handleError = useCallback((
    error: any,
    context?: ApiErrorContext | Record<string, any>
  ) => {
    const errorObj = error instanceof Error ? error : new Error(String(error));

    // Add context to error reporting
    errorReporter.reportError(errorObj, {
      ...config.context,
      ...context,
      source: 'useErrorHandling',
    });

    setError(errorObj);

    // Auto-retry logic
    if (config.autoRetry && errorState.retryCount < (config.maxRetries || 3)) {
      const delay = config.retryDelay || 1000;
      retryTimeoutRef.current = setTimeout(() => {
        retry();
      }, delay * Math.pow(2, errorState.retryCount)); // Exponential backoff
    }
  }, [config, errorState.retryCount, setError]);

  /**
   * Handle API errors specifically
   */
  const handleApiError = useCallback(async (
    error: any,
    apiContext: ApiErrorContext
  ) => {
    try {
      const apiError = await apiErrorHandler.handleError(error, apiContext);
      setError(apiError);

      // Auto-retry for retryable API errors
      if (config.autoRetry && apiError.retry && errorState.retryCount < (config.maxRetries || 3)) {
        const delay = config.retryDelay || 1000;
        retryTimeoutRef.current = setTimeout(() => {
          retry();
        }, delay * Math.pow(2, errorState.retryCount));
      }
    } catch (err) {
      // Fallback if error handler fails
      handleError(err, apiContext);
    }
  }, [config, errorState.retryCount, handleError, setError]);

  /**
   * Retry the last failed operation
   */
  const retry = useCallback(async () => {
    if (!retryFnRef.current || errorState.isRetrying) return;

    setErrorState(prev => ({
      ...prev,
      isRetrying: true,
      retryCount: prev.retryCount + 1,
    }));

    config.onRetry?.();

    try {
      await retryFnRef.current();

      // Success - clear error
      clearError();
      config.onSuccess?.();

      toast({
        title: 'Success',
        description: 'Operation completed successfully',
      });
    } catch (error) {
      // Retry failed
      setErrorState(prev => ({
        ...prev,
        isRetrying: false,
      }));

      // Update error with new retry count
      if (error instanceof Error || (error && typeof error === 'object' && 'message' in error)) {
        handleError(error, { retry: true, attempt: errorState.retryCount + 1 });
      }
    }
  }, [errorState.isRetrying, errorState.retryCount, config, clearError, handleError, toast]);

  /**
   * Wrapper function for async operations with error handling
   */
  const withErrorHandling = useCallback(async <T,>(
    asyncFn: () => Promise<T>,
    options?: { showLoading?: boolean; context?: Record<string, any> }
  ): Promise<T | null> => {
    // Store the function for potential retry
    retryFnRef.current = async () => {
      await asyncFn();
    };

    if (options?.showLoading) {
      setIsLoading(true);
    }

    try {
      clearError();
      const result = await asyncFn();

      config.onSuccess?.();

      if (options?.showLoading) {
        setIsLoading(false);
      }

      return result;
    } catch (error) {
      if (options?.showLoading) {
        setIsLoading(false);
      }

      // Check if it's an API error
      if (options?.context && 'url' in options.context && 'method' in options.context) {
        await handleApiError(error, options.context as ApiErrorContext);
      } else {
        handleError(error, options?.context);
      }

      return null;
    }
  }, [clearError, config, handleApiError, handleError]);

  return {
    error: errorState,
    setError,
    clearError,
    handleError,
    handleApiError,
    retry,
    isLoading,
    setIsLoading,
    withErrorHandling,
  };
}

/**
 * Hook for handling form errors
 */
export function useFormErrorHandling<T extends Record<string, any>>() {
  const [fieldErrors, setFieldErrors] = useState<Partial<Record<keyof T, string>>>({});
  const { toast } = useToast();

  const setFieldError = useCallback((field: keyof T, error: string | null) => {
    setFieldErrors(prev => {
      if (error) {
        return { ...prev, [field]: error };
      } else {
        const { [field]: _, ...rest } = prev;
        return rest;
      }
    });
  }, []);

  const clearFieldErrors = useCallback(() => {
    setFieldErrors({});
  }, []);

  const handleValidationError = useCallback((errors: Partial<Record<keyof T, string>>) => {
    setFieldErrors(errors);

    // Show first error in toast
    const firstError = Object.values(errors).find(Boolean);
    if (firstError) {
      toast({
        title: 'Validation Error',
        description: firstError as string,
        variant: 'destructive',
      });
    }
  }, [toast]);

  return {
    fieldErrors,
    setFieldError,
    clearFieldErrors,
    handleValidationError,
    hasErrors: Object.keys(fieldErrors).length > 0,
  };
}

/**
 * Hook for handling async operations with loading and error states
 */
export function useAsyncError() {
  const [state, setState] = useState<{
    loading: boolean;
    error: Error | null;
    data: any;
  }>({
    loading: false,
    error: null,
    data: null,
  });

  const execute = useCallback(async (asyncFunction: () => Promise<any>) => {
    setState({ loading: true, error: null, data: null });

    try {
      const data = await asyncFunction();
      setState({ loading: false, error: null, data });
      return data;
    } catch (error) {
      const errorObj = error instanceof Error ? error : new Error(String(error));
      setState({ loading: false, error: errorObj, data: null });
      throw errorObj;
    }
  }, []);

  const reset = useCallback(() => {
    setState({ loading: false, error: null, data: null });
  }, []);

  return {
    ...state,
    execute,
    reset,
  };
}

import { useState, useEffect, useCallback } from "react";
import { supabase } from "@/integrations/supabase/client";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import type { Tables } from "@/integrations/supabase/types";

type InspectorPresence = Tables<'inspector_presence'> & {
  metadata: Record<string, any>;
};

type CollaborationConflict = Tables<'collaboration_conflicts'> & {
  inspector_1_action: Record<string, any>;
  inspector_2_action: Record<string, any>;
};

export const useInspectorCollaboration = (inspectionId: string) => {
  const { user } = useAuth();
  const { toast } = useToast();
  const [activeInspectors, setActiveInspectors] = useState<InspectorPresence[]>([]);
  const [conflicts, setConflicts] = useState<CollaborationConflict[]>([]);

  // Subscribe to inspector presence updates
  useEffect(() => {
    if (!inspectionId) return;

    const presenceChannel = supabase
      .channel(`inspector-presence-${inspectionId}`)
      .on(
        'postgres_changes',
        {
          event: '*',
          schema: 'public',
          table: 'inspector_presence',
          filter: `inspection_id=eq.${inspectionId}`
        },
        (payload) => {
          console.log('Presence update:', payload);
          fetchActiveInspectors();
        }
      )
      .subscribe();

    // Subscribe to collaboration conflicts
    const conflictsChannel = supabase
      .channel(`conflicts-${inspectionId}`)
      .on(
        'postgres_changes',
        {
          event: '*',
          schema: 'public',
          table: 'collaboration_conflicts',
          filter: `inspection_id=eq.${inspectionId}`
        },
        (payload) => {
          console.log('Conflict update:', payload);
          fetchConflicts();
        }
      )
      .subscribe();

    // Initial data fetch
    fetchActiveInspectors();
    fetchConflicts();

    return () => {
      supabase.removeChannel(presenceChannel);
      supabase.removeChannel(conflictsChannel);
    };
  }, [inspectionId]);

  const fetchActiveInspectors = useCallback(async () => {
    if (!inspectionId) return;

    try {
      const { data, error } = await supabase
        .from('inspector_presence')
        .select('*')
        .eq('inspection_id', inspectionId)
        .neq('status', 'offline')
        .gte('last_seen', new Date(Date.now() - 5 * 60 * 1000).toISOString()); // Active in last 5 minutes

      if (error) throw error;

      // Transform the data to ensure metadata is properly typed
      const transformedData: InspectorPresence[] = (data || []).map(item => ({
        ...item,
        metadata: (item.metadata as Record<string, any>) || {}
      }));

      setActiveInspectors(transformedData);
    } catch (error) {
      console.error('Error fetching active inspectors:', error);
    }
  }, [inspectionId]);

  const fetchConflicts = useCallback(async () => {
    if (!inspectionId) return;

    try {
      const { data, error } = await supabase
        .from('collaboration_conflicts')
        .select('*')
        .eq('inspection_id', inspectionId)
        .eq('resolution_status', 'pending')
        .order('created_at', { ascending: false });

      if (error) throw error;

      // Transform the data to ensure actions are properly typed
      const transformedData: CollaborationConflict[] = (data || []).map(item => ({
        ...item,
        inspector_1_action: (item.inspector_1_action as Record<string, any>) || {},
        inspector_2_action: (item.inspector_2_action as Record<string, any>) || {}
      }));

      setConflicts(transformedData);
    } catch (error) {
      console.error('Error fetching conflicts:', error);
    }
  }, [inspectionId]);

  const assignChecklistItem = useCallback(async (itemId: string) => {
    if (!user) return;

    try {
      const { data, error } = await supabase.rpc('assign_checklist_item', {
        p_item_id: itemId,
        p_inspector_id: user.id
      });

      if (error) throw error;

      toast({
        title: "Item Assigned",
        description: "You've been assigned to this checklist item.",
      });

      return data;
    } catch (error) {
      console.error('Error assigning checklist item:', error);
      toast({
        title: "Assignment Failed",
        description: "Failed to assign item. It may already be assigned to another inspector.",
        variant: "destructive",
      });
      return null;
    }
  }, [user, toast]);

  const resolveConflict = useCallback(async (
    conflictId: string,
    resolution: 'resolved' | 'escalated',
    notes?: string
  ) => {
    if (!user) return;

    try {
      const { error } = await supabase
        .from('collaboration_conflicts')
        .update({
          resolution_status: resolution,
          resolved_by: user.id,
          resolved_at: new Date().toISOString(),
          resolution_notes: notes || null,
          updated_at: new Date().toISOString()
        })
        .eq('id', conflictId);

      if (error) throw error;

      toast({
        title: "Conflict Resolved",
        description: `Conflict has been ${resolution}.`,
      });

      fetchConflicts();
    } catch (error) {
      console.error('Error resolving conflict:', error);
      toast({
        title: "Resolution Failed",
        description: "Failed to resolve conflict. Please try again.",
        variant: "destructive",
      });
    }
  }, [user, toast, fetchConflicts]);

  return {
    activeInspectors,
    conflicts,
    assignChecklistItem,
    resolveConflict,
    fetchActiveInspectors,
    fetchConflicts
  };
};

import { useEffect, useCallback, useRef } from "react";
import { supabase } from "@/integrations/supabase/client";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { useChannelManager } from "@/hooks/useChannelManager";

export const useInspectorPresence = (inspectionId: string) => {
  const { user } = useAuth();
  const { toast } = useToast();
  const { cleanupChannel } = useChannelManager();
  const retryTimeoutRef = useRef<NodeJS.Timeout>();
  const isUpdatingRef = useRef(false);
  const isMountedRef = useRef(true);

  const updatePresence = useCallback(async (
    status: 'online' | 'offline' | 'viewing' | 'working',
    currentItemId?: string,
    metadata?: Record<string, any>
  ) => {
    if (!user || !inspectionId || isUpdatingRef.current || !isMountedRef.current) return;

    isUpdatingRef.current = true;

    try {
      const { error } = await supabase.rpc('update_inspector_presence', {
        p_inspection_id: inspectionId,
        p_status: status,
        p_current_item_id: currentItemId || null,
        p_metadata: metadata ? JSON.stringify(metadata) : '{}'
      });

      if (error) {
        console.error('Failed to update presence:', error);

        // Only show toast for critical errors, not for routine connection issues
        if (error.code !== 'PGRST301' && !error.message.includes('infinite recursion')) {
          toast({
            title: "Presence Update Failed",
            description: "Failed to update your presence status.",
            variant: "destructive",
          });
        }

        // Retry after a delay for certain types of errors
        if (retryTimeoutRef.current) clearTimeout(retryTimeoutRef.current);
        if (isMountedRef.current) {
          retryTimeoutRef.current = setTimeout(() => {
            if (isMountedRef.current) {
              updatePresence(status, currentItemId, metadata);
            }
          }, 3000);
        }

        throw error;
      }

      // Clear any pending retries on success
      if (retryTimeoutRef.current) {
        clearTimeout(retryTimeoutRef.current);
        retryTimeoutRef.current = undefined;
      }

    } catch (error) {
      console.error('Error updating inspector presence:', error);
      // Don't throw here to prevent cascading errors
    } finally {
      isUpdatingRef.current = false;
    }
  }, [user, inspectionId, toast]);

  // Update presence on mount and when user becomes active
  useEffect(() => {
    if (!user || !inspectionId) return;

    let heartbeatInterval: NodeJS.Timeout;
    isMountedRef.current = true;

    // Set initial presence with error handling
    const setInitialPresence = async () => {
      if (!isMountedRef.current) return;

      try {
        await updatePresence('online');
      } catch (error) {
        console.error('Failed to set initial presence:', error);
      }
    };

    setInitialPresence();

    // Update presence on visibility change
    const handleVisibilityChange = () => {
      if (!isMountedRef.current) return;

      try {
        updatePresence(document.hidden ? 'offline' : 'online');
      } catch (error) {
        console.error('Failed to update presence on visibility change:', error);
      }
    };

    // Update presence on beforeunload
    const handleBeforeUnload = () => {
      if (!isMountedRef.current) return;

      try {
        // Use sendBeacon for more reliable cleanup on page unload
        const presenceData = {
          inspection_id: inspectionId,
          status: 'offline',
          inspector_id: user.id
        };

        if (navigator.sendBeacon) {
          navigator.sendBeacon('/api/presence-cleanup', JSON.stringify(presenceData));
        } else {
          updatePresence('offline');
        }
      } catch (error) {
        console.error('Failed to update presence on beforeunload:', error);
      }
    };

    document.addEventListener('visibilitychange', handleVisibilityChange);
    window.addEventListener('beforeunload', handleBeforeUnload);

    // Heartbeat to keep presence alive (reduced frequency to avoid excessive calls)
    heartbeatInterval = setInterval(() => {
      if (!document.hidden && isMountedRef.current) {
        updatePresence('online');
      }
    }, 45000); // Increased to 45 seconds

    return () => {
      isMountedRef.current = false;

      document.removeEventListener('visibilitychange', handleVisibilityChange);
      window.removeEventListener('beforeunload', handleBeforeUnload);

      if (heartbeatInterval) clearInterval(heartbeatInterval);
      if (retryTimeoutRef.current) clearTimeout(retryTimeoutRef.current);

      // Clean up any presence-related channels
      cleanupChannel(`presence-indicator-${inspectionId}`);

      // Mark as offline on cleanup (but don't block component unmounting)
      updatePresence('offline').catch(console.error);
    };
  }, [user, inspectionId, updatePresence, cleanupChannel]);

  return { updatePresence };
};

import { useQuery } from "@tanstack/react-query";
import { useCallback } from "react";
import { supabase } from "@/integrations/supabase/client";

interface MobilePropertyData {
  property_id: string;
  property_name: string;
  property_address: string;
  property_vrbo_url: string | null;
  property_airbnb_url: string | null;
  property_status: string;
  property_created_at: string;
  inspection_count: number;
  completed_inspection_count: number;
  active_inspection_count: number;
  latest_inspection_id: string | null;
  latest_inspection_completed: boolean | null;
}

export const useMobilePropertyData = (userId?: string) => {
  return useQuery({
    queryKey: ['mobile-properties', userId],
    queryFn: async () => {
      console.log('üì± Fetching mobile property data...');
      const startTime = Date.now();

      try {
        // Mobile-optimized query with timeout
        const queryPromise = supabase.rpc('get_properties_with_inspections');

        // Mobile timeout (5 seconds)
        const timeoutPromise = new Promise((_, reject) => {
          setTimeout(() => reject(new Error('Mobile query timeout - please check your connection')), 5000);
        });

        const { data, error } = await Promise.race([queryPromise, timeoutPromise]) as any;

        const fetchDuration = Date.now() - startTime;

        if (error) {
          console.error('‚ùå Mobile property fetch error:', error);
          throw new Error(`Failed to load properties: ${error.message}`);
        }

        console.log(`‚úÖ Mobile properties loaded in ${fetchDuration}ms`, {
          count: data?.length || 0,
          fetchDuration,
          timestamp: new Date().toISOString()
        });

        return (data || []) as MobilePropertyData[];
      } catch (error) {
        console.error('‚ùå Mobile property data fetch failed:', error);
        throw error;
      }
    },
    staleTime: 30000, // 30 seconds stale time for mobile
    gcTime: 300000, // 5 minutes cache for mobile
    refetchOnWindowFocus: false,
    refetchOnReconnect: true,
    retry: (failureCount, error) => {
      console.log(`üîÑ Mobile property query retry attempt ${failureCount + 1}`);
      return failureCount < 1; // Only 1 retry on mobile
    },
    retryDelay: 2000,
    enabled: !!userId
  });
};

export const useMobilePropertyStatus = () => {
  const getPropertyStatus = useCallback((completedCount: number, activeCount: number) => {
    if (activeCount > 0) {
      return {
        status: 'in-progress',
        color: 'bg-yellow-500',
        textLabel: 'In Progress',
        badgeColor: 'bg-yellow-100 text-yellow-800'
      };
    }

    if (completedCount > 0) {
      return {
        status: 'completed',
        color: 'bg-green-500',
        textLabel: 'Completed',
        badgeColor: 'bg-green-100 text-green-800'
      };
    }

    return {
      status: 'pending',
      color: 'bg-gray-500',
      textLabel: 'Not Started',
      badgeColor: 'bg-gray-100 text-gray-800'
    };
  }, []);

  return { getPropertyStatus };
};

// Performance Monitoring Hook for STR Certified
// Tracks component renders, AI processing, and user interactions

import { useEffect, useRef, useState, useCallback, useMemo } from 'react';
import { useQuery, useMutation } from '@tanstack/react-query';

interface PerformanceMetrics {
  renderTime: number;
  aiProcessingTime: number;
  interactionTime: number;
  memoryUsage: number;
  fps: number;
}

interface PerformanceAlert {
  id: string;
  type: 'slow_render' | 'ai_delay' | 'memory_warning' | 'fps_drop' | 'interaction_lag';
  severity: 'warning' | 'error' | 'critical';
  message: string;
  timestamp: Date;
  metrics: Partial<PerformanceMetrics>;
  suggestions?: string[];
}

interface UsePerformanceMonitoringOptions {
  componentName: string;
  enableAlerts?: boolean;
  thresholds?: {
    renderTime?: number;      // ms
    aiProcessingTime?: number; // ms
    interactionTime?: number;  // ms
    memoryUsage?: number;      // MB
    fps?: number;             // frames per second
  };
  sampleRate?: number; // 0-1, percentage of events to track
  onAlert?: (alert: PerformanceAlert) => void;
}

interface UsePerformanceMonitoringReturn {
  // Current metrics
  metrics: PerformanceMetrics;
  isPerformant: boolean;

  // Tracking methods
  trackRender: () => void;
  trackAIProcessing: (operation: string) => PerformanceTracker;
  trackInteraction: (interaction: string) => PerformanceTracker;

  // Alerts
  alerts: PerformanceAlert[];
  clearAlerts: () => void;

  // Analysis
  getReport: () => PerformanceReport;
  optimize: () => OptimizationSuggestion[];
}

export const usePerformanceMonitoring = (
  options: UsePerformanceMonitoringOptions
): UsePerformanceMonitoringReturn => {
  // State
  const [metrics, setMetrics] = useState<PerformanceMetrics>({
    renderTime: 0,
    aiProcessingTime: 0,
    interactionTime: 0,
    memoryUsage: 0,
    fps: 60
  });
  const [alerts, setAlerts] = useState<PerformanceAlert[]>([]);

  // Refs
  const renderCount = useRef(0);
  const renderStartTime = useRef<number>(0);
  const metricsHistory = useRef<PerformanceMetrics[]>([]);
  const fpsFrames = useRef<number[]>([]);
  const lastFrameTime = useRef<number>(performance.now());
  const animationFrame = useRef<number>();

  // Default thresholds
  const thresholds = useMemo(() => ({
    renderTime: 16.67,      // 60fps target
    aiProcessingTime: 1000, // 1 second
    interactionTime: 100,   // 100ms for responsive feel
    memoryUsage: 100,       // 100MB
    fps: 30,                // Minimum acceptable FPS
    ...options.thresholds
  }), [options.thresholds]);

  // Initialize performance observer
  useEffect(() => {
    if (typeof window === 'undefined') return;

    // Track render time
    renderStartTime.current = performance.now();

    // Create Performance Observer for long tasks
    if ('PerformanceObserver' in window) {
      try {
        const observer = new PerformanceObserver((list) => {
          for (const entry of list.getEntries()) {
            if (entry.duration > thresholds.renderTime) {
              handlePerformanceEntry(entry);
            }
          }
        });

        observer.observe({ entryTypes: ['measure', 'navigation'] });

        return () => observer.disconnect();
      } catch (error) {
        console.warn('PerformanceObserver not supported:', error);
      }
    }
  }, [thresholds.renderTime]);

  // Monitor FPS
  useEffect(() => {
    const measureFPS = () => {
      const now = performance.now();
      const delta = now - lastFrameTime.current;
      lastFrameTime.current = now;

      // Calculate FPS
      const fps = 1000 / delta;
      fpsFrames.current.push(fps);

      // Keep only last 60 frames
      if (fpsFrames.current.length > 60) {
        fpsFrames.current.shift();
      }

      // Update average FPS
      const avgFPS = fpsFrames.current.reduce((a, b) => a + b, 0) / fpsFrames.current.length;

      setMetrics(prev => {
        const updated = { ...prev, fps: Math.round(avgFPS) };

        // Check for FPS drops
        if (avgFPS < thresholds.fps && options.enableAlerts) {
          createAlert('fps_drop', 'critical', 
            `FPS dropped to ${Math.round(avgFPS)}`,
            { fps: avgFPS }
          );
        }

        return updated;
      });

      animationFrame.current = requestAnimationFrame(measureFPS);
    };

    animationFrame.current = requestAnimationFrame(measureFPS);

    return () => {
      if (animationFrame.current) {
        cancelAnimationFrame(animationFrame.current);
      }
    };
  }, [thresholds.fps, options.enableAlerts]);

  // Monitor memory usage
  useEffect(() => {
    if (!('memory' in performance)) return;

    const checkMemory = () => {
      const memory = (performance as any).memory;
      const usedMB = memory.usedJSHeapSize / 1024 / 1024;

      setMetrics(prev => {
        const updated = { ...prev, memoryUsage: Math.round(usedMB) };

        // Check for high memory usage
        if (usedMB > thresholds.memoryUsage && options.enableAlerts) {
          createAlert('memory_warning', 'warning',
            `High memory usage: ${Math.round(usedMB)}MB`,
            { memoryUsage: usedMB },
            [
              'Consider lazy loading components',
              'Implement virtualization for long lists',
              'Clear unused cache data'
            ]
          );
        }

        return updated;
      });
    };

    const interval = setInterval(checkMemory, 5000); // Check every 5 seconds
    checkMemory(); // Initial check

    return () => clearInterval(interval);
  }, [thresholds.memoryUsage, options.enableAlerts]);

  // Track component renders
  const trackRender = useCallback(() => {
    renderCount.current++;

    // Sample based on rate
    if (Math.random() > (options.sampleRate || 1)) return;

    const renderTime = performance.now() - renderStartTime.current;

    setMetrics(prev => {
      const updated = { ...prev, renderTime };
      metricsHistory.current.push(updated);

      // Keep only last 100 entries
      if (metricsHistory.current.length > 100) {
        metricsHistory.current.shift();
      }

      // Check for slow renders
      if (renderTime > thresholds.renderTime && options.enableAlerts) {
        createAlert('slow_render', 'warning',
          `Slow render detected: ${renderTime.toFixed(2)}ms`,
          { renderTime },
          [
            'Use React.memo for expensive components',
            'Optimize re-renders with useMemo/useCallback',
            'Consider code splitting'
          ]
        );
      }

      return updated;
    });

    // Mark for next render
    renderStartTime.current = performance.now();
  }, [options.sampleRate, options.enableAlerts, thresholds.renderTime]);

  // Track AI processing
  const trackAIProcessing = useCallback((operation: string): PerformanceTracker => {
    const startTime = performance.now();
    let completed = false;

    const tracker: PerformanceTracker = {
      complete: () => {
        if (completed) return;
        completed = true;

        const duration = performance.now() - startTime;

        setMetrics(prev => {
          const updated = { ...prev, aiProcessingTime: duration };

          // Check for slow AI processing
          if (duration > thresholds.aiProcessingTime && options.enableAlerts) {
            createAlert('ai_delay', 'error',
              `Slow AI processing for ${operation}: ${duration.toFixed(0)}ms`,
              { aiProcessingTime: duration },
              [
                'Enable response caching',
                'Use smaller model variants',
                'Implement progressive loading'
              ]
            );
          }

          // Log to performance timeline
          if ('performance' in window && 'measure' in performance) {
            try {
              performance.mark(`ai-${operation}-end`);
              performance.measure(
                `AI: ${operation}`,
                `ai-${operation}-start`,
                `ai-${operation}-end`
              );
            } catch (e) {
              // Marks might not exist
            }
          }

          return updated;
        });
      },

      cancel: () => {
        completed = true;
      }
    };

    // Mark start
    if ('performance' in window && 'mark' in performance) {
      performance.mark(`ai-${operation}-start`);
    }

    return tracker;
  }, [thresholds.aiProcessingTime, options.enableAlerts]);

  // Track user interactions
  const trackInteraction = useCallback((interaction: string): PerformanceTracker => {
    const startTime = performance.now();
    let completed = false;

    const tracker: PerformanceTracker = {
      complete: () => {
        if (completed) return;
        completed = true;

        const duration = performance.now() - startTime;

        setMetrics(prev => {
          const updated = { ...prev, interactionTime: duration };

          // Check for laggy interactions
          if (duration > thresholds.interactionTime && options.enableAlerts) {
            createAlert('interaction_lag', 'warning',
              `Slow interaction "${interaction}": ${duration.toFixed(0)}ms`,
              { interactionTime: duration },
              [
                'Debounce expensive operations',
                'Use optimistic updates',
                'Move heavy computation to Web Workers'
              ]
            );
          }

          // Log interaction
          logInteraction(interaction, duration);

          return updated;
        });
      },

      cancel: () => {
        completed = true;
      }
    };

    return tracker;
  }, [thresholds.interactionTime, options.enableAlerts]);

  // Create performance alert
  const createAlert = useCallback((
    type: PerformanceAlert['type'],
    severity: PerformanceAlert['severity'],
    message: string,
    metrics: Partial<PerformanceMetrics>,
    suggestions?: string[]
  ) => {
    const alert: PerformanceAlert = {
      id: `alert_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
      type,
      severity,
      message,
      timestamp: new Date(),
      metrics,
      suggestions
    };

    setAlerts(prev => [...prev.slice(-9), alert]); // Keep last 10 alerts

    // Notify callback
    options.onAlert?.(alert);

    // Log to console in development
    if (process.env.NODE_ENV === 'development') {
      console.warn(`[Performance Alert] ${message}`, metrics);
    }
  }, [options]);

  // Clear alerts
  const clearAlerts = useCallback(() => {
    setAlerts([]);
  }, []);

  // Generate performance report
  const getReport = useCallback((): PerformanceReport => {
    const history = metricsHistory.current;

    if (history.length === 0) {
      return {
        componentName: options.componentName,
        renderCount: renderCount.current,
        averages: { ...metrics },
        percentiles: {
          p50: { ...metrics },
          p75: { ...metrics },
          p90: { ...metrics },
          p95: { ...metrics },
          p99: { ...metrics }
        },
        alerts: alerts.length,
        suggestions: []
      };
    }

    // Calculate averages
    const averages = history.reduce((acc, m) => {
      Object.keys(m).forEach(key => {
        acc[key] = (acc[key] || 0) + m[key as keyof PerformanceMetrics];
      });
      return acc;
    }, {} as any);

    Object.keys(averages).forEach(key => {
      averages[key] /= history.length;
    });

    // Calculate percentiles for render time
    const renderTimes = history.map(m => m.renderTime).sort((a, b) => a - b);
    const getPercentile = (p: number) => {
      const index = Math.ceil((p / 100) * renderTimes.length) - 1;
      return renderTimes[Math.max(0, index)];
    };

    const percentiles = {
      p50: { ...averages, renderTime: getPercentile(50) },
      p75: { ...averages, renderTime: getPercentile(75) },
      p90: { ...averages, renderTime: getPercentile(90) },
      p95: { ...averages, renderTime: getPercentile(95) },
      p99: { ...averages, renderTime: getPercentile(99) }
    };

    // Generate suggestions
    const suggestions = generateOptimizationSuggestions(averages, percentiles);

    return {
      componentName: options.componentName,
      renderCount: renderCount.current,
      averages,
      percentiles,
      alerts: alerts.length,
      suggestions
    };
  }, [options.componentName, metrics, alerts]);

  // Generate optimization suggestions
  const optimize = useCallback((): OptimizationSuggestion[] => {
    const report = getReport();
    return report.suggestions;
  }, [getReport]);

  // Check if performance is acceptable
  const isPerformant = useMemo(() => {
    return (
      metrics.renderTime < thresholds.renderTime &&
      metrics.aiProcessingTime < thresholds.aiProcessingTime &&
      metrics.interactionTime < thresholds.interactionTime &&
      metrics.memoryUsage < thresholds.memoryUsage &&
      metrics.fps >= thresholds.fps
    );
  }, [metrics, thresholds]);

  // Track initial render
  useEffect(() => {
    trackRender();
  }, []); // eslint-disable-line react-hooks/exhaustive-deps

  return {
    metrics,
    isPerformant,
    trackRender,
    trackAIProcessing,
    trackInteraction,
    alerts,
    clearAlerts,
    getReport,
    optimize
  };
};

// Performance tracker interface
interface PerformanceTracker {
  complete: () => void;
  cancel: () => void;
}

// Helper functions

function handlePerformanceEntry(entry: PerformanceEntry): void {
  // Log long tasks
  if (entry.duration > 50) {
    console.warn(`Long task detected: ${entry.name} (${entry.duration.toFixed(2)}ms)`);
  }
}

function logInteraction(interaction: string, duration: number): void {
  // Log to analytics
  if (typeof window !== 'undefined' && 'gtag' in window) {
    (window as any).gtag('event', 'timing_complete', {
      name: interaction,
      value: Math.round(duration),
      event_category: 'User Interaction'
    });
  }
}

function generateOptimizationSuggestions(
  averages: PerformanceMetrics,
  percentiles: any
): OptimizationSuggestion[] {
  const suggestions: OptimizationSuggestion[] = [];

  // Render performance
  if (averages.renderTime > 16.67) {
    suggestions.push({
      category: 'rendering',
      priority: 'high',
      issue: `Average render time is ${averages.renderTime.toFixed(2)}ms`,
      suggestion: 'Optimize component rendering',
      actions: [
        'Use React.memo for expensive child components',
        'Move state closer to where it\'s used',
        'Use useMemo for expensive computations',
        'Consider virtualization for long lists'
      ],
      estimatedImpact: 'high'
    });
  }

  // AI performance
  if (averages.aiProcessingTime > 500) {
    suggestions.push({
      category: 'ai_processing',
      priority: 'medium',
      issue: `AI processing takes ${averages.aiProcessingTime.toFixed(0)}ms on average`,
      suggestion: 'Optimize AI operations',
      actions: [
        'Implement response caching',
        'Use progressive loading for results',
        'Consider using faster model variants',
        'Batch similar requests'
      ],
      estimatedImpact: 'medium'
    });
  }

  // Memory usage
  if (averages.memoryUsage > 50) {
    suggestions.push({
      category: 'memory',
      priority: 'medium',
      issue: `Memory usage is ${averages.memoryUsage.toFixed(0)}MB`,
      suggestion: 'Reduce memory footprint',
      actions: [
        'Implement lazy loading',
        'Clear unused data from state',
        'Use weak references for cache',
        'Optimize image sizes'
      ],
      estimatedImpact: 'medium'
    });
  }

  // FPS
  if (averages.fps < 50) {
    suggestions.push({
      category: 'animation',
      priority: 'high',
      issue: `FPS is ${averages.fps}, below smooth threshold`,
      suggestion: 'Improve animation performance',
      actions: [
        'Use CSS transforms instead of position',
        'Reduce DOM mutations during animation',
        'Use will-change CSS property',
        'Consider using Web Animations API'
      ],
      estimatedImpact: 'high'
    });
  }

  return suggestions;
}

// Hook for monitoring specific AI operations
export const useAIPerformanceMonitoring = (operationName: string) => {
  const [metrics, setMetrics] = useState<AIOperationMetrics>({
    callCount: 0,
    totalTime: 0,
    averageTime: 0,
    minTime: Infinity,
    maxTime: 0,
    errors: 0,
    cacheHits: 0,
    cacheMisses: 0
  });

  const track = useCallback(async <T,>(
    operation: () => Promise<T>,
    options?: { cached?: boolean }
  ): Promise<T> => {
    const startTime = performance.now();

    try {
      const result = await operation();
      const duration = performance.now() - startTime;

      setMetrics(prev => ({
        callCount: prev.callCount + 1,
        totalTime: prev.totalTime + duration,
        averageTime: (prev.totalTime + duration) / (prev.callCount + 1),
        minTime: Math.min(prev.minTime, duration),
        maxTime: Math.max(prev.maxTime, duration),
        errors: prev.errors,
        cacheHits: prev.cacheHits + (options?.cached ? 1 : 0),
        cacheMisses: prev.cacheMisses + (options?.cached ? 0 : 1)
      }));

      return result;
    } catch (error) {
      setMetrics(prev => ({
        ...prev,
        errors: prev.errors + 1
      }));
      throw error;
    }
  }, []);

  const reset = useCallback(() => {
    setMetrics({
      callCount: 0,
      totalTime: 0,
      averageTime: 0,
      minTime: Infinity,
      maxTime: 0,
      errors: 0,
      cacheHits: 0,
      cacheMisses: 0
    });
  }, []);

  return { metrics, track, reset };
};

// Hook for monitoring component lifecycle
export const useComponentLifecycleMonitoring = (componentName: string) => {
  const mountTime = useRef(performance.now());
  const updateCount = useRef(0);
  const [lifecycle, setLifecycle] = useState<ComponentLifecycle>({
    mounted: true,
    mountDuration: 0,
    updates: 0,
    lastUpdateDuration: 0,
    totalUpdateTime: 0
  });

  useEffect(() => {
    // Component mounted
    const mountDuration = performance.now() - mountTime.current;
    setLifecycle(prev => ({ ...prev, mountDuration }));

    return () => {
      // Component unmounting
      const lifetime = performance.now() - mountTime.current;
      console.log(`[${componentName}] Unmounting after ${lifetime.toFixed(0)}ms, ${updateCount.current} updates`);
    };
  }, []); // eslint-disable-line react-hooks/exhaustive-deps

  useEffect(() => {
    // Track updates
    const updateStart = performance.now();
    updateCount.current++;

    return () => {
      const updateDuration = performance.now() - updateStart;
      setLifecycle(prev => ({
        ...prev,
        updates: updateCount.current,
        lastUpdateDuration: updateDuration,
        totalUpdateTime: prev.totalUpdateTime + updateDuration
      }));
    };
  });

  return lifecycle;
};

// Types

interface PerformanceReport {
  componentName: string;
  renderCount: number;
  averages: PerformanceMetrics;
  percentiles: {
    p50: PerformanceMetrics;
    p75: PerformanceMetrics;
    p90: PerformanceMetrics;
    p95: PerformanceMetrics;
    p99: PerformanceMetrics;
  };
  alerts: number;
  suggestions: OptimizationSuggestion[];
}

interface OptimizationSuggestion {
  category: 'rendering' | 'ai_processing' | 'memory' | 'animation' | 'interaction';
  priority: 'low' | 'medium' | 'high';
  issue: string;
  suggestion: string;
  actions: string[];
  estimatedImpact: 'low' | 'medium' | 'high';
}

interface AIOperationMetrics {
  callCount: number;
  totalTime: number;
  averageTime: number;
  minTime: number;
  maxTime: number;
  errors: number;
  cacheHits: number;
  cacheMisses: number;
}

interface ComponentLifecycle {
  mounted: boolean;
  mountDuration: number;
  updates: number;
  lastUpdateDuration: number;
  totalUpdateTime: number;
}

