export const usePhotoGuidance = (options: UsePhotoGuidanceOptions): UsePhotoGuidanceReturn => {
  const {
    checklistItem,
    referencePhoto,
    enableOfflineMode = true,
    autoAdvance = false
  } = options;

  // Determine room type from checklist item
  const roomType = checklistItem.roomName?.toLowerCase().replace(/\s+/g, '-') || 'bedroom';
  const guidanceSteps = DEFAULT_GUIDANCE_STEPS[roomType] || DEFAULT_GUIDANCE_STEPS.bedroom;

  // State management
  const [currentStep, setCurrentStep] = useState(0);
  const [guidanceState, setGuidanceState] = useState<PhotoGuidanceState>({
    currentStep: 0,
    totalSteps: guidanceSteps.length,
    stepsCompleted: 0,
    isComplete: false,
    currentGuidance: guidanceSteps[0]?.description || '',
    history: []
  });

  // Refs
  const saveTimeoutRef = useRef<NodeJS.Timeout | null>(null);

  // Load saved progress on mount
  useEffect(() => {
    if (enableOfflineMode) {
      loadProgress();
    }
  }, [checklistItem.id]);

  // Auto-save progress
  useEffect(() => {
    if (enableOfflineMode && guidanceState.history.length > 0) {
      if (saveTimeoutRef.current) {
        clearTimeout(saveTimeoutRef.current);
      }
      saveTimeoutRef.current = setTimeout(() => {
        saveProgress();
      }, 1000);
    }
  }, [guidanceState]);

  // Navigation functions
  const nextStep = useCallback(() => {
    if (currentStep < guidanceSteps.length - 1) {
      const newStep = currentStep + 1;
      setCurrentStep(newStep);
      setGuidanceState(prev => ({
        ...prev,
        currentStep: newStep,
        currentGuidance: guidanceSteps[newStep].description
      }));
    }
  }, [currentStep, guidanceSteps]);

  const previousStep = useCallback(() => {
    if (currentStep > 0) {
      const newStep = currentStep - 1;
      setCurrentStep(newStep);
      setGuidanceState(prev => ({
        ...prev,
        currentStep: newStep,
        currentGuidance: guidanceSteps[newStep].description
      }));
    }
  }, [currentStep, guidanceSteps]);

  const goToStep = useCallback((step: number) => {
    if (step >= 0 && step < guidanceSteps.length) {
      setCurrentStep(step);
      setGuidanceState(prev => ({
        ...prev,
        currentStep: step,
        currentGuidance: guidanceSteps[step].description
      }));
    }
  }, [guidanceSteps]);

  const markStepComplete = useCallback((qualityScore: number, photoUrl?: string) => {
    const stepId = guidanceSteps[currentStep].id;

    setGuidanceState(prev => {
      // Check if step already completed
      const alreadyCompleted = prev.history.some(h => h.stepId === stepId);

      const newHistory = [...prev.history];
      if (!alreadyCompleted) {
        newHistory.push({
          stepId,
          timestamp: new Date(),
          qualityScore,
          photoUrl
        });
      }

      const stepsCompleted = new Set(newHistory.map(h => h.stepId)).size;
      const isComplete = stepsCompleted === guidanceSteps.length;

      return {
        ...prev,
        history: newHistory,
        stepsCompleted,
        isComplete
      };
    });

    // Auto-advance if enabled and quality meets requirements
    if (autoAdvance && qualityScore >= guidanceSteps[currentStep].requiredQualityScore) {
      setTimeout(() => nextStep(), 500);
    }
  }, [currentStep, guidanceSteps, autoAdvance, nextStep]);

  const resetGuidance = useCallback(() => {
    setCurrentStep(0);
    setGuidanceState({
      currentStep: 0,
      totalSteps: guidanceSteps.length,
      stepsCompleted: 0,
      isComplete: false,
      currentGuidance: guidanceSteps[0]?.description || '',
      history: []
    });

    // Clear saved progress
    if (enableOfflineMode) {
      localStorage.removeItem(`photo_guidance_${checklistItem.id}`);
    }
  }, [guidanceSteps, checklistItem.id, enableOfflineMode]);

  const getProgress = useCallback(() => {
    return (guidanceState.stepsCompleted / guidanceState.totalSteps) * 100;
  }, [guidanceState]);

  const getCurrentRequirements = useCallback(() => {
    const currentGuidanceStep = guidanceSteps[currentStep];
    return [
      `Minimum quality score: ${currentGuidanceStep.requiredQualityScore}%`,
      ...currentGuidanceStep.tips
    ];
  }, [currentStep, guidanceSteps]);

  const saveProgress = useCallback(async () => {
    if (!enableOfflineMode) return;

    const progressData = {
      checklistItemId: checklistItem.id,
      currentStep,
      guidanceState,
      savedAt: new Date().toISOString()
    };

    localStorage.setItem(
      `photo_guidance_${checklistItem.id}`,
      JSON.stringify(progressData)
    );
  }, [checklistItem.id, currentStep, guidanceState, enableOfflineMode]);

  const loadProgress = useCallback(async () => {
    if (!enableOfflineMode) return;

    try {
      const saved = localStorage.getItem(`photo_guidance_${checklistItem.id}`);
      if (saved) {
        const progressData = JSON.parse(saved);

        // Restore state
        setCurrentStep(progressData.currentStep);
        setGuidanceState(progressData.guidanceState);
      }
    } catch (error) {
      console.error('Failed to load progress:', error);
    }
  }, [checklistItem.id, enableOfflineMode]);

  // Cleanup on unmount
  useEffect(() => {
    return () => {
      if (saveTimeoutRef.current) {
        clearTimeout(saveTimeoutRef.current);
      }
    };
  }, []);

  return {
    currentStep,
    guidance: guidanceState,
    steps: guidanceSteps,
    isComplete: guidanceState.isComplete,
    isLoading: false,
    error: null,
    nextStep,
    previousStep,
    goToStep,
    markStepComplete,
    resetGuidance,
    getProgress,
    getCurrentRequirements,
    saveProgress,
    loadProgress
  };
};

// Utility hook for offline guidance caching
export const useOfflineGuidance = () => {
  const [cachedGuidance, setCachedGuidance] = useState<Record<string, any>>({});
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const loadCachedGuidance = () => {
      try {
        const keys = Object.keys(localStorage).filter(key => 
          key.startsWith('photo_guidance_')
        );

        const cached: Record<string, any> = {};
        keys.forEach(key => {
          try {
            cached[key] = JSON.parse(localStorage.getItem(key) || '{}');
          } catch (e) {
            // Skip invalid entries
          }
        });

        setCachedGuidance(cached);
      } catch (error) {
        console.error('Failed to load cached guidance:', error);
      } finally {
        setIsLoading(false);
      }
    };

    loadCachedGuidance();
  }, []);

  const clearCache = useCallback(() => {
    Object.keys(localStorage)
      .filter(key => key.startsWith('photo_guidance_'))
      .forEach(key => localStorage.removeItem(key));
    setCachedGuidance({});
  }, []);

  const getCachedProgress = useCallback((checklistItemId: string) => {
    return cachedGuidance[`photo_guidance_${checklistItemId}`];
  }, [cachedGuidance]);

  return {
    cachedGuidance,
    isLoading,
    clearCache,
    getCachedProgress
  };
};

import { useState, useEffect } from "react";
import { useAuth } from "@/components/AuthProvider";
import { useAuthRecovery } from "@/hooks/useAuthRecovery";

interface AuthDebugInfo {
  authStatus: {
    authenticated: boolean;
    userId?: string;
    userEmail?: string;
    userRole?: string;
    timestamp: string;
    roleSource: 'database' | 'fallback' | 'default';
    loadingState: boolean;
    error?: string;
    recoveryState?: any;
  };
}

export const usePropertyFormAuth = () => {
  const { user, userRole, loading, error } = useAuth();
  const { recoveryState, attemptRecovery, canRecover } = useAuthRecovery();
  const [authDebugInfo, setAuthDebugInfo] = useState<AuthDebugInfo>({} as AuthDebugInfo);

  // Enhanced authentication validation with recovery support
  useEffect(() => {
    console.log('üîç PropertyFormAuth - Auth state updated:', {
      user: !!user,
      userRole,
      loading,
      error,
      recoveryState,
      timestamp: new Date().toISOString()
    });

    if (loading && !recoveryState.isRecovering) {
      console.log('‚è≥ Auth still loading...');
      setAuthDebugInfo({
        authStatus: {
          authenticated: false,
          loadingState: loading,
          roleSource: 'default',
          timestamp: new Date().toISOString(),
          error: error || undefined,
          recoveryState
        }
      });
      return;
    }

    if (error && canRecover) {
      console.warn('‚ö†Ô∏è Auth error detected:', error);
      setAuthDebugInfo({
        authStatus: {
          authenticated: false,
          loadingState: loading,
          roleSource: 'default',
          timestamp: new Date().toISOString(),
          error,
          recoveryState
        }
      });
      return;
    }

    if (!user && !loading) {
      console.warn('‚ö†Ô∏è User not authenticated');
      setAuthDebugInfo({
        authStatus: {
          authenticated: false,
          loadingState: loading,
          roleSource: 'default',
          timestamp: new Date().toISOString(),
          error: error || undefined,
          recoveryState
        }
      });
      return;
    }

    // Determine role source for debugging
    const roleSource = userRole === 'inspector' ? 'fallback' : 'database';

    console.log('üë§ Enhanced Auth Status:', {
      user: {
        id: user?.id,
        email: user?.email,
        role: userRole,
        roleSource,
        loading,
        error
      },
      recoveryState,
      timestamp: new Date().toISOString()
    });

    setAuthDebugInfo({
      authStatus: {
        authenticated: !!user,
        userId: user?.id,
        userEmail: user?.email,
        userRole: userRole || 'inspector',
        roleSource,
        loadingState: loading,
        timestamp: new Date().toISOString(),
        error: error || undefined,
        recoveryState
      }
    });
  }, [user, userRole, loading, error, recoveryState, canRecover]);

  return {
    user,
    userRole,
    loading,
    error,
    authDebugInfo,
    recoveryState,
    attemptRecovery,
    canRecover
  };
};

import { useToast } from "@/hooks/use-toast";
import type { PropertyFormData } from "@/types/propertySubmission";

interface ValidationResult {
  isValid: boolean;
  errors: string[];
}

export const usePropertyFormValidation = () => {
  const { toast } = useToast();

  const validateFormData = (formData: PropertyFormData): ValidationResult => {
    const errors: string[] = [];

    // Name validation
    if (!formData.name || formData.name.trim().length === 0) {
      errors.push("Property name is required");
    } else if (formData.name.trim().length < 2) {
      errors.push("Property name must be at least 2 characters long");
    } else if (formData.name.trim().length > 200) {
      errors.push("Property name cannot exceed 200 characters");
    }

    // Address validation
    if (!formData.address || formData.address.trim().length === 0) {
      errors.push("Property address is required");
    } else if (formData.address.trim().length < 5) {
      errors.push("Address must be at least 5 characters long");
    } else if (formData.address.trim().length > 500) {
      errors.push("Address cannot exceed 500 characters");
    }

    // URL validation (optional but if provided must be valid)
    if (formData.vrbo_url && formData.vrbo_url.trim()) {
      if (!isValidUrl(formData.vrbo_url.trim())) {
        errors.push("Vrbo URL must be a valid URL");
      } else if (!formData.vrbo_url.toLowerCase().includes('vrbo.com')) {
        errors.push("Vrbo URL must be from vrbo.com domain");
      }
    }

    if (formData.airbnb_url && formData.airbnb_url.trim()) {
      if (!isValidUrl(formData.airbnb_url.trim())) {
        errors.push("Airbnb URL must be a valid URL");
      } else if (!formData.airbnb_url.toLowerCase().includes('airbnb.com')) {
        errors.push("Airbnb URL must be from airbnb.com domain");
      }
    }

    // At least one URL should be provided
    const hasVrbo = formData.vrbo_url && formData.vrbo_url.trim();
    const hasAirbnb = formData.airbnb_url && formData.airbnb_url.trim();

    if (!hasVrbo && !hasAirbnb) {
      errors.push("At least one listing URL (Vrbo or Airbnb) is required");
    }

    return {
      isValid: errors.length === 0,
      errors
    };
  };

  const isValidUrl = (url: string): boolean => {
    try {
      new URL(url);
      return url.startsWith('http://') || url.startsWith('https://');
    } catch {
      return false;
    }
  };

  const validateSubmission = (
    user: any,
    isOnline: boolean,
    formData: PropertyFormData
  ): boolean => {
    console.log('üîç Starting comprehensive validation...', {
      hasUser: !!user,
      isOnline,
      formData: {
        name: formData.name?.substring(0, 50) + (formData.name?.length > 50 ? '...' : ''),
        address: formData.address?.substring(0, 50) + (formData.address?.length > 50 ? '...' : ''),
        hasVrbo: !!formData.vrbo_url,
        hasAirbnb: !!formData.airbnb_url
      }
    });

    // Authentication check
    if (!user) {
      console.error('‚ùå Validation failed: No authenticated user');
      toast({
        title: "Authentication Required",
        description: "Please log in to add or edit properties.",
        variant: "destructive",
      });
      return false;
    }

    // Network check
    if (!isOnline) {
      console.error('‚ùå Validation failed: No network connection');
      toast({
        title: "Network Error",
        description: "Please check your internet connection and try again.",
        variant: "destructive",
      });
      return false;
    }

    // Form data validation
    const validation = validateFormData(formData);
    if (!validation.isValid) {
      console.error('‚ùå Validation failed: Form errors', validation.errors);
      toast({
        title: "Validation Error",
        description: validation.errors[0], // Show first error
        variant: "destructive",
      });
      return false;
    }

    console.log('‚úÖ All validation checks passed');
    return true;
  };

  return { 
    validateSubmission,
    validateFormData 
  };
};

import { useState } from "react";
import { useNavigate, useSearchParams } from "react-router-dom";
import { supabase } from "@/integrations/supabase/client";
import { useToast } from "@/hooks/use-toast";

interface LoadDebugInfo {
  loadError?: {
    error: string;
    code?: string;
    details?: string;
    hint?: string;
    propertyId: string;
    timestamp: string;
  };
  unexpectedLoadError?: {
    error: string;
    stack?: string;
    timestamp: string;
  };
  loadedProperty?: {
    id: string;
    name: string;
    hasVrboUrl: boolean;
    hasAirbnbUrl: boolean;
    timestamp: string;
  };
}

export const usePropertyLoader = (user: any) => {
  const navigate = useNavigate();
  const [searchParams] = useSearchParams();
  const { toast } = useToast();
  const editId = searchParams.get('edit');
  const isEditing = !!editId;

  const [isLoadingProperty, setIsLoadingProperty] = useState(false);
  const [loadDebugInfo, setLoadDebugInfo] = useState<LoadDebugInfo>({});

  const loadProperty = async () => {
    if (!isEditing || !editId || !user) return null;

    setIsLoadingProperty(true);
    console.log('üìù Loading property for editing:', editId);

    try {
      console.log('üîç Fetching property data...');
      const { data, error } = await supabase
        .from('properties')
        .select('*')
        .eq('id', editId)
        .single();

      if (error) {
        console.error('‚ùå Error loading property:', {
          error,
          propertyId: editId,
          userId: user.id
        });

        setLoadDebugInfo(prev => ({
          ...prev,
          loadError: {
            error: error.message,
            code: error.code,
            details: error.details,
            hint: error.hint,
            propertyId: editId,
            timestamp: new Date().toISOString()
          }
        }));

        toast({
          title: "Error Loading Property",
          description: `Failed to load property: ${error.message}`,
          variant: "destructive",
        });
        navigate('/properties');
        return null;
      }

      console.log('‚úÖ Property loaded successfully:', data);
      setLoadDebugInfo(prev => ({
        ...prev,
        loadedProperty: {
          id: data.id,
          name: data.name,
          hasVrboUrl: !!data.vrbo_url,
          hasAirbnbUrl: !!data.airbnb_url,
          timestamp: new Date().toISOString()
        }
      }));

      return {
        name: data.name || "",
        address: data.address || "",
        vrbo_url: data.vrbo_url || "",
        airbnb_url: data.airbnb_url || ""
      };
    } catch (error) {
      console.error('üí• Unexpected error loading property:', error);

      setLoadDebugInfo(prev => ({
        ...prev,
        unexpectedLoadError: {
          error: error instanceof Error ? error.message : 'Unknown error',
          stack: error instanceof Error ? error.stack : undefined,
          timestamp: new Date().toISOString()
        }
      }));

      toast({
        title: "Error",
        description: "An unexpected error occurred while loading the property.",
        variant: "destructive",
      });
      navigate('/properties');
      return null;
    } finally {
      setIsLoadingProperty(false);
    }
  };

  return {
    isEditing,
    isLoadingProperty,
    loadProperty,
    loadDebugInfo
  };
};

import { supabase } from "@/integrations/supabase/client";
import type { PropertyFormData } from "@/types/propertySubmission";

export const usePropertyMutation = () => {
  const executePropertyMutation = async (
    formData: PropertyFormData,
    user: any,
    isEditing: boolean,
    editId: string | null
  ) => {
    const submitData = {
      name: formData.name.trim(),
      address: formData.address.trim(),
      vrbo_url: formData.vrbo_url.trim() || null,
      airbnb_url: formData.airbnb_url.trim() || null,
    };

    console.log(`${isEditing ? 'üìù Updating' : '‚ûï Creating'} property with data:`, {
      ...submitData,
      userId: user.id,
      userEmail: user.email,
      isEditing,
      editId,
      timestamp: new Date().toISOString()
    });

    if (isEditing) {
      console.log('üîÑ Executing UPDATE operation...');
      return await supabase
        .from('properties')
        .update({
          ...submitData,
          updated_at: new Date().toISOString()
        })
        .eq('id', editId)
        .select()
        .single();
    } else {
      console.log('üÜï Executing INSERT operation...');
      return await supabase
        .from('properties')
        .insert({
          ...submitData,
          added_by: user.id,
          created_at: new Date().toISOString()
        })
        .select()
        .single();
    }
  };

  return { executePropertyMutation };
};

import { useNavigate, useSearchParams } from "react-router-dom";
import { usePropertyValidation } from "./usePropertyValidation";
import { usePropertyMutation } from "./usePropertyMutation";
import { usePropertySubmissionState } from "./usePropertySubmissionState";
import { usePropertyErrorHandler } from "@/utils/propertySubmissionErrors";
import type { PropertyFormData } from "@/types/propertySubmission";

export const usePropertySubmission = (user: any, userRole: string) => {
  const navigate = useNavigate();
  const [searchParams] = useSearchParams();
  const editId = searchParams.get('edit');
  const isEditing = !!editId;

  // Use the smaller, focused hooks
  const { validateSubmission } = usePropertyValidation();
  const { executePropertyMutation } = usePropertyMutation();
  const { 
    isLoading, 
    setIsLoading, 
    submissionDebugInfo, 
    updateDebugInfo 
  } = usePropertySubmissionState();
  const { 
    handleSubmissionError, 
    handleUnexpectedError, 
    handleSuccess 
  } = usePropertyErrorHandler();

  const submitProperty = async (formData: PropertyFormData, isOnline: boolean) => {
    console.log('üöÄ Starting form submission process...');

    // Pre-submission validation
    if (!validateSubmission(user, isOnline, formData)) {
      return false;
    }

    setIsLoading(true);
    const submitStartTime = Date.now();

    try {
      const result = await executePropertyMutation(formData, user, isEditing, editId);
      const { data, error } = result;
      const submitDuration = Date.now() - submitStartTime;

      if (error) {
        console.error(`‚ùå Database error during ${isEditing ? 'update' : 'insert'}:`, {
          error: {
            message: error.message,
            code: error.code,
            details: error.details,
            hint: error.hint
          },
          operation: isEditing ? 'update' : 'insert',
          userId: user.id,
          duration: submitDuration,
          timestamp: new Date().toISOString()
        });

        updateDebugInfo({
          submitError: {
            operation: isEditing ? 'update' : 'insert',
            error: error.message,
            code: error.code,
            details: error.details,
            hint: error.hint,
            duration: submitDuration,
            timestamp: new Date().toISOString()
          }
        });

        handleSubmissionError(error, isEditing);
        return false;
      }

      console.log(`‚úÖ Property ${isEditing ? 'updated' : 'created'} successfully:`, {
        data,
        duration: submitDuration,
        timestamp: new Date().toISOString()
      });

      updateDebugInfo({
        submitSuccess: {
          operation: isEditing ? 'update' : 'insert',
          propertyId: data?.id,
          duration: submitDuration,
          timestamp: new Date().toISOString()
        }
      });

      handleSuccess(formData.name.trim(), isEditing);

      // Small delay to ensure UI feedback is seen
      setTimeout(() => {
        navigate('/properties');
      }, 500);

      return true;
    } catch (error) {
      const submitDuration = Date.now() - submitStartTime;
      console.error(`üí• Unexpected error during ${isEditing ? 'update' : 'create'}:`, {
        error: error instanceof Error ? {
          message: error.message,
          stack: error.stack,
          name: error.name
        } : error,
        duration: submitDuration,
        timestamp: new Date().toISOString()
      });

      updateDebugInfo({
        unexpectedSubmitError: {
          error: error instanceof Error ? error.message : 'Unknown error',
          stack: error instanceof Error ? error.stack : undefined,
          duration: submitDuration,
          timestamp: new Date().toISOString()
        }
      });

      handleUnexpectedError();
      return false;
    } finally {
      setIsLoading(false);
    }
  };

  return {
    isLoading,
    submitProperty,
    submissionDebugInfo
  };
};

import { useState } from "react";

interface SubmissionMetrics {
  startTime: number;
  endTime?: number;
  duration?: number;
  attempts: number;
  errors: any[];
  success: boolean;
}

interface MonitoringState {
  currentSubmission: SubmissionMetrics | null;
  recentSubmissions: SubmissionMetrics[];
}

export const usePropertySubmissionMonitoring = () => {
  const [state, setState] = useState<MonitoringState>({
    currentSubmission: null,
    recentSubmissions: []
  });

  const startSubmissionTracking = () => {
    const submission: SubmissionMetrics = {
      startTime: Date.now(),
      attempts: 1,
      errors: [],
      success: false
    };

    setState(prev => ({
      ...prev,
      currentSubmission: submission
    }));

    console.log('üìä Started submission tracking:', {
      startTime: new Date(submission.startTime).toISOString(),
      submissionId: submission.startTime
    });

    return submission.startTime; // Return as submission ID
  };

  const recordError = (error: any) => {
    setState(prev => {
      if (!prev.currentSubmission) return prev;

      const updatedSubmission = {
        ...prev.currentSubmission,
        errors: [...prev.currentSubmission.errors, {
          timestamp: Date.now(),
          error: error instanceof Error ? {
            message: error.message,
            stack: error.stack,
            name: error.name
          } : error
        }]
      };

      console.log('‚ùå Recorded submission error:', {
        submissionId: updatedSubmission.startTime,
        errorCount: updatedSubmission.errors.length,
        latestError: updatedSubmission.errors[updatedSubmission.errors.length - 1]
      });

      return {
        ...prev,
        currentSubmission: updatedSubmission
      };
    });
  };

  const recordRetry = () => {
    setState(prev => {
      if (!prev.currentSubmission) return prev;

      const updatedSubmission = {
        ...prev.currentSubmission,
        attempts: prev.currentSubmission.attempts + 1
      };

      console.log('üîÑ Recorded submission retry:', {
        submissionId: updatedSubmission.startTime,
        attemptNumber: updatedSubmission.attempts
      });

      return {
        ...prev,
        currentSubmission: updatedSubmission
      };
    });
  };

  const completeSubmission = (success: boolean) => {
    setState(prev => {
      if (!prev.currentSubmission) return prev;

      const completedSubmission: SubmissionMetrics = {
        ...prev.currentSubmission,
        endTime: Date.now(),
        duration: Date.now() - prev.currentSubmission.startTime,
        success
      };

      console.log(`${success ? '‚úÖ' : '‚ùå'} Completed submission tracking:`, {
        submissionId: completedSubmission.startTime,
        success,
        duration: completedSubmission.duration,
        attempts: completedSubmission.attempts,
        errorCount: completedSubmission.errors.length
      });

      // Keep only the last 10 submissions for memory efficiency
      const recentSubmissions = [
        completedSubmission,
        ...prev.recentSubmissions.slice(0, 9)
      ];

      return {
        currentSubmission: null,
        recentSubmissions
      };
    });
  };

  const getSubmissionStats = () => {
    const { recentSubmissions } = state;

    if (recentSubmissions.length === 0) {
      return {
        totalSubmissions: 0,
        successRate: 0,
        averageDuration: 0,
        commonErrors: []
      };
    }

    const successCount = recentSubmissions.filter(s => s.success).length;
    const submissionsWithDuration = recentSubmissions.filter(s => s.duration && typeof s.duration === 'number');

    // Calculate total duration
    const totalDuration = submissionsWithDuration.reduce((sum: number, s) => {
      return sum + (s.duration as number);
    }, 0);

    const allErrors = recentSubmissions.flatMap(s => s.errors);
    const errorCounts = allErrors.reduce((acc, errorEntry) => {
      const errorKey = errorEntry.error?.message || 'Unknown error';
      acc[errorKey] = (acc[errorKey] || 0) + 1;
      return acc;
    }, {} as Record<string, number>);

    const commonErrors = Object.entries(errorCounts)
      .sort(([, a], [, b]) => (b as number) - (a as number))
      .slice(0, 5)
      .map(([error, count]) => ({ error, count }));

    // Calculate average duration
    const averageDuration = submissionsWithDuration.length > 0 
      ? totalDuration / submissionsWithDuration.length 
      : 0;

    return {
      totalSubmissions: recentSubmissions.length,
      successRate: (successCount / recentSubmissions.length) * 100,
      averageDuration,
      commonErrors
    };
  };

  return {
    startSubmissionTracking,
    recordError,
    recordRetry,
    completeSubmission,
    getSubmissionStats,
    currentSubmission: state.currentSubmission,
    recentSubmissions: state.recentSubmissions
  };
};

import { useToast } from "@/hooks/use-toast";

export const usePropertySubmissionRetry = () => {
  const { toast } = useToast();

  const isRetryable = (error: any): boolean => {
    // Network/timeout errors are retryable
    if (error.code === 'PGRST301' || // Network error
        error.code === 'PGRST504' || // Gateway timeout
        error.message?.includes('timeout') ||
        error.message?.includes('network') ||
        error.message?.includes('connection')) {
      return true;
    }

    // Authentication errors might be retryable (token refresh)
    if (error.code === '42501' || error.message?.includes('JWT')) {
      return true;
    }

    // Business logic errors are not retryable
    if (error.code === '23505' || // Unique constraint
        error.code === 'PGRST116' || // Not found
        error.message?.includes('violates')) {
      return false;
    }

    // Default to retryable for unknown errors
    return true;
  };

  const showRetryToast = (attempt: number, maxRetries: number, isUnexpected: boolean = false) => {
    const title = isUnexpected ? "Unexpected Error" : "Connection Issue";
    toast({
      title,
      description: `Retrying... (${attempt}/${maxRetries})`,
    });
  };

  const createBackoffDelay = (attempt: number): number => {
    // Exponential backoff: 1s, 2s
    return Math.pow(2, attempt - 1) * 1000;
  };

  return {
    isRetryable,
    showRetryToast,
    createBackoffDelay
  };
};

