import { useState } from "react";
import { useNavigate, useSearchParams } from "react-router-dom";
import { useToast } from "@/hooks/use-toast";
import { supabase } from "@/integrations/supabase/client";
import type { PropertyFormData } from "@/types/propertySubmission";

export const useReliablePropertySubmission = (user: any) => {
  const navigate = useNavigate();
  const [searchParams] = useSearchParams();
  const editId = searchParams.get('edit');
  const isEditing = !!editId;
  const { toast } = useToast();
  const [isLoading, setIsLoading] = useState(false);

  const validateSubmission = (formData: PropertyFormData, isOnline: boolean): boolean => {
    console.log('üîç Validating submission...', { formData, isOnline, hasUser: !!user });

    // Check authentication
    if (!user) {
      toast({
        title: "Authentication Required",
        description: "Please log in to add or edit properties.",
        variant: "destructive",
      });
      return false;
    }

    // Check network
    if (!isOnline) {
      toast({
        title: "Network Error",
        description: "Please check your internet connection and try again.",
        variant: "destructive",
      });
      return false;
    }

    // Validate form data
    if (!formData.name || formData.name.trim().length < 2) {
      toast({
        title: "Validation Error",
        description: "Property name must be at least 2 characters long.",
        variant: "destructive",
      });
      return false;
    }

    if (!formData.address || formData.address.trim().length < 5) {
      toast({
        title: "Validation Error",
        description: "Please provide a complete address.",
        variant: "destructive",
      });
      return false;
    }

    // Check that at least one URL is provided
    const hasVrbo = formData.vrbo_url && formData.vrbo_url.trim();
    const hasAirbnb = formData.airbnb_url && formData.airbnb_url.trim();

    if (!hasVrbo && !hasAirbnb) {
      toast({
        title: "Validation Error",
        description: "At least one listing URL (Vrbo or Airbnb) is required.",
        variant: "destructive",
      });
      return false;
    }

    return true;
  };

  const submitProperty = async (formData: PropertyFormData, isOnline: boolean) => {
    console.log('üöÄ Starting property submission...', { isEditing, formData });

    if (!validateSubmission(formData, isOnline)) {
      return false;
    }

    setIsLoading(true);

    try {
      const submitData = {
        name: formData.name.trim(),
        address: formData.address.trim(),
        vrbo_url: formData.vrbo_url.trim() || null,
        airbnb_url: formData.airbnb_url.trim() || null,
      };

      console.log('üìù Submitting data:', submitData);

      let result;
      if (isEditing) {
        result = await supabase
          .from('properties')
          .update({
            ...submitData,
            updated_at: new Date().toISOString()
          })
          .eq('id', editId)
          .select()
          .single();
      } else {
        result = await supabase
          .from('properties')
          .insert({
            ...submitData,
            added_by: user.id,
            created_at: new Date().toISOString()
          })
          .select()
          .single();
      }

      const { data, error } = result;

      if (error) {
        console.error('‚ùå Database error:', error);

        let errorMessage = "An error occurred while saving the property.";

        if (error.code === '23505') {
          if (error.message?.includes('properties_name')) {
            errorMessage = "A property with this name already exists.";
          } else if (error.message?.includes('vrbo_url')) {
            errorMessage = "This Vrbo URL is already registered.";
          } else if (error.message?.includes('airbnb_url')) {
            errorMessage = "This Airbnb URL is already registered.";
          }
        } else if (error.code === '42501' || error.message?.includes('JWT')) {
          errorMessage = "Your session has expired. Please log in again.";
        } else if (error.message?.includes('violates row-level security')) {
          errorMessage = "You don't have permission to perform this action.";
        }

        toast({
          title: `Error ${isEditing ? 'Updating' : 'Creating'} Property`,
          description: errorMessage,
          variant: "destructive",
        });
        return false;
      }

      console.log('‚úÖ Property saved successfully:', data);

      toast({
        title: `Property ${isEditing ? 'Updated' : 'Added'}`,
        description: `The property "${formData.name.trim()}" has been ${isEditing ? 'updated' : 'added'} successfully.`,
      });

      setTimeout(() => {
        navigate('/properties');
      }, 500);

      return true;
    } catch (error) {
      console.error('üí• Unexpected error:', error);

      toast({
        title: "Unexpected Error",
        description: "An unexpected error occurred. Please try again or contact support if the problem persists.",
        variant: "destructive",
      });
      return false;
    } finally {
      setIsLoading(false);
    }
  };

  return {
    isLoading,
    submitProperty,
    isEditing
  };
};

import { useState, useCallback } from "react";
import { useNavigate } from "react-router-dom";
import { useToast } from "@/hooks/use-toast";
import { supabase } from "@/integrations/supabase/client";

interface PropertyActionError {
  type: 'network' | 'validation' | 'auth' | 'system';
  message: string;
  action: string;
  retryable: boolean;
}

interface PropertyActionState {
  isLoading: boolean;
  error: PropertyActionError | null;
  retryCount: number;
}

export const useRobustPropertyActions = () => {
  const [actionState, setActionState] = useState<PropertyActionState>({
    isLoading: false,
    error: null,
    retryCount: 0
  });

  const { toast } = useToast();
  const navigate = useNavigate();

  const classifyError = (error: any, action: string): PropertyActionError => {
    const errorMessage = error?.message || 'Unknown error occurred';

    if (errorMessage.includes('network') || errorMessage.includes('fetch')) {
      return {
        type: 'network',
        message: 'Network connection failed. Please check your internet.',
        action,
        retryable: true
      };
    }

    if (errorMessage.includes('unauthorized') || errorMessage.includes('forbidden')) {
      return {
        type: 'auth',
        message: 'You don\'t have permission to perform this action.',
        action,
        retryable: false
      };
    }

    if (errorMessage.includes('validation') || errorMessage.includes('invalid')) {
      return {
        type: 'validation',
        message: 'The data provided is invalid. Please check and try again.',
        action,
        retryable: false
      };
    }

    return {
      type: 'system',
      message: errorMessage,
      action,
      retryable: true
    };
  };

  const executeWithRetry = useCallback(async <T>(
    operation: () => Promise<T>,
    actionName: string,
    maxRetries = 3
  ): Promise<T | null> => {
    setActionState(prev => ({ ...prev, isLoading: true, error: null }));

    for (let attempt = 0; attempt <= maxRetries; attempt++) {
      try {
        const result = await operation();

        setActionState({
          isLoading: false,
          error: null,
          retryCount: 0
        });

        return result;
      } catch (error) {
        console.error(`‚ùå ${actionName} attempt ${attempt + 1} failed:`, error);

        const classifiedError = classifyError(error, actionName);

        if (attempt === maxRetries || !classifiedError.retryable) {
          setActionState({
            isLoading: false,
            error: classifiedError,
            retryCount: attempt + 1
          });

          toast({
            title: `${actionName} Failed`,
            description: classifiedError.message,
            variant: "destructive",
          });

          return null;
        }

        setActionState(prev => ({ ...prev, retryCount: attempt + 1 }));

        // Wait before retry (exponential backoff)
        await new Promise(resolve => setTimeout(resolve, Math.pow(2, attempt) * 1000));
      }
    }

    return null;
  }, [toast]);

  const deleteProperty = useCallback(async (propertyId: string) => {
    console.log('üóëÔ∏è Starting comprehensive property deletion:', propertyId);

    return executeWithRetry(async () => {
      // First, delete all related inspections and their data
      const { data: inspections, error: fetchError } = await supabase
        .from('inspections')
        .select('id')
        .eq('property_id', propertyId);

      if (fetchError) throw fetchError;

      if (inspections && inspections.length > 0) {
        const inspectionIds = inspections.map(i => i.id);

        // Delete checklist items for all inspections
        const { error: checklistError } = await supabase
          .from('checklist_items')
          .delete()
          .in('inspection_id', inspectionIds);

        if (checklistError) throw checklistError;

        // Delete media for all inspections
        const { error: mediaError } = await supabase
          .from('media')
          .delete()
          .in('checklist_item_id', 
            await supabase
              .from('checklist_items')
              .select('id')
              .in('inspection_id', inspectionIds)
              .then(res => res.data?.map(item => item.id) || [])
          );

        // Delete inspections
        const { error: inspectionError } = await supabase
          .from('inspections')
          .delete()
          .eq('property_id', propertyId);

        if (inspectionError) throw inspectionError;
      }

      // Finally, delete the property
      const { error: propertyError } = await supabase
        .from('properties')
        .delete()
        .eq('id', propertyId);

      if (propertyError) throw propertyError;

      toast({
        title: "Property Deleted",
        description: "The property and all associated data have been permanently removed.",
      });

      return true;
    }, 'Delete Property');
  }, [executeWithRetry, toast]);

  const editProperty = useCallback((propertyId: string) => {
    try {
      console.log('üîß Navigating to edit property:', propertyId);
      navigate(`/add-property?edit=${propertyId}`);
    } catch (error) {
      console.error('‚ùå Navigation error:', error);
      toast({
        title: "Navigation Failed",
        description: "Could not navigate to edit page. Please try again.",
        variant: "destructive",
      });
    }
  }, [navigate, toast]);

  const startInspection = useCallback(async (propertyId: string) => {
    console.log('üöÄ Starting inspection for property:', propertyId);

    return executeWithRetry(async () => {
      // Check if there's already an active inspection
      const { data: existingInspection, error: checkError } = await supabase
        .from('inspections')
        .select('id')
        .eq('property_id', propertyId)
        .eq('completed', false)
        .single();

      if (checkError && checkError.code !== 'PGRST116') {
        throw checkError;
      }

      if (existingInspection) {
        console.log('üìã Joining existing inspection:', existingInspection.id);
        navigate(`/inspection/${existingInspection.id}`);
        return existingInspection.id;
      }

      // Create new inspection
      const { data: newInspection, error: createError } = await supabase
        .from('inspections')
        .insert({
          property_id: propertyId,
          start_time: new Date().toISOString(),
          completed: false
        })
        .select()
        .single();

      if (createError) throw createError;

      console.log('‚úÖ Created new inspection:', newInspection.id);
      navigate(`/inspection/${newInspection.id}`);

      toast({
        title: "Inspection Started",
        description: "A new inspection has been created successfully.",
      });

      return newInspection.id;
    }, 'Start Inspection');
  }, [executeWithRetry, navigate, toast]);

  const clearError = useCallback(() => {
    setActionState(prev => ({ ...prev, error: null }));
  }, []);

  const retry = useCallback(() => {
    if (actionState.error?.retryable) {
      setActionState(prev => ({ ...prev, error: null, retryCount: 0 }));
    }
  }, [actionState.error]);

  return {
    deleteProperty,
    editProperty,
    startInspection,
    actionState,
    clearError,
    retry
  };
};

