import { useToast } from "@/hooks/use-toast";
import type { PropertyFormData } from "@/types/propertySubmission";

export const useSimplePropertyValidation = () => {
  const { toast } = useToast();

  const isValidUrl = (url: string): boolean => {
    try {
      new URL(url);
      return url.startsWith('http://') || url.startsWith('https://');
    } catch {
      return false;
    }
  };

  const validateFormData = (formData: PropertyFormData) => {
    const errors: string[] = [];

    // Name validation
    if (!formData.name || formData.name.trim().length === 0) {
      errors.push("Property name is required");
    } else if (formData.name.trim().length < 2) {
      errors.push("Property name must be at least 2 characters long");
    }

    // Address validation
    if (!formData.address || formData.address.trim().length === 0) {
      errors.push("Property address is required");
    } else if (formData.address.trim().length < 5) {
      errors.push("Address must be at least 5 characters long");
    }

    // URL validation (optional but if provided must be valid)
    if (formData.vrbo_url && formData.vrbo_url.trim()) {
      if (!isValidUrl(formData.vrbo_url.trim())) {
        errors.push("Vrbo URL must be a valid URL");
      }
    }

    if (formData.airbnb_url && formData.airbnb_url.trim()) {
      if (!isValidUrl(formData.airbnb_url.trim())) {
        errors.push("Airbnb URL must be a valid URL");
      }
    }

    // At least one URL should be provided
    const hasVrbo = formData.vrbo_url && formData.vrbo_url.trim();
    const hasAirbnb = formData.airbnb_url && formData.airbnb_url.trim();

    if (!hasVrbo && !hasAirbnb) {
      errors.push("At least one listing URL (Vrbo or Airbnb) is required");
    }

    return {
      isValid: errors.length === 0,
      errors
    };
  };

  const validateSubmission = (
    user: any,
    isOnline: boolean,
    formData: PropertyFormData
  ): boolean => {
    console.log('üîç Starting validation...', {
      hasUser: !!user,
      isOnline,
      formDataValid: !!formData.name && !!formData.address
    });

    // Authentication check
    if (!user) {
      console.error('‚ùå Validation failed: No authenticated user');
      toast({
        title: "Authentication Required",
        description: "Please log in to add or edit properties.",
        variant: "destructive",
      });
      return false;
    }

    // Network check
    if (!isOnline) {
      console.error('‚ùå Validation failed: No network connection');
      toast({
        title: "Network Error",
        description: "Please check your internet connection and try again.",
        variant: "destructive",
      });
      return false;
    }

    // Form data validation
    const validation = validateFormData(formData);
    if (!validation.isValid) {
      console.error('‚ùå Validation failed: Form errors', validation.errors);
      toast({
        title: "Validation Error",
        description: validation.errors[0], // Show first error
        variant: "destructive",
      });
      return false;
    }

    console.log('‚úÖ All validation checks passed');
    return true;
  };

  return { 
    validateSubmission,
    validateFormData 
  };
};

// Video Recording Hook for STR Certified

import { useState, useCallback, useRef, useEffect, MutableRefObject } from 'react';
import { useMutation } from '@tanstack/react-query';
import { createVideoProcessor } from '@/lib/video/video-processor';
import type {
  VideoRecording,
  VideoStatus,
  VideoRecordingConfig,
  VideoRecordingStats,
  VideoTimestamp
} from '@/types/video';

export interface UseVideoRecordingOptions {
  videoRef?: MutableRefObject<HTMLVideoElement | null>;
  maxDuration?: number;
  audioEnabled?: boolean;
  onStats?: (stats: VideoRecordingStats) => void;
  onStatusChange?: (status: VideoStatus) => void;
  autoSave?: boolean;
  compressionEnabled?: boolean;
}

export interface UseVideoRecordingReturn {
  // State
  status: VideoStatus;
  stream: MediaStream | null;
  recording: VideoRecording | null;
  isRecording: boolean;
  isPaused: boolean;
  duration: number;
  error: Error | null;

  // Controls
  startRecording: (propertyId: string, inspectorId: string) => Promise<void>;
  pauseRecording: () => void;
  resumeRecording: () => void;
  stopRecording: () => Promise<VideoRecording | null>;
  cancelRecording: () => void;

  // Permissions
  hasPermission: boolean;
  requestPermission: () => Promise<void>;

  // Processing
  isProcessing: boolean;
  processingProgress: number;
  timestamps: VideoTimestamp[];
}

export const useVideoRecording = (
  options: UseVideoRecordingOptions = {}
): UseVideoRecordingReturn => {
  const {
    videoRef,
    maxDuration = 600, // 10 minutes default
    audioEnabled = true,
    onStats,
    onStatusChange,
    autoSave = true,
    compressionEnabled = true
  } = options;

  // State
  const [status, setStatus] = useState<VideoStatus>('stopped');
  const [stream, setStream] = useState<MediaStream | null>(null);
  const [recording, setRecording] = useState<VideoRecording | null>(null);
  const [duration, setDuration] = useState(0);
  const [hasPermission, setHasPermission] = useState(false);
  const [processingProgress, setProcessingProgress] = useState(0);
  const [timestamps, setTimestamps] = useState<VideoTimestamp[]>([]);
  const [error, setError] = useState<Error | null>(null);

  // Refs
  const videoProcessorRef = useRef(createVideoProcessor());
  const durationIntervalRef = useRef<NodeJS.Timeout | null>(null);
  const startTimeRef = useRef<number>(0);
  const pausedDurationRef = useRef<number>(0);
  const lastPauseTimeRef = useRef<number>(0);
  const currentPropertyIdRef = useRef<string>('');
  const currentInspectorIdRef = useRef<string>('');

  // Derived state
  const isRecording = status === 'recording';
  const isPaused = status === 'paused';
  const isProcessing = status === 'processing' || status === 'analyzing';

  // Update status and notify
  const updateStatus = useCallback((newStatus: VideoStatus) => {
    setStatus(newStatus);
    onStatusChange?.(newStatus);
  }, [onStatusChange]);

  // Check camera permission
  const checkPermission = useCallback(async (): Promise<boolean> => {
    try {
      // Check if we already have a stream
      if (stream) {
        setHasPermission(true);
        return true;
      }

      // Try to check permission without triggering prompt
      if (navigator.permissions && navigator.permissions.query) {
        try {
          const result = await navigator.permissions.query({ name: 'camera' as PermissionName });
          const granted = result.state === 'granted';
          setHasPermission(granted);
          return granted;
        } catch {
          // Permissions API not supported
        }
      }

      return false;
    } catch {
      return false;
    }
  }, [stream]);

  // Request camera permission
  const requestPermission = useCallback(async (): Promise<void> => {
    try {
      const constraints: MediaStreamConstraints = {
        video: {
          width: { ideal: 1920 },
          height: { ideal: 1080 },
          facingMode: 'environment'
        },
        audio: audioEnabled
      };

      const mediaStream = await navigator.mediaDevices.getUserMedia(constraints);
      setStream(mediaStream);
      setHasPermission(true);
      setError(null);

      // Attach to video element if provided
      if (videoRef?.current) {
        videoRef.current.srcObject = mediaStream;
      }
    } catch (err) {
      const error = err as Error;
      setError(error);
      setHasPermission(false);
      throw error;
    }
  }, [audioEnabled, videoRef]);

  // Start recording
  const startRecording = useCallback(async (
    propertyId: string,
    inspectorId: string
  ): Promise<void> => {
    try {
      setError(null);

      // Ensure we have permission
      if (!hasPermission) {
        await requestPermission();
      }

      if (!stream) {
        throw new Error('No media stream available');
      }

      // Store IDs
      currentPropertyIdRef.current = propertyId;
      currentInspectorIdRef.current = inspectorId;

      // Reset timing
      startTimeRef.current = Date.now();
      pausedDurationRef.current = 0;
      setDuration(0);

      // Start duration tracking
      durationIntervalRef.current = setInterval(() => {
        const elapsed = Date.now() - startTimeRef.current - pausedDurationRef.current;
        const seconds = Math.floor(elapsed / 1000);
        setDuration(seconds);

        // Auto-stop at max duration
        if (seconds >= maxDuration) {
          stopRecording();
        }
      }, 100);

      // Configure recording
      const recordingConfig: VideoRecordingConfig = {
        maxDuration,
        targetResolution: { width: 1920, height: 1080, aspectRatio: '16:9' },
        targetBitrate: 2500,
        audioEnabled,
        stabilizationEnabled: true,
        autoFocusEnabled: true,
        lowLightEnhancement: true
      };

      // Start recording
      updateStatus('recording');

      const processor = videoProcessorRef.current;
      const videoRecording = await processor.recordWalkthrough(
        stream,
        recordingConfig,
        onStats
      );

      // Update with property and inspector IDs
      videoRecording.propertyId = propertyId;
      videoRecording.inspectorId = inspectorId;

      setRecording(videoRecording);

    } catch (err) {
      const error = err as Error;
      setError(error);
      updateStatus('failed');
      throw error;
    }
  }, [hasPermission, stream, maxDuration, audioEnabled, onStats, requestPermission, updateStatus]);

  // Pause recording
  const pauseRecording = useCallback(() => {
    if (status === 'recording') {
      lastPauseTimeRef.current = Date.now();
      videoProcessorRef.current.pauseRecording();
      updateStatus('paused');
    }
  }, [status, updateStatus]);

  // Resume recording
  const resumeRecording = useCallback(() => {
    if (status === 'paused') {
      pausedDurationRef.current += Date.now() - lastPauseTimeRef.current;
      videoProcessorRef.current.resumeRecording();
      updateStatus('recording');
    }
  }, [status, updateStatus]);

  // Stop recording
  const stopRecording = useCallback(async (): Promise<VideoRecording | null> => {
    try {
      // Clear duration interval
      if (durationIntervalRef.current) {
        clearInterval(durationIntervalRef.current);
        durationIntervalRef.current = null;
      }

      // Stop recording
      videoProcessorRef.current.stopRecording();
      updateStatus('processing');

      if (!recording) {
        throw new Error('No recording available');
      }

      // Process video frames
      setProcessingProgress(20);
      const extractedTimestamps = await videoProcessorRef.current.processVideoFrames(
        recording.file
      );

      setProcessingProgress(60);

      // Analyze video content
      const analysis = await videoProcessorRef.current.analyzeVideoContent(
        recording.file,
        extractedTimestamps
      );

      setProcessingProgress(80);

      // Generate navigation timestamps
      const navigationTimestamps = videoProcessorRef.current.generateTimestamps(analysis);

      // Update recording with analysis
      const finalRecording: VideoRecording = {
        ...recording,
        timestamps: navigationTimestamps,
        status: 'completed',
        processedAt: new Date()
      };

      setRecording(finalRecording);
      setTimestamps(navigationTimestamps);
      setProcessingProgress(100);

      // Save if auto-save enabled
      if (autoSave) {
        await saveRecording(finalRecording);
      }

      updateStatus('completed');
      return finalRecording;

    } catch (err) {
      const error = err as Error;
      setError(error);
      updateStatus('failed');
      return null;
    } finally {
      setProcessingProgress(0);
    }
  }, [recording, autoSave, updateStatus]);

  // Cancel recording
  const cancelRecording = useCallback(() => {
    // Clear duration interval
    if (durationIntervalRef.current) {
      clearInterval(durationIntervalRef.current);
      durationIntervalRef.current = null;
    }

    // Stop processor
    videoProcessorRef.current.stopRecording();

    // Reset state
    setRecording(null);
    setDuration(0);
    setTimestamps([]);
    updateStatus('stopped');
  }, [updateStatus]);

  // Save recording to storage
  const saveRecording = async (recording: VideoRecording): Promise<void> => {
    try {
      // In production, this would upload to cloud storage
      // For now, we'll save to IndexedDB or localStorage
      const savedRecordings = JSON.parse(
        localStorage.getItem('video_recordings') || '[]'
      );

      // Save metadata only (not the actual file)
      const metadata = {
        id: recording.id,
        propertyId: recording.propertyId,
        inspectorId: recording.inspectorId,
        duration: recording.duration,
        size: recording.size,
        createdAt: recording.createdAt,
        timestamps: recording.timestamps.length
      };

      savedRecordings.push(metadata);
      localStorage.setItem('video_recordings', JSON.stringify(savedRecordings));

    } catch (error) {
      console.error('Failed to save recording:', error);
    }
  };

  // Initialize permission check
  useEffect(() => {
    checkPermission();
  }, [checkPermission]);

  // Cleanup on unmount
  useEffect(() => {
    return () => {
      // Clear intervals
      if (durationIntervalRef.current) {
        clearInterval(durationIntervalRef.current);
      }

      // Stop any active recording
      if (status === 'recording' || status === 'paused') {
        videoProcessorRef.current.stopRecording();
      }

      // Stop media stream
      if (stream) {
        stream.getTracks().forEach(track => track.stop());
      }
    };
  }, [status, stream]);

  return {
    // State
    status,
    stream,
    recording,
    isRecording,
    isPaused,
    duration,
    error,

    // Controls
    startRecording,
    pauseRecording,
    resumeRecording,
    stopRecording,
    cancelRecording,

    // Permissions
    hasPermission,
    requestPermission,

    // Processing
    isProcessing,
    processingProgress,
    timestamps
  };
};

// Hook for managing multiple video recordings
export const useVideoRecordingManager = () => {
  const [recordings, setRecordings] = useState<VideoRecording[]>([]);
  const [isLoading, setIsLoading] = useState(true);

  // Load saved recordings
  useEffect(() => {
    const loadRecordings = async () => {
      try {
        const saved = localStorage.getItem('video_recordings');
        if (saved) {
          setRecordings(JSON.parse(saved));
        }
      } catch (error) {
        console.error('Failed to load recordings:', error);
      } finally {
        setIsLoading(false);
      }
    };

    loadRecordings();
  }, []);

  // Delete recording
  const deleteRecording = useCallback((id: string) => {
    setRecordings(prev => {
      const updated = prev.filter(r => r.id !== id);
      localStorage.setItem('video_recordings', JSON.stringify(updated));
      return updated;
    });
  }, []);

  // Get recordings for property
  const getPropertyRecordings = useCallback((propertyId: string) => {
    return recordings.filter(r => r.propertyId === propertyId);
  }, [recordings]);

  return {
    recordings,
    isLoading,
    deleteRecording,
    getPropertyRecordings
  };
};

// Video Review Hook for STR Certified Auditor Interface

import { useState, useEffect, useCallback, useRef, MutableRefObject } from 'react';
import { useMutation, useQuery } from '@tanstack/react-query';
import type { VideoRecording, VideoTimestamp, SceneType } from '@/types/video';
import type { VideoAnnotation } from '@/components/audit/VideoAnnotationTools';

export interface VideoBookmark {
  id: string;
  time: number;
  description: string;
  createdAt: Date;
}

export interface UseVideoReviewReturn {
  // Playback state
  isPlaying: boolean;
  currentTime: number;
  duration: number;
  buffered: number;
  volume: number;
  playbackRate: number;

  // Scene information
  currentScene: VideoTimestamp | null;
  nearestTimestamp: VideoTimestamp | null;
  activeScenes: VideoTimestamp[];

  // Controls
  play: () => void;
  pause: () => void;
  seek: (time: number) => void;
  setVolume: (volume: number) => void;
  setPlaybackRate: (rate: number) => void;

  // Navigation
  jumpToTimestamp: (timestamp: VideoTimestamp) => void;
  jumpToNextScene: () => void;
  jumpToPreviousScene: () => void;
  jumpToRoom: (roomType: string) => void;

  // Bookmarks
  bookmarks: VideoBookmark[];
  addBookmark: (time: number, description: string) => void;
  removeBookmark: (id: string) => void;
  isBookmarked: (time: number) => boolean;

  // Annotations
  annotations: VideoAnnotation[];
  addAnnotation: (annotation: Omit<VideoAnnotation, 'id' | 'createdAt'>) => void;
  updateAnnotation: (id: string, annotation: Partial<VideoAnnotation>) => void;
  deleteAnnotation: (id: string) => void;

  // Review progress
  reviewProgress: number;
  markAsReviewed: (startTime: number, endTime: number) => void;
  isReviewed: (time: number) => boolean;
  reviewedSegments: Array<{ start: number; end: number }>;
}

export const useVideoReview = (
  video: VideoRecording,
  videoRef: MutableRefObject<HTMLVideoElement | null>
): UseVideoReviewReturn => {
  // Playback state
  const [isPlaying, setIsPlaying] = useState(false);
  const [currentTime, setCurrentTime] = useState(0);
  const [duration, setDuration] = useState(video.duration);
  const [buffered, setBuffered] = useState(0);
  const [volume, setVolume] = useState(1);
  const [playbackRate, setPlaybackRate] = useState(1);

  // Scene state
  const [currentScene, setCurrentScene] = useState<VideoTimestamp | null>(null);
  const [nearestTimestamp, setNearestTimestamp] = useState<VideoTimestamp | null>(null);

  // Bookmarks and annotations
  const [bookmarks, setBookmarks] = useState<VideoBookmark[]>([]);
  const [annotations, setAnnotations] = useState<VideoAnnotation[]>([]);

  // Review progress
  const [reviewedSegments, setReviewedSegments] = useState<Array<{ start: number; end: number }>>([]);
  const progressTrackingRef = useRef<{ start: number; accumulated: number }>({ start: 0, accumulated: 0 });

  // Load saved data
  useEffect(() => {
    // Load bookmarks
    const savedBookmarks = localStorage.getItem(`video_bookmarks_${video.id}`);
    if (savedBookmarks) {
      setBookmarks(JSON.parse(savedBookmarks));
    }

    // Load annotations
    const savedAnnotations = localStorage.getItem(`video_annotations_${video.id}`);
    if (savedAnnotations) {
      setAnnotations(JSON.parse(savedAnnotations));
    }

    // Load review progress
    const savedProgress = localStorage.getItem(`video_progress_${video.id}`);
    if (savedProgress) {
      setReviewedSegments(JSON.parse(savedProgress));
    }
  }, [video.id]);

  // Save data on changes
  useEffect(() => {
    localStorage.setItem(`video_bookmarks_${video.id}`, JSON.stringify(bookmarks));
  }, [bookmarks, video.id]);

  useEffect(() => {
    localStorage.setItem(`video_annotations_${video.id}`, JSON.stringify(annotations));
  }, [annotations, video.id]);

  useEffect(() => {
    localStorage.setItem(`video_progress_${video.id}`, JSON.stringify(reviewedSegments));
  }, [reviewedSegments, video.id]);

  // Video event handlers
  useEffect(() => {
    const videoElement = videoRef.current;
    if (!videoElement) return;

    const handleTimeUpdate = () => {
      setCurrentTime(videoElement.currentTime);

      // Update buffered amount
      if (videoElement.buffered.length > 0) {
        const bufferedEnd = videoElement.buffered.end(videoElement.buffered.length - 1);
        setBuffered(bufferedEnd);
      }

      // Track review progress
      if (isPlaying) {
        progressTrackingRef.current.accumulated += 
          videoElement.currentTime - progressTrackingRef.current.start;
        progressTrackingRef.current.start = videoElement.currentTime;
      }
    };

    const handlePlay = () => {
      setIsPlaying(true);
      progressTrackingRef.current.start = videoElement.currentTime;
    };

    const handlePause = () => {
      setIsPlaying(false);
      // Mark segment as reviewed
      if (progressTrackingRef.current.accumulated > 2) { // At least 2 seconds
        const start = Math.max(0, videoElement.currentTime - progressTrackingRef.current.accumulated);
        const end = videoElement.currentTime;
        markAsReviewed(start, end);
      }
      progressTrackingRef.current.accumulated = 0;
    };

    const handleLoadedMetadata = () => {
      setDuration(videoElement.duration);
    };

    const handleVolumeChange = () => {
      setVolume(videoElement.volume);
    };

    const handleRateChange = () => {
      setPlaybackRate(videoElement.playbackRate);
    };

    // Add event listeners
    videoElement.addEventListener('timeupdate', handleTimeUpdate);
    videoElement.addEventListener('play', handlePlay);
    videoElement.addEventListener('pause', handlePause);
    videoElement.addEventListener('loadedmetadata', handleLoadedMetadata);
    videoElement.addEventListener('volumechange', handleVolumeChange);
    videoElement.addEventListener('ratechange', handleRateChange);

    return () => {
      videoElement.removeEventListener('timeupdate', handleTimeUpdate);
      videoElement.removeEventListener('play', handlePlay);
      videoElement.removeEventListener('pause', handlePause);
      videoElement.removeEventListener('loadedmetadata', handleLoadedMetadata);
      videoElement.removeEventListener('volumechange', handleVolumeChange);
      videoElement.removeEventListener('ratechange', handleRateChange);
    };
  }, [videoRef, isPlaying]);

  // Update current scene based on time
  useEffect(() => {
    const scene = video.timestamps.find(
      ts => currentTime >= ts.time && 
            currentTime < (video.timestamps[video.timestamps.indexOf(ts) + 1]?.time || duration)
    );
    setCurrentScene(scene || null);

    // Find nearest timestamp
    let nearest = video.timestamps[0];
    let minDiff = Math.abs(video.timestamps[0].time - currentTime);

    for (const ts of video.timestamps) {
      const diff = Math.abs(ts.time - currentTime);
      if (diff < minDiff) {
        minDiff = diff;
        nearest = ts;
      }
    }

    setNearestTimestamp(minDiff < 10 ? nearest : null); // Within 10 seconds
  }, [currentTime, video.timestamps, duration]);

  // Calculate active scenes (within view)
  const activeScenes = video.timestamps.filter(
    ts => Math.abs(ts.time - currentTime) < 30 // Within 30 seconds
  );

  // Playback controls
  const play = useCallback(() => {
    videoRef.current?.play();
  }, [videoRef]);

  const pause = useCallback(() => {
    videoRef.current?.pause();
  }, [videoRef]);

  const seek = useCallback((time: number) => {
    if (videoRef.current) {
      videoRef.current.currentTime = Math.max(0, Math.min(time, duration));
    }
  }, [videoRef, duration]);

  const updateVolume = useCallback((vol: number) => {
    if (videoRef.current) {
      videoRef.current.volume = Math.max(0, Math.min(1, vol));
    }
  }, [videoRef]);

  const updatePlaybackRate = useCallback((rate: number) => {
    if (videoRef.current) {
      videoRef.current.playbackRate = rate;
    }
  }, [videoRef]);

  // Navigation
  const jumpToTimestamp = useCallback((timestamp: VideoTimestamp) => {
    seek(timestamp.time);
  }, [seek]);

  const jumpToNextScene = useCallback(() => {
    const nextScene = video.timestamps.find(ts => ts.time > currentTime);
    if (nextScene) {
      jumpToTimestamp(nextScene);
    }
  }, [currentTime, video.timestamps, jumpToTimestamp]);

  const jumpToPreviousScene = useCallback(() => {
    const previousScenes = video.timestamps.filter(ts => ts.time < currentTime - 1);
    if (previousScenes.length > 0) {
      jumpToTimestamp(previousScenes[previousScenes.length - 1]);
    }
  }, [currentTime, video.timestamps, jumpToTimestamp]);

  const jumpToRoom = useCallback((roomType: string) => {
    const roomScene = video.timestamps.find(ts => ts.roomDetected === roomType);
    if (roomScene) {
      jumpToTimestamp(roomScene);
    }
  }, [video.timestamps, jumpToTimestamp]);

  // Bookmarks
  const addBookmark = useCallback((time: number, description: string) => {
    const bookmark: VideoBookmark = {
      id: `bookmark_${Date.now()}`,
      time,
      description,
      createdAt: new Date()
    };
    setBookmarks(prev => [...prev, bookmark].sort((a, b) => a.time - b.time));
  }, []);

  const removeBookmark = useCallback((id: string) => {
    setBookmarks(prev => prev.filter(b => b.id !== id));
  }, []);

  const isBookmarked = useCallback((time: number): boolean => {
    return bookmarks.some(b => Math.abs(b.time - time) < 1);
  }, [bookmarks]);

  // Annotations
  const addAnnotation = useCallback((annotation: Omit<VideoAnnotation, 'id' | 'createdAt'>) => {
    const newAnnotation: VideoAnnotation = {
      ...annotation,
      id: `annotation_${Date.now()}`,
      createdAt: new Date()
    };
    setAnnotations(prev => [...prev, newAnnotation].sort((a, b) => a.timestamp - b.timestamp));
  }, []);

  const updateAnnotation = useCallback((id: string, updates: Partial<VideoAnnotation>) => {
    setAnnotations(prev => prev.map(a => a.id === id ? { ...a, ...updates } : a));
  }, []);

  const deleteAnnotation = useCallback((id: string) => {
    setAnnotations(prev => prev.filter(a => a.id !== id));
  }, []);

  // Review progress
  const markAsReviewed = useCallback((startTime: number, endTime: number) => {
    setReviewedSegments(prev => {
      // Merge overlapping segments
      const newSegment = { start: startTime, end: endTime };
      const merged = [...prev, newSegment].sort((a, b) => a.start - b.start);

      const result: typeof prev = [];
      for (const segment of merged) {
        if (result.length === 0) {
          result.push(segment);
        } else {
          const last = result[result.length - 1];
          if (segment.start <= last.end) {
            // Merge segments
            last.end = Math.max(last.end, segment.end);
          } else {
            result.push(segment);
          }
        }
      }

      return result;
    });
  }, []);

  const isReviewed = useCallback((time: number): boolean => {
    return reviewedSegments.some(seg => time >= seg.start && time <= seg.end);
  }, [reviewedSegments]);

  // Calculate review progress
  const reviewProgress = (() => {
    const totalReviewed = reviewedSegments.reduce(
      (sum, seg) => sum + (seg.end - seg.start),
      0
    );
    return Math.min(100, (totalReviewed / duration) * 100);
  })();

  return {
    // Playback state
    isPlaying,
    currentTime,
    duration,
    buffered,
    volume,
    playbackRate,

    // Scene information
    currentScene,
    nearestTimestamp,
    activeScenes,

    // Controls
    play,
    pause,
    seek,
    setVolume: updateVolume,
    setPlaybackRate: updatePlaybackRate,

    // Navigation
    jumpToTimestamp,
    jumpToNextScene,
    jumpToPreviousScene,
    jumpToRoom,

    // Bookmarks
    bookmarks,
    addBookmark,
    removeBookmark,
    isBookmarked,

    // Annotations
    annotations,
    addAnnotation,
    updateAnnotation,
    deleteAnnotation,

    // Review progress
    reviewProgress,
    markAsReviewed,
    isReviewed,
    reviewedSegments
  };
};

// Hook for managing video review sessions
export const useVideoReviewSession = (videoId: string, auditorId: string) => {
  const [sessionId] = useState(`session_${Date.now()}`);
  const [sessionStart] = useState(new Date());
  const [sessionNotes, setSessionNotes] = useState('');

  // Save session data
  const saveSession = useCallback(async (
    annotations: VideoAnnotation[],
    reviewProgress: number,
    reviewedSegments: Array<{ start: number; end: number }>
  ) => {
    const sessionData = {
      id: sessionId,
      videoId,
      auditorId,
      startTime: sessionStart,
      endTime: new Date(),
      annotations,
      reviewProgress,
      reviewedSegments,
      notes: sessionNotes
    };

    // In production, this would save to database
    localStorage.setItem(`review_session_${sessionId}`, JSON.stringify(sessionData));

    return sessionData;
  }, [sessionId, videoId, auditorId, sessionStart, sessionNotes]);

  // Load previous sessions
  const loadSessions = useCallback(() => {
    const sessions = [];
    for (let i = 0; i < localStorage.length; i++) {
      const key = localStorage.key(i);
      if (key?.startsWith('review_session_')) {
        try {
          const session = JSON.parse(localStorage.getItem(key) || '{}');
          if (session.videoId === videoId) {
            sessions.push(session);
          }
        } catch (error) {
          console.error('Failed to load session:', error);
        }
      }
    }
    return sessions.sort((a, b) => 
      new Date(b.startTime).getTime() - new Date(a.startTime).getTime()
    );
  }, [videoId]);

  return {
    sessionId,
    sessionStart,
    sessionNotes,
    setSessionNotes,
    saveSession,
    loadSessions
  };
};

// Architecture Decision Records (ADR) Manager for STR Certified
// Integrates with AI decision logging system for comprehensive architectural documentation

import { aiDecisionLogger } from './decision-logger';
import { logger } from '../../utils/logger';
import { errorReporter } from '../monitoring/error-reporter';
import { supabase } from '../supabase';
import * as fs from 'fs';
import * as path from 'path';

// ADR Types
export interface ADRRecord {
  id: string;
  number: number;
  title: string;
  status: ADRStatus;
  context: string;
  decision: string;
  rationale: string;
  consequences: ADRConsequences;
  implementation: string;
  alternatives_considered: string;
  related_decisions: string[];
  monitoring_and_review: string;
  notes: string;
  ai_agent_info: AIAgentInfo;
  created_date: string;
  last_modified: string;
  file_path: string;
  metadata: Record<string, any>;
}

export type ADRStatus = 'proposed' | 'accepted' | 'rejected' | 'superseded';

export interface ADRConsequences {
  positive: string[];
  negative: string[];
}

export interface AIAgentInfo {
  created_by: string;
  date: string;
  session_id: string;
  decision_logger_id?: string;
  context_handoff_id?: string;
}

export interface ADRQuery {
  status?: ADRStatus;
  ai_agent?: string;
  date_range?: {
    start: string;
    end: string;
  };
  search_term?: string;
  tags?: string[];
  limit?: number;
}

export interface ADRTemplate {
  title: string;
  context: string;
  decision: string;
  rationale: string;
  positive_consequences: string[];
  negative_consequences: string[];
  implementation: string;
  alternatives: string[];
  related_decisions: string[];
  monitoring: string;
  notes: string;
}

export class ADRManager {
  private static instance: ADRManager;
  private adrs: ADRRecord[] = [];
  private adrDirectory: string;
  private nextADRNumber: number = 1;

  private constructor() {
    this.adrDirectory = path.join(process.cwd(), 'ADR');
    this.loadExistingADRs();
  }

  static getInstance(): ADRManager {
    if (!ADRManager.instance) {
      ADRManager.instance = new ADRManager();
    }
    return ADRManager.instance;
  }

  /**
   * Create a new ADR from template
   */
  async createADR(template: ADRTemplate): Promise<string> {
    const adrNumber = this.getNextADRNumber();
    const adrId = this.generateADRId(adrNumber);

    const adr: ADRRecord = {
      id: adrId,
      number: adrNumber,
      title: template.title,
      status: 'proposed',
      context: template.context,
      decision: template.decision,
      rationale: template.rationale,
      consequences: {
        positive: template.positive_consequences,
        negative: template.negative_consequences
      },
      implementation: template.implementation,
      alternatives_considered: template.alternatives.join('\n'),
      related_decisions: template.related_decisions,
      monitoring_and_review: template.monitoring,
      notes: template.notes,
      ai_agent_info: {
        created_by: this.getCurrentAIAgent(),
        date: new Date().toISOString().split('T')[0],
        session_id: this.getCurrentSessionId(),
        decision_logger_id: undefined,
        context_handoff_id: undefined
      },
      created_date: new Date().toISOString(),
      last_modified: new Date().toISOString(),
      file_path: this.generateADRFilePath(adrNumber, template.title),
      metadata: {}
    };

    // Add to local cache
    this.adrs.push(adr);
    this.nextADRNumber = adrNumber + 1;

    // Generate markdown content
    const markdownContent = this.generateMarkdownContent(adr);

    try {
      // Write to file
      await this.writeADRFile(adr.file_path, markdownContent);

      // Log the ADR creation
      const decisionId = await aiDecisionLogger.logSimpleDecision(
        `Created ADR-${adrNumber.toString().padStart(4, '0')}: ${template.title}`,
        'architectural_choice',
        `Created Architecture Decision Record for: ${template.decision}`,
        [adr.file_path],
        'high'
      );

      adr.ai_agent_info.decision_logger_id = decisionId;

      // Persist to database
      await this.persistADR(adr);

      logger.info(`ADR created: ${adr.title}`, {
        adr_id: adrId,
        adr_number: adrNumber,
        status: adr.status,
        file_path: adr.file_path
      }, 'ADR_CREATION');

      return adrId;
    } catch (error) {
      errorReporter.reportError(error, {
        context: 'ADR_CREATION',
        adr_id: adrId,
        adr_number: adrNumber,
        title: template.title
      });
      throw error;
    }
  }

  /**
   * Create a simple ADR with minimal input
   */
  async createSimpleADR(
    title: string,
    decision: string,
    rationale: string,
    context: string = 'Not specified'
  ): Promise<string> {
    return this.createADR({
      title,
      context,
      decision,
      rationale,
      positive_consequences: ['Decision implemented'],
      negative_consequences: ['No significant negative consequences identified'],
      implementation: 'To be implemented',
      alternatives: ['No alternatives considered'],
      related_decisions: [],
      monitoring: 'No specific monitoring planned',
      notes: 'Simple ADR created during AI session'
    });
  }

  /**
   * Update ADR status
   */
  async updateADRStatus(adrId: string, newStatus: ADRStatus, notes?: string): Promise<void> {
    const adr = this.adrs.find(a => a.id === adrId);
    if (!adr) {
      throw new Error(`ADR not found: ${adrId}`);
    }

    const oldStatus = adr.status;
    adr.status = newStatus;
    adr.last_modified = new Date().toISOString();

    if (notes) {
      adr.notes += `\n\n**Status Change (${new Date().toISOString()})**: ${oldStatus} ‚Üí ${newStatus}\n${notes}`;
    }

    // Regenerate markdown content
    const markdownContent = this.generateMarkdownContent(adr);

    try {
      await this.writeADRFile(adr.file_path, markdownContent);
      await this.persistADR(adr);

      await aiDecisionLogger.logSimpleDecision(
        `Updated ADR-${adr.number.toString().padStart(4, '0')} status: ${oldStatus} ‚Üí ${newStatus}`,
        'architectural_choice',
        `Status change for ADR "${adr.title}": ${notes || 'No additional notes'}`,
        [adr.file_path],
        'medium'
      );

      logger.info(`ADR status updated: ${adr.title}`, {
        adr_id: adrId,
        old_status: oldStatus,
        new_status: newStatus,
        notes: notes
      }, 'ADR_STATUS_UPDATE');
    } catch (error) {
      errorReporter.reportError(error, {
        context: 'ADR_STATUS_UPDATE',
        adr_id: adrId,
        old_status: oldStatus,
        new_status: newStatus
      });
      throw error;
    }
  }

  /**
   * Supersede an ADR with a new one
   */
  async supersedeADR(oldADRId: string, newADRTemplate: ADRTemplate): Promise<string> {
    const oldADR = this.adrs.find(a => a.id === oldADRId);
    if (!oldADR) {
      throw new Error(`ADR not found: ${oldADRId}`);
    }

    // Create new ADR
    const newADRId = await this.createADR({
      ...newADRTemplate,
      related_decisions: [...newADRTemplate.related_decisions, `ADR-${oldADR.number.toString().padStart(4, '0')}`]
    });

    // Update old ADR status
    await this.updateADRStatus(oldADRId, 'superseded', `Superseded by new ADR: ${newADRId}`);

    return newADRId;
  }

  /**
   * Query ADRs
   */
  queryADRs(query: ADRQuery): ADRRecord[] {
    let filtered = [...this.adrs];

    if (query.status) {
      filtered = filtered.filter(adr => adr.status === query.status);
    }

    if (query.ai_agent) {
      filtered = filtered.filter(adr => adr.ai_agent_info.created_by === query.ai_agent);
    }

    if (query.date_range) {
      filtered = filtered.filter(adr => {
        const created = new Date(adr.created_date);
        const start = new Date(query.date_range!.start);
        const end = new Date(query.date_range!.end);
        return created >= start && created <= end;
      });
    }

    if (query.search_term) {
      const searchTerm = query.search_term.toLowerCase();
      filtered = filtered.filter(adr => 
        adr.title.toLowerCase().includes(searchTerm) ||
        adr.context.toLowerCase().includes(searchTerm) ||
        adr.decision.toLowerCase().includes(searchTerm) ||
        adr.rationale.toLowerCase().includes(searchTerm)
      );
    }

    // Sort by number (newest first)
    filtered.sort((a, b) => b.number - a.number);

    if (query.limit) {
      filtered = filtered.slice(0, query.limit);
    }

    return filtered;
  }

  /**
   * Get ADR by ID
   */
  getADRById(id: string): ADRRecord | null {
    return this.adrs.find(adr => adr.id === id) || null;
  }

  /**
   * Get ADR by number
   */
  getADRByNumber(number: number): ADRRecord | null {
    return this.adrs.find(adr => adr.number === number) || null;
  }

  /**
   * Get all ADRs
   */
  getAllADRs(): ADRRecord[] {
    return [...this.adrs].sort((a, b) => a.number - b.number);
  }

  /**
   * Get active ADRs (accepted status)
   */
  getActiveADRs(): ADRRecord[] {
    return this.queryADRs({ status: 'accepted' });
  }

  /**
   * Generate ADR index
   */
  generateADRIndex(): string {
    const adrs = this.getAllADRs();

    return `# Architecture Decision Records (ADRs)

This directory contains Architecture Decision Records for STR Certified.

## Index

${adrs.map(adr => `- [ADR-${adr.number.toString().padStart(4, '0')}](${path.basename(adr.file_path)}) - ${adr.title} (${adr.status})`).join('\n')}

## Status Summary

- **Proposed**: ${adrs.filter(a => a.status === 'proposed').length}
- **Accepted**: ${adrs.filter(a => a.status === 'accepted').length}
- **Rejected**: ${adrs.filter(a => a.status === 'rejected').length}
- **Superseded**: ${adrs.filter(a => a.status === 'superseded').length}

## Recent ADRs

${adrs.slice(0, 5).map(adr => `- [ADR-${adr.number.toString().padStart(4, '0')}](${path.basename(adr.file_path)}) - ${adr.title} (${adr.ai_agent_info.date})`).join('\n')}

---
*This index was automatically generated by the ADR Management System.*
*Last updated: ${new Date().toISOString()}*
`;
  }

  /**
   * Export ADRs
   */
  exportADRs(format: 'json' | 'csv' = 'json'): string {
    if (format === 'json') {
      return JSON.stringify(this.adrs, null, 2);
    } else {
      const headers = ['number', 'title', 'status', 'created_date', 'ai_agent', 'decision', 'rationale'];
      const rows = this.adrs.map(adr => [
        adr.number,
        adr.title,
        adr.status,
        adr.created_date,
        adr.ai_agent_info.created_by,
        adr.decision.substring(0, 100) + '...',
        adr.rationale.substring(0, 100) + '...'
      ]);

      return [headers.join(','), ...rows.map(row => row.map(cell => `"${cell}"`).join(','))].join('\n');
    }
  }

  /**
   * Private helper methods
   */
  private loadExistingADRs(): void {
    try {
      // This would load existing ADRs from the filesystem
      // For now, just initialize with empty array
      this.adrs = [];
      this.nextADRNumber = 1;
    } catch (error) {
      logger.error('Failed to load existing ADRs', error, 'ADR_LOAD');
    }
  }

  private getNextADRNumber(): number {
    return this.nextADRNumber;
  }

  private generateADRId(number: number): string {
    return `adr_${number.toString().padStart(4, '0')}_${Date.now()}`;
  }

  private generateADRFilePath(number: number, title: string): string {
    const sanitizedTitle = title.toLowerCase()
      .replace(/[^a-z0-9\s-]/g, '')
      .replace(/\s+/g, '-')
      .substring(0, 50);

    return path.join(this.adrDirectory, `${number.toString().padStart(4, '0')}-${sanitizedTitle}.md`);
  }

  private generateMarkdownContent(adr: ADRRecord): string {
    return `# ADR-${adr.number.toString().padStart(4, '0')}: ${adr.title}

## Status
${adr.status.charAt(0).toUpperCase() + adr.status.slice(1)}

## Context
${adr.context}

## Decision
${adr.decision}

## Rationale
${adr.rationale}

## Consequences
### Positive
${adr.consequences.positive.map(c => `- ${c}`).join('\n')}

### Negative
${adr.consequences.negative.map(c => `- ${c}`).join('\n')}

## Implementation
${adr.implementation}

## Alternatives Considered
${adr.alternatives_considered}

## Related Decisions
${adr.related_decisions.map(d => `- ${d}`).join('\n')}

## Monitoring and Review
${adr.monitoring_and_review}

## Notes
${adr.notes}

## AI Agent Information
- **Created by**: ${adr.ai_agent_info.created_by}
- **Date**: ${adr.ai_agent_info.date}
- **Session ID**: ${adr.ai_agent_info.session_id}
- **Decision Logger ID**: ${adr.ai_agent_info.decision_logger_id || 'N/A'}
- **Context Handoff ID**: ${adr.ai_agent_info.context_handoff_id || 'N/A'}

---
*This ADR was created as part of the AI decision logging and multi-AI collaboration system for STR Certified.*
*Last modified: ${adr.last_modified}*`;
  }

  private async writeADRFile(filePath: string, content: string): Promise<void> {
    // In a real implementation, this would write to the filesystem
    // For now, just simulate the operation
    logger.info(`ADR file would be written to: ${filePath}`, { content_length: content.length }, 'ADR_FILE_WRITE');
  }

  private async persistADR(adr: ADRRecord): Promise<void> {
    try {
      const { error } = await supabase
        .from('architecture_decision_records')
        .upsert([{
          id: adr.id,
          number: adr.number,
          title: adr.title,
          status: adr.status,
          context: adr.context,
          decision: adr.decision,
          rationale: adr.rationale,
          consequences: adr.consequences,
          implementation: adr.implementation,
          alternatives_considered: adr.alternatives_considered,
          related_decisions: adr.related_decisions,
          monitoring_and_review: adr.monitoring_and_review,
          notes: adr.notes,
          ai_agent_info: adr.ai_agent_info,
          created_date: adr.created_date,
          last_modified: adr.last_modified,
          file_path: adr.file_path,
          metadata: adr.metadata
        }]);

      if (error) {
        throw error;
      }
    } catch (error) {
      logger.error('Failed to persist ADR to database', error, 'ADR_PERSIST');
      throw error;
    }
  }

  private getCurrentAIAgent(): string {
    return process.env.AI_AGENT || 'claude-sonnet-4';
  }

  private getCurrentSessionId(): string {
    return `session_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
  }
}

