  validEntries: number;
  expiredEntries: number;
  deprecatedEntries: number;
  issues: ValidationIssue[];
  lastValidated: Date;
}

interface ImportResult {
  imported: number;
  skipped: number;
  errors: string[];
}

interface CleanupResult {
  removed: number;
  remaining: number;
}

