export interface ErrorBoundaryState {
  hasError: boolean;
  error: Error | null;
  errorInfo: ErrorInfo | null;
  errorId: string | null;
  retryCount: number;
}

export interface ErrorBoundaryProps {
  children: ReactNode;
  fallback?: (props: {
    error: Error;
    errorInfo: ErrorInfo;
    resetError: () => void;
    errorId: string;
  }) => ReactNode;
  onError?: (error: Error, errorInfo: ErrorInfo) => void;
  isolate?: boolean;
  level?: 'page' | 'section' | 'component';
  resetKeys?: Array<string | number>;
  resetOnPropsChange?: boolean;
  showErrorDetails?: boolean;
}

export class ErrorBoundary extends Component<ErrorBoundaryProps, ErrorBoundaryState> {
  private resetTimeoutId: NodeJS.Timeout | null = null;
  private previousResetKeys: Array<string | number> = [];

  constructor(props: ErrorBoundaryProps) {
    super(props);

    this.state = {
      hasError: false,
      error: null,
      errorInfo: null,
      errorId: null,
      retryCount: 0,
    };
  }

  static getDerivedStateFromError(error: Error): Partial<ErrorBoundaryState> {
    const errorId = `error_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;

    return {
      hasError: true,
      error,
      errorId,
    };
  }

  componentDidCatch(error: Error, errorInfo: ErrorInfo) {
    const { onError, level = 'component' } = this.props;
    const { errorId, retryCount } = this.state;

    // Log to console in development
    if (env.isDevelopment()) {
      console.error('Error caught by ErrorBoundary:', error);
      console.error('Error Info:', errorInfo);
      console.error('Component Stack:', errorInfo.componentStack);
    }

    // Update state with error info
    this.setState({ errorInfo });

    // Report to monitoring service
    ErrorReporter.getInstance().reportError(error, {
      errorBoundary: true,
      level,
      errorId: errorId || undefined,
      componentStack: errorInfo.componentStack,
      retryCount,
      props: this.sanitizeProps(),
    });

    // Call custom error handler if provided
    if (onError) {
      onError(error, errorInfo);
    }

    // Set up automatic retry for transient errors
    if (this.shouldAutoRetry(error) && retryCount < 3) {
      this.scheduleRetry();
    }
  }

  componentDidUpdate(prevProps: ErrorBoundaryProps) {
    const { resetKeys, resetOnPropsChange } = this.props;
    const { hasError } = this.state;

    // Reset on prop changes if enabled
    if (hasError && resetOnPropsChange && this.propsHaveChanged(prevProps)) {
      this.resetError();
      return;
    }

    // Reset on resetKeys change
    if (resetKeys && hasError) {
      const hasResetKeyChanged = resetKeys.some(
        (key, index) => key !== this.previousResetKeys[index]
      );

      if (hasResetKeyChanged) {
        this.resetError();
      }
    }

    this.previousResetKeys = resetKeys || [];
  }

  componentWillUnmount() {
    if (this.resetTimeoutId) {
      clearTimeout(this.resetTimeoutId);
    }
  }

  private shouldAutoRetry(error: Error): boolean {
    // Auto-retry network errors and specific known transient errors
    const transientErrors = [
      'NetworkError',
      'TimeoutError',
      'ChunkLoadError',
      'Loading chunk',
      'Failed to fetch',
      'Load failed',
    ];

    return transientErrors.some(msg => 
      error.message.includes(msg) || error.name.includes(msg)
    );
  }

  private scheduleRetry() {
    const { retryCount } = this.state;
    const delay = Math.min(1000 * Math.pow(2, retryCount), 10000); // Exponential backoff, max 10s

    this.resetTimeoutId = setTimeout(() => {
      this.setState(prevState => ({
        hasError: false,
        error: null,
        errorInfo: null,
        retryCount: prevState.retryCount + 1,
      }));
    }, delay);
  }

  private propsHaveChanged(prevProps: ErrorBoundaryProps): boolean {
    // Simple shallow comparison of props
    return Object.keys(this.props).some(key => {
      if (key === 'children' || key === 'fallback') return false;
      return (this.props as any)[key] !== (prevProps as any)[key];
    });
  }

  private sanitizeProps(): Record<string, any> {
    // Remove sensitive data from props before reporting
    const { children, fallback, ...safeProps } = this.props;
    return safeProps;
  }

  resetError = () => {
    if (this.resetTimeoutId) {
      clearTimeout(this.resetTimeoutId);
      this.resetTimeoutId = null;
    }

    this.setState({
      hasError: false,
      error: null,
      errorInfo: null,
      errorId: null,
      retryCount: 0,
    });
  };

  render() {
    const { hasError, error, errorInfo, errorId } = this.state;
    const { children, fallback, isolate = true } = this.props;

    if (hasError && error && errorInfo) {
      // Use custom fallback if provided
      if (fallback) {
        return (
          <>
            {fallback({
              error,
              errorInfo,
              resetError: this.resetError,
              errorId: errorId || 'unknown',
            })}
          </>
        );
      }

      // Default error UI
      return (
        <DefaultErrorFallback
          error={error}
          errorInfo={errorInfo}
          resetError={this.resetError}
          errorId={errorId || 'unknown'}
          showDetails={this.props.showErrorDetails ?? env.isDevelopment()}
        />
      );
    }

    // Wrap children in a div if isolation is enabled
    // This prevents errors from propagating to sibling components
    if (isolate) {
      return <div className="error-boundary-container">{children}</div>;
    }

    return children;
  }
}

// Default error fallback component
function DefaultErrorFallback({
  error,
  errorInfo,
  resetError,
  errorId,
  showDetails,
}: {
  error: Error;
  errorInfo: ErrorInfo;
  resetError: () => void;
  errorId: string;
  showDetails: boolean;
}) {
  return (
    <div className="min-h-[200px] flex items-center justify-center p-6">
      <div className="max-w-md w-full bg-white dark:bg-gray-800 rounded-lg shadow-lg p-6">
        <div className="text-center">
          <div className="mx-auto flex items-center justify-center h-12 w-12 rounded-full bg-red-100 dark:bg-red-900">
            <svg
              className="h-6 w-6 text-red-600 dark:text-red-400"
              fill="none"
              viewBox="0 0 24 24"
              stroke="currentColor"
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth={2}
                d="M12 9v2m0 4h.01m-6.938 4h13.856c1.54 0 2.502-1.667 1.732-3L13.732 4c-.77-1.333-2.694-1.333-3.464 0L3.34 16c-.77 1.333.192 3 1.732 3z"
              />
            </svg>
          </div>

          <h3 className="mt-4 text-lg font-medium text-gray-900 dark:text-gray-100">
            Something went wrong
          </h3>

          <p className="mt-2 text-sm text-gray-500 dark:text-gray-400">
            We're sorry for the inconvenience. The error has been reported and we'll look into it.
          </p>

          {showDetails && (
            <details className="mt-4 text-left">
              <summary className="cursor-pointer text-sm font-medium text-gray-700 dark:text-gray-300">
                Error Details
              </summary>
              <div className="mt-2 text-xs">
                <p className="text-gray-600 dark:text-gray-400">
                  <strong>Error ID:</strong> {errorId}
                </p>
                <p className="mt-1 text-gray-600 dark:text-gray-400">
                  <strong>Message:</strong> {error.message}
                </p>
                <pre className="mt-2 p-2 bg-gray-100 dark:bg-gray-900 rounded text-xs overflow-auto max-h-40">
                  {error.stack}
                </pre>
              </div>
            </details>
          )}

          <div className="mt-6 flex flex-col sm:flex-row gap-3 justify-center">
            <button
              onClick={resetError}
              className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md shadow-sm text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
            >
              Try Again
            </button>

            <button
              onClick={() => window.location.reload()}
              className="inline-flex items-center px-4 py-2 border border-gray-300 dark:border-gray-600 text-sm font-medium rounded-md text-gray-700 dark:text-gray-300 bg-white dark:bg-gray-800 hover:bg-gray-50 dark:hover:bg-gray-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
            >
              Refresh Page
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

// Higher-order component for adding error boundary
export function withErrorBoundary<P extends object>(
  Component: React.ComponentType<P>,
  errorBoundaryProps?: Omit<ErrorBoundaryProps, 'children'>
) {
  const WrappedComponent = (props: P) => (
    <ErrorBoundary {...errorBoundaryProps}>
      <Component {...props} />
    </ErrorBoundary>
  );

  WrappedComponent.displayName = `withErrorBoundary(${Component.displayName || Component.name})`;

  return WrappedComponent;
}

// Hook for imperatively triggering error boundary
export function useErrorHandler() {
  const [error, setError] = React.useState<Error | null>(null);

  React.useEffect(() => {
    if (error) {
      throw error;
    }
  }, [error]);

  return (error: Error) => {
    setError(error);
  };
}

import { supabase } from '../supabase';
import { env } from '../config/environment';
import { errorReporter } from '../monitoring/error-reporter';
import { performanceTracker } from '../monitoring/performance-tracker';

export interface SystemHealthCheck {
  component: string;
  status: 'healthy' | 'degraded' | 'unhealthy';
  responseTime: number;
  details?: Record<string, any>;
  error?: string;
  lastChecked: string;
}

export interface SystemHealthReport {
  overall: 'healthy' | 'degraded' | 'unhealthy';
  timestamp: string;
  checks: SystemHealthCheck[];
  summary: {
    totalChecks: number;
    healthyChecks: number;
    degradedChecks: number;
    unhealthyChecks: number;
    avgResponseTime: number;
  };
  recommendations?: string[];
}

export interface AIServiceConfig {
  name: string;
  endpoint: string;
  apiKey: string;
  timeout: number;
}

export class SystemHealthValidator {
  private static instance: SystemHealthValidator;
  private healthChecks: Map<string, SystemHealthCheck> = new Map();

  private constructor() {}

  static getInstance(): SystemHealthValidator {
    if (!SystemHealthValidator.instance) {
      SystemHealthValidator.instance = new SystemHealthValidator();
    }
    return SystemHealthValidator.instance;
  }

  /**
   * Perform comprehensive system health check
   */
  async performFullHealthCheck(): Promise<SystemHealthReport> {
    const startTime = Date.now();

    try {
      // Run all health checks in parallel
      const checks = await Promise.allSettled([
        this.checkDatabase(),
        this.checkAIServices(),
        this.checkFileUpload(),
        this.checkVideoProcessing(),
        this.checkMobileCompatibility(),
        this.checkOfflineCapabilities(),
        this.checkAuthenticationSystem(),
        this.checkEnvironmentConfiguration(),
      ]);

      const healthChecks = checks.map((result, index) => {
        if (result.status === 'fulfilled') {
          return result.value;
        } else {
          const componentNames = [
            'database',
            'ai_services',
            'file_upload',
            'video_processing',
            'mobile_compatibility',
            'offline_capabilities',
            'authentication',
            'environment_config',
          ];

          return {
            component: componentNames[index] || 'unknown',
            status: 'unhealthy' as const,
            responseTime: Date.now() - startTime,
            error: result.reason?.message || 'Unknown error',
            lastChecked: new Date().toISOString(),
          };
        }
      });

      // Calculate summary
      const summary = {
        totalChecks: healthChecks.length,
        healthyChecks: healthChecks.filter(c => c.status === 'healthy').length,
        degradedChecks: healthChecks.filter(c => c.status === 'degraded').length,
        unhealthyChecks: healthChecks.filter(c => c.status === 'unhealthy').length,
        avgResponseTime: healthChecks.reduce((sum, c) => sum + c.responseTime, 0) / healthChecks.length,
      };

      // Determine overall status
      let overall: SystemHealthReport['overall'] = 'healthy';
      if (summary.unhealthyChecks > 0) {
        overall = 'unhealthy';
      } else if (summary.degradedChecks > 0) {
        overall = 'degraded';
      }

      const report: SystemHealthReport = {
        overall,
        timestamp: new Date().toISOString(),
        checks: healthChecks,
        summary,
        recommendations: this.generateRecommendations(healthChecks),
      };

      // Track performance
      performanceTracker.trackMetric('system_health_check', Date.now() - startTime, 'ms', {
        overall: report.overall,
        unhealthyChecks: summary.unhealthyChecks,
      });

      // Report critical issues
      if (overall === 'unhealthy') {
        errorReporter.reportError(new Error('System health check failed'), {
          category: 'system_health',
          severity: 'high',
          details: report,
        });
      }

      return report;
    } catch (error) {
      throw new Error(`System health check failed: ${error instanceof Error ? error.message : 'Unknown error'}`);
    }
  }

  /**
   * Check database connectivity and performance
   */
  private async checkDatabase(): Promise<SystemHealthCheck> {
    const startTime = Date.now();

    try {
      // Test basic connectivity
      const { data: connectionTest, error: connectionError } = await supabase
        .from('properties')
        .select('id')
        .limit(1);

      if (connectionError) {
        throw new Error(`Database connection failed: ${connectionError.message}`);
      }

      // Test write operations
      const { error: writeError } = await supabase
        .from('system_health_logs')
        .insert({
          component: 'database',
          status: 'healthy',
          checked_at: new Date().toISOString(),
        });

      const responseTime = Date.now() - startTime;

      // Determine status based on response time and errors
      let status: SystemHealthCheck['status'] = 'healthy';
      if (writeError) {
        status = 'degraded';
      } else if (responseTime > 1000) {
        status = 'degraded';
      }

      return {
        component: 'database',
        status,
        responseTime,
        details: {
          connectionTest: !!connectionTest,
          writeTest: !writeError,
          latency: responseTime,
        },
        error: writeError?.message,
        lastChecked: new Date().toISOString(),
      };
    } catch (error) {
      return {
        component: 'database',
        status: 'unhealthy',
        responseTime: Date.now() - startTime,
        error: error instanceof Error ? error.message : 'Database check failed',
        lastChecked: new Date().toISOString(),
      };
    }
  }

  /**
   * Check AI services connectivity and functionality
   */
  private async checkAIServices(): Promise<SystemHealthCheck> {
    const startTime = Date.now();

    try {
      const services = [
        {
          name: 'OpenAI',
          endpoint: 'https://api.openai.com/v1/models',
          apiKey: env.openai.apiKey,
        },
      ];

      const results = await Promise.allSettled(
        services.map(service => this.testAIService(service))
      );

      const serviceStatuses = results.map((result, index) => {
        const serviceName = services[index].name;
        if (result.status === 'fulfilled') {
          return { name: serviceName, ...result.value };
        } else {
          return {
            name: serviceName,
            status: 'unhealthy',
            error: result.reason?.message || 'Service check failed',
          };
        }
      });

      const responseTime = Date.now() - startTime;
      const unhealthyServices = serviceStatuses.filter(s => s.status === 'unhealthy');

      let status: SystemHealthCheck['status'] = 'healthy';
      if (unhealthyServices.length === serviceStatuses.length) {
        status = 'unhealthy';
      } else if (unhealthyServices.length > 0) {
        status = 'degraded';
      }

      return {
        component: 'ai_services',
        status,
        responseTime,
        details: {
          services: serviceStatuses,
          totalServices: serviceStatuses.length,
          healthyServices: serviceStatuses.filter(s => s.status === 'healthy').length,
        },
        error: unhealthyServices.length > 0 ? `${unhealthyServices.length} service(s) unhealthy` : undefined,
        lastChecked: new Date().toISOString(),
      };
    } catch (error) {
      return {
        component: 'ai_services',
        status: 'unhealthy',
        responseTime: Date.now() - startTime,
        error: error instanceof Error ? error.message : 'AI services check failed',
        lastChecked: new Date().toISOString(),
      };
    }
  }

  /**
   * Test individual AI service
   */
  private async testAIService(service: { name: string; endpoint: string; apiKey: string }) {
    const startTime = Date.now();

    try {
      const response = await fetch(service.endpoint, {
        method: 'GET',
        headers: {
          'Authorization': `Bearer ${service.apiKey}`,
          'Content-Type': 'application/json',
        },
        signal: AbortSignal.timeout(5000),
      });

      const responseTime = Date.now() - startTime;

      if (!response.ok) {
        return {
          status: 'unhealthy' as const,
          responseTime,
          error: `HTTP ${response.status}: ${response.statusText}`,
        };
      }

      // Test rate limits
      const rateLimitRemaining = response.headers.get('x-ratelimit-remaining');
      const rateLimitTotal = response.headers.get('x-ratelimit-limit');

      let status: 'healthy' | 'degraded' | 'unhealthy' = 'healthy';
      if (rateLimitRemaining && rateLimitTotal) {
        const remaining = parseInt(rateLimitRemaining);
        const total = parseInt(rateLimitTotal);
        if (remaining / total < 0.1) { // Less than 10% remaining
          status = 'degraded';
        }
      }

      return {
        status,
        responseTime,
        details: {
          rateLimitRemaining,
          rateLimitTotal,
          httpStatus: response.status,
        },
      };
    } catch (error) {
      return {
        status: 'unhealthy' as const,
        responseTime: Date.now() - startTime,
        error: error instanceof Error ? error.message : 'Service test failed',
      };
    }
  }

  /**
   * Check file upload capabilities
   */
  private async checkFileUpload(): Promise<SystemHealthCheck> {
    const startTime = Date.now();

    try {
      // Test storage bucket accessibility
      const { data: buckets, error: bucketError } = await supabase.storage.listBuckets();

      if (bucketError) {
        throw new Error(`Storage bucket check failed: ${bucketError.message}`);
      }

      const requiredBuckets = ['property-photos', 'inspection-videos'];
      const availableBuckets = buckets?.map(b => b.name) || [];
      const missingBuckets = requiredBuckets.filter(b => !availableBuckets.includes(b));

      // Test file upload with a small test file
      const testFile = new Blob(['test'], { type: 'text/plain' });
      const testFileName = `health-check-${Date.now()}.txt`;

      const { error: uploadError } = await supabase.storage
        .from('property-photos')
        .upload(`test/${testFileName}`, testFile);

      // Clean up test file
      if (!uploadError) {
        await supabase.storage
          .from('property-photos')
          .remove([`test/${testFileName}`]);
      }

      const responseTime = Date.now() - startTime;

      let status: SystemHealthCheck['status'] = 'healthy';
      if (missingBuckets.length > 0 || uploadError) {
        status = missingBuckets.length === requiredBuckets.length ? 'unhealthy' : 'degraded';
      }

      return {
        component: 'file_upload',
        status,
        responseTime,
        details: {
          bucketsFound: availableBuckets.length,
          bucketsRequired: requiredBuckets.length,
          missingBuckets,
          uploadTest: !uploadError,
        },
        error: uploadError?.message || (missingBuckets.length > 0 ? `Missing buckets: ${missingBuckets.join(', ')}` : undefined),
        lastChecked: new Date().toISOString(),
      };
    } catch (error) {
      return {
        component: 'file_upload',
        status: 'unhealthy',
        responseTime: Date.now() - startTime,
        error: error instanceof Error ? error.message : 'File upload check failed',
        lastChecked: new Date().toISOString(),
      };
    }
  }

  /**
   * Check video processing pipeline
   */
  private async checkVideoProcessing(): Promise<SystemHealthCheck> {
    const startTime = Date.now();

    try {
      // Check if MediaRecorder API is available
      const mediaRecorderAvailable = typeof MediaRecorder !== 'undefined';

      // Check supported video formats
      const supportedFormats: string[] = [];
      if (mediaRecorderAvailable) {
        const formats = ['video/webm', 'video/mp4', 'video/webm;codecs=vp8', 'video/webm;codecs=vp9'];
        formats.forEach(format => {
          if (MediaRecorder.isTypeSupported(format)) {
            supportedFormats.push(format);
          }
        });
      }

      // Check getUserMedia availability
      const getUserMediaAvailable = !!(navigator.mediaDevices && navigator.mediaDevices.getUserMedia);

      const responseTime = Date.now() - startTime;

      let status: SystemHealthCheck['status'] = 'healthy';
      if (!mediaRecorderAvailable || !getUserMediaAvailable) {
        status = 'unhealthy';
      } else if (supportedFormats.length === 0) {
        status = 'degraded';
      }

      return {
        component: 'video_processing',
        status,
        responseTime,
        details: {
          mediaRecorderAvailable,
          getUserMediaAvailable,
          supportedFormats,
          browserSupport: {
            webRTC: !!window.RTCPeerConnection,
            webGL: !!window.WebGLRenderingContext,
          },
        },
        error: !mediaRecorderAvailable ? 'MediaRecorder API not available' : 
               !getUserMediaAvailable ? 'getUserMedia not available' :
               supportedFormats.length === 0 ? 'No supported video formats' : undefined,
        lastChecked: new Date().toISOString(),
      };
    } catch (error) {
      return {
        component: 'video_processing',
        status: 'unhealthy',
        responseTime: Date.now() - startTime,
        error: error instanceof Error ? error.message : 'Video processing check failed',
        lastChecked: new Date().toISOString(),
      };
    }
  }

  /**
   * Check mobile compatibility
   */
  private async checkMobileCompatibility(): Promise<SystemHealthCheck> {
    const startTime = Date.now();

    try {
      const capabilities = {
        touchSupport: 'ontouchstart' in window,
        orientationAPI: 'orientation' in window || 'onorientationchange' in window,
        deviceMotion: 'DeviceMotionEvent' in window,
        geolocation: 'geolocation' in navigator,
        vibration: 'vibrate' in navigator,
        camera: !!(navigator.mediaDevices && navigator.mediaDevices.getUserMedia),
        serviceWorker: 'serviceWorker' in navigator,
        pushNotifications: 'PushManager' in window,
        localStorage: (() => {
          try {
            localStorage.setItem('test', 'test');
            localStorage.removeItem('test');
            return true;
          } catch {
            return false;
          }
        })(),
      };

      const supportedFeatures = Object.values(capabilities).filter(Boolean).length;
      const totalFeatures = Object.keys(capabilities).length;
      const supportPercentage = (supportedFeatures / totalFeatures) * 100;

      const responseTime = Date.now() - startTime;

      let status: SystemHealthCheck['status'] = 'healthy';
      if (supportPercentage < 50) {
        status = 'unhealthy';
      } else if (supportPercentage < 80) {
        status = 'degraded';
      }

      return {
        component: 'mobile_compatibility',
        status,
        responseTime,
        details: {
          capabilities,
          supportedFeatures,
          totalFeatures,
          supportPercentage,
          userAgent: navigator.userAgent,
          screenSize: {
            width: window.screen.width,
            height: window.screen.height,
          },
        },
        error: supportPercentage < 50 ? 'Low mobile feature support' : undefined,
        lastChecked: new Date().toISOString(),
      };
    } catch (error) {
      return {
        component: 'mobile_compatibility',
        status: 'unhealthy',
        responseTime: Date.now() - startTime,
        error: error instanceof Error ? error.message : 'Mobile compatibility check failed',
        lastChecked: new Date().toISOString(),
      };
    }
  }

  /**
   * Check offline capabilities
   */
  private async checkOfflineCapabilities(): Promise<SystemHealthCheck> {
    const startTime = Date.now();

    try {
      const offlineCapabilities = {
        serviceWorker: 'serviceWorker' in navigator,
        indexedDB: !!window.indexedDB,
        localStorage: (() => {
          try {
            localStorage.setItem('test', 'test');
            localStorage.removeItem('test');
            return true;
          } catch {
            return false;
          }
        })(),
        cacheAPI: 'caches' in window,
        backgroundSync: !!(navigator.serviceWorker && 'sync' in window.ServiceWorkerRegistration.prototype),
      };

      // Test cache functionality
      let cacheTest = false;
      if (offlineCapabilities.cacheAPI) {
        try {
          const cache = await caches.open('health-check-test');
          await cache.put(new Request('/test'), new Response('test'));
          const cached = await cache.match('/test');
          cacheTest = !!cached;
          await caches.delete('health-check-test');
        } catch {
          cacheTest = false;
        }
      }

      const supportedFeatures = Object.values(offlineCapabilities).filter(Boolean).length;
      const totalFeatures = Object.keys(offlineCapabilities).length;
      const responseTime = Date.now() - startTime;

      let status: SystemHealthCheck['status'] = 'healthy';
      if (supportedFeatures < 3) {
        status = 'unhealthy';
      } else if (supportedFeatures < totalFeatures) {
        status = 'degraded';
      }

      return {
        component: 'offline_capabilities',
        status,
        responseTime,
        details: {
          ...offlineCapabilities,
          cacheTest,
          supportedFeatures,
          totalFeatures,
          isOnline: navigator.onLine,
        },
        error: supportedFeatures < 3 ? 'Insufficient offline support' : undefined,
        lastChecked: new Date().toISOString(),
      };
    } catch (error) {
      return {
        component: 'offline_capabilities',
        status: 'unhealthy',
        responseTime: Date.now() - startTime,
        error: error instanceof Error ? error.message : 'Offline capabilities check failed',
        lastChecked: new Date().toISOString(),
      };
    }
  }

  /**
   * Check authentication system
   */
  private async checkAuthenticationSystem(): Promise<SystemHealthCheck> {
    const startTime = Date.now();

    try {
      // Check current session
      const { data: { session }, error: sessionError } = await supabase.auth.getSession();

      // Test auth endpoint responsiveness
      const { data: user, error: userError } = await supabase.auth.getUser();

      const responseTime = Date.now() - startTime;

      let status: SystemHealthCheck['status'] = 'healthy';
      if (sessionError || userError) {
        status = 'degraded';
      }

      return {
        component: 'authentication',
        status,
        responseTime,
        details: {
          hasSession: !!session,
          hasUser: !!user?.user,
          sessionValid: !sessionError,
          userValid: !userError,
          authMethod: session?.user?.app_metadata?.provider || 'unknown',
        },
        error: sessionError?.message || userError?.message,
        lastChecked: new Date().toISOString(),
      };
    } catch (error) {
      return {
        component: 'authentication',
        status: 'unhealthy',
        responseTime: Date.now() - startTime,
        error: error instanceof Error ? error.message : 'Authentication check failed',
        lastChecked: new Date().toISOString(),
      };
    }
  }

  /**
   * Check environment configuration
   */
  private async checkEnvironmentConfiguration(): Promise<SystemHealthCheck> {
    const startTime = Date.now();

    try {
      const config = {
        hasSupabaseUrl: !!env.supabase.url,
        hasSupabaseKey: !!env.supabase.anonKey,
        hasOpenAIKey: !!env.openai.apiKey,
        environment: env.getEnvironment(),
        features: env.features,
        validSupabaseConfig: env.validateSupabaseConfig(),
        validAIConfig: env.validateAIConfig(),
      };

      const requiredConfigs = [
        config.hasSupabaseUrl,
        config.hasSupabaseKey,
        config.validSupabaseConfig,
      ];

      const optionalConfigs = [
        config.hasOpenAIKey,
        config.validAIConfig,
      ];

      const missingRequired = requiredConfigs.filter(c => !c).length;
      const missingOptional = optionalConfigs.filter(c => !c).length;

      const responseTime = Date.now() - startTime;

      let status: SystemHealthCheck['status'] = 'healthy';
      if (missingRequired > 0) {
        status = 'unhealthy';
      } else if (missingOptional > 0) {
        status = 'degraded';
      }

      return {
        component: 'environment_config',
        status,
        responseTime,
        details: {
          ...config,
          missingRequired,
          missingOptional,
        },
        error: missingRequired > 0 ? 'Missing required configuration' : 
               missingOptional > 0 ? 'Missing optional configuration' : undefined,
        lastChecked: new Date().toISOString(),
      };
    } catch (error) {
      return {
        component: 'environment_config',
        status: 'unhealthy',
        responseTime: Date.now() - startTime,
        error: error instanceof Error ? error.message : 'Environment config check failed',
        lastChecked: new Date().toISOString(),
      };
    }
  }

  /**
   * Generate recommendations based on health check results
   */
  private generateRecommendations(checks: SystemHealthCheck[]): string[] {
    const recommendations: string[] = [];

    checks.forEach(check => {
      if (check.status === 'unhealthy') {
        switch (check.component) {
          case 'database':
            recommendations.push('Check database connection and credentials');
            break;
          case 'ai_services':
            recommendations.push('Verify AI service API keys and network connectivity');
            break;
          case 'file_upload':
            recommendations.push('Ensure storage buckets are properly configured');
            break;
          case 'video_processing':
            recommendations.push('Update browser or check camera permissions');
            break;
          case 'mobile_compatibility':
            recommendations.push('Use a modern mobile browser with full feature support');
            break;
          case 'offline_capabilities':
            recommendations.push('Enable service workers and check cache storage');
            break;
          case 'authentication':
            recommendations.push('Check authentication service configuration');
            break;
          case 'environment_config':
            recommendations.push('Review environment variables and configuration');
            break;
        }
      } else if (check.status === 'degraded') {
        recommendations.push(`Monitor ${check.component} for potential issues`);
      }
    });

    if (recommendations.length === 0) {
      recommendations.push('All systems are operating normally');
    }

    return recommendations;
  }

  /**
   * Get cached health check result
   */
  getCachedHealthCheck(component: string): SystemHealthCheck | null {
    return this.healthChecks.get(component) || null;
  }

  /**
   * Cache health check result
   */
  cacheHealthCheck(check: SystemHealthCheck): void {
    this.healthChecks.set(check.component, check);
  }
}

// Export singleton instance
export const systemHealthValidator = SystemHealthValidator.getInstance();

// Export convenience functions
export const performHealthCheck = () => systemHealthValidator.performFullHealthCheck();
export const checkComponent = (component: string) => systemHealthValidator.getCachedHealthCheck(component);

// Photo Optimizer for STR Certified Mobile Upload

import type { PhotoOptimizationConfig, OptimizationResult, DeviceCapabilities } from '@/types/photo';

export interface PhotoOptimizerOptions {
  maxWidth?: number;
  maxHeight?: number;
  quality?: number;
  format?: 'jpeg' | 'webp';
  maxFileSize?: number;
  preserveMetadata?: boolean;
  enableProgressive?: boolean;
}

export class PhotoOptimizer {
  private defaultOptions: Required<PhotoOptimizerOptions> = {
    maxWidth: 2048,
    maxHeight: 2048,
    quality: 0.85,
    format: 'jpeg',
    maxFileSize: 5 * 1024 * 1024, // 5MB
    preserveMetadata: false,
    enableProgressive: true
  };

  private canvas: HTMLCanvasElement | null = null;
  private ctx: CanvasRenderingContext2D | null = null;
  private deviceCapabilities: DeviceCapabilities | null = null;

  constructor(options: PhotoOptimizerOptions = {}) {
    this.defaultOptions = { ...this.defaultOptions, ...options };
    this.initializeCanvas();
    this.detectDeviceCapabilities();
  }

  /**
   * Optimizes a photo for upload while maintaining quality for AI analysis
   * @param file - Original photo file
   * @param options - Override default optimization options
   * @returns Promise<OptimizationResult>
   */
  async optimizeForUpload(
    file: File,
    options: Partial<PhotoOptimizerOptions> = {}
  ): Promise<OptimizationResult> {
    const startTime = performance.now();
    const opts = { ...this.defaultOptions, ...options };

    try {
      // Validate input
      if (!file.type.startsWith('image/')) {
        throw new Error('Invalid file type. Expected an image.');
      }

      // Load image
      const originalImage = await this.loadImage(file);
      const originalSize = file.size;
      const originalDimensions = {
        width: originalImage.width,
        height: originalImage.height
      };

      // Calculate optimal dimensions
      const { width, height } = this.calculateOptimalDimensions(
        originalImage.width,
        originalImage.height,
        opts.maxWidth,
        opts.maxHeight
      );

      // Resize image
      const resizedImage = await this.resizeImage(originalImage, width, height);

      // Apply device-specific optimizations
      const deviceOptimizedQuality = this.getDeviceOptimizedQuality(opts.quality);

      // Convert to blob with quality adjustments
      let blob = await this.imageToBlob(resizedImage, opts.format, deviceOptimizedQuality);

      // If file is still too large, progressively reduce quality
      if (blob.size > opts.maxFileSize) {
        blob = await this.progressiveQualityReduction(
          resizedImage,
          opts.format,
          deviceOptimizedQuality,
          opts.maxFileSize
        );
      }

      // Create optimized file
      const optimizedFile = new File([blob], file.name, {
        type: `image/${opts.format}`,
        lastModified: Date.now()
      });

      // Calculate metrics
      const compressionRatio = ((originalSize - blob.size) / originalSize) * 100;
      const processingTime = performance.now() - startTime;

      return {
        originalFile: file,
        optimizedFile,
        originalSize,
        optimizedSize: blob.size,
        compressionRatio,
        originalDimensions,
        optimizedDimensions: { width, height },
        format: opts.format,
        quality: deviceOptimizedQuality,
        processingTime,
        success: true,
        metadata: {
          deviceCapabilities: this.deviceCapabilities,
          optimizationSettings: opts
        }
      };

    } catch (error) {
      return {
        originalFile: file,
        optimizedFile: file,
        originalSize: file.size,
        optimizedSize: file.size,
        compressionRatio: 0,
        originalDimensions: { width: 0, height: 0 },
        optimizedDimensions: { width: 0, height: 0 },
        format: 'jpeg',
        quality: 0,
        processingTime: performance.now() - startTime,
        success: false,
        error: error instanceof Error ? error.message : 'Optimization failed'
      };
    }
  }

  /**
   * Batch optimize multiple photos
   * @param files - Array of photo files
   * @param options - Optimization options
   * @returns Promise<OptimizationResult[]>
   */
  async batchOptimize(
    files: File[],
    options: Partial<PhotoOptimizerOptions> = {}
  ): Promise<OptimizationResult[]> {
    // Process in chunks to avoid memory issues
    const chunkSize = this.getOptimalBatchSize();
    const results: OptimizationResult[] = [];

    for (let i = 0; i < files.length; i += chunkSize) {
      const chunk = files.slice(i, i + chunkSize);
      const chunkResults = await Promise.all(
        chunk.map(file => this.optimizeForUpload(file, options))
      );
      results.push(...chunkResults);
    }

    return results;
  }

  /**
   * Prepares photo for AI analysis (higher quality preservation)
   * @param file - Photo file
   * @returns Promise<OptimizationResult>
   */
  async optimizeForAI(file: File): Promise<OptimizationResult> {
    return this.optimizeForUpload(file, {
      quality: 0.95,
      maxWidth: 4096,
      maxHeight: 4096,
      maxFileSize: 10 * 1024 * 1024 // 10MB for AI analysis
    });
  }

  /**
   * Creates a thumbnail for preview
   * @param file - Photo file
   * @param size - Thumbnail size
   * @returns Promise<string> - Data URL
   */
  async createThumbnail(file: File, size: number = 200): Promise<string> {
    try {
      const image = await this.loadImage(file);
      const { width, height } = this.calculateOptimalDimensions(
        image.width,
        image.height,
        size,
        size
      );

      const thumbnail = await this.resizeImage(image, width, height);
      return thumbnail.toDataURL('image/jpeg', 0.8);
    } catch (error) {
      console.error('Failed to create thumbnail:', error);
      return '';
    }
  }

  // Private helper methods

  private initializeCanvas(): void {
    if (typeof document !== 'undefined') {
      this.canvas = document.createElement('canvas');
      this.ctx = this.canvas.getContext('2d');
    }
  }

  private detectDeviceCapabilities(): void {
    if (typeof window === 'undefined') return;

    const canvas = document.createElement('canvas');
    const gl = canvas.getContext('webgl') || canvas.getContext('experimental-webgl');

    this.deviceCapabilities = {
      maxTextureSize: gl ? gl.getParameter(gl.MAX_TEXTURE_SIZE) : 4096,
      deviceMemory: (navigator as any).deviceMemory || 4,
      hardwareConcurrency: navigator.hardwareConcurrency || 4,
      connection: (navigator as any).connection ? {
        effectiveType: (navigator as any).connection.effectiveType,
        downlink: (navigator as any).connection.downlink,
        saveData: (navigator as any).connection.saveData
      } : null,
      userAgent: navigator.userAgent,
      platform: navigator.platform,
      screenResolution: {
        width: window.screen.width,
        height: window.screen.height,
        pixelRatio: window.devicePixelRatio || 1
      }
    };
  }

  private async loadImage(file: File): Promise<HTMLImageElement> {
    return new Promise((resolve, reject) => {
      const img = new Image();
      const url = URL.createObjectURL(file);

      img.onload = () => {
        URL.revokeObjectURL(url);
        resolve(img);
      };

      img.onerror = () => {
        URL.revokeObjectURL(url);
        reject(new Error('Failed to load image'));
      };

      img.src = url;
    });
  }

  private calculateOptimalDimensions(
    originalWidth: number,
    originalHeight: number,
    maxWidth: number,
    maxHeight: number
  ): { width: number; height: number } {
    // Don't upscale
    if (originalWidth <= maxWidth && originalHeight <= maxHeight) {
      return { width: originalWidth, height: originalHeight };
    }

    const aspectRatio = originalWidth / originalHeight;
    let width = maxWidth;
    let height = maxHeight;

    if (originalWidth / originalHeight > maxWidth / maxHeight) {
      height = Math.round(maxWidth / aspectRatio);
    } else {
      width = Math.round(maxHeight * aspectRatio);
    }

    return { width, height };
  }

  private async resizeImage(
    image: HTMLImageElement,
    width: number,
    height: number
  ): Promise<HTMLCanvasElement> {
    if (!this.canvas || !this.ctx) {
      throw new Error('Canvas not initialized');
    }

    // Use step-down approach for better quality
    let currentWidth = image.width;
    let currentHeight = image.height;
    let currentImage: HTMLImageElement | HTMLCanvasElement = image;

    // Step down in halves for better quality
    while (currentWidth > width * 2 || currentHeight > height * 2) {
      currentWidth = Math.round(currentWidth / 2);
      currentHeight = Math.round(currentHeight / 2);

      const tempCanvas = document.createElement('canvas');
      tempCanvas.width = currentWidth;
      tempCanvas.height = currentHeight;

      const tempCtx = tempCanvas.getContext('2d');
      if (!tempCtx) throw new Error('Failed to get canvas context');

      tempCtx.imageSmoothingEnabled = true;
      tempCtx.imageSmoothingQuality = 'high';
      tempCtx.drawImage(currentImage, 0, 0, currentWidth, currentHeight);

      currentImage = tempCanvas;
    }

    // Final resize
    this.canvas.width = width;
    this.canvas.height = height;
    this.ctx.imageSmoothingEnabled = true;
    this.ctx.imageSmoothingQuality = 'high';
    this.ctx.drawImage(currentImage, 0, 0, width, height);

    const finalCanvas = document.createElement('canvas');
    finalCanvas.width = width;
    finalCanvas.height = height;
    const finalCtx = finalCanvas.getContext('2d');
    if (!finalCtx) throw new Error('Failed to get canvas context');

    finalCtx.drawImage(this.canvas, 0, 0);
    return finalCanvas;
  }

  private async imageToBlob(
    canvas: HTMLCanvasElement,
    format: 'jpeg' | 'webp',
    quality: number
  ): Promise<Blob> {
    return new Promise((resolve, reject) => {
      canvas.toBlob(
        (blob) => {
          if (blob) resolve(blob);
          else reject(new Error('Failed to create blob'));
        },
        `image/${format}`,
        quality
      );
    });
  }

  private async progressiveQualityReduction(
    canvas: HTMLCanvasElement,
    format: 'jpeg' | 'webp',
    startQuality: number,
    targetSize: number
  ): Promise<Blob> {
    let quality = startQuality;
    let blob = await this.imageToBlob(canvas, format, quality);

    // Reduce quality in steps until target size is reached
    while (blob.size > targetSize && quality > 0.3) {
      quality -= 0.05;
      blob = await this.imageToBlob(canvas, format, quality);
    }

    return blob;
  }

  private getDeviceOptimizedQuality(baseQuality: number): number {
    if (!this.deviceCapabilities) return baseQuality;

    // Adjust quality based on device capabilities
    let quality = baseQuality;

    // Lower quality for low-memory devices
    if (this.deviceCapabilities.deviceMemory < 4) {
      quality *= 0.9;
    }

    // Lower quality for slow connections
    if (this.deviceCapabilities.connection?.effectiveType === '2g' ||
        this.deviceCapabilities.connection?.effectiveType === 'slow-2g') {
      quality *= 0.85;
    }

    // Honor data saver preferences
    if (this.deviceCapabilities.connection?.saveData) {
      quality *= 0.8;
    }

    return Math.max(0.5, Math.min(1, quality));
  }

  private getOptimalBatchSize(): number {
    if (!this.deviceCapabilities) return 3;

    const memory = this.deviceCapabilities.deviceMemory;
    const cores = this.deviceCapabilities.hardwareConcurrency;

    if (memory >= 8 && cores >= 8) return 10;
    if (memory >= 4 && cores >= 4) return 5;
    return 3;
  }
}

// Export types
export interface OptimizationResult {
  originalFile: File;
  optimizedFile: File;
  originalSize: number;
  optimizedSize: number;
  compressionRatio: number;
  originalDimensions: { width: number; height: number };
  optimizedDimensions: { width: number; height: number };
  format: string;
  quality: number;
  processingTime: number;
  success: boolean;
  error?: string;
  metadata?: any;
}

export interface DeviceCapabilities {
  maxTextureSize: number;
  deviceMemory: number;
  hardwareConcurrency: number;
  connection: {
    effectiveType: string;
    downlink: number;
    saveData: boolean;
  } | null;
  userAgent: string;
  platform: string;
  screenResolution: {
    width: number;
    height: number;
    pixelRatio: number;
  };
}

