// Health Check Service for STR Certified
// Provides health endpoint and system status monitoring

import { createClient } from '@supabase/supabase-js';
import { env } from '../config/environment';

export interface HealthCheckResult {
  status: 'healthy' | 'degraded' | 'unhealthy';
  timestamp: string;
  version: string;
  uptime: number;
  services: {
    database: ServiceStatus;
    ai: ServiceStatus;
    storage: ServiceStatus;
    cache: ServiceStatus;
  };
  metrics: {
    memory: MemoryMetrics;
    performance: PerformanceMetrics;
    errors: ErrorMetrics;
  };
  environment: {
    node: string;
    deployment: string;
    region?: string;
  };
}

interface ServiceStatus {
  name: string;
  status: 'up' | 'down' | 'degraded';
  latency?: number;
  lastCheck: string;
  error?: string;
  details?: Record<string, any>;
}

interface MemoryMetrics {
  used: number;
  total: number;
  percentage: number;
  rss: number;
  heapUsed: number;
  heapTotal: number;
}

interface PerformanceMetrics {
  cpuUsage: number;
  loadAverage: number[];
  responseTime: number;
  requestsPerSecond: number;
}

interface ErrorMetrics {
  rate: number;
  total: number;
  recent: Array<{
    type: string;
    count: number;
    lastOccurred: string;
  }>;
}

export class HealthCheckService {
  private static instance: HealthCheckService;
  private startTime: number;
  private errorCount: number = 0;
  private requestCount: number = 0;
  private recentErrors: Map<string, { count: number; lastOccurred: Date }> = new Map();
  private supabase: any;

  private constructor() {
    this.startTime = Date.now();

    // Initialize Supabase client for health checks
    if (env.validateSupabaseConfig()) {
      this.supabase = createClient(
        env.supabase.url,
        env.supabase.anonKey
      );
    }
  }

  static getInstance(): HealthCheckService {
    if (!HealthCheckService.instance) {
      HealthCheckService.instance = new HealthCheckService();
    }
    return HealthCheckService.instance;
  }

  /**
   * Performs comprehensive health check
   */
  async performHealthCheck(): Promise<HealthCheckResult> {
    const startCheck = Date.now();

    // Check all services in parallel
    const [database, ai, storage, cache] = await Promise.all([
      this.checkDatabase(),
      this.checkAIService(),
      this.checkStorage(),
      this.checkCache()
    ]);

    // Determine overall status
    const services = { database, ai, storage, cache };
    const serviceStatuses = Object.values(services).map(s => s.status);

    let overallStatus: HealthCheckResult['status'] = 'healthy';
    if (serviceStatuses.includes('down')) {
      overallStatus = 'unhealthy';
    } else if (serviceStatuses.includes('degraded')) {
      overallStatus = 'degraded';
    }

    // Collect metrics
    const metrics = {
      memory: this.getMemoryMetrics(),
      performance: this.getPerformanceMetrics(Date.now() - startCheck),
      errors: this.getErrorMetrics()
    };

    return {
      status: overallStatus,
      timestamp: new Date().toISOString(),
      version: this.getVersion(),
      uptime: this.getUptime(),
      services,
      metrics,
      environment: {
        node: process.version,
        deployment: env.getEnvironment(),
        region: process.env.RAILWAY_REGION
      }
    };
  }

  /**
   * Simple health check for load balancer
   */
  async getBasicHealth(): Promise<{ status: string; timestamp: string }> {
    return {
      status: 'ok',
      timestamp: new Date().toISOString()
    };
  }

  /**
   * Readiness check for container orchestration
   */
  async checkReadiness(): Promise<{ ready: boolean; checks: Record<string, boolean> }> {
    const checks = {
      database: false,
      environment: false,
      dependencies: false
    };

    // Check database connection
    if (this.supabase) {
      try {
        const { error } = await this.supabase
          .from('system_health')
          .select('id')
          .limit(1);
        checks.database = !error;
      } catch {
        checks.database = false;
      }
    }

    // Check environment variables
    checks.environment = env.validateSupabaseConfig() && env.validateApiConfig();

    // Check critical dependencies
    checks.dependencies = true; // In production, would check actual dependencies

    const ready = Object.values(checks).every(check => check === true);

    return { ready, checks };
  }

  /**
   * Liveness check for container orchestration
   */
  async checkLiveness(): Promise<{ alive: boolean; pid: number; uptime: number }> {
    return {
      alive: true,
      pid: process.pid,
      uptime: this.getUptime()
    };
  }

  // Private service check methods

  private async checkDatabase(): Promise<ServiceStatus> {
    const start = Date.now();
    const status: ServiceStatus = {
      name: 'Database (Supabase)',
      status: 'down',
      lastCheck: new Date().toISOString()
    };

    if (!this.supabase) {
      status.error = 'Supabase client not initialized';
      return status;
    }

    try {
      // Perform a simple query
      const { data, error } = await this.supabase
        .from('system_health')
        .select('id')
        .limit(1);

      if (error) throw error;

      status.status = 'up';
      status.latency = Date.now() - start;

      // Get additional database metrics
      const { data: stats } = await this.supabase.rpc('get_database_stats');
      if (stats) {
        status.details = {
          connections: stats.connections,
          size: stats.database_size,
          tables: stats.table_count
        };
      }

    } catch (error) {
      status.status = 'down';
      status.error = error instanceof Error ? error.message : 'Unknown error';
      status.latency = Date.now() - start;
    }

    return status;
  }

  private async checkAIService(): Promise<ServiceStatus> {
    const start = Date.now();
    const status: ServiceStatus = {
      name: 'AI Service (OpenAI)',
      status: 'down',
      lastCheck: new Date().toISOString()
    };

    if (!env.hasOpenAI()) {
      status.status = 'degraded';
      status.error = 'OpenAI API key not configured';
      return status;
    }

    try {
      // In production, would make a test API call
      // For now, simulate with a simple check
      const response = await fetch('https://api.openai.com/v1/models', {
        method: 'GET',
        headers: {
          'Authorization': `Bearer ${env.openai.apiKey}`,
          'OpenAI-Organization': env.openai.orgId || ''
        },
        signal: AbortSignal.timeout(5000) // 5 second timeout
      });

      if (response.ok) {
        status.status = 'up';
        status.latency = Date.now() - start;

        // Get rate limit info from headers
        const remaining = response.headers.get('x-ratelimit-remaining');
        const limit = response.headers.get('x-ratelimit-limit');

        if (remaining && limit) {
          status.details = {
            rateLimitRemaining: parseInt(remaining),
            rateLimitTotal: parseInt(limit),
            rateLimitUsage: ((parseInt(limit) - parseInt(remaining)) / parseInt(limit)) * 100
          };
        }
      } else {
        status.status = response.status === 429 ? 'degraded' : 'down';
        status.error = `API returned ${response.status}`;
      }

    } catch (error) {
      status.status = 'down';
      status.error = error instanceof Error ? error.message : 'Connection failed';
      status.latency = Date.now() - start;
    }

    return status;
  }

  private async checkStorage(): Promise<ServiceStatus> {
    const start = Date.now();
    const status: ServiceStatus = {
      name: 'Storage',
      status: 'up',
      lastCheck: new Date().toISOString()
    };

    try {
      if (typeof window !== 'undefined' && 'storage' in navigator && 'estimate' in navigator.storage) {
        const estimate = await navigator.storage.estimate();

        status.latency = Date.now() - start;
        status.details = {
          used: estimate.usage || 0,
          quota: estimate.quota || 0,
          percentage: estimate.quota ? ((estimate.usage || 0) / estimate.quota) * 100 : 0
        };

        // Degrade if storage is almost full
        if (status.details.percentage > 90) {
          status.status = 'degraded';
          status.error = 'Storage usage above 90%';
        }
      } else {
        // Server-side or unsupported
        status.details = {
          available: true,
          type: 'server'
        };
      }
    } catch (error) {
      status.status = 'down';
      status.error = error instanceof Error ? error.message : 'Storage check failed';
    }

    return status;
  }

  private async checkCache(): Promise<ServiceStatus> {
    const start = Date.now();
    const status: ServiceStatus = {
      name: 'Cache',
      status: 'up',
      lastCheck: new Date().toISOString()
    };

    try {
      // Test cache operations
      const testKey = 'health_check_test';
      const testValue = { timestamp: Date.now() };

      // Test write
      if (typeof window !== 'undefined') {
        localStorage.setItem(testKey, JSON.stringify(testValue));

        // Test read
        const retrieved = localStorage.getItem(testKey);
        if (!retrieved) throw new Error('Cache read failed');

        // Test delete
        localStorage.removeItem(testKey);

        status.latency = Date.now() - start;

        // Get cache stats
        const cacheSize = new Blob(Object.values(localStorage)).size;
        status.details = {
          type: 'localStorage',
          size: cacheSize,
          items: localStorage.length
        };
      } else {
        // Server-side cache check
        status.details = {
          type: 'memory',
          available: true
        };
      }

    } catch (error) {
      status.status = 'down';
      status.error = error instanceof Error ? error.message : 'Cache check failed';
    }

    return status;
  }

  // Metrics collection methods

  private getMemoryMetrics(): MemoryMetrics {
    const usage = process.memoryUsage();
    const totalMemory = process.arch === 'x64' ? 8 * 1024 * 1024 * 1024 : 4 * 1024 * 1024 * 1024; // Estimate

    return {
      used: usage.heapUsed + usage.external,
      total: totalMemory,
      percentage: ((usage.heapUsed + usage.external) / totalMemory) * 100,
      rss: usage.rss,
      heapUsed: usage.heapUsed,
      heapTotal: usage.heapTotal
    };
  }

  private getPerformanceMetrics(checkDuration: number): PerformanceMetrics {
    this.requestCount++;

    return {
      cpuUsage: process.cpuUsage ? process.cpuUsage().user / 1000000 : 0, // Convert to seconds
      loadAverage: typeof process.loadavg === 'function' ? process.loadavg() : [0, 0, 0],
      responseTime: checkDuration,
      requestsPerSecond: this.requestCount / (this.getUptime() / 1000)
    };
  }

  private getErrorMetrics(): ErrorMetrics {
    const recentErrors = Array.from(this.recentErrors.entries())
      .map(([type, data]) => ({
        type,
        count: data.count,
        lastOccurred: data.lastOccurred.toISOString()
      }))
      .sort((a, b) => b.count - a.count)
      .slice(0, 10);

    return {
      rate: this.errorCount / (this.getUptime() / 1000),
      total: this.errorCount,
      recent: recentErrors
    };
  }

  // Utility methods

  private getVersion(): string {
    return process.env.npm_package_version || '1.0.0';
  }

  private getUptime(): number {
    return Date.now() - this.startTime;
  }

  /**
   * Records an error for metrics
   */
  recordError(type: string): void {
    this.errorCount++;

    const existing = this.recentErrors.get(type);
    if (existing) {
      existing.count++;
      existing.lastOccurred = new Date();
    } else {
      this.recentErrors.set(type, {
        count: 1,
        lastOccurred: new Date()
      });
    }

    // Keep only recent error types (last 100)
    if (this.recentErrors.size > 100) {
      const oldest = Array.from(this.recentErrors.entries())
        .sort((a, b) => a[1].lastOccurred.getTime() - b[1].lastOccurred.getTime())[0];
      this.recentErrors.delete(oldest[0]);
    }
  }

  /**
   * Express/HTTP handler for health endpoint
   */
  async handleHealthRequest(req: any, res: any): Promise<void> {
    try {
      const fullCheck = req.query.full === 'true';

      if (fullCheck) {
        const health = await this.performHealthCheck();
        res.status(health.status === 'healthy' ? 200 : 503).json(health);
      } else {
        const basic = await this.getBasicHealth();
        res.status(200).json(basic);
      }
    } catch (error) {
      res.status(500).json({
        status: 'error',
        message: error instanceof Error ? error.message : 'Health check failed'
      });
    }
  }
}

// Export singleton instance
export const healthCheck = HealthCheckService.getInstance();

// Export convenience functions
export const checkHealth = () => healthCheck.performHealthCheck();
export const checkReadiness = () => healthCheck.checkReadiness();
export const checkLiveness = () => healthCheck.checkLiveness();

// Vite server middleware for health endpoint
export function healthCheckMiddleware() {
  return {
    name: 'health-check',
    configureServer(server: any) {
      server.middlewares.use(async (req: any, res: any, next: any) => {
        if (req.url === '/health' || req.url.startsWith('/health?')) {
          res.setHeader('Content-Type', 'application/json');
          await healthCheck.handleHealthRequest(req, res);
        } else if (req.url === '/ready') {
          const readiness = await healthCheck.checkReadiness();
          res.setHeader('Content-Type', 'application/json');
          res.statusCode = readiness.ready ? 200 : 503;
          res.end(JSON.stringify(readiness));
        } else if (req.url === '/live') {
          const liveness = await healthCheck.checkLiveness();
          res.setHeader('Content-Type', 'application/json');
          res.statusCode = 200;
          res.end(JSON.stringify(liveness));
        } else {
          next();
        }
      });
    }
  };
}

import { errorReporter } from './error-reporter';
import { env } from '../config/environment';

export interface PerformanceMetric {
  name: string;
  value: number;
  unit: 'ms' | 's' | 'bytes' | 'count' | 'percent';
  timestamp: string;
  tags?: Record<string, string>;
  metadata?: Record<string, any>;
}

export interface PerformanceReport {
  metrics: PerformanceMetric[];
  summary: {
    pageLoadTime: number;
    timeToInteractive: number;
    firstContentfulPaint: number;
    largestContentfulPaint: number;
    cumulativeLayoutShift: number;
    firstInputDelay: number;
    totalBlockingTime: number;
  };
  resources: ResourceTiming[];
  userAgent: string;
  url: string;
  timestamp: string;
  sessionId: string;
}

export interface ResourceTiming {
  name: string;
  type: string;
  duration: number;
  size: number;
  cached: boolean;
}

export interface AIProcessingMetrics {
  operationType: string;
  duration: number;
  modelUsed: string;
  inputSize: number;
  outputSize: number;
  success: boolean;
  error?: string;
  metadata?: Record<string, any>;
}

export interface TrackerConfig {
  enableWebVitals?: boolean;
  enableResourceTiming?: boolean;
  enableAIMetrics?: boolean;
  enableUserTiming?: boolean;
  sampleRate?: number;
  slowThreshold?: {
    pageLoad?: number;
    api?: number;
    ai?: number;
    database?: number;
  };
  reportInterval?: number;
  maxMetricsQueue?: number;
}

const DEFAULT_CONFIG: TrackerConfig = {
  enableWebVitals: true,
  enableResourceTiming: true,
  enableAIMetrics: true,
  enableUserTiming: true,
  sampleRate: env.isProduction() ? 0.1 : 1.0, // 10% in production, 100% in development
  slowThreshold: {
    pageLoad: 3000,
    api: 1000,
    ai: 5000,
    database: 500,
  },
  reportInterval: 60000, // 1 minute
  maxMetricsQueue: 100,
};

export class PerformanceTracker {
  private static instance: PerformanceTracker;
  private config: TrackerConfig;
  private metricsQueue: PerformanceMetric[] = [];
  private observer: PerformanceObserver | null = null;
  private reportTimer: NodeJS.Timeout | null = null;
  private sessionId: string;
  private isInitialized = false;

  private constructor() {
    this.config = DEFAULT_CONFIG;
    this.sessionId = this.generateSessionId();
  }

  static getInstance(): PerformanceTracker {
    if (!PerformanceTracker.instance) {
      PerformanceTracker.instance = new PerformanceTracker();
    }
    return PerformanceTracker.instance;
  }

  /**
   * Initialize performance tracking
   */
  initialize(config?: Partial<TrackerConfig>) {
    if (this.isInitialized) return;

    this.config = { ...DEFAULT_CONFIG, ...config };

    // Check if we should track based on sample rate
    if (Math.random() > (this.config.sampleRate || 1)) {
      console.log('[PerformanceTracker] Skipping initialization due to sampling');
      return;
    }

    if (this.config.enableWebVitals) {
      this.setupWebVitals();
    }

    if (this.config.enableResourceTiming) {
      this.setupResourceTiming();
    }

    if (this.config.enableUserTiming) {
      this.setupUserTiming();
    }

    this.startReportTimer();
    this.isInitialized = true;

    // Track initial page load
    this.trackPageLoad();

    if (env.isDevelopment()) {
      console.log('[PerformanceTracker] Initialized with config:', this.config);
    }
  }

  /**
   * Track page load performance
   */
  private trackPageLoad() {
    if (!window.performance || !window.performance.timing) return;

    // Wait for page to be fully loaded
    if (document.readyState !== 'complete') {
      window.addEventListener('load', () => this.trackPageLoad());
      return;
    }

    const timing = window.performance.timing;
    const navigation = window.performance.getEntriesByType('navigation')[0] as PerformanceNavigationTiming;

    const metrics = {
      pageLoadTime: timing.loadEventEnd - timing.navigationStart,
      domContentLoaded: timing.domContentLoadedEventEnd - timing.navigationStart,
      timeToFirstByte: timing.responseStart - timing.navigationStart,
      dnsLookup: timing.domainLookupEnd - timing.domainLookupStart,
      tcpConnection: timing.connectEnd - timing.connectStart,
      request: timing.responseStart - timing.requestStart,
      response: timing.responseEnd - timing.responseStart,
      domProcessing: timing.domComplete - timing.domLoading,
    };

    // Track each metric
    Object.entries(metrics).forEach(([name, value]) => {
      this.trackMetric(name, value, 'ms', { category: 'page_load' });
    });

    // Check for slow page load
    if (metrics.pageLoadTime > (this.config.slowThreshold?.pageLoad || 3000)) {
      this.reportSlowOperation('page_load', metrics.pageLoadTime, metrics);
    }
  }

  /**
   * Setup Web Vitals tracking
   */
  private setupWebVitals() {
    if (!window.PerformanceObserver) return;

    try {
      // Track Largest Contentful Paint (LCP)
      const lcpObserver = new PerformanceObserver((list) => {
        const entries = list.getEntries();
        const lastEntry = entries[entries.length - 1] as any;
        this.trackMetric('lcp', lastEntry.startTime, 'ms', { 
          category: 'web_vitals',
          element: lastEntry.element?.tagName,
        });
      });
      lcpObserver.observe({ entryTypes: ['largest-contentful-paint'] });

      // Track First Input Delay (FID)
      const fidObserver = new PerformanceObserver((list) => {
        const entries = list.getEntries();
        entries.forEach((entry: any) => {
          this.trackMetric('fid', entry.processingStart - entry.startTime, 'ms', {
            category: 'web_vitals',
            eventType: entry.name,
          });
        });
      });
      fidObserver.observe({ entryTypes: ['first-input'] });

      // Track Cumulative Layout Shift (CLS)
      let clsValue = 0;
      const clsObserver = new PerformanceObserver((list) => {
        const entries = list.getEntries();
        entries.forEach((entry: any) => {
          if (!entry.hadRecentInput) {
            clsValue += entry.value;
          }
        });
        this.trackMetric('cls', clsValue, 'count', { category: 'web_vitals' });
      });
      clsObserver.observe({ entryTypes: ['layout-shift'] });

    } catch (error) {
      console.error('[PerformanceTracker] Error setting up Web Vitals:', error);
    }
  }

  /**
   * Setup resource timing tracking
   */
  private setupResourceTiming() {
    if (!window.PerformanceObserver) return;

    try {
      const observer = new PerformanceObserver((list) => {
        const entries = list.getEntries();
        entries.forEach((entry: any) => {
          if (entry.entryType === 'resource') {
            const resource: ResourceTiming = {
              name: entry.name,
              type: entry.initiatorType,
              duration: entry.duration,
              size: entry.transferSize || 0,
              cached: entry.transferSize === 0 && entry.decodedBodySize > 0,
            };

            // Track slow resources
            if (resource.duration > 1000) {
              this.trackMetric('slow_resource', resource.duration, 'ms', {
                category: 'resource',
                url: resource.name,
                type: resource.type,
                cached: resource.cached,
              });
            }
          }
        });
      });

      observer.observe({ entryTypes: ['resource'] });
      this.observer = observer;
    } catch (error) {
      console.error('[PerformanceTracker] Error setting up resource timing:', error);
    }
  }

  /**
   * Setup user timing tracking
   */
  private setupUserTiming() {
    if (!window.PerformanceObserver) return;

    try {
      const observer = new PerformanceObserver((list) => {
        const entries = list.getEntries();
        entries.forEach((entry) => {
          if (entry.entryType === 'measure') {
            this.trackMetric(entry.name, entry.duration, 'ms', {
              category: 'user_timing',
              startMark: (entry as PerformanceMeasure).detail?.start,
              endMark: (entry as PerformanceMeasure).detail?.end,
            });
          }
        });
      });

      observer.observe({ entryTypes: ['measure'] });
    } catch (error) {
      console.error('[PerformanceTracker] Error setting up user timing:', error);
    }
  }

  /**
   * Track a custom metric
   */
  trackMetric(
    name: string,
    value: number,
    unit: PerformanceMetric['unit'] = 'ms',
    metadata?: Record<string, any>
  ) {
    const metric: PerformanceMetric = {
      name,
      value,
      unit,
      timestamp: new Date().toISOString(),
      tags: {
        environment: env.getEnvironment(),
        ...metadata?.tags,
      },
      metadata,
    };

    this.metricsQueue.push(metric);

    // Flush if queue is full
    if (this.metricsQueue.length >= (this.config.maxMetricsQueue || 100)) {
      this.flush();
    }

    // Log in development
    if (env.isDevelopment()) {
      console.log('[PerformanceTracker] Metric:', metric);
    }
  }

  /**
   * Track API call performance
   */
  trackApiCall(url: string, method: string, duration: number, status: number) {
    this.trackMetric('api_call', duration, 'ms', {
      category: 'api',
      url,
      method,
      status,
      success: status >= 200 && status < 300,
    });

    // Check for slow API calls
    if (duration > (this.config.slowThreshold?.api || 1000)) {
      this.reportSlowOperation('api', duration, { url, method, status });
    }
  }

  /**
   * Track AI processing performance
   */
  trackAIProcessing(metrics: AIProcessingMetrics) {
    this.trackMetric('ai_processing', metrics.duration, 'ms', {
      category: 'ai',
      operation: metrics.operationType,
      model: metrics.modelUsed,
      inputSize: metrics.inputSize,
      outputSize: metrics.outputSize,
      success: metrics.success,
      ...metrics.metadata,
    });

    // Check for slow AI operations
    if (metrics.duration > (this.config.slowThreshold?.ai || 5000)) {
      this.reportSlowOperation('ai', metrics.duration, metrics);
    }

    // Report errors
    if (!metrics.success && metrics.error) {
      errorReporter.reportError(new Error(`AI Processing Failed: ${metrics.error}`), {
        category: 'ai',
        operation: metrics.operationType,
        model: metrics.modelUsed,
      });
    }
  }

  /**
   * Track database query performance
   */
  trackDatabaseQuery(query: string, duration: number, success: boolean, rowCount?: number) {
    this.trackMetric('database_query', duration, 'ms', {
      category: 'database',
      query: this.sanitizeQuery(query),
      success,
      rowCount,
    });

    // Check for slow queries
    if (duration > (this.config.slowThreshold?.database || 500)) {
      this.reportSlowOperation('database', duration, { query, rowCount });
    }
  }

  /**
   * Create a performance mark
   */
  mark(name: string) {
    if (window.performance && window.performance.mark) {
      window.performance.mark(name);
    }
  }

  /**
   * Measure between two marks
   */
  measure(name: string, startMark: string, endMark?: string) {
    if (window.performance && window.performance.measure) {
      try {
        window.performance.measure(name, startMark, endMark);
      } catch (error) {
        console.error('[PerformanceTracker] Error creating measure:', error);
      }
    }
  }

  /**
   * Start a timer for measuring operations
   */
  startTimer(name: string): () => number {
    const startTime = performance.now();
    const markName = `${name}_start`;
    this.mark(markName);

    return () => {
      const endTime = performance.now();
      const duration = endTime - startTime;

      this.measure(name, markName);
      this.trackMetric(name, duration, 'ms', { category: 'timer' });

      return duration;
    };
  }

  /**
   * Report slow operations
   */
  private reportSlowOperation(type: string, duration: number, details: any) {
    errorReporter.addBreadcrumb({
      type: 'custom',
      category: 'performance',
      message: `Slow ${type} operation: ${duration}ms`,
      level: 'warning',
      data: details,
    });

    // Log to console in development
    if (env.isDevelopment()) {
      console.warn(`[PerformanceTracker] Slow ${type} operation:`, {
        duration,
        details,
      });
    }
  }

  /**
   * Get current performance summary
   */
  getPerformanceSummary(): PerformanceReport['summary'] {
    const navigation = window.performance.getEntriesByType('navigation')[0] as PerformanceNavigationTiming;
    const paint = window.performance.getEntriesByType('paint');

    const fcp = paint.find(entry => entry.name === 'first-contentful-paint');
    const lcp = this.metricsQueue.find(m => m.name === 'lcp')?.value || 0;
    const fid = this.metricsQueue.find(m => m.name === 'fid')?.value || 0;
    const cls = this.metricsQueue.find(m => m.name === 'cls')?.value || 0;

    return {
      pageLoadTime: navigation?.loadEventEnd - navigation?.fetchStart || 0,
      timeToInteractive: navigation?.domInteractive - navigation?.fetchStart || 0,
      firstContentfulPaint: fcp?.startTime || 0,
      largestContentfulPaint: lcp,
      cumulativeLayoutShift: cls,
      firstInputDelay: fid,
      totalBlockingTime: this.calculateTotalBlockingTime(),
    };
  }

  /**
   * Calculate total blocking time
   */
  private calculateTotalBlockingTime(): number {
    const longTasks = window.performance.getEntriesByType('longtask') as any[];
    return longTasks.reduce((total, task) => {
      const blockingTime = task.duration - 50; // Tasks over 50ms are considered blocking
      return total + (blockingTime > 0 ? blockingTime : 0);
    }, 0);
  }

  /**
   * Flush metrics to monitoring service
   */
  private async flush() {
    if (this.metricsQueue.length === 0) return;

    const report: PerformanceReport = {
      metrics: [...this.metricsQueue],
      summary: this.getPerformanceSummary(),
      resources: this.getResourceTimings(),
      userAgent: navigator.userAgent,
      url: window.location.href,
      timestamp: new Date().toISOString(),
      sessionId: this.sessionId,
    };

    this.metricsQueue = [];

    try {
      // In production, send to monitoring service
      if (env.isProduction()) {
        await this.sendToMonitoringService(report);
      }

      // Log summary in development
      if (env.isDevelopment()) {
        console.log('[PerformanceTracker] Performance Report:', report);
      }
    } catch (error) {
      console.error('[PerformanceTracker] Failed to flush metrics:', error);
    }
  }

  /**
   * Get resource timings
   */
  private getResourceTimings(): ResourceTiming[] {
    const entries = window.performance.getEntriesByType('resource') as PerformanceResourceTiming[];

    return entries
      .slice(-50) // Last 50 resources
      .map(entry => ({
        name: entry.name,
        type: entry.initiatorType,
        duration: entry.duration,
        size: entry.transferSize || 0,
        cached: entry.transferSize === 0 && entry.decodedBodySize > 0,
      }));
  }

  /**
   * Send report to monitoring service
   */
  private async sendToMonitoringService(report: PerformanceReport) {
    // Implementation would depend on the monitoring service used
    // This is a placeholder for the actual implementation
  }

  /**
   * Sanitize database queries
   */
  private sanitizeQuery(query: string): string {
    // Remove potentially sensitive data from queries
    return query
      .replace(/\b\d{4,}\b/g, 'XXX') // Replace long numbers
      .replace(/(['"])([^'"]{20,})\1/g, '$1...$1') // Truncate long strings
      .substring(0, 200); // Limit length
  }

  /**
   * Start report timer
   */
  private startReportTimer() {
    if (this.reportTimer) return;

    this.reportTimer = setInterval(() => {
      this.flush();
    }, this.config.reportInterval || 60000);
  }

  /**
   * Utility methods
   */
  private generateSessionId(): string {
    return `perf_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
  }

  /**
   * Cleanup and destroy
   */
  destroy() {
    if (this.observer) {
      this.observer.disconnect();
    }

    if (this.reportTimer) {
      clearInterval(this.reportTimer);
    }

    this.flush();
  }
}

