  persistentLimit: 200 * 1024 * 1024, // 200MB
  ttl: 60 * 60 * 1000, // 1 hour
  compressionThreshold: 10 * 1024 // 10KB
};

// Export factory function
export const createCacheManager = (config?: Partial<CacheConfig>): CacheManager => {
