// Enhanced Static VRBO Photo Extractor
// Optimized version of the original working photo extraction method

import axios, { AxiosInstance } from 'axios';
import { logger } from '../../utils/logger';
import type { PhotoData, ScrapingResult, ScrapingError } from './types';

interface StaticPhotoExtractionOptions {
  maxImages: number;
  includeHighRes: boolean;
  includeThumbnails: boolean;
  deduplicateImages: boolean;
  userAgent: string;
  timeout: number;
}

interface StaticPhotoExtractionResult {
  photos: PhotoData[];
  extractionStats: {
    staticImages: number;
    lazyImages: number;
    galleryImages: number;
    jsonLdImages: number;
    totalFound: number;
    totalProcessed: number;
    duplicatesRemoved: number;
  };
  sourceHtml: string;
  processingTime: number;
}

export class StaticVRBOPhotoExtractor {
  private httpClient: AxiosInstance;
  private options: StaticPhotoExtractionOptions;

  constructor(options: Partial<StaticPhotoExtractionOptions> = {}) {
    this.options = {
      maxImages: 50,
      includeHighRes: true,
      includeThumbnails: true,
      deduplicateImages: true,
      userAgent: 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/120.0.0.0 Safari/537.36',
      timeout: 30000,
      ...options
    };

    this.httpClient = axios.create({
      timeout: this.options.timeout,
      headers: {
        'User-Agent': this.options.userAgent,
        'Accept': 'text/html,application/xhtml+xml,application/xml;q=0.9,image/webp,*/*;q=0.8',
        'Accept-Language': 'en-US,en;q=0.9',
        'Accept-Encoding': 'gzip, deflate, br',
        'Connection': 'keep-alive',
        'Upgrade-Insecure-Requests': '1',
        'Sec-Fetch-Dest': 'document',
        'Sec-Fetch-Mode': 'navigate',
        'Sec-Fetch-Site': 'none',
        'Cache-Control': 'no-cache'
      }
    });
  }

  /**
   * Extract photos using static HTML parsing (the original working method)
   * @param url - VRBO property URL
   * @returns Promise<ScrapingResult<StaticPhotoExtractionResult>>
   */
  async extractPhotos(url: string): Promise<ScrapingResult<StaticPhotoExtractionResult>> {
    const startTime = Date.now();

    try {
      logger.info('Starting static photo extraction', { url }, 'STATIC_VRBO_PHOTO_EXTRACTOR');

      // Fetch the HTML page
      const response = await this.httpClient.get(url);
      const html = response.data;

      // Extract images using multiple strategies
      const staticImages = this.extractStaticImages(html);
      const lazyImages = this.extractLazyImages(html);
      const galleryImages = this.extractGalleryImages(html);
      const jsonLdImages = this.extractJsonLdImages(html);

      // Combine all images
      const allImages = [
        ...staticImages,
        ...lazyImages,
        ...galleryImages,
        ...jsonLdImages
      ];

      // Remove duplicates
      const uniqueImages = this.options.deduplicateImages ? 
        this.deduplicateImages(allImages) : allImages;

      // Filter by resolution preferences
      const filteredImages = this.filterImagesByResolution(uniqueImages);

      // Limit to max images
      const limitedImages = filteredImages.slice(0, this.options.maxImages);

      // Convert to PhotoData format
      const photos = this.convertToPhotoData(limitedImages);

      const processingTime = Date.now() - startTime;

      const result: StaticPhotoExtractionResult = {
        photos,
        extractionStats: {
          staticImages: staticImages.length,
          lazyImages: lazyImages.length,
          galleryImages: galleryImages.length,
          jsonLdImages: jsonLdImages.length,
          totalFound: allImages.length,
          totalProcessed: limitedImages.length,
          duplicatesRemoved: allImages.length - uniqueImages.length
        },
        sourceHtml: html,
        processingTime
      };

      logger.info('Static photo extraction completed', {
        url,
        totalPhotos: photos.length,
        staticImages: staticImages.length,
        lazyImages: lazyImages.length,
        galleryImages: galleryImages.length,
        jsonLdImages: jsonLdImages.length,
        processingTime
      }, 'STATIC_VRBO_PHOTO_EXTRACTOR');

      return {
        success: true,
        data: result,
        errors: [],
        metadata: {
          scrapedAt: new Date(),
          duration: processingTime,
          sourceUrl: url,
          userAgent: this.options.userAgent,
          rateLimited: false,
          dataCompleteness: Math.min(100, (photos.length / 20) * 100),
          fieldsScraped: ['photos'],
          fieldsFailed: []
        }
      };

    } catch (error) {
      const processingTime = Date.now() - startTime;
      const scrapingError: ScrapingError = {
        code: 'STATIC_PHOTO_EXTRACTION_FAILED',
        message: error instanceof Error ? error.message : 'Unknown error',
        severity: 'high',
        recoverable: true
      };

      logger.error('Static photo extraction failed', error, 'STATIC_VRBO_PHOTO_EXTRACTOR');

      return {
        success: false,
        errors: [scrapingError],
        metadata: {
          scrapedAt: new Date(),
          duration: processingTime,
          sourceUrl: url,
          userAgent: this.options.userAgent,
          rateLimited: false,
          dataCompleteness: 0,
          fieldsScraped: [],
          fieldsFailed: ['photos']
        }
      };
    }
  }

  /**
   * Extract static images from img tags (Method 1 - Basic)
   */
  private extractStaticImages(html: string): string[] {
    const images: string[] = [];

    // Enhanced img tag regex
    const imgRegex = /<img[^>]*src=["']([^"']+)["'][^>]*>/gi;
    let match;

    while ((match = imgRegex.exec(html)) !== null) {
      const imageUrl = match[1];
      if (this.isValidImageUrl(imageUrl)) {
        images.push(this.normalizeImageUrl(imageUrl));
      }
    }

    return images;
  }

  /**
   * Extract lazy-loaded images (Method 2 - Lazy Loading)
   */
  private extractLazyImages(html: string): string[] {
    const images: string[] = [];

    // Enhanced lazy loading patterns
    const lazyPatterns = [
      // Standard data-src patterns
      /<img[^>]*data-src=["']([^"']+)["'][^>]*>/gi,
      /<img[^>]*data-lazy-src=["']([^"']+)["'][^>]*>/gi,
      /<img[^>]*data-original=["']([^"']+)["'][^>]*>/gi,
      /<img[^>]*data-url=["']([^"']+)["'][^>]*>/gi,

      // VRBO-specific patterns
      /<img[^>]*data-hero-src=["']([^"']+)["'][^>]*>/gi,
      /<img[^>]*data-gallery-src=["']([^"']+)["'][^>]*>/gi,
      /<img[^>]*data-photo-src=["']([^"']+)["'][^>]*>/gi,

      // Srcset patterns
      /<img[^>]*srcset=["']([^"']+)["'][^>]*>/gi,

      // Background image patterns
      /style=["'][^"']*background-image:\s*url\(["']?([^"')]+)["']?\)/gi,

      // Picture element patterns
      /<source[^>]*srcset=["']([^"']+)["'][^>]*>/gi
    ];

    lazyPatterns.forEach(pattern => {
      let match;
      while ((match = pattern.exec(html)) !== null) {
        const imageUrl = match[1];
        if (this.isValidImageUrl(imageUrl)) {
          // Handle srcset (multiple images)
          if (imageUrl.includes(',')) {
            const srcsetImages = imageUrl.split(',').map(src => 
              src.trim().split(' ')[0] // Get URL, ignore size descriptor
            );
            srcsetImages.forEach(src => {
              if (this.isValidImageUrl(src)) {
                images.push(this.normalizeImageUrl(src));
              }
            });
          } else {
            images.push(this.normalizeImageUrl(imageUrl));
          }
        }
      }
    });

    return images;
  }

  /**
   * Extract gallery images from JavaScript objects (Method 3 - JS Variables)
   */
  private extractGalleryImages(html: string): string[] {
    const images: string[] = [];

    // Enhanced JavaScript image patterns
    const jsImagePatterns = [
      // Common JS variable patterns
      /images?\s*[:=]\s*\[([\s\S]*?)\]/gi,
      /photos?\s*[:=]\s*\[([\s\S]*?)\]/gi,
      /gallery\s*[:=]\s*\[([\s\S]*?)\]/gi,
      /imageUrls?\s*[:=]\s*\[([\s\S]*?)\]/gi,

      // VRBO-specific patterns
      /propertyPhotos?\s*[:=]\s*\[([\s\S]*?)\]/gi,
      /heroImages?\s*[:=]\s*\[([\s\S]*?)\]/gi,
      /galleryData\s*[:=]\s*\[([\s\S]*?)\]/gi,

      // Object property patterns
      /"images?"\s*:\s*\[([\s\S]*?)\]/gi,
      /"photos?"\s*:\s*\[([\s\S]*?)\]/gi,
      /"gallery"\s*:\s*\[([\s\S]*?)\]/gi,

      // Window object patterns
      /window\.images?\s*=\s*\[([\s\S]*?)\]/gi,
      /window\.photos?\s*=\s*\[([\s\S]*?)\]/gi
    ];

    jsImagePatterns.forEach(pattern => {
      let match;
      while ((match = pattern.exec(html)) !== null) {
        const imageArray = match[1];

        // Extract URLs from the array content
        const urlMatches = imageArray.match(/["']([^"']*\.(jpg|jpeg|png|webp|gif))["']/gi);

        if (urlMatches) {
          urlMatches.forEach(urlMatch => {
            const imageUrl = urlMatch.replace(/["']/g, '');
            if (this.isValidImageUrl(imageUrl)) {
              images.push(this.normalizeImageUrl(imageUrl));
            }
          });
        }
      }
    });

    return images;
  }

  /**
   * Extract images from JSON-LD structured data (Method 4 - Structured Data)
   */
  private extractJsonLdImages(html: string): string[] {
    const images: string[] = [];

    try {
      // More comprehensive JSON-LD extraction
      const jsonLdRegex = /<script[^>]*type=["']application\/ld\+json["'][^>]*>([\s\S]*?)<\/script>/gi;
      let match;

      while ((match = jsonLdRegex.exec(html)) !== null) {
        try {
          const jsonContent = match[1].trim();
          const data = JSON.parse(jsonContent);

          // Handle array of JSON-LD objects
          const jsonLdObjects = Array.isArray(data) ? data : [data];

          jsonLdObjects.forEach(obj => {
            // Extract images from various JSON-LD properties
            const imageProperties = [
              'image', 'photo', 'photos', 'images',
              'primaryImageOfPage', 'thumbnailUrl', 'contentUrl',
              'url', 'mainEntity', 'about'
            ];

            imageProperties.forEach(prop => {
              if (obj[prop]) {
                const imageData = Array.isArray(obj[prop]) ? obj[prop] : [obj[prop]];

                imageData.forEach((img: any) => {
                  let imageUrl: string;

                  if (typeof img === 'string') {
                    imageUrl = img;
                  } else if (img && typeof img === 'object') {
                    imageUrl = img.url || img.contentUrl || img.thumbnailUrl || img.src;
                  } else {
                    return;
                  }

                  if (this.isValidImageUrl(imageUrl)) {
                    images.push(this.normalizeImageUrl(imageUrl));
                  }
                });
              }
            });
          });
        } catch (e) {
          // Continue to next script tag
        }
      }
    } catch (error) {
      logger.warn('Failed to extract JSON-LD images', error, 'STATIC_VRBO_PHOTO_EXTRACTOR');
    }

    return images;
  }

  /**
   * Validate if URL is a valid image URL
   */
  private isValidImageUrl(url: string): boolean {
    if (!url || typeof url !== 'string') return false;

    // Check for valid image extensions
    const imageExtensions = /\.(jpg|jpeg|png|webp|gif|avif|svg)(\?|$)/i;
    if (!imageExtensions.test(url)) return false;

    // Check for valid URL format
    const urlPattern = /^https?:\/\/.+/i;
    if (!urlPattern.test(url)) return false;

    // Filter out unwanted image types
    const unwantedPatterns = [
      /pixel|tracking|analytics|beacon/i,
      /1x1|blank|empty|placeholder/i,
      /\.svg$/i, // Remove SVG icons
      /logo|icon|favicon/i,
      /spinner|loader|loading/i
    ];

    return !unwantedPatterns.some(pattern => pattern.test(url));
  }

  /**
   * Normalize image URL (convert relative to absolute, clean up)
   */
  private normalizeImageUrl(url: string): string {
    // Remove quotes and trim
    let normalizedUrl = url.replace(/["']/g, '').trim();

    // Convert protocol-relative URLs
    if (normalizedUrl.startsWith('//')) {
      normalizedUrl = 'https:' + normalizedUrl;
    }

    // Handle relative URLs (though rare in VRBO)
    if (normalizedUrl.startsWith('/')) {
      normalizedUrl = 'https://www.vrbo.com' + normalizedUrl;
    }

    // Clean up common URL issues
    normalizedUrl = normalizedUrl.replace(/&amp;/g, '&');

    return normalizedUrl;
  }

  /**
   * Remove duplicate images
   */
  private deduplicateImages(images: string[]): string[] {
    const seen = new Set<string>();
    const unique: string[] = [];

    images.forEach(img => {
      // Normalize for comparison (remove query params for deduplication)
      const normalizedForComparison = img.split('?')[0].toLowerCase();

      if (!seen.has(normalizedForComparison)) {
        seen.add(normalizedForComparison);
        unique.push(img);
      }
    });

    return unique;
  }

  /**
   * Filter images by resolution preferences
   */
  private filterImagesByResolution(images: string[]): string[] {
    const filtered: string[] = [];

    images.forEach(url => {
      const isThumbnail = this.isThumbnailUrl(url);

      if (this.options.includeHighRes && !isThumbnail) {
        filtered.push(url);
      } else if (this.options.includeThumbnails && isThumbnail) {
        filtered.push(url);
      }
    });

    // If no high-res images found, include thumbnails anyway
    if (filtered.length === 0 && images.length > 0) {
      return images;
    }

    return filtered;
  }

  /**
   * Check if URL is a thumbnail
   */
  private isThumbnailUrl(url: string): boolean {
    const thumbnailIndicators = [
      /thumb/i, /small/i, /preview/i, /mini/i,
      /_s\.|_t\.|_xs\.|_sm\./i,
      /150x|300x|400x/i,
      /w_150|w_300|w_400/i,
      /c_thumb|c_fill|c_fit/i,
      /resize.*200|resize.*150/i
    ];

    return thumbnailIndicators.some(indicator => indicator.test(url));
  }

  /**
   * Convert image URLs to PhotoData format
   */
  private convertToPhotoData(images: string[]): PhotoData[] {
    return images.map((url, index) => ({
      url,
      thumbnailUrl: this.isThumbnailUrl(url) ? url : undefined,
      alt: `Property photo ${index + 1}`,
      category: this.categorizeImageByUrl(url),
      order: index + 1,
      size: this.estimateImageSize(url)
    }));
  }

  /**
   * Categorize image by URL patterns
   */
  private categorizeImageByUrl(url: string): 'exterior' | 'interior' | 'kitchen' | 'bedroom' | 'bathroom' | 'living_area' | 'outdoor_space' | 'view' | 'amenity' {
    const lowerUrl = url.toLowerCase();

    if (lowerUrl.includes('exterior') || lowerUrl.includes('outside') || lowerUrl.includes('facade')) return 'exterior';
    if (lowerUrl.includes('kitchen') || lowerUrl.includes('dining')) return 'kitchen';
    if (lowerUrl.includes('bedroom') || lowerUrl.includes('bed')) return 'bedroom';
    if (lowerUrl.includes('bathroom') || lowerUrl.includes('bath')) return 'bathroom';
    if (lowerUrl.includes('living') || lowerUrl.includes('lounge') || lowerUrl.includes('family')) return 'living_area';
    if (lowerUrl.includes('pool') || lowerUrl.includes('deck') || lowerUrl.includes('patio') || lowerUrl.includes('yard')) return 'outdoor_space';
    if (lowerUrl.includes('view') || lowerUrl.includes('scenic') || lowerUrl.includes('landscape')) return 'view';
    if (lowerUrl.includes('amenity') || lowerUrl.includes('facility')) return 'amenity';

    return 'interior';
  }

  /**
   * Estimate image size from URL
   */
  private estimateImageSize(url: string): { width: number; height: number } {
    // Try to extract dimensions from URL
    const dimensionMatch = url.match(/(\d+)x(\d+)/);
    if (dimensionMatch) {
      return {
        width: parseInt(dimensionMatch[1]),
        height: parseInt(dimensionMatch[2])
      };
    }

    // Default sizes based on URL patterns
    if (this.isThumbnailUrl(url)) {
      return { width: 300, height: 200 };
    }

    return { width: 1200, height: 800 };
  }
}

// Export factory function
export const createStaticVRBOPhotoExtractor = (options?: Partial<StaticPhotoExtractionOptions>) => {
  return new StaticVRBOPhotoExtractor(options);
};

// Export convenience function
export const extractVRBOPhotosStatic = async (url: string, options?: Partial<StaticPhotoExtractionOptions>): Promise<PhotoData[]> => {
  const extractor = createStaticVRBOPhotoExtractor(options);
  const result = await extractor.extractPhotos(url);

  if (result.success) {
    return result.data!.photos;
  }

  return [];
};

// Enhanced VRBO Browser Scraper with Dynamic Image Loading
// Combines browser automation with comprehensive data extraction

import { ComprehensiveVRBOScraper } from './comprehensive-vrbo-scraper';
import { BrowserManager } from './browser-manager';
import { VRBOGalleryAutomation } from './vrbo-gallery-automation';
import { aiDecisionLogger } from '../ai/decision-logger';
import { logger } from '../../utils/logger';
import { errorReporter } from '../monitoring/error-reporter';
import type {
  ScrapingResult,
  ScrapingError,
  ScrapingMetadata,
  VRBOPropertyData,
  PhotoData,
  ScraperConfig
} from './types';

export interface BrowserScrapingOptions {
  useStaticFallback: boolean;
  enableScreenshots: boolean;
  screenshotPath: string;
  scrollCycles: number;
  scrollWaitTime: number;
  browserTimeout: number;
  enableStealth: boolean;
  headless: boolean;
}

export interface BrowserScrapingResult {
  propertyData: VRBOPropertyData;
  galleryImages: PhotoData[];
  staticImages: PhotoData[];
  totalImages: number;
  galleryLoadingResult: {
    scrollCyclesCompleted: number;
    loadingTime: number;
    errors: string[];
  };
  browserMetadata: {
    sessionId: string;
    userAgent: string;
    screenResolution: string;
    processingTime: number;
    memoryUsage: number;
  };
}

export class VRBOBrowserScraper extends ComprehensiveVRBOScraper {
  private browserManager: BrowserManager;
  private galleryAutomation: VRBOGalleryAutomation;
  private browserOptions: BrowserScrapingOptions;

  private defaultBrowserOptions: BrowserScrapingOptions = {
    useStaticFallback: true,
    enableScreenshots: false,
    screenshotPath: '/tmp/vrbo-screenshots',
    scrollCycles: 5,
    scrollWaitTime: 3000,
    browserTimeout: 120000,
    enableStealth: true,
    headless: true
  };

  constructor(config: Partial<ScraperConfig> = {}, browserOptions: Partial<BrowserScrapingOptions> = {}) {
    super(config);

    this.browserOptions = { ...this.defaultBrowserOptions, ...browserOptions };

    this.browserManager = new BrowserManager({
      headless: this.browserOptions.headless,
      timeout: this.browserOptions.browserTimeout,
      enableStealth: this.browserOptions.enableStealth,
      blockImages: false, // We need images for VRBO
      blockCSS: true // Block CSS for faster loading
    });

    this.galleryAutomation = new VRBOGalleryAutomation(this.browserManager, {
      scrollCycles: this.browserOptions.scrollCycles,
      scrollWaitTime: this.browserOptions.scrollWaitTime,
      enableScreenshots: this.browserOptions.enableScreenshots,
      screenshotPath: this.browserOptions.screenshotPath
    });
  }

  /**
   * Scrapes VRBO property with browser automation for complete image extraction
   * @param url - VRBO property URL
   * @param options - Browser scraping options
   * @returns Promise<ScrapingResult<BrowserScrapingResult>>
   */
  async scrapeWithBrowserAutomation(
    url: string,
    options: Partial<BrowserScrapingOptions> = {}
  ): Promise<ScrapingResult<BrowserScrapingResult>> {
    const finalOptions = { ...this.browserOptions, ...options };
    const startTime = Date.now();
    const errors: ScrapingError[] = [];

    const metadata: ScrapingMetadata = {
      scrapedAt: new Date(),
      duration: 0,
      sourceUrl: url,
      userAgent: this.browserManager.config?.userAgent || 'Unknown',
      rateLimited: false,
      dataCompleteness: 0,
      fieldsScraped: [],
      fieldsFailed: []
    };

    let browserSession: any = null;
    let sessionId = '';

    try {
      // Log browser scraping start
      await aiDecisionLogger.logSimpleDecision(
        `Starting browser-based VRBO scraping: ${url}`,
        'browser_scraping',
        `Using Puppeteer automation with ${finalOptions.scrollCycles} scroll cycles`,
        [url],
        'high'
      );

      // Step 1: Create browser session
      logger.info('Creating browser session for VRBO scraping', { url }, 'VRBO_BROWSER_SCRAPER');
      browserSession = await this.browserManager.createSession();
      sessionId = browserSession.sessionId;

      // Step 2: Load property data using browser automation
      const galleryResult = await this.galleryAutomation.loadAllGalleryImages(browserSession.page, url);

      // Step 3: Extract additional data from the loaded page
      const pageData = await this.extractPageData(browserSession.page, url);

      // Step 4: Combine with static scraping if enabled
      let staticImages: PhotoData[] = [];
      let staticPropertyData: VRBOPropertyData | null = null;

      if (finalOptions.useStaticFallback) {
        try {
          const staticResult = await this.scrapeComprehensiveProperty(url, {
            includeImages: true,
            includeAdvancedAmenities: true,
            includeDetailedDescriptions: true,
            includeRoomData: true,
            maxImages: 100
          });

          if (staticResult.success) {
            staticImages = staticResult.data!.images;
            staticPropertyData = staticResult.data!.propertyData;
          }
        } catch (staticError) {
          logger.warn('Static scraping fallback failed', staticError, 'VRBO_BROWSER_SCRAPER');
        }
      }

      // Step 5: Merge browser and static data
      const mergedData = this.mergeBrowserAndStaticData(
        pageData,
        staticPropertyData,
        galleryResult.images,
        staticImages
      );

      // Step 6: Calculate browser metadata
      const browserMetadata = await this.calculateBrowserMetadata(browserSession, startTime);

      // Step 7: Create final result
      const totalImages = mergedData.images.length;
      const result: BrowserScrapingResult = {
        propertyData: mergedData.propertyData,
        galleryImages: galleryResult.images,
        staticImages: staticImages,
        totalImages,
        galleryLoadingResult: {
          scrollCyclesCompleted: galleryResult.scrollCyclesCompleted,
          loadingTime: galleryResult.loadingTime,
          errors: galleryResult.errors
        },
        browserMetadata
      };

      // Calculate final metadata
      metadata.duration = Date.now() - startTime;
      metadata.dataCompleteness = this.calculateBrowserDataCompleteness(result);
      metadata.fieldsScraped = ['browser_images', 'static_images', 'property_data'];

      if (galleryResult.errors.length > 0) {
        metadata.fieldsFailed.push('gallery_automation');
        galleryResult.errors.forEach(error => {
          errors.push({
            code: 'GALLERY_AUTOMATION_ERROR',
            message: error,
            severity: 'medium',
            recoverable: true
          });
        });
      }

      logger.info('Browser-based VRBO scraping completed successfully', {
        url,
        totalImages,
        galleryImages: galleryResult.images.length,
        staticImages: staticImages.length,
        scrollCycles: galleryResult.scrollCyclesCompleted,
        processingTime: metadata.duration,
        dataCompleteness: metadata.dataCompleteness
      }, 'VRBO_BROWSER_SCRAPER');

      return {
        success: true,
        data: result,
        errors,
        metadata
      };

    } catch (error) {
      const scrapingError: ScrapingError = {
        code: 'BROWSER_SCRAPING_FAILED',
        message: error instanceof Error ? error.message : 'Unknown browser scraping error',
        severity: 'high',
        recoverable: finalOptions.useStaticFallback
      };

      errors.push(scrapingError);
      metadata.duration = Date.now() - startTime;
      metadata.fieldsFailed = ['browser_scraping'];

      logger.error('Browser-based VRBO scraping failed', error, 'VRBO_BROWSER_SCRAPER');

      errorReporter.reportError(error as Error, {
        context: 'VRBO_BROWSER_SCRAPER',
        url,
        sessionId,
        metadata
      });

      // Try static fallback if enabled
      if (finalOptions.useStaticFallback) {
        try {
          logger.info('Attempting static fallback scraping', { url }, 'VRBO_BROWSER_SCRAPER');
          const fallbackResult = await this.scrapeComprehensiveProperty(url);

          if (fallbackResult.success) {
            const browserMetadata = {
              sessionId: sessionId || 'failed',
              userAgent: this.browserManager.config?.userAgent || 'Unknown',
              screenResolution: 'Unknown',
              processingTime: Date.now() - startTime,
              memoryUsage: 0
            };

            const result: BrowserScrapingResult = {
              propertyData: fallbackResult.data!.propertyData,
              galleryImages: [],
              staticImages: fallbackResult.data!.images,
              totalImages: fallbackResult.data!.images.length,
              galleryLoadingResult: {
                scrollCyclesCompleted: 0,
                loadingTime: 0,
                errors: ['Browser automation failed, used static fallback']
              },
              browserMetadata
            };

            metadata.duration = Date.now() - startTime;
            metadata.dataCompleteness = 60; // Lower score for fallback
            metadata.fieldsScraped = ['static_fallback'];

            logger.info('Static fallback scraping succeeded', {
              url,
              totalImages: result.totalImages
            }, 'VRBO_BROWSER_SCRAPER');

            return {
              success: true,
              data: result,
              errors,
              metadata
            };
          }
        } catch (fallbackError) {
          logger.error('Static fallback also failed', fallbackError, 'VRBO_BROWSER_SCRAPER');
        }
      }

      return {
        success: false,
        errors,
        metadata
      };

    } finally {
      // Clean up browser session
      if (sessionId) {
        try {
          await this.browserManager.closeSession(sessionId);
        } catch (cleanupError) {
          logger.error('Error cleaning up browser session', cleanupError, 'VRBO_BROWSER_SCRAPER');
        }
      }
    }
  }

  /**
   * Extracts property data from the browser-loaded page
   * @param page - Puppeteer page instance
   * @param url - Original URL
   * @returns Promise<VRBOPropertyData>
   */
  private async extractPageData(page: any, url: string): Promise<VRBOPropertyData> {
    const html = await page.content();
    const propertyId = this.extractPropertyId(url) || 'unknown';

    // Use existing data extraction methods on the browser-loaded HTML
    const extractedData = await this.extractPropertyData(html, url, propertyId);

    return extractedData;
  }

  /**
   * Merges browser automation data with static scraping data
   * @param browserData - Data from browser automation
   * @param staticData - Data from static scraping
   * @param galleryImages - Images from gallery automation
   * @param staticImages - Images from static scraping
   * @returns Combined data
   */
  private mergeBrowserAndStaticData(
    browserData: VRBOPropertyData,
    staticData: VRBOPropertyData | null,
    galleryImages: PhotoData[],
    staticImages: PhotoData[]
  ): { propertyData: VRBOPropertyData; images: PhotoData[] } {
    // Merge property data, prioritizing browser data
    const mergedPropertyData: VRBOPropertyData = {
      ...browserData,
      // Enhance with static data if available
      ...(staticData ? {
        amenities: this.mergeAmenities(browserData.amenities || [], staticData.amenities || []),
        rooms: staticData.rooms && staticData.rooms.length > 0 ? staticData.rooms : browserData.rooms,
        description: browserData.description || staticData.description,
        specifications: {
          ...browserData.specifications,
          ...staticData.specifications
        }
      } : {})
    };

    // Merge images, prioritizing gallery images
    const allImages = [...galleryImages, ...staticImages];
    const uniqueImages = this.deduplicateImages(allImages);

    // Update property data with merged images
    mergedPropertyData.photos = uniqueImages;

    return {
      propertyData: mergedPropertyData,
      images: uniqueImages
    };
  }

  /**
   * Calculates browser-specific metadata
   * @param browserSession - Browser session
   * @param startTime - Start time
   * @returns Browser metadata
   */
  private async calculateBrowserMetadata(browserSession: any, startTime: number): Promise<BrowserScrapingResult['browserMetadata']> {
    try {
      const viewport = await browserSession.page.viewport();
      const metrics = await browserSession.page.metrics();

      return {
        sessionId: browserSession.sessionId,
        userAgent: await browserSession.page.evaluate(() => navigator.userAgent),
        screenResolution: `${viewport.width}x${viewport.height}`,
        processingTime: Date.now() - startTime,
        memoryUsage: metrics.JSHeapUsedSize || 0
      };
    } catch (error) {
      logger.warn('Failed to calculate browser metadata', error, 'VRBO_BROWSER_SCRAPER');
      return {
        sessionId: browserSession?.sessionId || 'unknown',
        userAgent: 'Unknown',
        screenResolution: 'Unknown',
        processingTime: Date.now() - startTime,
        memoryUsage: 0
      };
    }
  }

  /**
   * Calculates data completeness score for browser scraping
   * @param result - Browser scraping result
   * @returns Completeness score (0-100)
   */
  private calculateBrowserDataCompleteness(result: BrowserScrapingResult): number {
    let score = 0;

    // Base property data (40%)
    if (result.propertyData.title) score += 10;
    if (result.propertyData.description) score += 10;
    if (result.propertyData.amenities && result.propertyData.amenities.length > 0) score += 10;
    if (result.propertyData.specifications?.bedrooms) score += 5;
    if (result.propertyData.specifications?.bathrooms) score += 5;

    // Image extraction (50%)
    if (result.totalImages > 0) score += 20;
    if (result.totalImages >= 10) score += 10;
    if (result.totalImages >= 20) score += 10;
    if (result.galleryImages.length > 0) score += 10; // Bonus for gallery images

    // Automation success (10%)
    if (result.galleryLoadingResult.scrollCyclesCompleted > 0) score += 5;
    if (result.galleryLoadingResult.errors.length === 0) score += 5;

    return Math.round(Math.min(score, 100));
  }

  /**
   * Utility methods
   */
  private mergeAmenities(browserAmenities: any[], staticAmenities: any[]): any[] {
    const amenityMap = new Map<string, any>();

    // Add browser amenities first
    browserAmenities.forEach(amenity => {
      amenityMap.set(amenity.name.toLowerCase(), amenity);
    });

    // Add static amenities, avoiding duplicates
    staticAmenities.forEach(amenity => {
      if (!amenityMap.has(amenity.name.toLowerCase())) {
        amenityMap.set(amenity.name.toLowerCase(), amenity);
      }
    });

    return Array.from(amenityMap.values());
  }

  private deduplicateImages(images: PhotoData[]): PhotoData[] {
    const seen = new Set<string>();
    const unique: PhotoData[] = [];

    images.forEach(img => {
      const normalizedUrl = img.url.replace(/\?.*$/, '').toLowerCase();

      if (!seen.has(normalizedUrl)) {
        seen.add(normalizedUrl);
        unique.push(img);
      }
    });

    return unique;
  }

  /**
   * Cleanup method
   */
  async cleanup(): Promise<void> {
    await this.browserManager.closeAllSessions();
  }
}

// Export factory function
export const createVRBOBrowserScraper = (
  config?: Partial<ScraperConfig>,
  browserOptions?: Partial<BrowserScrapingOptions>
): VRBOBrowserScraper => {
  return new VRBOBrowserScraper(config, browserOptions);
};

// Export convenience function for direct browser scraping
export const scrapeBrowserVRBOProperty = async (
  url: string,
  options: Partial<BrowserScrapingOptions> = {}
): Promise<ScrapingResult<BrowserScrapingResult>> => {
  const scraper = createVRBOBrowserScraper();

  try {
    const result = await scraper.scrapeWithBrowserAutomation(url, options);
    return result;
  } finally {
    await scraper.cleanup();
  }
};

// VRBO Data Extractor - Phase 3: Comprehensive Data Extraction
// Handles amenity parsing, detailed descriptions, and room-by-room data extraction

import { ProductionVRBOScraper } from './production-vrbo-scraper';
import { aiDecisionLogger } from '../ai/decision-logger';
import { logger } from '../../utils/logger';
import type { 
  ScrapingResult, 
  PropertyAmenity, 
  PropertyRoom, 
  VRBOPropertyData,
  AmenityCategory,
  RoomType,
  PropertySpecifications,
  PropertyLocation,
  ScrapingMetadata,
  ScrapingError
} from './types';

interface AmenityExtractionOptions {
  includeHidden: boolean;
  expandCollapsed: boolean;
  categorizeByRoom: boolean;
  verifyWithAI: boolean;
  extractDescriptions: boolean;
}

interface DescriptionExtractionOptions {
  includeFormatting: boolean;
  extractHighlights: boolean;
  parseStructuredData: boolean;
  maxLength: number;
}

interface RoomExtractionOptions {
  detectRoomTypes: boolean;
  extractDimensions: boolean;
  includeFeatures: boolean;
  linkToAmenities: boolean;
}

interface DataExtractionResult {
  amenities: PropertyAmenity[];
  descriptions: {
    main: string;
    highlights: string[];
    structured: Record<string, string>;
  };
  rooms: PropertyRoom[];
  specifications: PropertySpecifications;
  location: PropertyLocation;
  extractionStats: {
    amenitiesFound: number;
    roomsDetected: number;
    descriptionsExtracted: number;
    hiddenDataRevealed: number;
  };
}

export class VRBODataExtractor extends ProductionVRBOScraper {
  private defaultAmenityOptions: AmenityExtractionOptions = {
    includeHidden: true,
    expandCollapsed: true,
    categorizeByRoom: true,
    verifyWithAI: false,
    extractDescriptions: true
  };

  private defaultDescriptionOptions: DescriptionExtractionOptions = {
    includeFormatting: true,
    extractHighlights: true,
    parseStructuredData: true,
    maxLength: 5000
  };

  private defaultRoomOptions: RoomExtractionOptions = {
    detectRoomTypes: true,
    extractDimensions: true,
    includeFeatures: true,
    linkToAmenities: true
  };

  /**
   * Comprehensive data extraction from VRBO property page
   * @param url - VRBO property URL
   * @param options - Extraction options
   * @returns Promise<ScrapingResult<DataExtractionResult>>
   */
  async extractComprehensiveData(
    url: string, 
    amenityOptions: Partial<AmenityExtractionOptions> = {},
    descriptionOptions: Partial<DescriptionExtractionOptions> = {},
    roomOptions: Partial<RoomExtractionOptions> = {}
  ): Promise<ScrapingResult<DataExtractionResult>> {
    const finalAmenityOptions = { ...this.defaultAmenityOptions, ...amenityOptions };
    const finalDescriptionOptions = { ...this.defaultDescriptionOptions, ...descriptionOptions };
    const finalRoomOptions = { ...this.defaultRoomOptions, ...roomOptions };
    const startTime = Date.now();

    const metadata: ScrapingMetadata = {
      scrapedAt: new Date(),
      duration: 0,
      sourceUrl: url,
      userAgent: this.httpClient.defaults.headers['User-Agent'] as string,
      rateLimited: false,
      dataCompleteness: 0,
      fieldsScraped: [],
      fieldsFailed: []
    };

    try {
      // Log comprehensive data extraction start
      await aiDecisionLogger.logSimpleDecision(
        `Starting comprehensive VRBO data extraction: ${url}`,
        'data_extraction',
        `Extracting amenities, descriptions, and room data with advanced parsing`,
        [url],
        'high'
      );

      // Validate URL
      if (!this.isValidVRBOUrl(url)) {
        throw new Error('Invalid VRBO URL provided');
      }

      // Fetch the main property page
      const html = await this.fetchMainPropertyPage(url);

      // Extract amenities with advanced parsing
      const amenities = await this.extractAdvancedAmenities(html, finalAmenityOptions);

      // Extract detailed descriptions
      const descriptions = await this.extractDetailedDescriptions(html, finalDescriptionOptions);

      // Extract room-by-room data
      const rooms = await this.extractRoomData(html, finalRoomOptions);

      // Extract enhanced specifications
      const specifications = await this.extractEnhancedSpecifications(html, amenities, rooms);

      // Extract detailed location data
      const location = await this.extractDetailedLocation(html);

      // Calculate extraction statistics
      const extractionStats = {
        amenitiesFound: amenities.length,
        roomsDetected: rooms.length,
        descriptionsExtracted: Object.keys(descriptions.structured).length + (descriptions.main ? 1 : 0),
        hiddenDataRevealed: this.calculateHiddenDataCount(html, amenities, descriptions)
      };

      const result: DataExtractionResult = {
        amenities,
        descriptions,
        rooms,
        specifications,
        location,
        extractionStats
      };

      // Calculate metadata
      metadata.duration = Date.now() - startTime;
      metadata.dataCompleteness = this.calculateComprehensiveDataCompleteness(result);
      metadata.fieldsScraped = ['amenities', 'descriptions', 'rooms', 'specifications', 'location'];

      logger.info('VRBO comprehensive data extraction completed successfully', {
        amenitiesFound: extractionStats.amenitiesFound,
        roomsDetected: extractionStats.roomsDetected,
        dataCompleteness: metadata.dataCompleteness,
        duration: metadata.duration
      }, 'VRBO_DATA_EXTRACTOR');

      return {
        success: true,
        data: result,
        errors: [],
        metadata
      };

    } catch (error) {
      const scrapingError: ScrapingError = {
        code: 'DATA_EXTRACTION_FAILED',
        message: error instanceof Error ? error.message : 'Unknown data extraction error',
        severity: 'high',
        recoverable: true
      };

      metadata.duration = Date.now() - startTime;
      metadata.fieldsFailed = ['comprehensive_data'];

      logger.error('VRBO comprehensive data extraction failed', error, 'VRBO_DATA_EXTRACTOR');

      return {
        success: false,
        errors: [scrapingError],
        metadata
      };
    }
  }

  /**
   * Extracts amenities with advanced parsing including hidden/collapsed sections
   * @param html - HTML content
   * @param options - Amenity extraction options
   * @returns Promise<PropertyAmenity[]>
   */
  private async extractAdvancedAmenities(html: string, options: AmenityExtractionOptions): Promise<PropertyAmenity[]> {
    const amenities: PropertyAmenity[] = [];
    const foundAmenityNames = new Set<string>();

    // Extract from structured JSON-LD data first
    const jsonLdAmenities = this.extractJsonLdAmenities(html);
    amenities.push(...jsonLdAmenities);
    jsonLdAmenities.forEach(amenity => foundAmenityNames.add(amenity.name.toLowerCase()));

    // Extract from visible amenity lists
    const visibleAmenities = this.extractVisibleAmenities(html);
    visibleAmenities.forEach(amenity => {
      if (!foundAmenityNames.has(amenity.name.toLowerCase())) {
        amenities.push(amenity);
        foundAmenityNames.add(amenity.name.toLowerCase());
      }
    });

    // Extract from collapsed/hidden sections if enabled
    if (options.includeHidden) {
      const hiddenAmenities = this.extractHiddenAmenities(html);
      hiddenAmenities.forEach(amenity => {
        if (!foundAmenityNames.has(amenity.name.toLowerCase())) {
          amenities.push(amenity);
          foundAmenityNames.add(amenity.name.toLowerCase());
        }
      });
    }

    // Extract from data attributes and JavaScript
    const dataAttributeAmenities = this.extractDataAttributeAmenities(html);
    dataAttributeAmenities.forEach(amenity => {
      if (!foundAmenityNames.has(amenity.name.toLowerCase())) {
        amenities.push(amenity);
        foundAmenityNames.add(amenity.name.toLowerCase());
      }
    });

    // Categorize amenities by room if enabled
    if (options.categorizeByRoom) {
      return this.categorizeAmenitiesByRoom(amenities, html);
    }

    return amenities;
  }

  /**
   * Extracts amenities from JSON-LD structured data
   * @param html - HTML content
   * @returns PropertyAmenity[]
   */
  private extractJsonLdAmenities(html: string): PropertyAmenity[] {
    const amenities: PropertyAmenity[] = [];

    try {
      const jsonLdRegex = /<script[^>]*type=[\"']application\/ld\+json[\"'][^>]*>(.*?)<\/script>/gis;
      const matches = html.match(jsonLdRegex);

      if (matches) {
        matches.forEach(match => {
          try {
            const jsonContent = match.replace(/<script[^>]*>/i, '').replace(/<\/script>/i, '');
            const data = JSON.parse(jsonContent);

            // Look for amenity features in various JSON-LD properties
            const amenityProperties = ['amenityFeature', 'features', 'additionalProperty'];

            amenityProperties.forEach(prop => {
              if (data[prop]) {
                const features = Array.isArray(data[prop]) ? data[prop] : [data[prop]];

                features.forEach((feature: any) => {
                  let amenityName: string;

                  if (typeof feature === 'string') {
                    amenityName = feature;
                  } else if (feature.name) {
                    amenityName = feature.name;
                  } else if (feature.value) {
                    amenityName = feature.value;
                  } else {
                    return;
                  }

                  amenities.push({
                    name: this.cleanAmenityName(amenityName),
                    verified: true,
                    category: this.categorizeAmenity(amenityName),
                    priority: this.prioritizeAmenity(amenityName),
                    description: feature.description || undefined
                  });
                });
              }
            });
          } catch (e) {
            // Continue to next script tag
          }
        });
      }
    } catch (error) {
      logger.warn('Failed to extract JSON-LD amenities', error, 'VRBO_DATA_EXTRACTOR');
    }

    return amenities;
  }

  /**
   * Extracts amenities from visible UI elements
   * @param html - HTML content
   * @returns PropertyAmenity[]
   */
  private extractVisibleAmenities(html: string): PropertyAmenity[] {
    const amenities: PropertyAmenity[] = [];

    // Common patterns for amenity lists
    const amenityPatterns = [
      // List items with amenity-related classes
      /<li[^>]*class="[^"]*amenity[^"]*"[^>]*>([^<]+)</gi,
      /<li[^>]*class="[^"]*feature[^"]*"[^>]*>([^<]+)</gi,
      /<div[^>]*class="[^"]*amenity[^"]*"[^>]*>([^<]+)<\/div>/gi,
      /<span[^>]*class="[^"]*amenity[^"]*"[^>]*>([^<]+)<\/span>/gi,

      // Data attributes
      /data-amenity="([^"]+)"/gi,
      /data-feature="([^"]+)"/gi,

      // Icon-based amenity detection
      /<i[^>]*class="[^"]*(?:wifi|pool|parking|kitchen|gym|spa)[^"]*"[^>]*><\/i>[^<]*<[^>]*>([^<]+)/gi
    ];

    amenityPatterns.forEach(pattern => {
      let match;
      while ((match = pattern.exec(html)) !== null) {
        const amenityText = this.cleanAmenityName(match[1]);
        if (amenityText && amenityText.length > 2 && amenityText.length < 100) {
          amenities.push({
            name: amenityText,
            verified: false,
            category: this.categorizeAmenity(amenityText),
            priority: this.prioritizeAmenity(amenityText)
          });
        }
      }
    });

    return amenities;
  }

  /**
   * Extracts amenities from hidden/collapsed sections
   * @param html - HTML content
   * @returns PropertyAmenity[]
   */
  private extractHiddenAmenities(html: string): PropertyAmenity[] {
    const amenities: PropertyAmenity[] = [];

    // Look for hidden content sections
    const hiddenPatterns = [
      // Hidden divs with display:none or similar
      /<div[^>]*style="[^"]*display:\s*none[^"]*"[^>]*>(.*?)<\/div>/gis,
      /<div[^>]*class="[^"]*hidden[^"]*"[^>]*>(.*?)<\/div>/gis,
      /<div[^>]*class="[^"]*collapse[^"]*"[^>]*>(.*?)<\/div>/gis,

      // Expandable sections
      /<div[^>]*class="[^"]*expandable[^"]*"[^>]*>(.*?)<\/div>/gis,
      /<div[^>]*data-toggle="collapse"[^>]*>(.*?)<\/div>/gis,

      // Modal content
      /<div[^>]*class="[^"]*modal[^"]*"[^>]*>(.*?)<\/div>/gis
    ];

    hiddenPatterns.forEach(pattern => {
      let match;
      while ((match = pattern.exec(html)) !== null) {
        const hiddenContent = match[1];

        // Extract amenities from hidden content using standard patterns
        const hiddenAmenities = this.extractVisibleAmenities(hiddenContent);
        amenities.push(...hiddenAmenities);
      }
    });

    return amenities;
  }

  /**
   * Extracts amenities from data attributes and JavaScript variables
   * @param html - HTML content
   * @returns PropertyAmenity[]
   */
  private extractDataAttributeAmenities(html: string): PropertyAmenity[] {
    const amenities: PropertyAmenity[] = [];

    // Extract from JavaScript configuration objects
    const jsPatterns = [
      /amenities\s*:\s*\[([^\]]+)\]/gi,
      /features\s*:\s*\[([^\]]+)\]/gi,
      /"amenities":\s*\[([^\]]+)\]/gi,
      /"features":\s*\[([^\]]+)\]/gi
    ];

    jsPatterns.forEach(pattern => {
      let match;
      while ((match = pattern.exec(html)) !== null) {
        const amenityArray = match[1];

        // Extract quoted strings from the array
        const quotedStrings = amenityArray.match(/"([^"]+)"/g) || [];
        quotedStrings.forEach(quoted => {
          const amenityName = quoted.replace(/"/g, '');
          if (amenityName && amenityName.length > 2) {
            amenities.push({
              name: this.cleanAmenityName(amenityName),
              verified: true,
              category: this.categorizeAmenity(amenityName),
              priority: this.prioritizeAmenity(amenityName)
            });
          }
        });
      }
    });

    return amenities;
  }

  /**
   * Categorizes amenities by room type
   * @param amenities - Array of amenities
   * @param html - HTML content for context
   * @returns PropertyAmenity[]
   */
  private categorizeAmenitiesByRoom(amenities: PropertyAmenity[], html: string): PropertyAmenity[] {
    return amenities.map(amenity => {
      const roomContext = this.detectAmenityRoomContext(amenity.name, html);
      return {
        ...amenity,
        roomType: roomContext
      };
    });
  }

  /**
   * Detects which room an amenity belongs to based on context
   * @param amenityName - Name of the amenity
   * @param html - HTML content for context analysis
   * @returns RoomType | undefined
   */
  private detectAmenityRoomContext(amenityName: string, html: string): RoomType | undefined {
    const name = amenityName.toLowerCase();

    // Direct room indicators in amenity name
    if (name.includes('kitchen') || name.includes('cooking') || name.includes('fridge') || name.includes('microwave')) {
      return 'kitchen';
    }
    if (name.includes('bedroom') || name.includes('bed ') || name.includes('mattress')) {
      return 'bedroom';
    }
    if (name.includes('bathroom') || name.includes('shower') || name.includes('bathtub')) {
      return 'bathroom';
    }
    if (name.includes('living') || name.includes('sofa') || name.includes('couch')) {
      return 'living_room';
    }
    if (name.includes('dining') || name.includes('table')) {
      return 'dining_room';
    }
    if (name.includes('office') || name.includes('desk') || name.includes('workspace')) {
      return 'office';
    }
    if (name.includes('game') || name.includes('entertainment') || name.includes('pool table')) {
      return 'game_room';
    }
    if (name.includes('balcony') || name.includes('terrace')) {
      return 'balcony';
    }
    if (name.includes('patio') || name.includes('deck') || name.includes('outdoor dining')) {
      return 'patio';
    }
    if (name.includes('garage') || name.includes('car') || name.includes('parking')) {
      return 'garage';
    }

    // Look for context clues in surrounding HTML
    const amenityPattern = new RegExp(amenityName.replace(/[.*+?^${}()|[\]\\]/g, '\\$&'), 'i');
    const amenityMatch = html.search(amenityPattern);

    if (amenityMatch !== -1) {
      const contextStart = Math.max(0, amenityMatch - 500);
      const contextEnd = Math.min(html.length, amenityMatch + 500);
      const context = html.slice(contextStart, contextEnd).toLowerCase();

      if (context.includes('kitchen')) return 'kitchen';
      if (context.includes('bedroom')) return 'bedroom';
      if (context.includes('bathroom')) return 'bathroom';
      if (context.includes('living')) return 'living_room';
      if (context.includes('dining')) return 'dining_room';
    }

    return undefined;
  }

  /**
   * Extracts detailed property descriptions with formatting
   * @param html - HTML content
   * @param options - Description extraction options
   * @returns Object with main description, highlights, and structured data
   */
  private async extractDetailedDescriptions(html: string, options: DescriptionExtractionOptions): Promise<{
    main: string;
    highlights: string[];
    structured: Record<string, string>;
  }> {
    const descriptions = {
      main: '',
      highlights: [] as string[],
      structured: {} as Record<string, string>
    };

    // Extract main description from common patterns
    const descriptionPatterns = [
      // Meta description
      /<meta[^>]*name=["']description["'][^>]*content=["']([^"']+)/i,
      // Open Graph description
      /<meta[^>]*property=["']og:description["'][^>]*content=["']([^"']+)/i,
      // Structured description sections
      /<div[^>]*class="[^"]*description[^"]*"[^>]*>(.*?)<\/div>/gis,
      /<section[^>]*class="[^"]*description[^"]*"[^>]*>(.*?)<\/section>/gis,
      // Property overview sections
      /<div[^>]*class="[^"]*overview[^"]*"[^>]*>(.*?)<\/div>/gis,
      /<div[^>]*class="[^"]*summary[^"]*"[^>]*>(.*?)<\/div>/gis
    ];

    for (const pattern of descriptionPatterns) {
      const match = html.match(pattern);
      if (match && match[1]) {
        let description = match[1];

        if (options.includeFormatting) {
          description = this.preserveDescriptionFormatting(description);
        } else {
          description = this.cleanText(description);
        }

        if (description.length > descriptions.main.length && description.length <= options.maxLength) {
          descriptions.main = description;
        }
      }
    }

    // Extract highlights/bullet points
    if (options.extractHighlights) {
      descriptions.highlights = this.extractDescriptionHighlights(html);
    }

    // Extract structured data sections
    if (options.parseStructuredData) {
      descriptions.structured = this.extractStructuredDescriptions(html);
    }

    return descriptions;
  }

  /**
   * Preserves formatting in descriptions while cleaning HTML
   * @param description - Raw description HTML
   * @returns Formatted description text
   */
  private preserveDescriptionFormatting(description: string): string {
    return description
      .replace(/<br\s*\/?>/gi, '\n')
      .replace(/<\/p>/gi, '\n\n')
      .replace(/<li[^>]*>/gi, '‚Ä¢ ')
      .replace(/<\/li>/gi, '\n')
      .replace(/<[^>]+>/g, '')
      .replace(/&nbsp;/g, ' ')
      .replace(/&amp;/g, '&')
      .replace(/&lt;/g, '<')
      .replace(/&gt;/g, '>')
      .replace(/&quot;/g, '"')
      .replace(/&#39;/g, "'")
      .replace(/\n\s*\n\s*\n/g, '\n\n')
      .trim();
  }

  /**
   * Extracts highlight points from descriptions
   * @param html - HTML content
   * @returns Array of highlight strings
   */
  private extractDescriptionHighlights(html: string): string[] {
    const highlights: string[] = [];

    // Look for bullet point lists
    const listPatterns = [
      /<ul[^>]*>(.*?)<\/ul>/gis,
      /<ol[^>]*>(.*?)<\/ol>/gis
    ];

    listPatterns.forEach(pattern => {
      let match;
      while ((match = pattern.exec(html)) !== null) {
        const listContent = match[1];
        const listItems = listContent.match(/<li[^>]*>(.*?)<\/li>/gis) || [];

        listItems.forEach(item => {
          const cleanItem = this.cleanText(item.replace(/<li[^>]*>|<\/li>/gi, ''));
          if (cleanItem && cleanItem.length > 10 && cleanItem.length < 200) {
            highlights.push(cleanItem);
          }
        });
      }
    });

    return highlights.slice(0, 20); // Limit to 20 highlights
  }

  /**
   * Extracts structured description sections
   * @param html - HTML content
   * @returns Object with structured description data
   */
  private extractStructuredDescriptions(html: string): Record<string, string> {
    const structured: Record<string, string> = {};

    // Look for labeled sections
    const sectionPatterns = [
      // Sections with headers
      /<h[1-6][^>]*>([^<]+)<\/h[1-6]>[^<]*<[^>]*>([^<]+)/gi,
      // Definition lists
      /<dt[^>]*>([^<]+)<\/dt>[^<]*<dd[^>]*>([^<]+)<\/dd>/gi,
      // Labeled divs
      /<div[^>]*class="[^"]*label[^"]*"[^>]*>([^<]+)<\/div>[^<]*<div[^>]*>([^<]+)<\/div>/gi
    ];

    sectionPatterns.forEach(pattern => {
      let match;
      while ((match = pattern.exec(html)) !== null) {
        const label = this.cleanText(match[1]);
        const content = this.cleanText(match[2]);

        if (label && content && label.length < 100 && content.length < 1000) {
          structured[label] = content;
        }
      }
    });

    return structured;
  }

  /**
   * Extracts room-by-room data from the property page
   * @param html - HTML content
   * @param options - Room extraction options
   * @returns PropertyRoom[]
   */
  private async extractRoomData(html: string, options: RoomExtractionOptions): Promise<PropertyRoom[]> {
    const rooms: PropertyRoom[] = [];

    // Extract from structured data first
    const jsonLdRooms = this.extractJsonLdRooms(html);
    rooms.push(...jsonLdRooms);

    // Extract from description text patterns
    const descriptionRooms = this.extractRoomsFromDescription(html);
    rooms.push(...descriptionRooms);

    // Extract from explicit room sections
    const sectionRooms = this.extractRoomsFromSections(html);
    rooms.push(...sectionRooms);

    // Deduplicate and enhance rooms
    const uniqueRooms = this.deduplicateRooms(rooms);

    if (options.extractDimensions) {
      return this.enhanceRoomsWithDimensions(uniqueRooms, html);
    }

    return uniqueRooms;
  }

  /**
   * Utility methods for amenity processing
   */
  private cleanAmenityName(name: string): string {
    return name
      .replace(/[^\w\s\-\/&]/g, '')
      .replace(/\s+/g, ' ')
      .trim();
  }

  private prioritizeAmenity(amenityName: string): 'essential' | 'important' | 'nice_to_have' {
    const name = amenityName.toLowerCase();

    // Essential amenities
    if (name.includes('wifi') || name.includes('internet') || 
        name.includes('kitchen') || name.includes('parking') ||
        name.includes('heating') || name.includes('air conditioning')) {
      return 'essential';
    }

    // Important amenities
    if (name.includes('pool') || name.includes('hot tub') || 
        name.includes('washer') || name.includes('dryer') ||
        name.includes('tv') || name.includes('dishwasher')) {
      return 'important';
    }

    return 'nice_to_have';
  }

  /**
   * Placeholder methods for room extraction (to be implemented)
   */
  private extractJsonLdRooms(html: string): PropertyRoom[] {
    // Implementation for JSON-LD room extraction
    return [];
  }

  private extractRoomsFromDescription(html: string): PropertyRoom[] {
    // Implementation for description-based room extraction
    return [];
  }

  private extractRoomsFromSections(html: string): PropertyRoom[] {
    // Implementation for section-based room extraction
    return [];
  }

  private deduplicateRooms(rooms: PropertyRoom[]): PropertyRoom[] {
    // Implementation for room deduplication
    return rooms;
  }

  private enhanceRoomsWithDimensions(rooms: PropertyRoom[], html: string): PropertyRoom[] {
    // Implementation for dimension extraction
    return rooms;
  }

  private async extractEnhancedSpecifications(html: string, amenities: PropertyAmenity[], rooms: PropertyRoom[]): Promise<PropertySpecifications> {
    // Enhanced specifications extraction
    return this.getDefaultSpecifications();
  }

  private async extractDetailedLocation(html: string): Promise<PropertyLocation> {
    // Detailed location extraction
    return this.getDefaultLocation();
  }

  private calculateHiddenDataCount(html: string, amenities: PropertyAmenity[], descriptions: any): number {
    // Calculate how much hidden data was revealed
    return 0;
  }

  private calculateComprehensiveDataCompleteness(result: DataExtractionResult): number {
    let score = 0;

    // Amenities score (30%)
    if (result.amenities.length > 0) {
      score += Math.min((result.amenities.length / 20) * 30, 30);
    }

    // Descriptions score (25%)
    if (result.descriptions.main) score += 15;
    if (result.descriptions.highlights.length > 0) score += 5;
    if (Object.keys(result.descriptions.structured).length > 0) score += 5;

    // Rooms score (25%)
    if (result.rooms.length > 0) {
      score += Math.min((result.rooms.length / 5) * 25, 25);
    }

    // Specifications score (10%)
    if (result.specifications.bedrooms > 0) score += 3;
    if (result.specifications.bathrooms > 0) score += 3;
    if (result.specifications.maxGuests > 0) score += 4;

    // Location score (10%)
    if (result.location.city && result.location.city !== 'Unknown') score += 5;
    if (result.location.state && result.location.state !== 'Unknown') score += 3;
    if (result.location.address) score += 2;

    return Math.round(Math.min(score, 100));
  }
}

