// VRBO Gallery Automation - Handles Dynamic Photo Loading
// Implements the specific click + scroll sequence needed for VRBO image extraction

import { Page } from 'puppeteer';
import { logger } from '../../utils/logger';
import { aiDecisionLogger } from '../ai/decision-logger';
import { BrowserManager } from './browser-manager';
import type { PhotoData } from './types';

export interface GalleryAutomationConfig {
  scrollCycles: number;
  scrollWaitTime: number;
  clickWaitTime: number;
  loadWaitTime: number;
  maxWaitForImages: number;
  enableScreenshots: boolean;
  screenshotPath?: string;
}

export interface GalleryLoadingResult {
  images: PhotoData[];
  totalImagesFound: number;
  scrollCyclesCompleted: number;
  loadingTime: number;
  screenshots: string[];
  errors: string[];
}

export class VRBOGalleryAutomation {
  private browserManager: BrowserManager;
  private config: GalleryAutomationConfig;

  private defaultConfig: GalleryAutomationConfig = {
    scrollCycles: 5,
    scrollWaitTime: 3000,
    clickWaitTime: 2000,
    loadWaitTime: 1000,
    maxWaitForImages: 30000,
    enableScreenshots: false,
    screenshotPath: '/tmp/vrbo-screenshots'
  };

  constructor(
    browserManager: BrowserManager, 
    config: Partial<GalleryAutomationConfig> = {}
  ) {
    this.browserManager = browserManager;
    this.config = { ...this.defaultConfig, ...config };
  }

  /**
   * Executes the complete VRBO gallery loading sequence
   * @param page - Puppeteer page instance
   * @param url - VRBO property URL
   * @returns Promise<GalleryLoadingResult>
   */
  async loadAllGalleryImages(page: Page, url: string): Promise<GalleryLoadingResult> {
    const startTime = Date.now();
    const screenshots: string[] = [];
    const errors: string[] = [];

    try {
      await aiDecisionLogger.logSimpleDecision(
        `Starting VRBO gallery automation for ${url}`,
        'gallery_automation',
        'Executing click + scroll sequence to load all property images',
        [url],
        'high'
      );

      // Step 1: Navigate to the property page
      logger.info('Navigating to VRBO property page', { url }, 'GALLERY_AUTOMATION');
      await page.goto(url, { 
        waitUntil: 'networkidle2',
        timeout: this.config.maxWaitForImages 
      });

      // Take initial screenshot
      if (this.config.enableScreenshots) {
        const screenshotPath = await this.takeScreenshot(page, 'initial-load');
        screenshots.push(screenshotPath);
      }

      // Step 2: Find and click the first photo to open gallery
      const galleryOpened = await this.openPhotoGallery(page);
      if (!galleryOpened) {
        errors.push('Failed to open photo gallery');
        return this.createErrorResult(errors, screenshots, startTime);
      }

      // Take screenshot after gallery opens
      if (this.config.enableScreenshots) {
        const screenshotPath = await this.takeScreenshot(page, 'gallery-opened');
        screenshots.push(screenshotPath);
      }

      // Step 3: Execute the scroll sequence to load all images
      const scrollResults = await this.executeScrollSequence(page, screenshots);

      // Step 4: Extract all loaded images
      const images = await this.extractGalleryImages(page);

      const loadingTime = Date.now() - startTime;

      logger.info('Gallery automation completed successfully', {
        url,
        totalImages: images.length,
        scrollCycles: scrollResults.cyclesCompleted,
        loadingTime,
        errorsCount: errors.length
      }, 'GALLERY_AUTOMATION');

      return {
        images,
        totalImagesFound: images.length,
        scrollCyclesCompleted: scrollResults.cyclesCompleted,
        loadingTime,
        screenshots,
        errors
      };

    } catch (error) {
      const errorMessage = error instanceof Error ? error.message : 'Unknown error';
      errors.push(errorMessage);

      logger.error('Gallery automation failed', error, 'GALLERY_AUTOMATION');

      return this.createErrorResult(errors, screenshots, startTime);
    }
  }

  /**
   * Finds and clicks the first photo to open the gallery modal
   * @param page - Puppeteer page instance
   * @returns Promise<boolean> - Success status
   */
  private async openPhotoGallery(page: Page): Promise<boolean> {
    try {
      logger.info('Attempting to open photo gallery', {}, 'GALLERY_AUTOMATION');

      // Wait for images to load
      await page.waitForTimeout(this.config.loadWaitTime);

      // Common selectors for VRBO photo gallery triggers
      const gallerySelectors = [
        'button[data-testid="photo-gallery-button"]',
        'img[data-testid="hero-image"]',
        '.hero-image img',
        '.property-photos img:first-child',
        '.photo-gallery-trigger',
        '.property-image-gallery img:first-child',
        '[data-testid="property-image"] img:first-child',
        '.carousel-item img:first-child',
        '.image-gallery img:first-child'
      ];

      // Try each selector until one works
      for (const selector of gallerySelectors) {
        try {
          const element = await page.$(selector);
          if (element) {
            logger.info('Found gallery trigger element', { selector }, 'GALLERY_AUTOMATION');

            // Scroll element into view
            await page.evaluate((sel) => {
              const elem = document.querySelector(sel);
              if (elem) {
                elem.scrollIntoView({ behavior: 'smooth', block: 'center' });
              }
            }, selector);

            await page.waitForTimeout(500);

            // Human-like click
            await this.browserManager.humanMouseMove(page, 
              await element.evaluate(el => el.getBoundingClientRect().x + el.getBoundingClientRect().width / 2),
              await element.evaluate(el => el.getBoundingClientRect().y + el.getBoundingClientRect().height / 2)
            );

            await element.click();
            await page.waitForTimeout(this.config.clickWaitTime);

            // Check if gallery modal opened
            const galleryOpened = await this.waitForGalleryModal(page);
            if (galleryOpened) {
              logger.info('Gallery modal opened successfully', { selector }, 'GALLERY_AUTOMATION');
              return true;
            }
          }
        } catch (selectorError) {
          // Continue to next selector
          logger.debug('Gallery selector failed', { selector, error: selectorError }, 'GALLERY_AUTOMATION');
        }
      }

      // Alternative approach: click on any large image
      try {
        const largeImages = await page.$$('img');
        for (const img of largeImages) {
          const dimensions = await img.evaluate(el => ({
            width: el.offsetWidth,
            height: el.offsetHeight
          }));

          // Click on images that are likely to be gallery triggers (large images)
          if (dimensions.width > 200 && dimensions.height > 150) {
            await img.click();
            await page.waitForTimeout(this.config.clickWaitTime);

            const galleryOpened = await this.waitForGalleryModal(page);
            if (galleryOpened) {
              logger.info('Gallery opened via large image click', { dimensions }, 'GALLERY_AUTOMATION');
              return true;
            }
          }
        }
      } catch (error) {
        logger.debug('Large image click approach failed', { error }, 'GALLERY_AUTOMATION');
      }

      logger.warn('Could not open gallery modal', {}, 'GALLERY_AUTOMATION');
      return false;

    } catch (error) {
      logger.error('Error opening photo gallery', error, 'GALLERY_AUTOMATION');
      return false;
    }
  }

  /**
   * Waits for the gallery modal to appear
   * @param page - Puppeteer page instance
   * @returns Promise<boolean> - Whether modal opened
   */
  private async waitForGalleryModal(page: Page): Promise<boolean> {
    const modalSelectors = [
      '[data-testid="photo-gallery-modal"]',
      '.photo-gallery-modal',
      '.gallery-modal',
      '.image-gallery-modal',
      '.photo-carousel-modal',
      '.property-photos-modal',
      '[role="dialog"]',
      '.modal-content'
    ];

    try {
      for (const selector of modalSelectors) {
        try {
          await page.waitForSelector(selector, { timeout: 2000 });
          logger.info('Gallery modal detected', { selector }, 'GALLERY_AUTOMATION');
          return true;
        } catch (error) {
          // Continue to next selector
        }
      }

      // Alternative: check for overlay or backdrop
      const hasOverlay = await page.evaluate(() => {
        const overlays = document.querySelectorAll('[class*="overlay"], [class*="backdrop"], [class*="modal"]');
        return overlays.length > 0;
      });

      if (hasOverlay) {
        logger.info('Gallery modal detected via overlay', {}, 'GALLERY_AUTOMATION');
        return true;
      }

      return false;

    } catch (error) {
      logger.debug('Modal detection failed', { error }, 'GALLERY_AUTOMATION');
      return false;
    }
  }

  /**
   * Executes the scroll sequence to load all images
   * @param page - Puppeteer page instance
   * @param screenshots - Array to store screenshot paths
   * @returns Promise<{cyclesCompleted: number}>
   */
  private async executeScrollSequence(page: Page, screenshots: string[]): Promise<{cyclesCompleted: number}> {
    logger.info('Starting scroll sequence', { 
      totalCycles: this.config.scrollCycles,
      waitTime: this.config.scrollWaitTime 
    }, 'GALLERY_AUTOMATION');

    let cyclesCompleted = 0;

    for (let cycle = 1; cycle <= this.config.scrollCycles; cycle++) {
      try {
        logger.debug(`Executing scroll cycle ${cycle}/${this.config.scrollCycles}`, {}, 'GALLERY_AUTOMATION');

        // Count images before scrolling
        const imagesBefore = await this.countVisibleImages(page);

        // Scroll down in the gallery
        await this.browserManager.humanScroll(page, 'down', 400);

        // Wait for images to load
        await page.waitForTimeout(this.config.scrollWaitTime);

        // Count images after scrolling
        const imagesAfter = await this.countVisibleImages(page);

        logger.debug(`Scroll cycle ${cycle} completed`, {
          imagesBefore,
          imagesAfter,
          newImages: imagesAfter - imagesBefore
        }, 'GALLERY_AUTOMATION');

        // Take screenshot after each cycle
        if (this.config.enableScreenshots) {
          const screenshotPath = await this.takeScreenshot(page, `scroll-cycle-${cycle}`);
          screenshots.push(screenshotPath);
        }

        cyclesCompleted++;

        // If no new images loaded, we might have reached the end
        if (imagesAfter === imagesBefore && cycle > 2) {
          logger.info('No new images loaded, stopping scroll sequence', { 
            cycle,
            totalImages: imagesAfter 
          }, 'GALLERY_AUTOMATION');
          break;
        }

      } catch (error) {
        logger.error(`Scroll cycle ${cycle} failed`, error, 'GALLERY_AUTOMATION');
        // Continue with next cycle
      }
    }

    logger.info('Scroll sequence completed', { 
      cyclesCompleted,
      totalCycles: this.config.scrollCycles 
    }, 'GALLERY_AUTOMATION');

    return { cyclesCompleted };
  }

  /**
   * Counts visible images in the gallery
   * @param page - Puppeteer page instance
   * @returns Promise<number>
   */
  private async countVisibleImages(page: Page): Promise<number> {
    return await page.evaluate(() => {
      const images = document.querySelectorAll('img');
      let visibleCount = 0;

      images.forEach(img => {
        const rect = img.getBoundingClientRect();
        if (rect.width > 0 && rect.height > 0 && img.src && img.src.includes('vrbo')) {
          visibleCount++;
        }
      });

      return visibleCount;
    });
  }

  /**
   * Extracts all images from the gallery
   * @param page - Puppeteer page instance
   * @returns Promise<PhotoData[]>
   */
  private async extractGalleryImages(page: Page): Promise<PhotoData[]> {
    logger.info('Extracting gallery images', {}, 'GALLERY_AUTOMATION');

    const images = await page.evaluate(() => {
      const imageElements = document.querySelectorAll('img');
      const extractedImages: any[] = [];

      imageElements.forEach((img, index) => {
        const src = img.src;
        const alt = img.alt || '';
        const rect = img.getBoundingClientRect();

        // Filter for VRBO images that are visible and have reasonable size
        if (src && 
            src.includes('vrbo') && 
            rect.width > 50 && 
            rect.height > 50 &&
            !src.includes('avatar') &&
            !src.includes('icon')) {

          extractedImages.push({
            url: src,
            alt,
            size: {
              width: rect.width,
              height: rect.height
            },
            order: index + 1
          });
        }
      });

      return extractedImages;
    });

    // Convert to PhotoData format and categorize
    const photoData: PhotoData[] = images.map((img, index) => ({
      url: img.url,
      thumbnailUrl: this.generateThumbnailUrl(img.url),
      alt: img.alt,
      category: this.categorizeImageByUrl(img.url),
      room: this.categorizeImageByRoom(img.url, img.alt),
      size: img.size,
      order: index + 1
    }));

    // Remove duplicates
    const uniquePhotos = this.deduplicateImages(photoData);

    logger.info('Gallery image extraction completed', { 
      totalImages: images.length,
      uniqueImages: uniquePhotos.length 
    }, 'GALLERY_AUTOMATION');

    return uniquePhotos;
  }

  /**
   * Takes a screenshot for debugging
   * @param page - Puppeteer page instance
   * @param suffix - Filename suffix
   * @returns Promise<string> - Screenshot path
   */
  private async takeScreenshot(page: Page, suffix: string): Promise<string> {
    try {
      const timestamp = new Date().toISOString().replace(/[:.]/g, '-');
      const filename = `vrbo-gallery-${suffix}-${timestamp}.png`;
      const path = `${this.config.screenshotPath}/${filename}`;

      await page.screenshot({ path, fullPage: true });

      logger.debug('Screenshot taken', { path }, 'GALLERY_AUTOMATION');
      return path;

    } catch (error) {
      logger.error('Failed to take screenshot', error, 'GALLERY_AUTOMATION');
      return '';
    }
  }

  /**
   * Creates an error result object
   * @param errors - Array of error messages
   * @param screenshots - Array of screenshot paths
   * @param startTime - Start time for duration calculation
   * @returns GalleryLoadingResult
   */
  private createErrorResult(errors: string[], screenshots: string[], startTime: number): GalleryLoadingResult {
    return {
      images: [],
      totalImagesFound: 0,
      scrollCyclesCompleted: 0,
      loadingTime: Date.now() - startTime,
      screenshots,
      errors
    };
  }

  /**
   * Utility methods for image processing
   */
  private generateThumbnailUrl(url: string): string {
    // Try to generate thumbnail URL from high-res URL
    if (url.includes('vrbo') || url.includes('homeaway')) {
      return url.replace(/\/\d+\//, '/150/').replace(/\.(jpg|jpeg|png|webp)/i, '_150.$1');
    }
    return url;
  }

  private categorizeImageByUrl(url: string): 'exterior' | 'interior' | 'bedroom' | 'bathroom' | 'kitchen' | 'living_area' | 'outdoor_space' | 'amenity' | 'view' | 'other' {
    const lowerUrl = url.toLowerCase();

    if (lowerUrl.includes('exterior') || lowerUrl.includes('outside')) return 'exterior';
    if (lowerUrl.includes('kitchen')) return 'kitchen';
    if (lowerUrl.includes('bedroom') || lowerUrl.includes('bed')) return 'bedroom';
    if (lowerUrl.includes('bathroom') || lowerUrl.includes('bath')) return 'bathroom';
    if (lowerUrl.includes('living') || lowerUrl.includes('lounge')) return 'living_area';
    if (lowerUrl.includes('pool') || lowerUrl.includes('deck') || lowerUrl.includes('patio')) return 'outdoor_space';
    if (lowerUrl.includes('view') || lowerUrl.includes('scenic')) return 'view';
    if (lowerUrl.includes('amenity')) return 'amenity';

    return 'interior';
  }

  private categorizeImageByRoom(url: string, alt: string): 'bedroom' | 'bathroom' | 'kitchen' | 'living_room' | 'dining_room' | 'office' | 'game_room' | 'balcony' | 'patio' | 'garage' | 'basement' | 'attic' | 'other' | undefined {
    const lowerUrl = url.toLowerCase();
    const lowerAlt = alt.toLowerCase();

    if (lowerUrl.includes('kitchen') || lowerAlt.includes('kitchen')) return 'kitchen';
    if (lowerUrl.includes('bedroom') || lowerAlt.includes('bedroom')) return 'bedroom';
    if (lowerUrl.includes('bathroom') || lowerAlt.includes('bathroom')) return 'bathroom';
    if (lowerUrl.includes('living') || lowerAlt.includes('living')) return 'living_room';
    if (lowerUrl.includes('dining') || lowerAlt.includes('dining')) return 'dining_room';
    if (lowerUrl.includes('office') || lowerAlt.includes('office')) return 'office';
    if (lowerUrl.includes('game') || lowerAlt.includes('game')) return 'game_room';
    if (lowerUrl.includes('balcony') || lowerAlt.includes('balcony')) return 'balcony';
    if (lowerUrl.includes('patio') || lowerAlt.includes('patio')) return 'patio';
    if (lowerUrl.includes('garage') || lowerAlt.includes('garage')) return 'garage';

    return undefined;
  }

  private deduplicateImages(images: PhotoData[]): PhotoData[] {
    const seen = new Set<string>();
    const unique: PhotoData[] = [];

    images.forEach(img => {
      const normalizedUrl = img.url.replace(/\?.*$/, '').toLowerCase();

      if (!seen.has(normalizedUrl)) {
        seen.add(normalizedUrl);
        unique.push(img);
      }
    });

    return unique;
  }
}

// VRBO Image Scraper - Phase 2: Advanced Image Extraction
// Handles lazy loading, progressive loading, and comprehensive image extraction

import { ProductionVRBOScraper } from './production-vrbo-scraper';
import { aiDecisionLogger } from '../ai/decision-logger';
import { logger } from '../../utils/logger';
import type { PhotoData, ScrapingResult, ScrapingError, ScrapingMetadata, PhotoCategory, RoomType } from './types';

interface ImageExtractionOptions {
  includeThumbnails: boolean;
  includeHighRes: boolean;
  expandGalleries: boolean;
  deduplicateImages: boolean;
  maxImages: number;
  roomCategorization: boolean;
}

interface ImageDiscoveryResult {
  staticImages: string[];
  lazyImages: string[];
  galleryImages: string[];
  thumbnails: string[];
  highResImages: string[];
  metadata: Record<string, any>;
}

interface ImageProcessingResult {
  processedImages: PhotoData[];
  duplicatesRemoved: number;
  categorizedImages: Record<PhotoCategory, PhotoData[]>;
  roomImages: Record<RoomType, PhotoData[]>;
  extractionStats: {
    totalFound: number;
    totalProcessed: number;
    highResCount: number;
    thumbnailCount: number;
    categorizedCount: number;
  };
}

export class VRBOImageScraper extends ProductionVRBOScraper {
  private defaultImageOptions: ImageExtractionOptions = {
    includeThumbnails: true,
    includeHighRes: true,
    expandGalleries: true,
    deduplicateImages: true,
    maxImages: 100,
    roomCategorization: true
  };

  /**
   * Comprehensive image extraction from VRBO property page
   * @param url - VRBO property URL
   * @param options - Image extraction options
   * @returns Promise<ScrapingResult<PhotoData[]>>
   */
  async scrapeAllImages(url: string, options: Partial<ImageExtractionOptions> = {}): Promise<ScrapingResult<PhotoData[]>> {
    const finalOptions = { ...this.defaultImageOptions, ...options };
    const startTime = Date.now();

    const metadata: ScrapingMetadata = {
      scrapedAt: new Date(),
      duration: 0,
      sourceUrl: url,
      userAgent: this.httpClient.defaults.headers['User-Agent'] as string,
      rateLimited: false,
      dataCompleteness: 0,
      fieldsScraped: [],
      fieldsFailed: []
    };

    try {
      // Log image scraping start
      await aiDecisionLogger.logSimpleDecision(
        `Starting comprehensive VRBO image scraping: ${url}`,
        'image_scraping',
        `Extracting images with options: ${JSON.stringify(finalOptions)}`,
        [url],
        'high'
      );

      // Validate URL
      if (!this.isValidVRBOUrl(url)) {
        throw new Error('Invalid VRBO URL provided');
      }

      // Fetch the main property page
      const html = await this.fetchMainPropertyPage(url);

      // Discover all images on the page
      const imageDiscovery = await this.discoverAllImages(html, finalOptions);

      // Process and categorize images
      const imageProcessing = await this.processDiscoveredImages(imageDiscovery, finalOptions);

      // Calculate metadata
      metadata.duration = Date.now() - startTime;
      metadata.dataCompleteness = this.calculateImageDataCompleteness(imageProcessing);
      metadata.fieldsScraped = ['images', 'thumbnails', 'categories', 'rooms'];

      logger.info('VRBO image scraping completed successfully', {
        totalImages: imageProcessing.extractionStats.totalProcessed,
        highResCount: imageProcessing.extractionStats.highResCount,
        categorizedCount: imageProcessing.extractionStats.categorizedCount,
        duration: metadata.duration
      }, 'VRBO_IMAGE_SCRAPER');

      return {
        success: true,
        data: imageProcessing.processedImages,
        errors: [],
        metadata
      };

    } catch (error) {
      const scrapingError: ScrapingError = {
        code: 'IMAGE_SCRAPING_FAILED',
        message: error instanceof Error ? error.message : 'Unknown image scraping error',
        severity: 'high',
        recoverable: true
      };

      metadata.duration = Date.now() - startTime;
      metadata.fieldsFailed = ['images'];

      logger.error('VRBO image scraping failed', error, 'VRBO_IMAGE_SCRAPER');

      return {
        success: false,
        errors: [scrapingError],
        metadata
      };
    }
  }

  /**
   * Discovers all images on the page using multiple strategies
   * @param html - HTML content
   * @param options - Extraction options
   * @returns Promise<ImageDiscoveryResult>
   */
  private async discoverAllImages(html: string, options: ImageExtractionOptions): Promise<ImageDiscoveryResult> {
    const discovery: ImageDiscoveryResult = {
      staticImages: [],
      lazyImages: [],
      galleryImages: [],
      thumbnails: [],
      highResImages: [],
      metadata: {}
    };

    // Extract static images from img tags
    discovery.staticImages = this.extractStaticImages(html);

    // Extract lazy-loaded images
    discovery.lazyImages = this.extractLazyImages(html);

    // Extract gallery images from JavaScript/JSON
    discovery.galleryImages = this.extractGalleryImages(html);

    // Separate thumbnails and high-resolution images
    const { thumbnails, highRes } = this.categorizeImagesByResolution([
      ...discovery.staticImages,
      ...discovery.lazyImages,
      ...discovery.galleryImages
    ]);

    discovery.thumbnails = thumbnails;
    discovery.highResImages = highRes;

    // Extract metadata about image sections
    discovery.metadata = this.extractImageMetadata(html);

    return discovery;
  }

  /**
   * Extracts static images from standard img tags
   * @param html - HTML content
   * @returns string[] - Array of image URLs
   */
  private extractStaticImages(html: string): string[] {
    const images: string[] = [];

    // Match img tags with src attributes
    const imgRegex = /<img[^>]*src=["']([^"']+)["'][^>]*>/gi;
    let match;

    while ((match = imgRegex.exec(html)) !== null) {
      const imageUrl = match[1];
      if (this.isValidImageUrl(imageUrl)) {
        images.push(this.normalizeImageUrl(imageUrl));
      }
    }

    return images;
  }

  /**
   * Extracts lazy-loaded images from various lazy loading patterns
   * @param html - HTML content
   * @returns string[] - Array of image URLs
   */
  private extractLazyImages(html: string): string[] {
    const images: string[] = [];

    // Common lazy loading patterns
    const lazyPatterns = [
      // data-src attribute
      /<img[^>]*data-src=["']([^"']+)["'][^>]*>/gi,
      // data-lazy-src attribute
      /<img[^>]*data-lazy-src=["']([^"']+)["'][^>]*>/gi,
      // data-original attribute
      /<img[^>]*data-original=["']([^"']+)["'][^>]*>/gi,
      // srcset attribute
      /<img[^>]*srcset=["']([^"']+)["'][^>]*>/gi,
      // background-image in style attributes
      /style=["'][^"']*background-image:\s*url\(["']?([^"')]+)["']?\)/gi
    ];

    lazyPatterns.forEach(pattern => {
      let match;
      while ((match = pattern.exec(html)) !== null) {
        const imageUrl = match[1];
        if (this.isValidImageUrl(imageUrl)) {
          images.push(this.normalizeImageUrl(imageUrl));
        }
      }
    });

    return images;
  }

  /**
   * Extracts gallery images from JavaScript objects and JSON-LD
   * @param html - HTML content
   * @returns string[] - Array of image URLs
   */
  private extractGalleryImages(html: string): string[] {
    const images: string[] = [];

    // Extract from JavaScript variables
    const jsImagePatterns = [
      // Common JS image array patterns
      /images?\s*[:=]\s*\[([^\]]+)\]/gi,
      /photos?\s*[:=]\s*\[([^\]]+)\]/gi,
      /gallery\s*[:=]\s*\[([^\]]+)\]/gi,
      // JSON-LD images
      /"image"\s*:\s*\[([^\]]+)\]/gi,
      /"photo"\s*:\s*\[([^\]]+)\]/gi
    ];

    jsImagePatterns.forEach(pattern => {
      let match;
      while ((match = pattern.exec(html)) !== null) {
        const imageArray = match[1];
        const urlMatches = imageArray.match(/["']([^"']*\.(jpg|jpeg|png|webp|gif))["']/gi);

        if (urlMatches) {
          urlMatches.forEach(urlMatch => {
            const imageUrl = urlMatch.replace(/["']/g, '');
            if (this.isValidImageUrl(imageUrl)) {
              images.push(this.normalizeImageUrl(imageUrl));
            }
          });
        }
      }
    });

    // Extract from JSON-LD structured data
    const jsonLdImages = this.extractJsonLdImages(html);
    images.push(...jsonLdImages);

    return images;
  }

  /**
   * Extracts images from JSON-LD structured data
   * @param html - HTML content
   * @returns string[] - Array of image URLs
   */
  private extractJsonLdImages(html: string): string[] {
    const images: string[] = [];

    try {
      const jsonLdRegex = /<script[^>]*type=["']application\/ld\+json["'][^>]*>(.*?)<\/script>/gis;
      const matches = html.match(jsonLdRegex);

      if (matches) {
        matches.forEach(match => {
          try {
            const jsonContent = match.replace(/<script[^>]*>/i, '').replace(/<\/script>/i, '');
            const data = JSON.parse(jsonContent);

            // Extract images from various JSON-LD properties
            const imageProperties = ['image', 'photo', 'photos', 'images'];

            imageProperties.forEach(prop => {
              if (data[prop]) {
                const imageData = Array.isArray(data[prop]) ? data[prop] : [data[prop]];

                imageData.forEach((img: any) => {
                  let imageUrl: string;

                  if (typeof img === 'string') {
                    imageUrl = img;
                  } else if (img.url) {
                    imageUrl = img.url;
                  } else if (img.contentUrl) {
                    imageUrl = img.contentUrl;
                  } else {
                    return;
                  }

                  if (this.isValidImageUrl(imageUrl)) {
                    images.push(this.normalizeImageUrl(imageUrl));
                  }
                });
              }
            });
          } catch (e) {
            // Continue to next script tag
          }
        });
      }
    } catch (error) {
      logger.warn('Failed to extract JSON-LD images', error, 'VRBO_IMAGE_SCRAPER');
    }

    return images;
  }

  /**
   * Categorizes images by resolution (thumbnails vs high-res)
   * @param imageUrls - Array of image URLs
   * @returns Object with thumbnails and highRes arrays
   */
  private categorizeImagesByResolution(imageUrls: string[]): { thumbnails: string[], highRes: string[] } {
    const thumbnails: string[] = [];
    const highRes: string[] = [];

    imageUrls.forEach(url => {
      // Check for thumbnail indicators in URL
      const thumbnailIndicators = [
        /thumb/i,
        /small/i,
        /preview/i,
        /_s\./i,
        /_t\./i,
        /150x/i,
        /300x/i,
        /w_150/i,
        /w_300/i,
        /c_thumb/i
      ];

      const isThumbnail = thumbnailIndicators.some(indicator => indicator.test(url));

      if (isThumbnail) {
        thumbnails.push(url);
      } else {
        highRes.push(url);
      }
    });

    return { thumbnails, highRes };
  }

  /**
   * Extracts metadata about image sections and galleries
   * @param html - HTML content
   * @returns Object with metadata
   */
  private extractImageMetadata(html: string): Record<string, any> {
    const metadata: Record<string, any> = {};

    // Count gallery sections
    const gallerySelectors = [
      /class=["'][^"']*gallery[^"']*["']/gi,
      /class=["'][^"']*photos[^"']*["']/gi,
      /class=["'][^"']*images[^"']*["']/gi,
      /id=["'][^"']*gallery[^"']*["']/gi
    ];

    gallerySelectors.forEach((selector, index) => {
      const matches = html.match(selector);
      if (matches) {
        metadata[`gallerySection${index}`] = matches.length;
      }
    });

    // Extract alt text patterns for room detection
    const altTextRegex = /alt=["']([^"']+)["']/gi;
    const altTexts: string[] = [];
    let match;

    while ((match = altTextRegex.exec(html)) !== null) {
      altTexts.push(match[1]);
    }

    metadata.altTexts = altTexts;

    return metadata;
  }

  /**
   * Processes discovered images into structured PhotoData objects
   * @param discovery - Image discovery result
   * @param options - Processing options
   * @returns Promise<ImageProcessingResult>
   */
  private async processDiscoveredImages(discovery: ImageDiscoveryResult, options: ImageExtractionOptions): Promise<ImageProcessingResult> {
    const allImages = [
      ...discovery.staticImages,
      ...discovery.lazyImages,
      ...discovery.galleryImages
    ];

    // Deduplicate images
    const uniqueImages = options.deduplicateImages ? this.deduplicateImages(allImages) : allImages;

    // Limit number of images
    const limitedImages = uniqueImages.slice(0, options.maxImages);

    // Convert to PhotoData objects
    const photoData: PhotoData[] = limitedImages.map((url, index) => {
      const isHighRes = discovery.highResImages.includes(url);
      const isThumbnail = discovery.thumbnails.includes(url);

      return {
        url,
        thumbnailUrl: isThumbnail ? url : this.generateThumbnailUrl(url),
        alt: this.extractAltTextForImage(url, discovery.metadata.altTexts || []),
        category: this.categorizeImageByUrl(url),
        room: this.categorizeImageByRoom(url, discovery.metadata.altTexts || []),
        size: isHighRes ? { width: 1200, height: 800 } : { width: 400, height: 300 },
        order: index + 1
      };
    });

    // Categorize images
    const categorizedImages = this.categorizeImagesByType(photoData);
    const roomImages = this.categorizeImagesByRoom(photoData);

    return {
      processedImages: photoData,
      duplicatesRemoved: allImages.length - uniqueImages.length,
      categorizedImages,
      roomImages,
      extractionStats: {
        totalFound: allImages.length,
        totalProcessed: photoData.length,
        highResCount: discovery.highResImages.length,
        thumbnailCount: discovery.thumbnails.length,
        categorizedCount: Object.values(categorizedImages).flat().length
      }
    };
  }

  /**
   * Utility methods for image processing
   */
  private isValidImageUrl(url: string): boolean {
    if (!url || typeof url !== 'string') return false;

    // Check for valid image extensions
    const imageExtensions = /\.(jpg|jpeg|png|webp|gif|svg)(\?.*)?$/i;

    // Check for valid URL format
    const urlPattern = /^https?:\/\//i;

    return imageExtensions.test(url) && (urlPattern.test(url) || url.startsWith('//'));
  }

  private normalizeImageUrl(url: string): string {
    // Handle protocol-relative URLs
    if (url.startsWith('//')) {
      return `https:${url}`;
    }

    // Handle relative URLs (if any)
    if (url.startsWith('/')) {
      return `https://www.vrbo.com${url}`;
    }

    return url;
  }

  private deduplicateImages(images: string[]): string[] {
    const seen = new Set<string>();
    const unique: string[] = [];

    images.forEach(url => {
      // Normalize URL for comparison
      const normalizedUrl = url.replace(/\?.*$/, '').toLowerCase();

      if (!seen.has(normalizedUrl)) {
        seen.add(normalizedUrl);
        unique.push(url);
      }
    });

    return unique;
  }

  private categorizeImageByUrl(url: string): PhotoCategory {
    const lowerUrl = url.toLowerCase();

    if (lowerUrl.includes('exterior') || lowerUrl.includes('outside')) return 'exterior';
    if (lowerUrl.includes('kitchen')) return 'kitchen';
    if (lowerUrl.includes('bedroom') || lowerUrl.includes('bed')) return 'bedroom';
    if (lowerUrl.includes('bathroom') || lowerUrl.includes('bath')) return 'bathroom';
    if (lowerUrl.includes('living') || lowerUrl.includes('lounge')) return 'living_area';
    if (lowerUrl.includes('pool') || lowerUrl.includes('deck') || lowerUrl.includes('patio')) return 'outdoor_space';
    if (lowerUrl.includes('view') || lowerUrl.includes('scenic')) return 'view';
    if (lowerUrl.includes('amenity')) return 'amenity';

    return 'interior';
  }

  private categorizeImageByRoom(url: string, altTexts: string[]): RoomType | undefined {
    const lowerUrl = url.toLowerCase();

    if (lowerUrl.includes('kitchen')) return 'kitchen';
    if (lowerUrl.includes('bedroom') || lowerUrl.includes('bed')) return 'bedroom';
    if (lowerUrl.includes('bathroom') || lowerUrl.includes('bath')) return 'bathroom';
    if (lowerUrl.includes('living') || lowerUrl.includes('lounge')) return 'living_room';
    if (lowerUrl.includes('dining')) return 'dining_room';
    if (lowerUrl.includes('office') || lowerUrl.includes('study')) return 'office';
    if (lowerUrl.includes('game') || lowerUrl.includes('rec')) return 'game_room';
    if (lowerUrl.includes('balcony')) return 'balcony';
    if (lowerUrl.includes('patio') || lowerUrl.includes('deck')) return 'patio';
    if (lowerUrl.includes('garage')) return 'garage';

    return undefined;
  }

  private extractAltTextForImage(url: string, altTexts: string[]): string {
    // Try to find relevant alt text for this image
    const urlKeywords = url.toLowerCase().split(/[\/\-_\.]/);

    for (const altText of altTexts) {
      const altKeywords = altText.toLowerCase().split(/\s+/);
      const commonKeywords = urlKeywords.filter(keyword => 
        altKeywords.some(altKeyword => altKeyword.includes(keyword) || keyword.includes(altKeyword))
      );

      if (commonKeywords.length > 0) {
        return altText;
      }
    }

    return '';
  }

  private generateThumbnailUrl(url: string): string {
    // Try to generate thumbnail URL from high-res URL
    // This is VRBO-specific logic
    if (url.includes('vrbo') || url.includes('homeaway')) {
      return url.replace(/\/\d+\//, '/150/').replace(/\.(jpg|jpeg|png|webp)/i, '_150.$1');
    }

    return url;
  }

  private categorizeImagesByType(photos: PhotoData[]): Record<PhotoCategory, PhotoData[]> {
    const categorized: Record<PhotoCategory, PhotoData[]> = {
      exterior: [],
      interior: [],
      bedroom: [],
      bathroom: [],
      kitchen: [],
      living_area: [],
      outdoor_space: [],
      amenity: [],
      view: [],
      other: []
    };

    photos.forEach(photo => {
      categorized[photo.category].push(photo);
    });

    return categorized;
  }

  private categorizeImagesByRoom(photos: PhotoData[]): Record<RoomType, PhotoData[]> {
    const categorized: Record<RoomType, PhotoData[]> = {
      bedroom: [],
      bathroom: [],
      kitchen: [],
      living_room: [],
      dining_room: [],
      office: [],
      game_room: [],
      balcony: [],
      patio: [],
      garage: [],
      basement: [],
      attic: [],
      other: []
    };

    photos.forEach(photo => {
      if (photo.room) {
        categorized[photo.room].push(photo);
      }
    });

    return categorized;
  }

  private calculateImageDataCompleteness(processing: ImageProcessingResult): number {
    const stats = processing.extractionStats;

    // Base score for finding images
    let score = Math.min((stats.totalProcessed / 20) * 50, 50); // Up to 50% for having images

    // Bonus for high-res images
    if (stats.highResCount > 0) {
      score += Math.min((stats.highResCount / 10) * 20, 20); // Up to 20% for high-res
    }

    // Bonus for categorized images
    if (stats.categorizedCount > 0) {
      score += Math.min((stats.categorizedCount / stats.totalProcessed) * 20, 20); // Up to 20% for categorization
    }

    // Bonus for variety (different room types)
    const roomTypes = Object.values(processing.roomImages).filter(arr => arr.length > 0).length;
    score += Math.min(roomTypes * 2, 10); // Up to 10% for room variety

    return Math.round(Math.min(score, 100));
  }
}

