// Types
interface InspectionStep {
  id: string;
  title: string;
  description: string;
  status: 'pending' | 'in_progress' | 'completed' | 'error';
  required: boolean;
  data?: unknown;
}

interface Property {
  id: string;
  address: string;
  type: string;
  bedrooms: number;
  bathrooms: number;
  sqft: number;
  listingUrl?: string;
  images?: string[];
}

interface ChecklistData {
  items: DynamicChecklistItem[];
  estimatedTime: number;
  totalItems: number;
}

